INSERT INTO `quest_template_locale` VALUES (42213, 'deDE', 'Das Auge Azsharas: Golganneths Gezeitenstein', 'Verwendet den zentralen Teleporter in Dalaran und platziert Golganneths Gezeitenstein dann in in der Porträtgalerie der Kammer des Wächters.', '<Nachdem Ihr Königin Azsharas Zorn nun besiegt habt und den Gezeitenstein in Händen haltet, ist es an der Zeit, ihn in der Porträtgalerie der Kammer des Wächters in Dalaran zu platzieren.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42213, 'esES', 'Ojo de Azshara: La Piedramar de Golganneth', 'Usa el teletransportador central de Dalaran y coloca la Piedramar de Golganneth en la Sala de los Retratos de la Cámara del Guardián.', '<Ahora que has derrotado a la Cólera de la reina Azshara y tienes la Piedramar, ha llegado el momento de colocarla en la Sala de los Retratos de la Cámara del Guardián, en la zona inferior de Dalaran.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42213, 'esMX', 'Ojo de Azshara: La Maretista de Golganneth', 'Utiliza el teletransportador central de Dalaran y luego coloca la Maretista de Golganneth en la Sala de retratos de la Cámara del guardián.', '<Ahora que acabaste con la ira de la Reina Azshara y tomaste posesión de la Maretista, es hora de colocarla en la Sala de retratos de la Cámara del guardián en la parte baja de Dalaran.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42213, 'frFR', 'L’Œil d’Azshara : la Pierre-des-Marées de Golganneth', 'Utilisez le téléporteur central de Dalaran et déposez la Pierre-des-Marées de Golganneth dans la Salle des portraits de la Chambre du Gardien.', '<À présent que vous avez triomphé du courroux de la reine Azshara et que la Pierre-des-Marées est en votre possession, le moment est venu de la déposer dans la Salle des portraits de la chambre du Gardien à Dalaran.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42213, 'itIT', 'Occhio di Azshara: La Pietra delle Maree di Golganneth', 'Usa il teletrasporto centrale di Dalaran e posiziona la Pietra delle Maree di Golganneth presso la Sala dei Pilastri nella Torre del Guardiano.', '<Ora che hai sconfitto l\'Ira della Regina Azshara e hai la Pietra delle Maree in pugno, è ora di metterla al sicuro presso la Sala dei Pilastri, nella Torre del Guardiano di Dalaran.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42213, 'koKR', '아즈샤라의 눈: 골가네스의 해일석', '달라란 중앙의 순간이동기를 사용한 후, 골가네스의 해일석을 수호자의 방에 있는 초상화실에 보관해야 합니다.', '<여왕 아즈샤라의 분노를 처치하고 해일석을 손에 넣었습니다. 이제 달라란 하층, 수호자의 방에 있는 초상화실에 보관해야 합니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42213, 'ptBR', 'Olho de Azshara: a Pedramar de Golganneth', 'Usar o teleportador central em Dalaran e acrescentar as Pedramar de Golganneth à Sala dos Retratos da Câmara do Guardião.', '<Agora que você derrotou a Ira da Rainha Azshara e tem em mãos a Pedramar, é chegada a hora de colocá-la na Sala dos Retratos da Câmara do Guardião na parte baixa de Dalaran.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42213, 'ruRU', 'Око Азшары: Приливный Камень Голганнета', 'Пройдите через центральный телепорт Даларана и разместите Приливный Камень Голганнета в портретной галерее Палаты Хранителя.', '<Вы одолели Гнев королевы Азшары и вернули Приливный Камень. Пришла пора установить его в Портретной галерее Палаты Хранителя, что в подземельях Даларана.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42213, 'zhCN', '艾萨拉之眼：高戈奈斯潮汐之石', '使用达拉然的中央传送器，把高戈奈斯潮汐之石放到守护者大厅的画像室里。', '<你已经击败艾萨拉之怒并取得潮汐之石，是时候把它放到达拉然地下守护者大厅的画像室里了。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42213, 'zhTW', '艾薩拉之眼：葛剛尼斯潮石', '使用位於達拉然中央的傳送門，將葛剛尼斯潮石放在守護者之廳的肖像廳。', '<你已擊敗艾薩拉女王之怒，成功取得潮石。你應該前往達拉然底層，將它放到守護者之廳的肖像廳。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42214, 'deDE', 'Geschlechtsreife', 'Sammelt $1oa Salzaugenarmreife.', 'Da Ihr gerade so auf Murlocs konzentriert seid...$b$bViele Murlocs sind dafür bekannt, Schmuck zu tragen, um potenzielle Geschlechtspartner zu beeindrucken. Ich wette, dass wir ein paar von diesen Schmuckstücken verwenden können.$b$bSammelt einige davon ein und bringt sie mir. Je nasser, desto besser!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42214, 'esES', 'Aro con descaro', 'Reúne $1oa broches Ojosal.', 'Ya que te has centrado en los múrlocs...$b$bSe sabe que muchos múrlocs llevan joyas para atraer pareja. Me apuesto lo que sea a que podemos usar las mismas joyas que ellos.$b$bReúne varias de sus joyas y tráemelas. ¡Cuanto más húmedas, mejor!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42214, 'esMX', 'Atrapado desprevenido', 'Reúne $1oa brazaletes ojosalados.', 'Hablando de múrlocs...$b$bMuchos son conocidos por usar joyas para atraer a una pareja. Apuesto que podemos usar las mismas joyas que ellos.$b$bReúne algunas de sus joyas y tráemelas. ¡Entre más mojadas, mejor!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42214, 'frFR', 'Tout ce qui brille n’est pas or', 'Récupérez $1oa Bracelets sel-œil', 'Puisque vous vous intéressez aux Murlocs…$b$bNombre d’entre eux ont pour habitude de porter des bijoux afin de plaire au sexe opposé. Et je parie que certaines de ces babioles pourraient nous être utiles.$b$bAllez en chercher et rapportez-les-moi. Plus elles seront humides, mieux ce sera.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42214, 'itIT', 'Gioielli dei Murloc', 'Recupera $1oa Braccialetti degli Occhiosalino.', 'Già che sei concentrato sui Murloc...$b$bMolti di essi indossano gioielli che usano per attirare i loro simili. Scommetto che possiamo usare esattamente gli stessi oggetti.$b$bRecupera un po\' dei loro gioielli e portali da me. Più sono bagnati, meglio è!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42214, 'koKR', '헉!', '소금눈 팔찌 $1oa개를 모아야 합니다.', '당신은 멀록 자체에만 신경을 썼지만...$b$b사실 멀록은 일반적으로 몸을 장신구로 치장하여 이성을 유혹한다고 알려졌답니다. 그러니까 우리도 멀록이 차는 장신구를 이용하면 좋을 것 같습니다.$b$b멀록이 차고 다니는 장신구를 좀 가져다주십시오. 당연히 축축할수록 더 좋습니다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42214, 'ptBR', 'De boca aberta', 'Obtenha $1oa Braceletes de Olho-de-Sal.', 'Enquanto você está concentrad$Go:a; nos murlocs...$b$bSabe-se que muitos murlocs usam joias para atrair um parceiro. Aposto que podemos usar algumas das joias com que eles se enfeitam.$b$bRecolha algumas das joias dos murlocs e traga-as para mim. Quanto mais molhadas, melhor!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42214, 'ruRU', 'Убью за блестяшки', 'Соберите $1oa |4безделушку:безделушки:безделушек; племени Соленого Глаза.', 'Кстати, насчет мурлоков... $b$bМногие мурлоки в брачный период напяливают на себя всякие украшения, чтобы казаться привлекательнее. Наверняка мы сможем найти применения камням, из которых они делают свои украшения.$b$bРаздобудь несколько мурлочьих ювелирных украшений и принеси их мне. Мокрые, грязные – ничего страшного, сгодятся любые!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42214, 'zhCN', '强夺手镯', '收集$1oa个盐目手镯。', '既然你这么关注鱼人……$b$b许多鱼人会佩戴珠宝来吸引配偶。我敢打赌，他们的珠宝会让我们派上用场。$b$b帮我收集一些他们的珠宝。越是湿漉漉的就越好！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42214, 'zhTW', '為一枚戒環敲打', '蒐集$1oa個鹽目手鐲。', '正好你現在把注意力放在魚人身上…$b$b許多魚人都喜歡穿戴珠寶吸引配偶。我敢打賭他們有些珠寶正好讓我們派上用場。$b$b蒐集一些他們的珠寶，然後帶來給我。越濕黏越好！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42215, 'deDE', 'DEPRECATED: Singed Letter', 'Bringt 3 silbrige Salben und 1 Feldverbandskasten zu dem Ort, der auf Eurer Karte markiert ist. Behandelt den teufelsverbrannten Späher, wenn Ihr dort eintrefft.', 'Ich bete, dass dieser Brief in die richtigen Hände gerät. Bei meinem Glück wird er wohl im Magen irgendeiner Kreatur landen.$b$bWer das liest, möge mir bitte etwas für meine Verbrennungen mitbringen. Sie tun wirklich weh. Sehr.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42215, 'esES', 'DEPRECATED: Singed Letter', 'Lleva 3 bálsamos de plata y 1 mochila de campo a la ubicación señalada en tu mapa. Atiende al explorador quemado por energía vil cuando llegues.', 'Rezo para que esta carta llegue a las manos adecuadas. Conociendo mi suerte, seguramente acabará en el estómago de alguna criatura.$b$bSi lees esto, trae algo para tratarme las quemaduras, por favor. Duelen mucho. Muchísimo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42215, 'esMX', 'DEPRECATED: Singed Letter', 'Lleva 3 viales de bálsamo plateado y 1 mochila de suministros a la ubicación marcada en tu mapa. Atiende al explorador quemado en cuanto llegues.', 'Ruego que esta nota llegue a las manos indicadas. Conociendo mi suerte, lo más probable es que acabe en el estómago de alguna criatura.$b$bSi lees esto, por favor trae algo para las quemaduras. Duelen mucho. Muchísimo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42215, 'frFR', 'DEPRECATED: Singed Letter', 'Apportez 3 doses de Baume argenté et 1 Paquetage à l’endroit indiqué sur votre carte. Une fois sur place, soignez l’Éclaireur brûlé par la magie gangrenée.', 'Je prie pour que cette lettre tombe entre de bonnes mains. Avec la chance que j’ai, elle va probablement finir dans l’estomac d’une créature.$b$bSi vous lisez ceci, veuillez apporter de quoi soigner mes brûlures. Elles me font atrocement souffrir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42215, 'itIT', 'DEPRECATED: Singed Letter', 'Porta 3 Unguenti Argentati e 1 Fornitura da Campo sul luogo indicato sulla mappa. Quando sarai sul posto, cura l\'Esploratore Vilustionato.', 'Prego che questa lettera arrivi nelle mani giuste. Conoscendo la mia fortuna, probabilmente finirà nella pancia di qualche creatura.$b$bSe stai leggendo questa lettera, allora ti prego di portarmi qualcosa per curare le mie ustioni. Fanno davvero male. Molto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42215, 'koKR', '그을린 편지', '은빛 연고 3개와 야전 구급품 1개를 지도에 표시된 위치로 가져가서, 지옥화상 입은 정찰병을 치료해야 합니다.', '이 편지가 선한 이의 손에 들어가길... 지금까지의 내 운을 보면 어떤 괴물의 뱃속에 들어갈 가능성이 더 크지만.$b$b혹시 이걸 읽고 계시다면 제발 약을 가져와서 제 화상을 치료해 주세요. 정말 아픕니다. 아파서 죽을 것 같습니다...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42215, 'ptBR', 'DEPRECATED: Singed Letter', 'Leve 3 Bálsamos Prateados e 1 Mochila de Campo para o local marcado no seu mapa. Trate o Batedor com Queimadura Vil quando chegar.', 'Rezo para que esta carta chegue às mãos certas. Conhecendo minha sorte, provavelmente vai acabar no estômago de alguma criatura.$b$bSe você estiver lendo isto, então por favor traga alguma coisa para tratar minhas queimaduras. Elas realmente doem. Muito.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42215, 'ruRU', 'DEPRECATED: Singed Letter', 'Принесите 3 флакона серебристого бальзама и 1 полевую аптечку в место, отмеченное на вашей карте. Обработайте раны обожженного скверной разведчика, когда прибудете на место.', 'Надеюсь, это письмо попадет в нужные руки. Но учитывая то, как мне \"везет\", скорее всего его кто-нибудь съест.$b$bЕсли вы читаете это, пожалуйста, принесите что-нибудь, чтобы обработать мои ожоги. Они сильно болят. Очень сильно.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42215, 'zhCN', 'DEPRECATED: Singed Letter', '将3份银色药膏和1个野外急救包带到地图上标记的地点。到达之后，治疗邪灼斥候。', '希望能有合适的人看到这封信。以我这倒霉劲，说不定信就被什么怪物给吃掉了。$b$b如果你看到我的留言，请带一些东西来治疗我的烧伤。真的很痛。很痛。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42215, 'zhTW', 'DEPRECATED: Singed Letter', '將3瓶銀光油膏和1個野戰包送到地圖上標示的地點。抵達後，治療被魔化火焰灼傷的斥候。', '希望這封信能夠送到有能力救我一命的人手中。如果真的非常不幸，這封信很可能會被某種生物吃了。$b$b要是你看到這封信，請帶一些燒傷藥膏過來。傷口真的很痛，超痛。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42220, 'deDE', 'Schiffbrüchige Matrosen', 'Erlöst $1oa Matrosen der Revanche der Königin von ihren Qualen.', '<Die Gilblin und Makrura von Mak\'rana haben die Matrosen der Verlassenen aus dem nahen Schiffswrack gefangen genommen.>\n\n<Es sieht so aus, als würden die Matrosen vergiftet und anderweitig malträtiert werden. Es wäre das Beste, sie von ihren Qualen zu erlösen.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42220, 'esES', 'Marineros naufragados', 'Acaba con el sufrimiento de $1oa marineros de La Represalia de la Reina.', '<Los gilblin y makruras de Mak\'rana han capturado a los marineros del naufragio cercano.>\n\n<Parece que los marineros están siendo envenenados y cosa peores. Será mejor acabar con su sufrimiento.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42220, 'esMX', 'Marineros náufragos', 'Acaba con la miseria de $1oa marineros del Represalia de la Reina.', '<Los gilblin y los makrura de Mak\'rana capturaron a los marineros renegados en un naufragio cercano>.\n\n<Parece que los están envenenando, o algo peor. Será mejor que acabemos con su miseria>.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42220, 'frFR', 'Marins naufragés', 'Abrégez les souffrances de $1oa Marins du Représailles de la reine.', '<Les gloubelins et les makruras de Mak’rana ont capturé les marins réprouvés de l’épave voisine.>\n\n<Apparemment, ces pauvres bougres ont été empoisonnés, et pire encore. Autant abréger leurs souffrances.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42220, 'itIT', 'Marinai naufragati', 'Metti fine alle sofferenze di $1oa Marinai della Rappresaglia della Regina.', '<I Branclin e i Makrura di Mak\'rana hanno catturato i marinai Reietti naufragati qui vicino.>\n\n<Sembra che i marinai siano stati avvelenati. Sarà meglio liberarli da questa sofferenza.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42220, 'koKR', '선원에게 안식을', '여왕의 복수호 선원 $1oa명의 고통을 끝내주어야 합니다.', '<마크라나의 길블린과 마크루라가 근처의 난파선에서 포세이큰 선원을 붙잡았습니다.>\n\n<선원들은 중독되었거나 거의 죽기 직전의 상태로 보입니다. 그들이 더는 고통받지 않게 해주는 게 좋겠습니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42220, 'ptBR', 'Marinheiros náufragos', 'Acabe com o sofrimento de $1oa Marinheiros do Vingança da Rainha.', '<Os guelblins e os makruras de Mak\'rana capturaram os marinheiros abandonados no naufrágio que aconteceu na região.>\n\n<Parece que os marinheiros estão sendo envenenados, ou coisa pior. Melhor acabar com o sofrimento deles.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42220, 'ruRU', 'Моряки-утопленники', 'Избавьте от мучений $1oa |4матроса:матросов:матросов; с разбившейся \"Мести королевы\".', '<Жаблины и макруры Мак\'раны захватили в плен потерпевших кораблекрушение моряков с разбившегося корабля, остов которого находится неподалеку.>\n\n<Похоже, их всех отравили. Спасти никого не удастся, так что лучше будет избавить их от мучений.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42220, 'zhCN', '沉船水手', '为$1oa名女王的复仇号水手终结痛苦。', '<玛拉纳的海地精和龙虾人从附近的沉船处抓走了被遗忘者水手。>\n\n<水手们似乎受到了毒害，或许他们的境况比这更糟。你应该终结他们的痛苦。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42220, 'zhTW', '遇難水手', '幫$1oa名女王復仇號的水手脫離苦難。', '<馬克拉納的吉爾布林以及龍蝦人從附近的沉船處抓走了無依無靠的水手。>\n\n<看來這些水手除了身中劇毒，還會有更糟的下場。還是讓他們從痛苦中解脫吧。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42222, 'deDE', 'Bericht über die Empyreumgesellschaft', 'Kehrt zur Halle des Wächters zurück.', 'Ich kann kaum glauben, dass sich Erzmagier Vargoth mit so einer widerwärtigen Gruppe eingelassen haben soll! Es muss einen guten Grund dafür geben, warum er da war...\n\nAuf jeden Fall sollten wir unverzüglich in die Halle des Wächters zurückkehren und Meryl erzählen, was wir hier herausgefunden haben.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42222, 'esES', 'Informe de la Sociedad Empírea', 'Vuelve al Salón del Guardián.', 'Me cuesta creer que el archimago Vargoth estuviese involucrado con un grupo tan malvado. Tiene que haber una buena explicación que justifique por qué ha estado aquí...\n\nNo obstante, será mejor que regresemos al Salón del Guardián e informemos a Meryl de lo que hemos destapado.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42222, 'esMX', 'Informe de la Sociedad Empírea', 'Regresa a la Sala del Guardián.', '¡Me cuesta creer que el Archimago Vargoth se involucró con un grupo tan malévolo! Debe haber una buena explicación de por qué ha estado aquí... \n\nDe todos modos, será mejor que volvamos a la Sala del Guardián e informemos a Meryl de lo que descubrimos aquí.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42222, 'frFR', 'Rapport sur la Société empyréenne', 'Retournez au Hall du Gardien.', 'J’ai du mal à croire que l’archimage Vargoth puisse entretenir des liens avec un groupe aussi malfaisant ! Il doit bien y avoir une explication à sa présence ici…\n\nQuoi qu’il en soit, nous ferions mieux de retourner dans le hall du Gardien pour informer Meryl de ce que nous avons découvert ici.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42222, 'itIT', 'Rapporto sulla Società Empirea', 'Torna alle Sale dei Guardiani.', 'Trovo assurdo che l\'Arcimago Vargoth sia coinvolto con un gruppo così malvagio! Sono sicuro che abbia avuto un buon motivo per farlo...\n\nIn ogni caso, dobbiamo tornare alle Sale dei Guardiani e informare Meryl di cosa abbiamo scoperto qui.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42222, 'koKR', '창공 협회 관련 보고서', '수호자의 전당으로 가야 합니다.', '대마법사 바르고스 님이 이 사악한 무리와 관련이 있다니, 정말 믿을 수가 없습니다! 분명 뭔가 다른 이유로 이곳에 계셨을 겁니다...\n\n하지만 수호자의 전당으로 돌아가 이곳에서 우리가 알아낸 걸 메릴에게 알려주는 게 올바른 일이겠죠.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42222, 'ptBR', 'Relato da Sociedade Empírea', 'Volte ao Salão do Guardião.', 'Acho difícil acreditar que o Arquimago Vargoth estaria envolvido com um grupo tão maléfico! Deve haver uma boa explicação para ele ter vindo aqui... \n\nDe toda forma, é melhor voltarmos ao Salão do Guardião e informarmos Meryl sobre o que descobrimos aqui.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42222, 'ruRU', 'Отчет о Небесной академии', 'Вернитесь в Оплот Хранителя.', 'Не могу поверить, что верховный маг Варгот мог быть как-то связан с этой группировкой! Его присутствию здесь наверняка найдется объяснение... \n\nТак или иначе, нам следует вернуться в Оплот Хранителя и сообщить Мерилу о том, что мы нашли.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42222, 'zhCN', '苍穹会报告', '返回守护者圣殿。', '大法师瓦格斯竟然跟这种邪教组织有关系，真是难以置信！他最好为来这里的原因给个合理的解释……\n\n不管怎么说，我们最好能回到守护者圣殿，把我们发现的事情告诉梅瑞尔。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42222, 'zhTW', '秘天學會報告', '回到守護者殿堂。', '實在很難相信大法師瓦戈斯會和這種惡毒的團體有所牽連！他最好有個合理解釋，為何會來過此地…\n\n不管怎麼說，我們最好能先回守護者殿堂，把在這裡發現的事情告訴莫爾。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42223, 'deDE', 'Die Erkundung des Purpurdickichts', 'Sucht nach Lilryia Morgenwind im Morgenspalt.', 'Wenn Ihr nach Osten reisen solltet, möchte ich Euch um einen Gefallen bitten.$b$bWährend wir das Lager aufschlugen, hat sich ein Mitglied der Karawane aufgemacht, um die Umgebung der Stadt zu erkunden. Lilryia Morgenwind hätte schon längst wieder da sein sollen.$b$bIch wäre Euch sehr dankbar, wenn Ihr Ausschau nach ihr halten könntet. Sie wollte die Ruinen des Morgenspalts erkunden.', 'Sucht nach Lilryia Morgenwind im Morgenspalt.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42223, 'esES', 'Explorar la Espesura Carmesí', 'Encuentra a Lilryia Brisalborada en la Grieta del Alba.', 'Si te diriges hacia el este, hay algo que me gustaría pedirte.$B$BMientras establecíamos nuestro campamento, algunos miembros de nuestra caravana partieron para inspeccionar los alrededores de la ciudad. Lilryia Brisalborada lleva demasiado tiempo fuera.$B$BTe agradecería muchísimo que comprobaras si está a salvo. Dijo que iría a explorar las ruinas de la Grieta del Alba.', 'Encuentra a Lilryia Brisalborada en la Grieta del Alba.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42223, 'esMX', 'Explorando el zarzal carmesí', 'Encuentra a Lilryia Viento del Alba en Grieta del Alba.', 'Si te diriges al este, hay algo que me gustaría pedirte.$b$bMientras levantábamos campamento, una parte de nuestra caravana salió a explorar los alrededores de la ciudad. Ahora, Lilryia Viento del Alba lleva desaparecida mucho tiempo.$b$bTe agradecería mucho que vayas a comprobar que se encuentra bien. Dijo que iba a explorar las ruinas de Grieta del Alba.', 'Encuentra a Lilryia Viento del Alba en Grieta del Alba.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42223, 'frFR', 'Reconnaissance du fourré Cramoisi', 'Trouvez Lilryia Aubevent à Aubefaille.', 'Si vous allez vers l’est, j’aimerais vous demander une faveur.$b$bAlors que nous étions en train de poser le camp, une partie de notre caravane est allée explorer les environs de la ville. Lilryia Aubevent tarde à revenir.$b$bJ’apprécierais grandement si vous pouviez aller vérifier qu’elle va bien. Elle est partie explorer les ruines d’Aubefaille.', 'Trouver Lilryia Aubevent à Aubefaille', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42223, 'itIT', 'Esplorare il Bosco Cremisi', 'Trova Lilryia Albavento ad Albafaglia.', 'Se ti stai dirigendo a est, c\'è qualcosa che vorrei chiederti.$b$bMentre allestivamo l\'accampamento, una delle nostre carovane è partita all\'esplorazione dei dintorni della città. Lilryia Albavento è via da troppo tempo ormai, ti sarei grato se andassi a cercarla. Ha detto che sarebbe andata a esplorare le rovine di Albafaglia.', 'Trova Lilryia Albavento ad Albafaglia.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42223, 'koKR', '진홍빛 숲 정찰', '진홍빛 숲에 있는 릴리아 돈윈드를 찾아야 합니다.', '동쪽으로 가신다면 부탁할 게 있어요.$b$b저희가 야영지를 철수하는 동안 도시 주변을 정찰할 목적으로 짐마차 몇 대가 무리를 떠났거든요. 그런데 그중 릴리아 돈윈드가 아직도 돌아오지 않았어요.$b$b릴리아가 무사한지 알아봐 주신다면 정말 감사할 거예요. 그녀는 진홍빛 숲의 폐허를 정찰하러 간다고 했어요.', '진홍빛 숲에 있는 릴리아 돈윈드를 찾아야 합니다.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42223, 'ptBR', 'Fazendo o reconhecimento da Mata Carmesim', 'Encontre Lilryia Albavento em Leporalba.', 'Se você estiver indo para leste, tem uma coisa que quero lhe pedir.$b$bEnquanto desmontávamos acampamento, alguns membros da nossa caravana saíram para fazer reconhecimento da área ao redor da cidade. Lilryia Albavento partiu já faz muito tempo.$b$bEu agradeceria muito se você pudesse averiguar se ela está bem. Ela disse que estava indo inspecionar as ruínas de Leporalba.', 'Encontre Lilryia Albavento em Leporalba.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42223, 'ruRU', 'Багровая чаща', 'Найдите Лилрию Ветер Зари в Рассветном ущелье.', 'Если ты направляешься на восток, у меня к тебе просьба.$b$bПока мы разбивали лагерь, некоторые из нашего каравана направились на разведку по окрестностям города. Лилрия Ветер Зари уже как-то долго не возвращается...$b$bЕсли ты поищешь ее, моя благодарность не будет знать границ. Она сказала, что пойдет в руины в Рассветном ущелье.', 'Найдите Лилрию Ветер Зари в Рассветном ущелье.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42223, 'zhCN', '侦察深红林地', '在晨曦裂口寻找莉莉娅·晨风。', '如果你要去东边的话，我想请你帮个忙。$b$b我们拔营的同时，有一部分车队留下来绕城巡视。莉莉娅·晨风出去太久了。$b$b如果你能去看看她的情况，我将感激不尽。她之前说要去侦察晨曦裂口的废墟。', '在晨曦裂口寻找莉莉娅·晨风。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42223, 'zhTW', '偵察緋紅灌木林', '在晨曦斷崖尋找莉爾芮雅‧曦風。', '如果你此行向東，我有一件事要請你幫忙。$b$b當我們拔營時，部份車隊先行離開，在城市四周偵察。莉爾芮雅‧曦風去太久了。$b$b如果你能幫忙查看她安全，我感激不盡。她說她是去探查晨曦斷崖的廢墟。', '在晨曦斷崖尋找莉爾芮雅‧曦風。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42224, 'deDE', 'In Mondschatten gehüllt', 'Erkundet das Mondschleiersanktum.', 'Ich weiß nicht, was Theryn gefunden hat, aber ich weiß, wo er es gefunden hat.\n\nAm Mondschleiersanktum im Westen. Jenseits des Tempels von Fal\'adora.\n\nWas auch immer Theryns arkane Essenz veränderte... Welche Kraft auch immer es war, mit deren Hilfe es Kel\'danath gelang, einen besänftigenden Zauber für die Verdorrten zu wirken... Dort ist die Quelle.\n\nIch weiß es so sicher, als wäre ich selbst dort gewesen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42224, 'esES', 'Envuelto en sombra lunar', 'Explora el Sagrario Sombra Lunar.', 'No sé qué encontraría Theryn, pero sí sé dónde.\n\nEl Sagrario Sombra Lunar, hacia el oeste. Más allá del Templo de Fal\'adora.\n\nEl poder que alteró la esencia Arcana de Theryn..., el poder que le permitió a Kel\'danath crear un hechizo para confortar a los Marchitos... tiene allí su origen.\n\nLo tengo tan claro como si hubiera estado allí en persona.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42224, 'esMX', 'Invisible en Puestaluna', 'Explora el Sagrario Puestaluna.', 'No sé qué encontró Theryn, pero sí dónde lo hizo.\n\nEn el Sagrario Puestaluna, al oeste. Más allá del templo de Fal\'adora. \n\nSea cual sea el poder que alteró la esencia arcana de Theryn, que permitió a Kel\'danath crear un hechizo para calmar a los marchitos... su origen está allá.\n\nLo sé, y estoy tan seguro como si estuviera allá en persona.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42224, 'frFR', 'Camouflage à Lunombrage', 'Explorez le Sanctum de Lunombrage.', 'Je ne sais pas ce que Theryn a trouvé, mais je sais où il l’a trouvé.\n\nAu sanctum de Lunombrage, à l’ouest. Par-delà le temple de Fal’adora.\n\nLa puissance qui a modifié l’essence arcanique de Theryn, celle qui a permis à Kel’danath de créer un sort apaisant les Flétris… Là réside sa source.\n\nJ’ai beau n’y avoir jamais mis les pieds, j’en ai l’absolue certitude.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42224, 'itIT', 'Ammantato a Ombraluna', 'Esplora il Santuario di Ombraluna.', 'Non so cos\'ha trovato Theryn, ma so dove lo ha trovato.\n\nNel Santuario di Ombraluna, a ovest di qui, dietro il Tempio di Fal\'adora.\n\nQualsiasi potere abbia alterato l\'essenza arcana di Theryn... qualsiasi potere abbia permesso a Kel\'danath di creare un incantesimo per soggiogare gli Avvizziti... la sua fonte è lì.\n\nNe sono certa, come se l\'avessi visto con i miei occhi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42224, 'koKR', '달그늘의 수수께끼', '달그늘 성소를 탐험해야 합니다.', '테린이 뭘 찾았는진 모르지만, 어디서 그걸 찾았는지는 압니다. \n\n서쪽에 있는 달그늘 성소죠. 팔라도라의 사원 너머에 있지요. \n\n테린의 비전 정수를 변형시킨 힘... 켈다나스가 메마른 자들을 진정시킬 주문을 시전하게 해준 힘... 그 근원은 달그늘 성소에 있어요.\n\n마치 제가 그곳에 갔던 것처럼 확신한답니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42224, 'ptBR', 'Coberto pela Sombraluna', 'Explore o Sacrário Sombraluna.', 'Eu não sei o que Theryn encontrou, mas sei onde foi.\n\nNo Sacrário Sombraluna, a oeste. Depois do Templo de Fal\'adora.\n\nO poder que alterou a essência arcana de Theryn... o poder que permitiu a Kel\'danath criar um feitiço para acalmar os fenecidos... seja ele qual for, a origem é essa.\n\nTenho certeza absoluta, mesmo nunca tendo ido lá.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42224, 'ruRU', 'Под покровом лунной тени', 'Исследуйте обитель Лунной Тени.', 'Я не знаю, что именно нашел Терин, но зато знаю, где. \n\nЭто обитель Лунной Тени, что к западу отсюда. Она находится за храмом Фал\'адоры. \n\nКакая бы сила ни изменила чародейскую сущность Терина... Какая бы сила ни позволила Кел\'датату создать заклинание, успокаивающее иссохших... Ее источник находится там. \n\nЯ это знаю наверняка, как будто я только что оттуда.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42224, 'zhCN', '月影里的秘密', '探索月影圣殿。', '我不知道泰林发现了什么，但我知道这件事发生的地点。\n\n月影圣殿，就在这里的西边。在法尔多拉神殿更远一些的地方。\n\n不管是什么力量改变了泰林的奥术精华……不管是什么力量让凯尔丹纳斯创造出了能够安抚枯法者的咒语……它的本源就在那里。\n\n如果有人亲自去那里一看，也会得到和我相同的见解。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42224, 'zhTW', '披上月影', '探索月影聖所。', '我不清楚瑟倫找到什麼，但我知道他在哪裡找到它。\n\n就是西方的月影聖所。在菲阿多拉神殿之外。\n\n不管是改變瑟倫秘法精華的力量…或是讓克達納茲發明法術來安撫凋萎者的力量…源頭都在那裡。\n\n我就像是親自去過那裡一樣，所以我十分肯定。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42225, 'deDE', 'Das Siegel brechen', 'Brecht das Siegel der Gezeiten, das Siegel der Erde und das Siegel des Himmels.', '<Als Ihr das Relikt betrachtet, hört Ihr Thalyssras Stimme in Eurem Geist.>\n\nIhr habt es gefunden! Aber es wird durch Siegel geschützt.\n\nDurchsucht den Raum nach etwas, um diese Siegel zu brechen.', '', '', 'Erste Arkanistin Thalyssra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42225, 'esES', 'Romper el sello', 'Rompe el sello de las mareas, el sello de la tierra y el sello del cielo.', '<Mientras contemplas la reliquia, la voz de Thalyssra resuena en tu mente.>\n\n¡La has encontrado! Pero está sellada a cal y canto.\n\nBusca por la estancia el modo de romper esos sellos.', '', '', 'Primera arcanista Thalyssra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42225, 'esMX', 'Romper los sellos', 'Rompe el Sello de las Mareas, el Sello de la Tierra y el Sello del Cielo.', '<Mientras contemplas la reliquia, la voz de Thalyssra resuena en tu mente.>\n\n¡Lo encontraste! Pero está sellado.\n\nBusca en la habitación el modo de romper esos sellos.', '', '', 'Primera Arcanista Thalyssra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42225, 'frFR', 'Briser le sceau', 'Brisez le Sceau des marées, le Sceau de la terre et le Sceau du ciel.', '<La voix de Thalyssra emplit votre esprit tandis que vous contemplez la relique.>\n\nC’est bien ce que nous recherchions ! Mais les protections sont puissantes.\n\nFouillez la pièce et trouvez un moyen de briser ces sceaux.', '', '', 'Première arcaniste Thalyssra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42225, 'itIT', 'Spezzare il sigillo', 'Spezza il Sigillo delle Maree, il Sigillo della Terra e il Sigillo del Cielo.', '<Mentre osservi la reliquia, la voce di Thalyssra echeggia nella tua mente.>\n\nL\'hai trovata! Ma è sigillata.\n\nCerca nella stanza un modo per rompere quei sigilli.', '', '', 'Prima Arcanista Thalyssra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42225, 'koKR', '인장 깨뜨리기', '파도의 인장, 땅의 인장, 하늘의 인장을 깨뜨려야 합니다.', '<유물을 살펴보는 당신의 머릿속에 탈리스라의 목소리가 울려 퍼집니다.>\n\n찾으셨군요! 하지만 그 유물은 봉인되어 있어요.\n\n방 안을 찾아, 유물을 봉인하는 인장을 풀 방법을 찾아보세요.', '', '', '첫 번째 비전술사 탈리스라', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42225, 'ptBR', 'O rompimento dos selos', 'Rompa o Selo das Marés, o Selo da Terra e o Selo do Céu.', '<Ao contemplar a relíquia, a voz de Thalyssra ressoa na sua mente.>\n\nVocê achou! Mas ela está muito bem selada.\n\nVasculhe a câmara, deve ter algum jeito de romper os selos.', '', '', 'Primeira Arcanista Thalyssra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42225, 'ruRU', 'Сломанные печати', 'Сломайте печать приливов, печать земли и печать неба.', '<Вы смотрите на реликвию и слышите у себя в голове голос Талисры.>\n\nТы $gнашел:нашла; реликвию! Но ее защищают печати.\n\nОбыщи комнату и найди способ сломать их.', '', '', 'Первая чародейка Талисра', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42225, 'zhCN', '解除封印', '解除潮汐封印、大地封印和天空封印。', '<在你注视着这件圣物的同时，塔莉萨的声音在你的脑海中回响起来。>\n\n你找到它了！但它被牢固地封印了起来。\n\n我们得想办法解除这些封印。', '', '', '首席奥术师塔莉萨', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42225, 'zhTW', '破除封印', '破除海潮封印、大地封印和天空封印。', '<當你將聖物握在手中，薩莉瑟拉的聲音迴盪在你的腦海中。>\n\n你找到了！但上面的封印十分強大。\n\n搜索房間，尋找解除封印的方法。', '', '', '首席秘法師薩莉瑟拉', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42226, 'deDE', 'Mondschattenbastion', 'Tötet einen Netzschleuderer der Fal\'dorei, einen Brutrufer der Fal\'dorei und einen Witwenmacher der Fal\'dorei.', '<Das Hantieren mit dem Relikt hat ein paar garstige Kreaturen aus dem Untergrund von Falanaar herbeigelockt.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42226, 'esES', 'Resistencia Sombra Lunar', 'Mata a un lanzarred fal\'dorei, un clamaprogenies fal\'dorei y un creaviudas fal\'dorei.', '<La manipulación de la reliquia ha atraído la atención de horribles criaturas de las profundidades de Falanaar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42226, 'esMX', 'Resistencia de Puestaluna', 'Asesina una corretela fal\'dorei, un llamalinaje fal\'dorei y un creaviudas fal\'dorei.', '<La manipulación de la reliquia ha llamado la atención de algunas criaturas atroces de las profundidades de Falanaar>.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42226, 'frFR', 'Contretemps à Lunombrage', 'Tuez une Toile-grouillante fal’dorei, une Hurleuse des couvées fal’dorei et une Maleveuve fal’dorei.', '<En touchant la relique, vous avez attiré l’attention de certaines des plus affreuses créatures des profondeurs de Falanaar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42226, 'itIT', 'Trincea di Ombraluna', 'Uccidi lo Scagliatele Fal\'dorei, l\'Invocatore della Stirpe Fal\'dorei e l\'Assassino Fal\'dorei.', '<Sembra che la manomissione della reliquia abbia attirato l\'attenzione di qualche orrida creatura proveniente da sotto Falanaar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42226, 'koKR', '달그늘 전투', '팔도레이 그물거미, 팔도레이 무리소환사, 팔도레이 과부제조자를 처치해야 합니다.', '<유물을 건드린 것이 팔라나아르 지하에 있는 끔찍한 괴물들의 주의를 끌었습니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42226, 'ptBR', 'O Discordante de Sombraluna', 'Mate uma Cuspidora de Teia Fal\'dorei, uma Chamaprole Fal\'dorei e uma Fazedora de Viúvas Fal\'dorei.', '<Mexer na relíquia chamou a atenção de algumas criaturas hediondas de debaixo de Falanaar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42226, 'ruRU', 'Битва в обители Лунной Тени', 'Расправьтесь с фал\'дорайской метательницей паутины, фал\'дорайским призывателем выводка и фал\'дорайской вдовой.', '<Ваши манипуляции с реликвией привлекли внимание жутких существ из-под Фаланаара.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42226, 'zhCN', '月影秘地', '击杀一只法多雷撒网者、一只法多雷唤巢者和一只法多雷寡妇蛛。', '<鼓捣圣物发出的动静引来了法兰纳尔地下的可怕生物。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42226, 'zhTW', '月影陣地', '殺死一隻法多雷擲網者、法多雷喚巢蛛和法多雷奪命蛛。', '<干預聖物之後，似乎吸引了來自法拉納爾地底的可怕生物的注意。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42227, 'deDE', 'Reise in den Riss', 'Folgt Talwandler Farodin in die Tunnel von Falanaar.', 'Ich habe dieses Samenkorn seit zehntausend Jahren bewacht. Und Ihr lasst zu, dass es in die Hände der Fal\'dorei fällt!$B$BIch hätte Euch sterben lassen sollen.$B$BZahlt Eure Schuld zurück. Helft mir, das Samenkorn zurückzuholen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42227, 'esES', 'Hacia la grieta', 'Sigue al caminavalles Farodin hasta los Túneles de Falanaar.', 'Llevo diez mil años protegiendo esa semilla. Y tú has permitido que caiga en manos de los fal\'dorei.$b$bDebería haber dejado que murieras.$b$bSalda tu deuda. Ayúdame a recuperar la semilla.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42227, 'esMX', 'En la grieta', 'Sigue al caminante del valle Farodin a los Túneles de Falanaar.', 'He protegido esa semilla por diez mil años y tu la dejaste caer en las manos de los fal\'dorei.$B$BDebo dejarte morir.$B$BPaga tu deuda y ayúdame a recuperar la semilla.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42227, 'frFR', 'Dans la crevasse', 'Suivez le Marchevallon Farodin dans les Tunnels de Falanaar.', 'J’ai protégé cette graine pendant dix mille ans et vous l’avez laissée tomber entre les mains des Fal’dorei.$B$BJ’aurais mieux fait de vous laisser mourir.$B$BAcquittez-vous de votre dette en m’aidant à la récupérer.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42227, 'itIT', 'Dentro il crepaccio', 'Segui il Calcavalli Farodin nelle Gallerie di Falanaar.', 'Ho protetto il seme per diecimila anni e ora tu l\'hai fatto cadere nelle mani dei Fal\'dorei.$B$BAvrei dovuto lasciarti morire.$B$BRipaga il tuo debito. Aiutami a recuperarlo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42227, 'koKR', '갈라진 틈 속으로', '계곡방랑자 파로딘을 따라 팔라나아르 굴 속으로 가야 합니다.', '난 저 씨앗을 만 년 동안 지켜왔소. 당신이 그걸 팔도레이의 손안에 들어가게 한 것이오.$B$B당신이 그냥 죽게 놔뒀어야 했거늘.$B$B당신은 이 빚을 갚아야 하오. 씨앗을 되찾을 수 있게 날 도우시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42227, 'ptBR', 'Fenda adentro', 'Siga o Andarilho do Vale Farodin até os Túneis de Falanaar.', 'Eu protegi aquela semente por dez mil anos. Você deixou que ela caísse nas mãos dos fal\'dorei.$B$BEu deveria deixá-$glo:la; morrer.$B$BPague sua dívida. Ajudem-me a recuperar a semente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42227, 'ruRU', 'В темные глубины', 'Следуйте за странником дола Фародином в туннели Фаланаара.', 'Десять тысяч лет я защищаю это семя. А ты $gпозволил:позволила; ему попасть в лапы фал\'дорайских тварей.$B$BМне стоило бы предать тебя смерти.$B$BИсправляй свою ошибку. Помоги мне вернуть семя.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42227, 'zhCN', '深入裂缝', '跟随山谷行者法罗丁进入法兰纳尔隧道。', '我保护那颗种子已经一万年了。你却让它落入了法多雷之手。$B$B我真该让你死在那里。$B$B但我给你一个将功补过的机会。帮我把种子夺回来。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42227, 'zhTW', '深入裂隙', '跟著谷行者費羅丁進入法拉納爾隧道。', '我保護這顆種子已經有一萬年了。你居然讓它落入法多雷手中。$B$B我本該讓你自生自滅。$B$B協助我奪回種子，償還你欠下的債。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42228, 'deDE', 'Die versteckte Stadt', 'Folgt Orathiss durch die Tunnel von Falanaar und tötet sie.', 'Orathiss hat das Samenkorn unterschätzt. Es zehrt an ihrer Kraft und verlangsamt sie.$B$BWir müssen sie einholen und das Samenkorn in Sicherheit bringen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42228, 'esES', 'La ciudad oculta', 'Sigue a Orathiss por los Túneles de Falanaar y mátala.', 'Orathiss ha subestimado la semilla. Le está absorbiendo la fuerza y eso la ralentiza.$b$b¡Debemos alcanzarla y poner la semilla a buen recaudo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42228, 'esMX', 'La ciudad oculta', 'Sigue a Orathiss por los Túneles de Falanaar y asesínala.', 'Orathiss subestimó la semilla. Está acabando con su poder y la ralentiza.$B$B¡Debemos atraparla y dejar la semilla en un lugar seguro!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42228, 'frFR', 'La cité cachée', 'Suivez Orathiss dans les Tunnels de Falanaar et tuez-la.', 'Orathiss a sous-estimé le pouvoir de la graine. Elle sape sa puissance et la ralentit considérablement.$B$BNous devons la rattraper et mettre la graine en lieu sûr !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42228, 'itIT', 'La città nascosta', 'Segui Orathiss attraverso le Gallerie di Falanaar e uccidila.', 'Orathiss ha sottovalutato il seme. Ora esso sta assorbendo i suoi poteri... rallentandola.$B$BDobbiamo raggiungerla e riportare il seme al sicuro!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42228, 'koKR', '숨겨진 도시', '팔라나아르 굴 속으로 오라시스를 따라가 그녀를 처치해야 합니다.', '오라시스는 씨앗을 과소평가했소. 씨앗이 그녀의 힘을 약화시키면서, 그녀의 속도를 늦추고 있소.$B$B오라시스를 따라잡아 씨앗을 안전한 곳으로 가져가야 하오!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42228, 'ptBR', 'A cidade secreta', 'Siga Orathiss através dos Túneis de Falanaar e mate-a.', 'Orathiss subestimou a semente. Ela está consumindo seu poder, desacelerando-a.$B$BTemos que pegá-la e levar a semente para um lugar seguro!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42228, 'ruRU', 'Тайный город', 'Следуйте за Орафиссой через туннели Фаланаара и убейте ее.', 'Орафисса недооценила действие семени. Оно вытягивает из нее силу и замедляет.$B$BМы должны поймать ее и забрать семя.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42228, 'zhCN', '隐藏的城市', '在法兰纳尔隧道中追踪欧拉希斯，并将她击败。', '欧拉希斯低估了种子。种子在吸取她的力量，令她行动迟缓。$B$B我们必须追上她，并将种子带到安全的地方！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42228, 'zhTW', '隱秘之城', '跟著歐菈希絲穿過法拉納爾隧道並殺死她。', '歐菈希絲低估了種子的力量。它正在蠶食她的力量，拖慢她的速度。$B$B我們必須抓住她，帶種子到安全的地方！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42229, 'deDE', 'Shal\'Aran', 'Folgt Thalyssra, während sie Shal\'Aran erkundet.', 'Da ihre Wächterin nun besiegt ist, könnte diese Kammer dort vorn ein perfektes Versteck sein.\n\nKommt mit.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42229, 'esES', 'Shal\'Aran', 'Sigue a Thalyssra mientras explora Shal\'Aran.', 'La cámara de más allá puede ser el escondrijo perfecto, ahora que su guardián ya no está.\n\nVen conmigo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42229, 'esMX', 'Shal\'Aran', 'Sigue a Thalyssra mientras explora Shal\'Aran.', 'La siguiente cámara puede ser un perfecto escondite ahora que su guardián está fuera de nuestro camino.\n\nVen conmigo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42229, 'frFR', 'Shal’Aran', 'Suivez Thalyssra pendant son exploration de Shal’Aran.', 'La prochaine pièce pourrait constituer une cachette idéale maintenant que sa gardienne a été écartée.\n\nVenez avec moi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42229, 'itIT', 'Shal\'aran', 'Segui Thalyssra mentre esplora Shal\'aran.', 'La camera inferiore potrebbe essere un nascondiglio perfetto ora che ti sei $goccupato:occupata; della sua guardiana.\n\nVieni con me.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42229, 'koKR', '샬아란', '탈리스라를 따라 샬아란을 탐험해야 합니다.', '수호자들을 처리했으니, 저쪽에 있는 방에 숨으면 되겠군요. \n\n저와 함께 가요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42229, 'ptBR', 'Shal\'Aran', 'Siga Thalyssra enquanto ela explora Shal\'Aran.', 'A câmara adiante pode ser o esconderijo perfeito, agora que demos um jeito no guardião.\n\nVenha comigo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42229, 'ruRU', 'Шал\'Аран', 'Исследуйте Шал\'Аран вместе с Талисрой.', 'Давай осмотрим это место. С хранительницей мы разобрались, так что здесь можно устроить идеальное убежище.\n\nПойдем.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42229, 'zhCN', '沙尔艾兰', '跟随塔莉萨探索沙尔艾兰。', '前面的房间或许是个绝佳的躲藏地点，那里的守卫都被解决掉了。\n\n跟我来。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42229, 'zhTW', '沙亞蘭', '跟隨薩莉瑟拉，探索沙亞蘭。', '那個房間是完美的藏身處，現在守護者已經被解決了。\n\n跟我來。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42230, 'deDE', 'Die Bürde des Talwandlers', 'Aktiviert den Signalgeber von Fal\'adora im Tempel von Fal\'adora.', 'Das Samenkorn erwacht. Wenn es keine Energie erhält, wird es schnell verdorren und absterben.\n\nWir müssen das Samenkorn hier herausbringen und irgendwo einpflanzen, wo es sicher ist.\n\nIch bin nicht stark genug, eine solche Bürde zu schultern. Aber Ihr... Ihr strotzt vor lauter Kraft. Ich kann es spüren.\n\nBitte nehmt das Samenkorn.', '', '', 'Talwandler Farodin', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42230, 'esES', 'La carga del caminavalles', 'Activa la baliza de Fal\'adora en el Templo de Fal\'adora.', 'La semilla está despertando. Sin poder, pronto se marchitará y morirá.\n\nDebemos sacarla de aquí y plantarla en algún lugar seguro.\n\nYo no soy lo bastante fuerte para esta carga. Pero tú... tú rezumas poder. Lo noto.\n\nToma la semilla, por favor.', '', '', 'Caminavalles Farodin', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42230, 'esMX', 'La carga del Caminante del Valle', 'Activa la baliza en el Templo de Fal\'adora.', 'La semilla está despertando. Sin energía, se marchitará y morirá.\n\nDebemos sacarla de aquí y plantarla en algún lugar seguro.\n\nNo soy lo suficientemente fuerte como para soportar esta carga. Pero tú... tú rebozas de poder. Puedo sentirlo. \n\nPor favor, lleva la semilla contigo.', '', '', 'Caminante del valle Farodin', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42230, 'frFR', 'Le fardeau du marchevallon', 'Activez la Balise de Fal’adora dans le Temple de Fal’adora.', 'La graine s’éveille. Privée de puissance, elle risque de dépérir rapidement.\n\nNous devons la sortir d’ici afin de la planter en lieu sûr.\n\nJe ne suis pas de taille à porter ce fardeau, mais vous… vous débordez de puissance, je le sens.\n\nJe vous en supplie, prenez la graine.', '', '', 'Marchevallon Farodin', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42230, 'itIT', 'Il fardello del Calcavalli', 'Attiva il Faro di Fal\'adora nel Tempio di Fal\'adora.', 'Il seme sta per risvegliarsi. Senza energia si seccherà velocemente e morirà.\n\nDobbiamo portarlo fuori e piantarlo in un luogo sicuro.\n\nNon sono abbastanza forte per portare un simile fardello. Tu invece... tu hai un grande potere. Riesco a sentirlo.\n\nTi prego, prendi il seme.', '', '', 'Calcavalli Farodin', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42230, 'koKR', '계곡방랑자의 짐', '팔라도라의 사원에서 팔라도라 신호 장치를 가동해야 합니다.', '씨앗이 깨어나고 있소. 힘이 없으면 씨앗은 빠르게 시들어 죽고 말 것이오.\n\n씨앗을 밖으로 옮겨 다른 안전한 곳에 심어야 하오.\n\n나로서는 이 짐을 짊어지기엔 역부족이오. 하지만, 당신... 당신에겐 힘이 넘치오. 느낄 수 있소.\n\n씨앗을 맡아 주시오.', '', '', '계곡방랑자 파로딘', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42230, 'ptBR', 'O fardo do andarilho do vale', 'Ative o Sinalizador de Fal\'adora no Templo de Fal\'adora.', 'A semente está acordando. Sem poder, ela murchará e morrerá rapidamente.\n\nTemos que tirá-la daqui e plantá-la em um lugar seguro.\n\nEu não sou forte o bastante para carregar este fardo. Mas você... você está replet$go:a; de poder. Posso senti-lo.\n\nPor favor, leve a semente.', '', '', 'Andarilho do Vale Farodin', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42230, 'ruRU', 'Бремя странника дола', 'Активируйте маяк Фал\'адоры в храме Фал\'адоры.', 'Семя пробуждается. Без энергии оно быстро засохнет и погибнет.\n\nНадо унести его отсюда и посадить в безопасном месте.\n\nМне не хватит сил нести это бремя. Но ты... ты $gполон:полна; энергии. Я это чувствую. \n\nПрошу тебя, возьми семя.', '', '', 'Странник дола Фародин', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42230, 'zhCN', '山谷行者的重担', '在法尔多拉神殿里激活法尔多拉道标。', '种子即将觉醒。没有力量，它很快就会枯死。\n\n我们得带着种子离开这里，并把它种在安全的地方。\n\n我力量不够强，承受不了这个重担。不过你……浑身充盈着力量。我能感觉到。\n\n请拿上种子。', '', '', '山谷行者法罗丁', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42230, 'zhTW', '谷行者的負擔', '在菲阿多拉神殿啟動菲阿多拉信標。', '種子正在覺醒。沒有力量，它很快就會枯萎死去。\n\n我們必須把種子帶離這裡，種在安全的地方。\n\n我不夠強壯，無法承受這個重擔。但你不一樣…你全身充滿力量。我能夠感覺到。\n\n請收下這顆種子。', '', '', '谷行者費羅丁', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42231, 'deDE', 'Der geheimnisvolle Paladin', 'Sprecht mit Travard in Dalaran.', 'Uns scheint sich eine einmalige Gelegenheit zu bieten.$b$bEin kleiner Orden von Paladinen ist auf uns zugekommen und behauptet zu wissen, wo sich Tyrs Grabmal befindet. Sie sagten, sie seien die Beschützer dieses Kriegshammers, aber die uralte Waffe wäre jetzt in Gefahr.$b$bIch möchte, dass Ihr mit ihrem Anführer Travard sprecht, um mehr über das Artefakt herauszufinden.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42231, 'esES', 'El misterioso paladín', 'Habla con Travard en Dalaran.', 'Parece que disponemos de una oportunidad única.$B$BSe ha presentado una pequeña comitiva de paladines que afirman conocer la ubicación de la tumba de Tyr. Dicen que son los protectores de su martillo de guerra, pero que esa arma ancestral está ahora en peligro.$B$BMe gustaría que hablaras con Travard, su líder, para averiguar más acerca de este artefacto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42231, 'esMX', 'El paladín misterioso', 'Habla con Travard en Dalaran.', 'Parece que se nos presentó una oportunidad única.$b$bUn pequeño grupo de paladines llegó diciendo que conocen la ubicación de la tumba de Tyr. Dicen que son los protectores de su martillo, pero que el arma ancestral se encuentra ahora en problemas.$b$bQuisiera que hables con Travard, su líder, para averiguar más información acerca de este artefacto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42231, 'frFR', 'Le mystérieux paladin', 'Parlez à Travard à Dalaran.', 'Une occasion unique s’offre à nous, semble-t-il.$b$bUn petit ordre de paladins s’est fait jour et ses membres prétendent savoir où se trouve le tombeau de Tyr. À les en croire, ils seraient les protecteurs de son marteau de guerre et celui-ci serait en grand danger.$b$bJ’aimerais que vous alliez parler à leur chef, Travard, afin d’en apprendre plus sur cette arme prodigieuse.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42231, 'itIT', 'Il Paladino misterioso', 'Parla con Travard a Dalaran.', 'Pare si sia presentata un\'opportunità unica.$b$bUn piccolo ordine di Paladini si è fatto avanti affermando di conoscere la posizione della tomba di Tyr. Dicono di essere i custodi del suo martello da guerra, ma l\'arma si trova ora in pericolo.$b$bVorrei parlare con Travard, il loro capo, per scoprire di più su questo artefatto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42231, 'koKR', '의문의 성기사', '달라란에 있는 트래바드와 대화해야 합니다.', '우리가 흔치 않은 기회를 얻은 것 같군요.$b$b티르의 무덤이 있는 곳을 안다는 소규모 성기사 집단이 나타났습니다. 자신들이 티르의 전쟁망치의 수호자이고, 그 고대 무기가 지금 위태로운 상황이라고 하더군요.$b$b그들의 지도자인 트래바드와 이야기해 이 유물에 대해 더 알아보십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42231, 'ptBR', 'O paladino misterioso', 'Fale com Travard em Dalaran.', 'Parece que nos foi dada uma oportunidade única.$b$bUma ordem pequena de paladinos se apresentou alegando conhecer a localização da tumba de Tyr. Eles se dizem os protetores do martelo de guerra dele, mas contam que agora a arma ancestral corre perigo.$b$bEu gostaria que você conversasse com Travard, o líder deles, para descobrir mais sobre esse artefato.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42231, 'ruRU', 'Таинственный паладин', 'Поговорите с Траваром в Даларане.', 'Перед нами нежданно открылась уникальная возможность.$b$bСюда явился небольшой отряд паладинов. Они утверждают, что им известно местонахождение могилы Тира! По их словам, они много лет охраняли его боевой молот, но теперь это древнее оружие в опасности.$b$bЯ прошу тебя поговорить с их предводителем Траваром. Узнай как можно больше об этом артефакте.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42231, 'zhCN', '神秘的圣骑士', '在达拉然与塔瓦德谈一谈。', '看来我们遇上了一个难得的机会。$b$b有一小队圣骑士声称自己知道提尔之墓的位置！他们还说自己是提尔战锤的守护者，不过那把古老的武器最近陷入了纷争。$b$b我要你去跟他们的首领塔瓦德谈谈，了解神器的更多事情。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42231, 'zhTW', '神秘的聖騎士', '到達拉然與崔瓦交談。', '看來我們有個絕佳的機會。$b$b有一小群聖騎士前來宣稱自己知道提爾之墓的位置。他們說自己是提爾戰錘的看守者，不過那把古代武器狀況岌岌可危。$b$b我希望你去跟他們的首領崔瓦談談，進一步瞭解這把神器。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42233, 'deDE', 'Hochbergstämme', 'Schließt beliebige 4 Weltquests am Hochberg ab.', 'Unterstützt die Tauren vom Hochberg, indem Ihr 4 Weltquests abschließt.', '', '', '', '', '', 'Kehrt zu Ransa Graufeder in Donnertotem am Hochberg zurück.', 0);
INSERT INTO `quest_template_locale` VALUES (42233, 'esES', 'Tribus de Monte Alto', 'Completa 4 misiones del mundo en Monte Alto.', 'Ayuda a los tauren de Monte Alto completando 4 misiones del mundo.', '', '', '', '', '', 'Vuelve con: Ransa Pluma Gris. Zona: Tótem del Trueno, Monte Alto.', 0);
INSERT INTO `quest_template_locale` VALUES (42233, 'esMX', 'Tribus de Altamontaña', 'Completa 4 misiones de mundo en Altamontaña.', 'Ayuda a los tauren de Altamontaña completando 4 misiones de mundo.', '', '', '', '', '', 'Vuelve con Ransa Plumagris en el Tótem del Trueno en Altamontaña.', 0);
INSERT INTO `quest_template_locale` VALUES (42233, 'frFR', 'Tribus de Haut-Roc', 'Accomplissez 4 expéditions à Haut-Roc.', 'Aidez les Taurens de Haut-Roc en terminant 4 expéditions.', '', '', '', '', '', 'Retournez voir Ransa Plume-Grise à Totem-du-Tonnerre, à Haut-Roc.', 0);
INSERT INTO `quest_template_locale` VALUES (42233, 'itIT', 'Le Tribù di Alto Monte', 'Completa 4 missioni mondiali qualsiasi ad Alto Monte.', 'Aiuta i Tauren di Alto Monte completando 4 missioni mondiali.', '', '', '', '', '', 'Torna da Ransa Piuma Grigia a Totem del Fulmine ad Alto Monte.', 0);
INSERT INTO `quest_template_locale` VALUES (42233, 'koKR', '높은산 부족들', '높은산에서 전역 퀘스트 4개를 완료해야 합니다.', '전역 퀘스트 4개를 완료하여 높은산의 타우렌을 도와야 합니다.', '', '', '', '', '', '높은산의 천둥 토템에 있는 란사 그레이페더에게 가야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (42233, 'ptBR', 'Tribos de Alta Montanha', 'Conclua 4 missões mundiais quaisquer em Alta Montanha.', 'Ajude os Taurens de Alta Montanha: complete 4 missões mundiais.', '', '', '', '', '', 'Fale novamente com Ransa Pena Cinzenta no Totem do Trovão, que fica em Alta Montanha.', 0);
INSERT INTO `quest_template_locale` VALUES (42233, 'ruRU', 'Племена Крутогорья', 'Выполните 4 локальных задания в Крутогорье.', 'Помогите тауренам Крутогорья, выполнив 4 локальных задания.', '', '', '', '', '', 'Вернитесь к Ранзе Серое Перо в Громовой Тотем, что в Крутогорье.', 0);
INSERT INTO `quest_template_locale` VALUES (42233, 'zhCN', '高岭诸族', '完成至高岭的任意4个世界任务。', '帮助至高岭的牛头人完成4个世界任务。', '', '', '', '', '', '回至高岭的雷霆图腾找兰莎·灰羽。', 0);
INSERT INTO `quest_template_locale` VALUES (42233, 'zhTW', '高嶺部族', '在高嶺完成4個世界任務。', '完成4個世界任務來協助高嶺的牛頭人。', '', '', '', '', '', '回到高嶺的雷霆圖騰去找蘭莎‧灰羽', 0);
INSERT INTO `quest_template_locale` VALUES (42234, 'deDE', 'Die Valarjar', 'Schließt 4 beliebige Weltquests in Sturmheim ab.', 'Unterstützt die Vrykul von Sturmheim, indem Ihr 4 Weltquests abschließt.', '', '', '', '', '', 'Kehrt zu Valdemar Sturmsucher in Sturmheim zurück.', 0);
INSERT INTO `quest_template_locale` VALUES (42234, 'esES', 'Los Valarjar', 'Completa 4 misiones del mundo en Tormenheim.', 'Ayuda a los vrykuls de Tormenheim completando 4 misiones del mundo.', '', '', '', '', '', 'Vuelve con: Valdemar Buscatormentas. Zona: Tormenheim.', 0);
INSERT INTO `quest_template_locale` VALUES (42234, 'esMX', 'Los Valarjar', 'Completa 4 misiones de mundo en Stormheim.', 'Ayuda a los vry\'kul de Stormheim completando 4 misiones de mundo.', '', '', '', '', '', 'Vuelve con Valdemar Buscatormentas en Stormheim.', 0);
INSERT INTO `quest_template_locale` VALUES (42234, 'frFR', 'Les Valarjar', 'Accomplissez 4 expéditions en Tornheim.', 'Aidez les Vrykuls de Tornheim en terminant 4 expéditions.', '', '', '', '', '', 'Retournez voir Valdemar Cherche-Tempête à Tornheim.', 0);
INSERT INTO `quest_template_locale` VALUES (42234, 'itIT', 'I Valarjar', 'Completa 4 missioni mondiali qualsiasi a Stromheim.', 'Aiuta i Vrykul di Stromheim completando 4 missioni mondiali.', '', '', '', '', '', 'Torna da Valdemar Bramatempeste a Stromheim.', 0);
