INSERT INTO `quest_template_locale` VALUES (42234, 'koKR', '발라리아르', '스톰하임에서 전역 퀘스트 4개를 완료해야 합니다.', '전역 퀘스트 4개를 완료하여 스톰하임의 브리쿨을 도와야 합니다.', '', '', '', '', '', '스톰하임에 있는 폭풍탐구자 발데마르에게 가야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (42234, 'ptBR', 'Os valarjares', 'Conclua 4 missões mundiais em Trommheim.', 'Ajude os Vraikalen de Trommheim: complete 4 missões mundiais.', '', '', '', '', '', 'Fale novamente com Valdemar Busca-tempestade em Trommheim.', 0);
INSERT INTO `quest_template_locale` VALUES (42234, 'ruRU', 'Валарьяры', 'Выполните 4 локальных задания в Штормхейме.', 'Помогите врайкулам Штормхейма, выполнив 4 локальных задания.', '', '', '', '', '', 'Вернитесь к Вальдемару Искателю Бурь в Штормхейм.', 0);
INSERT INTO `quest_template_locale` VALUES (42234, 'zhCN', '瓦拉加尔', '完成风暴峡湾的任意4个世界任务。', '帮助风暴峡湾的维库人完成4个世界任务。', '', '', '', '', '', '回风暴峡湾找瓦德玛尔·逐风。', 0);
INSERT INTO `quest_template_locale` VALUES (42234, 'zhTW', '華爾拉亞', '在斯鐸海姆完成4個世界任務。', '完成4個世界任務來協助斯鐸海姆的維酷人。', '', '', '', '', '', '回到斯鐸海姆去找瓦德瑪‧探風者。', 0);
INSERT INTO `quest_template_locale` VALUES (42238, 'deDE', 'Vermisster Dämon', 'Sucht nach Daglop in Azsuna.', 'Ich habe wohl meinen Wichtel Daglop verloren.$b$bSagt ihm doch, dass er sich bei mir melden soll, falls Ihr ihn seht.$b$bNormalerweise würde ich ihn einfach neu beschwören, doch ich befürchte, dass der blinde Marius ihn auf der Stelle töten und erst dann Fragen stellen würde.$b$bDem fehlt einfach der Weitblick.', '', '', '', '', '', 'Sprecht mit Daglop in den Olivanhöhen.', 0);
INSERT INTO `quest_template_locale` VALUES (42238, 'esES', 'Demonio desaparecido', 'Busca a Daglop en Azsuna.', 'Creo que he perdido a mi diablillo, Daglop.$B$BSi lo ves, dile que contacte conmigo.$B$BNormalmente lo volvería a invocar y listo, pero me preocupa que Marius lo mate de entrada y pregunte luego.$B$BNo tiene nada de visión.', '', '', '', '', '', 'Habla con: Daglop. Zona: Velo Oliváceo.', 0);
INSERT INTO `quest_template_locale` VALUES (42238, 'esMX', 'Demonio perdido', 'Encuentra a Daglop en Azsuna.', 'Parece que perdí a mi diablillo, Daglop.$b$bSi lo ves, dile que se reporte.$b$bNormalmente volvería a invocarlo, pero me preocupa que el ciego Marius lo mate tan pronto como lo vea.$b$bSin visión, ese tipo.', '', '', '', '', '', 'Habla con Daglop en el Velo Oliviano.', 0);
INSERT INTO `quest_template_locale` VALUES (42238, 'frFR', 'Démon disparu', 'Retrouvez Daglop sur Azsuna.', 'J’ai perdu la trace de mon diablotin, Daglop.$b$bSi vous le voyez, dites-lui de venir me voir.$b$bEn temps normal, il me suffirait de l’invoquer à nouveau, mais je crains que Marius l’aveugle, ici présent, ne l’abatte sans sommation.$b$bCet abruti ne voit pas plus loin que le bout de son nez.', '', '', '', '', '', 'Parlez à Daglop au Voile Olivine.', 0);
INSERT INTO `quest_template_locale` VALUES (42238, 'itIT', 'Demoni dispersi', 'Trova Daglop ad Azsuna.', 'Credo di aver perso il mio Imp, Daglop.$b$bSe lo vedi, digli di tornare subito da me.$b$bNormalmente potrei semplicemente evocarlo, ma ho il timore che Marius non farebbe altro che ucciderlo senza fare domande.$b$bNon ha proprio rispetto, quello.', '', '', '', '', '', 'Parla con Daglop al Velo Olivastro.', 0);
INSERT INTO `quest_template_locale` VALUES (42238, 'koKR', '사라진 임프', '아즈스나에 있는 다글롭을 찾아야 합니다.', '내 임프 다글롭이 엉뚱한 장소에 소환된 것 같네.$b$b혹시 그 녀석을 보거든 어서 돌아오라고 전해 주게.$b$b보통 이런 상황이면 다글롭을 다시 소환하겠지만, 여기 이 마리우스라는 친구가 내 임프를 보자마자 죽일 것 같아서 말이야.$b$b그래, 꽉 막힌 녀석이라니까...', '', '', '', '', '', '올리비안 장막에 있는 다글롭과 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (42238, 'ptBR', 'Demônio desaparecido', 'Encontre Daglop em Azsuna.', 'Parece que eu perdi o meu diabrete, Daglop.$b$bSe você encontrá-lo, diga a ele para voltar.$b$bNormalmente eu simplesmente o evocaria de novo, mas o que me preocupa é que o cego do Marius aqui pode matá-lo antes e fazer perguntas depois.$b$bEsse aí não tem mesmo visão nenhuma.', '', '', '', '', '', 'Fale com Daglop no Véu Oliviano.', 0);
INSERT INTO `quest_template_locale` VALUES (42238, 'ruRU', 'Пропавший демон', 'Отыщите Даглопа в Азсуне.', 'Похоже, мой бес Даглоп куда-то запропал.$b$bЕсли вдруг он тебе попадется, вели ему вернуться ко мне.$b$bТак-то я бы просто призвал его заново, но опасаюсь, что мой незрячий друг Мариус тут же убьет его, а уж потом поинтересуется, кто это был.$b$bЖутко бестолковый тип.', '', '', '', '', '', 'Поговорите с Даглопом на Оливковом покрове.', 0);
INSERT INTO `quest_template_locale` VALUES (42238, 'zhCN', '失踪恶魔', '在阿苏纳寻找达格洛普。', '我好像把我的小鬼达格洛普弄丢了。$b$b如果你看到他，就叫他来找我。$b$b通常来说，我重新召唤一下就可以，不过我怕这个瞎子马瑞斯会来个先斩后奏。$b$b他看不见，很麻烦。', '', '', '', '', '', '在奥利维安影障与达格洛普谈一谈。', 0);
INSERT INTO `quest_template_locale` VALUES (42238, 'zhTW', '走失的惡魔', '在艾蘇納找到達格洛普。', '我似乎把我的小鬼達格洛普召喚到奇怪的地方了。$b$b如果你有看到他，請他向我回報。$b$b本來我可以直接重新召喚他，但只怕這位盲目的馬里斯會不分青紅皂白地痛下殺手。$b$b那傢伙啊，毫無遠見。', '', '', '', '', '', '前往橄欖崖與達格洛普說話。', 0);
