INSERT INTO `quest_template_locale` VALUES (42244, 'esES', 'El destino de La Represalia de la Reina', 'Lleva el diario del Capitán a la consejera Aguaserena, en el Santuario Brisaveloz en Dalaran.', '<Este es el tipo de información que se debería compartir. Tal vez alguien en el Santuario Brisaveloz de Dalaran sepa qué hacer con ella.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42244, 'esMX', 'El destino del Represalia de la Reina', 'Lleva el Diario del Capitán al Consejero Aguaserena en el Santuario Brisaveloz de Dalaran.', '<Es el tipo de inteligencia que debería compartirse. Tal vez alguien en el Santuario Brisaveloz de Dalaran sepa qué hacer.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42244, 'frFR', 'Le destin du Représailles de la reine', 'Apportez le Journal du capitaine du Représailles de la reine à la Conseillère Eau-Plate au Sanctuaire de Coursevent, à Dalaran.', '<Voilà le genre de renseignements qu’il ne faut pas garder pour soi. Peut-être quelqu’un saura-t-il quoi en faire au sanctuaire de Coursevent à Dalaran ?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42244, 'itIT', 'Il destino della Rappresaglia della Regina', 'Porta il Diario del Capitano alla Consigliera Acquaferma presso il Santuario di Ventolesto di Dalaran.', '<Queste informazioni dovrebbero essere consegnate a chi di dovere. Forse qualcuno al Santuario di Ventolesto a Dalaran saprà cosa farne.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42244, 'koKR', '여왕의 복수호의 운명', '달라란의 윈드러너 성소에 있는 조언자 스틸워터에게 선장의 일지를 가져가야 합니다.', '<이 일지의 내용은 혼자만 알면 안 될 것 같습니다. 일지를 달라란의 윈드러너 성소에 있는 누군가에게 가져가면 도움이 될 것 같습니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42244, 'ptBR', 'A Sina do Vingança da Rainha', 'Leve o Diário de bordo para a Conselheira Calmágua no Santuário Correventos, em Dalaran.', '<Esse é o tipo de informação que precisa ser compartilhada. Talvez alguém no Santuário Correventos, em Dalaran, saiba o que fazer com ela.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42244, 'ruRU', 'Судьба \"Мести королевы\"', 'Отнесите судовой журнал капитана советнице Тихой Заводи в прибежище Ветрокрылой, что в Даларане.', '<Эти разведданные следует донести до сведения командования. Возможно, кто-нибудь в Прибежище Ветрокрылой в Даларане знает, как с ними лучше поступить.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42244, 'zhCN', '女王的复仇号的命运', '前往达拉然，把船长日志交给风行者圣殿的顾问斯蒂沃特。', '<这种消息应该汇报上去。或许在达拉然的风行者圣殿会有人知道该怎么处理。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42244, 'zhTW', '女王復仇號的命運', '將船長日誌帶給達拉然風行者聖堂裡的諫言者靜水。', '<這是值得分享的情報。或許達拉然的風行者聖堂裡有人知道如何運用這項資訊。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42245, 'deDE', 'DEPRECATED: Singed Note', 'Bringt 3 silbrige Salben und 1 Feldverbandskasten zu dem Ort, der auf Eurer Karte markiert ist. Behandelt den teufelsverbrannten Späher, wenn Ihr dort eintrefft.', 'Bitte, wenn Ihr diese Zeilen lest, ist es vielleicht noch nicht zu spät. Ich habe im Kampf gegen die Legion schwere Verbrennungen erlitten.$b$bAnbei findet Ihr eine grobe Skizze, wo ich vom Zerfallenen Palast aus gesehen bin.$b$bBitte beeilt Euch und bringt mir Vorräte.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42245, 'esES', 'DEPRECATED: Singed Note', 'Lleva 3 bálsamos de plata y 1 mochila de campo a la ubicación señalada en tu mapa. Atiende al explorador quemado por energía vil cuando llegues.', 'Si lees esto, tal vez aún haya tiempo. Sufrí una grave quemadura combatiendo a la Legión.$b$bIncluyo un bosquejo de dónde estoy en relación al Palacio Desmoronado.$b$bDate prisa, por favor, y trae suministros.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42245, 'esMX', 'DEPRECATED: Singed Note', 'Lleva 3 viales de bálsamo plateado y 1 mochila de suministros a la ubicación marcada en tu mapa. Atiende al explorador quemado en cuanto llegues.', 'Si estás leyendo esto, entonces aún tengo esperanzas. Recibí quemaduras graves peleando con la Legión.$b$bIncluyo un mapa sencillo de dónde estoy con relación al Palacio Desmoronado.$b$bApúrate, por favor... y trae suministros.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42245, 'frFR', 'DEPRECATED: Singed Note', 'Apportez 3 doses de Baume argenté et 1 Paquetage à l’endroit indiqué sur votre carte. Une fois sur place, soignez l’Éclaireur brûlé par la magie gangrenée.', 'Si vous lisez ceci, il n’est peut-être pas trop tard. J’ai subi de graves brûlures en combattant la Légion.$b$bVous trouverez ci-joint un croquis de l’endroit où je me trouve par rapport au palais Croulant.$b$bDe grâce, faites vite, et apportez des fournitures.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42245, 'itIT', 'DEPRECATED: Singed Note', 'Porta 3 Unguenti Argentati e 1 Fornitura da Campo sul luogo indicato sulla mappa. Quando sarai sul posto, cura l\'Esploratore Vilustionato.', 'Vi prego, se state leggendo questo significa che c\'è ancora tempo. Sono rimasto gravemente ustionato durante il combattimento con la Legione.$b$bHo incluso un disegno abbozzato di dove mi trovo rispetto al Palazzo in Rovina.$b$bVi prego di fare in fretta e di portare delle provviste.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42245, 'koKR', 'DEPRECATED: Singed Note', '은빛 연고 3개와 야전 구급품 1개를 지도에 표시된 위치로 가져가서, 지옥화상 입은 정찰병을 치료해야 합니다.', '이 쪽지를 읽고 있는 당신, 아직 시간이 있는지도 모릅니다. 전 군단과 맞서 싸우다 심각한 화상을 입었습니다.$b$b바스러진 궁전을 기준으로 하여 제가 있는 곳의 위치를 간단히 그려 놓았습니다.$b$b서둘러 주십시오. 그리고 부디 보급품을 가져와 주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42245, 'ptBR', 'DEPRECATED: Singed Note', 'Leve 3 Bálsamos Prateados e 1 Mochila de Campo para o local marcado no seu mapa. Trate o Batedor com Queimadura Vil quando chegar.', 'Por favor, se você estiver lendo isto, então talvez ainda haja tempo. Me queimei gravemente enfrentando a Legião.$b$bIncluí um esboço grosseiro de onde estou em relação ao Palácio Desabado.$b$bPor favor, venha logo, e traga suprimentos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42245, 'ruRU', 'DEPRECATED: Singed Note', 'Принесите 3 флакона серебристого бальзама и 1 полевую аптечку в место, отмеченное на вашей карте. Обработайте раны обожженного скверной разведчика, когда прибудете на место.', 'Пожалуйста, не оставляйте меня в беде! Если вы читаете это, то, может быть, еще не слишком поздно. У меня сильные ожоги после битвы с Легионом.$b$bСейчас я возле Разрушенного дворца. Этот рисунок поможет понять, где меня искать.$b$bПожалуйста, принесите лекарства как можно скорее.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42245, 'zhCN', 'DEPRECATED: Singed Note', '将3份银色药膏和1个野外急救包带到地图上标记的地点。到达之后，治疗邪灼斥候。', '如果你能看到这张纸条，拜托你帮帮忙。我在和军团作战时被严重烧伤。$b$b我就在倾颓王宫附近，大概位置已经画出来了。$b$b请抓紧时间，务必带上医疗补给。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42245, 'zhTW', 'DEPRECATED: Singed Note', '將3瓶銀光油膏和1個野戰包送到地圖上標示的地點。抵達後，治療被魔化火焰灼傷的斥候。', '拜託，如果你看到這封信，請快點來救我。我在對抗燃燒軍團的時候，被嚴重燒傷。$b$b我在信裡附上一張簡要地圖，指出我與崩裂之殿的相對位置。$b$b請快點帶著補給品過來。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42250, 'deDE', 'DEPRECATED: Fevered Plea', 'Bringt 3 Feldverbandskästen zu dem Ort, der auf Eurer Karte markiert ist. Behandelt den fiebrigen Forscher, wenn Ihr dort eintrefft.', 'Ich bin einfach viel zu erschöpft. Dazu habe ich schon aufgrund der Hitze Halluzinationen.$b$bBitte, wer das hier findet, muss mir helfen. Und bitte Medizin mitbringen, um des Lichtes willen.$b$bIch glaube, ich sehe Todesschwinge kommen...$b$b<Die Tinte des letzten Buchstabens läuft hinab bis zum Ende der Notiz.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42250, 'esES', 'DEPRECATED: Fevered Plea', 'Lleva 3 mochilas de campo a la ubicación señalada en tu mapa. Trata al expedicionario febril cuando llegues.', 'No soporto más este agotamiento. Y el calor de este sitio me hace alucinar.$b$bPor favor, si alguien encuentra este mensaje, que me busque. Y que traiga medicinas, por el amor de la Luz.$b$bCreo que veo venir a Alamuerte...$b$b<La tinta de la última letra cae hasta el fondo de la nota.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42250, 'esMX', 'DEPRECATED: Fevered Plea', 'Lleva 3 mochilas de suministros a la ubicación marcada en tu mapa. Atiende al explorador febril en cuanto llegues.', 'Ya no aguanto más este agotamiento. Y este calor me está causando alucinaciones.$b$bSi hallas este mensaje, búscame, por favor. Y trae medicamentos, por la Luz.$b$bViene Alamuerte...$b$b<La tinta de la última letra se chorrea hasta el final del papel.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42250, 'frFR', 'DEPRECATED: Fevered Plea', 'Apportez 3 Paquetages à l’endroit indiqué sur votre carte. Une fois sur place, soignez l’Explorateur enfiévré.', 'Je ne supporte plus cet épuisement. Et la chaleur de cet endroit me donne des hallucinations.$b$bSi vous trouvez ce message, venez me chercher. Et apportez des médicaments, pour l’amour de la Lumière !$b$bJe crois que je vois arriver Aile de mort…$b$b<L’encre de la dernière lettre a coulé jusqu’au bas de la page.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42250, 'itIT', 'DEPRECATED: Fevered Plea', 'Porta 3 Forniture da Campo nel luogo indicato sulla tua mappa. Quando sarai sul posto, cura l\'Esploratore Febbricitante.', 'Non posso resistere ancora molto a tutta questa fatica. Il caldo mi sta facendo venire le visioni.$b$bVi prego, se qualcuno dovesse trovare questo messaggio, venite a cercarmi. E portate delle medicine, per la grazia della Luce.$b$bVedo Alamorte che sta arrivando...$b$b<L\'inchiostro sull\'ultima lettera è trascinato fino al fondo del messaggio.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42250, 'koKR', 'DEPRECATED: Fevered Plea', '야전 구급품 3개를 지도에 표시된 위치로 가져가서, 열병 걸린 탐험가를 치료해야 합니다.', '이제 움직일 힘도 없습니다. 게다가 이곳의 열기 때문인지 환각이 보이기 시작합니다.$b$b누군가 이 쪽지를 발견하거든 제발 절 찾아와 주십시오. 그리고 부디, 빛의 이름으로 의약품을 가져다 주시면 감사하겠습니다.$b$b눈앞에 데스윙이 보입니다...$b$b<마지막 글자의 잉크가 쪽지 아래쪽까지 이어져 있습니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42250, 'ptBR', 'DEPRECATED: Fevered Plea', 'Leve 3 Mochilas de Campo para o local marcado no seu mapa. Trate o Explorador Febril quando chegar.', 'Não aguento mais essa exaustão. E o calor deste lugar está me fazendo ver coisas.$b$bPor favor, se alguém encontrar esta mensagem, venha me procurar. E traga remédio, pelo amor da Luz.$b$bAcho que vejo Asa da Morte chegando...$b$b<A tinta da última letra escorre para o pé do bilhete.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42250, 'ruRU', 'DEPRECATED: Fevered Plea', 'Принесите 3 полевые аптечки в место, отмеченное на вашей карте. Дайте лекарство страдающему от лихорадки исследователю, когда прибудете на место.', 'Я больше не могу выносить эти мучения! И здесь так жарко, что мне начинает мерещиться всякое.$b$bПожалуйста, если найдете эту записку, помогите мне. И принесите лекарства, ради Света.$b$bКажется, Смертокрыл летит ко мне...$b$b<От последней буквы линия идет к нижнему краю листа.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42250, 'zhCN', 'DEPRECATED: Fevered Plea', '将3个野外急救包带到地图上标记的地点。到达之后，治疗发烧的探险者。', '我快支撑不下去了。而且，这里实在太热，我已经开始出现幻觉。$b$b如果有人看到这封信，请一定来帮帮我。圣光在上，别忘了带上医疗补给。$b$b我好像看到死亡之翼来了……$b$b<文字的最后一笔一直划到了纸条底端。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42250, 'zhTW', 'DEPRECATED: Fevered Plea', '將3個野戰包送到地圖上標示的地點。抵達後，治療發高燒的探險者。', '我再也撐不下去了。這個地方的高溫讓我產生幻覺。$b$b拜託，如果有人看到這則訊息，請去找我。看在聖光的份上，為我帶點藥來。$b$b我好像看到死亡之翼飛過來了…$b$b<最後一個字的墨水滴在紙條底部。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42255, 'deDE', 'DEPRECATED: Bloody Note', 'Bringt 3 seidengewebte Verbände und 1 Feldverbandskasten zu dem Ort, der auf Eurer Karte markiert ist. Behandelt den schwer verletzten Soldaten, wenn Ihr dort eintrefft.', 'Ich bin nicht sicher, wie lange ich noch Druck auf diese Wunde ausüben kann.$b$bWenn Ihr diese Notiz findet, dann bringt bitte Verbände mit. Sehr viele Verbände.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42255, 'esES', 'DEPRECATED: Bloody Note', 'Lleva 3 vendas de filoseda y 1 mochila de campo a la ubicación señalada en tu mapa. Atiende al soldado gravemente herido cuando llegues.', 'No sé cuánto tiempo más podré aplicar presión sobre esta herida.$b$bSi encuentras esta nota, trae vendas, por favor. Muchas vendas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42255, 'esMX', 'DEPRECATED: Bloody Note', 'Lleva 3 vendas de filoseda y 1 mochila de suministros a la ubicación marcada en tu mapa. Atiende al soldado gravemente herido en cuanto llegues. ', 'No sé por cuánto tiempo más pueda seguir haciendo presión en esta herida.$b$bSi encuentras esta nota, trae vendas, por favor. Muchísimas vendas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42255, 'frFR', 'DEPRECATED: Bloody Note', 'Apportez 3 Bandages en soie tissée et 1 Paquetage à l’endroit indiqué sur votre carte. Une fois sur place, soignez le Soldat grièvement blessé.', 'Je ne sais pas combien de temps je vais encore pouvoir faire pression sur cette blessure.$b$bSi vous trouvez ce message, apportez des bandages. Beaucoup de bandages.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42255, 'itIT', 'DEPRECATED: Bloody Note', 'Porta 3 Bende di Fildiseta e 1 Fornitura da Campo nel luogo indicato sulla tua mappa. Quando sarai sul posto, cura il Soldato Ferito Gravemente.', 'Non so quanto ancora riuscirò a tenere la ferita sotto pressione.$b$bSe qualcuno dovesse trovare questo messaggio, vi prego, portatemi delle bende. Un sacco di bende.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42255, 'koKR', 'DEPRECATED: Bloody Note', '비단매듭 붕대 3개와 야전 구급품 1개를 지도에 표시된 위치로 가져가서, 심하게 부상당한 병사를 치료해야 합니다.', '이 상처를 언제까지 압박할 수 있을지 모르겠습니다.$b$b이 쪽지를 발견하시거든, 부디 붕대를 가져와 주십시오. 아주... 많이 필요합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42255, 'ptBR', 'DEPRECATED: Bloody Note', 'Traga 3 Bandagens de Tramasseda e 1 Mochila de Campo para o local marcado no seu mapa. Trate o Soldado Gravemente Ferido quando chegar.', 'Não sei quanto tempo eu poderei manter pressão neste ferimento.$b$bSe você encontrar este bilhete, por favor traga bandagens. Muitas e muitas bandagens.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42255, 'ruRU', 'DEPRECATED: Bloody Note', 'Принесите 3 бинта из шелкового полотна и 1 полевую аптечку в место, отмеченное на вашей карте. Перевяжите тяжелораненого солдата, когда прибудете на место.', 'Не знаю, сколько еще я смогу зажимать рану.$b$bТому, кто найдет эту записку: пожалуйста, принесите бинты. И побольше бинтов!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42255, 'zhCN', 'DEPRECATED: Bloody Note', '将3份丝纹绷带和1个野外急救包带到地图上标记的地点。到达之后，治疗重伤的士兵。', '不知道我还能把伤口压多久。$b$b如果你看到这张便条，请务必为我带来绷带。我需要很多很多的绷带。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42255, 'zhTW', 'DEPRECATED: Bloody Note', '將3捲絲織繃帶和1個野戰包送到地圖上標示的地點。抵達後，治療受重傷的士兵。', '我不知道我還能壓住這個傷勢多久。$b$b如果你看到這張紙條，請帶些繃帶過來。越多越好。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42268, 'deDE', 'Schiffbrüchige Matrosen', 'Rettet $1oa Matrosen der Revanche der Königin.', '<Die Gilblin und Makrura von Mak\'rana haben die Matrosen der Verlassenen aus dem nahen Schiffswrack gefangen genommen.>\n\n<Es sieht so aus, als würden sie sehr schlecht behandelt. Also ist wohl eine Rettungsmission angebracht.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42268, 'esES', 'Marineros naufragados', 'Rescata a $1oa marineros de La Represalia de la Reina.', '<Los gilblin y makruras de Mak\'rana han capturado a los marineros del naufragio cercano.>\n\n<Los están tratando muy mal. Se impone una operación de rescate.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42268, 'esMX', 'Marineros náufragos', 'Rescata a $1oa marineros de la represalia de la reina.', '<Los gilblin y makrura de Mak\'rana capturaron a marineros de los renegados en un naufragio cercano>.\n\n<Los están torturando. Es hora de agendar una operación de rescate>.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42268, 'frFR', 'Marins naufragés', 'Secourez $1oa Marins du Représailles de la reine.', '<Les gloubelins et les makruras de Mak’rana ont capturé les marins réprouvés de l’épave voisine.>\n\n<Ces pauvres bougres sont cruellement maltraités. Il faut organiser une opération de sauvetage.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42268, 'itIT', 'Marinai naufragati', 'Salva $1oa Marinai della Rappresaglia della Regina.', '<I Branclin e i Makrura di Mak\'rana hanno catturato i marinai Reietti naufragati qui vicino.>\n\n<I marinai sono stati trattati con grande crudeltà, bisogna preparare un\'operazione di salvataggio.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42268, 'koKR', '선원에게 안식을', '여왕의 복수호 선원 $1oa명을 구출해야 합니다.', '<마크라나의 길블린과 마크루라가 근처의 난파선에서 포세이큰 선원을 붙잡았습니다.>\n\n<선원들의 몰골은 눈 뜨고 보기 힘들 정도입니다. 더 늦기 전에 그들을 구출해야 합니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42268, 'ptBR', 'Marinheiros náufragos', 'Resgate $1oa Marinheiros do Vingança da Rainha.', '<Os guelblins e os makruras de Mak\'rana capturaram os marinheiros abandonados no naufrágio que aconteceu na região.>\n\n<Os marinheiros estão sendo maltratados. Parece que uma operação de resgate é necessária.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42268, 'ruRU', 'Моряки-утопленники', 'Спасите $1oa |4матроса:матросов:матросов; с разбившейся \"Мести королевы\".', '<Жаблины и макруры Мак\'раны захватили в плен потерпевших кораблекрушение моряков с разбившегося корабля, остов которого находится неподалеку.>\n\n<Похоже, они очень плохо с ними обращаются. Следует организовать спасательную операцию.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42268, 'zhCN', '遇难水手', '解救$1oa名女王的复仇号水手。', '<玛拉纳的海地精和龙虾人从附近的沉船处抓走了被遗忘者水手。>\n\n<水手们似乎受到了毒害，或许他们的境况比这更糟。你应该终结他们的痛苦。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42268, 'zhTW', '船難水手', '救出$1oa名女王復仇號的水手。', '<馬克拉納的吉爾布林以及龍蝦人從附近的沉船處抓了無依無靠的水手。>\n\n<水手們遭受慘無人道的虐待。看來救援行動是勢在必行了。>', '', '', '', '', '', '', 0);
