INSERT INTO `quest_template_locale` VALUES (42396, 'ptBR', 'Faz a coisa!', '?????', 'Mrglrg! Mrlglr mrgllrg.$b$b<Pedro gesticula loucamente>$b$bMrlgrlg glrglmglr mrlglrg. Rglrgmrg mrglr glr. Glr! Gmrlgrmlg mrglmrgm...$b$b<Pedro olha para você com expectativa.>', '?????', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42396, 'ruRU', 'Ну же, давай!', '?????', 'Мрглмргл! Мрглмргл! Мрглмргл!$b$b<Фин бешено жестикулирует.>$b$bМрглмргл! Мрглмргл! Рглр! Мргл! Глр! Глр! Грлмгрмрл мрглмргл... $b$b<Фин смотрит на вас выжидающе.>', '?????', '', '', '', '', '', 0);
