INSERT INTO `quest_template_locale` VALUES (42857, 'deDE', 'Feuchtes Fresschen', 'Sammelt $1oa feuchte Graukämme in Grauholz.', 'Es scheint, als würden die Grell sich von diesen Früchten ernähren.\n\nDer alte Graurücken wäre sicher erfreut, wenn sie aus dem Dorf entfernt werden würden.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42857, 'esES', 'Setos frutales', 'Consigue $1oa chumbogrises húmedos en Forestagrís.', 'Estas frutas parecen ser comida para los grells.\n\nAl Viejo Espaldagrís le gustaría que desaparecieran de la aldea.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42857, 'esMX', 'Húmedo en los bordes', 'Recoge $1oa crestapardos húmedos en Forestaparda.', 'Esta fruta parece ser comida de grell.\n\nEl viejo Lomopardo estaría agradecido si pudieras erradicarlos de la aldea.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42857, 'frFR', 'Des fruits encombrants', 'Récupérez $1oa Grisitchis tendres à Grisbois.', 'Les grells semblent se nourrir de ces fruits.\n\nLe vieux Griséchine apprécierait que quelqu’un en débarrasse le village.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42857, 'itIT', 'L\'umidità sulle siepi', 'Raccogli $1oa Crestebrade Umide a Boscorozzo.', 'A quanto pare questi frutti sono cibo per i Grell.\n\nDorsobrado Anziano apprezzerà che vengano tolti dal villaggio.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42857, 'koKR', '수상한 과일', '회색숲에서 회색숲 물렁가시 $1oa개를 모아야 합니다.', '이 과일은 그렐의 식량인 것 같습니다.\n\n마을에서 이 과일을 없애 주면 늙은 그리즐백이 고마워할 것입니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42857, 'ptBR', 'Umidade nas bordas', 'Colete $1oa Cinzilos Suculentos no Descampado Cinzento.', 'Essas frutas parecem ser alimento para os capetas.\n\nO Velho Cinéreo deve ficar feliz se elas forem removidas da vila.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42857, 'ruRU', 'Кактус подзаборный', 'Добудьте $1oa |4сочный кактус:сочных кактуса:сочных кактусов; в Седой чаще.', 'Похоже, грелли питаются этими плодами.\n\nСтарик Седоспин будет признателен, если в деревне их больше не останется.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42857, 'zhCN', '潮湿之篱', '在灰白原野中收集$1oa个潮湿的刺肉果。', '这些水果是劣魔的食物。\n\n如果把它们从村庄里清除掉，老灰背也许会非常感激。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42857, 'zhTW', '潮濕樹籬', '前往灰白曠野，收集$1oa個富含水分的灰白氈果。', '這些水果看來是劣魔的食物。\n\n如果將這些水果從村莊移走，老灰背或許會承你的情。', '', '', '', '', '', '', 0);
