INSERT INTO `quest_template_locale` VALUES (42962, 'frFR', 'Correspondance secrète', 'Procurez-vous 3 Piles de lettres auprès de Messagers sacrenuit à l’aide de la Sorcelame sacrenuit.', '', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42962, 'itIT', 'Corrispondenza segreta', 'Recupera 3 Cataste di Lettere dai Corrieri Nobili Oscuri usando la Lama Incantata dei Nobili Oscuri.', '', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42962, 'koKR', '비밀 서신', '나이트본 마법검을 사용해 나이트본 급사로부터 편지 3통을 획득해야 합니다.', '', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42962, 'ptBR', 'Correspondência secreta', 'Consiga 3 Pilhas de Cartas dos Mensageiros Filhos da Noite utilizando a Lâmina Enfeitiçada dos Filhos da Noite.', '', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (42962, 'ruRU', 'Секретная переписка', 'Заберите 3 пачки писем у ночнорожденных-курьеров с помощью чародейского клинка ночнорожденных.', '', '', '', '', '', '', '', 0);
