INSERT INTO `quest_template_locale` VALUES (43179, 'frFR', 'Le Kirin Tor de Dalaran', 'Accomplissez 3 expéditions du Kirin Tor.', 'Aidez le Kirin Tor de Dalaran en accomplissant 3 expéditions.', '', '', '', '', '', 'Retournez voir le Mage de guerre Silva à Dalaran.', 0);
INSERT INTO `quest_template_locale` VALUES (43179, 'itIT', 'Il Kirin Tor di Dalaran', 'Completa 3 missioni mondiali del Kirin Tor.', 'Aiuta il Kirin Tor di Dalaran completando 3 missioni mondiali.', '', '', '', '', '', 'Torna dal Mago da Guerra Silva a Dalaran.', 0);
INSERT INTO `quest_template_locale` VALUES (43179, 'koKR', '달라란의 키린 토', '키린 토 전역 퀘스트 3개를 완료해야 합니다.', '전역 퀘스트 3개를 완료하여 달라란의 키린 토를 도와야 합니다.', '', '', '', '', '', '달라란에 있는 전쟁마법사 실바에게 가야 합니다.', 0);
