INSERT INTO `quest_template_locale` VALUES (43186, 'koKR', '내가 바로 학살자다!', '일리다리와 일리단의 하수인들이 환호하는 목소리를 들어야 합니다.', '$n, 우리는 그대의 지도력과 성공적인 위업, 고결한 희생에 경의를 표하기 위해 이곳에 모였소.', '', '', '', '', '', '', 0);
