INSERT INTO `quest_template_locale` VALUES (43635, 'frFR', 'Pour le bien de tous', 'Empruntez le portail de Dalaran flottant au-dessus de Karazhan pour gagner la Dalaran des îles Brisées.', '[CECI EST UNE QUÊTE TEMPORAIRE QUI VOUS MÈNERA À LA DALARAN DES ÎLES BRISÉES]$b$bVeuillez emprunter ce portail pour gagner Dalaran aux îles Brisées et partir en quête de votre arme prodigieuse !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43635, 'itIT', 'Il bisogno di tanti', 'Prendi il portale che conduce da Dalaran sopra Karazhan a Dalaran sopra le Isole Disperse.', '[THIS IS A TEMPORARY QUEST TO GET YOU TO DALARAN IN THE BROKEN ISLES.]$b$bPlease take this portal to Dalaran in the Broken Isles and begin your artifact quest!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43635, 'koKR', '다수의 요구', '카라잔 위에 있는 달라란에서 차원문을 통해 부서진 섬에 있는 달라란으로 가야 합니다.', '[부서진 섬의 달라란으로 가는 임시 퀘스트]$B$B어서 이 차원문을 통해 부서진 섬에 있는 달라란으로 가서, 유물 퀘스트를 시작하십시오!', '', '', '', '', '', '', 0);
