INSERT INTO `quest_template_locale` VALUES (43635, 'ruRU', 'Нужды многих', 'Шагните в портал в Даларане, что над Каражаном, и перенеситесь в Даларан на Расколотых островах.', '[ЭТО ВРЕМЕННОЕ ЗАДАНИЕ, КОТОРОЕ ПОМОЖЕТ ВАМ ПОПАСТЬ В ДАЛАРАН НА РАСКОЛОТЫХ ОСТРОВАХ.]$b$bШагните в этот портал, который перенесет вас в Даларан на Расколотых островах, и приступите к заданию на получение вашего артефакта!', '', '', '', '', '', '', 0);
