INSERT INTO `quest_template_locale` VALUES (43808, 'koKR', '달빛 수호자 순간이동기 가동!', '이동술 신호 장치에 고대 마나를 주입해야 합니다.', '<이동술 신호 장치가 비활성화된 상태이지만, 망가진 것은 아닙니다. 고대 마나가 있으면 다시 가동시킬 수 있을 것입니다.>', '고대 마나로 이동술 신호 장치를 재활성화해야 합니다.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43808, 'ptBR', 'Teleportador da Guarda da Lua ativado!', 'Infunda o Sinalizador de Telemancia em Mana Ancestral.', '<O sinalizador de telemancia está desativado, mas intacto. Um pouco de Mana Ancestral deve fazê-lo funcionar de novo.>', 'Use Mana Ancestral para reativar o Sinalizador de Telemancia.', '', '', '', '', '', 0);
