INSERT INTO `quest_template_locale` VALUES (43808, 'zhCN', '月之守卫传送门启动！', '将远古魔力注入传送道标。', '<传送道标并没有激活，但至少完好无损。只要一些远古魔力就能让它重新启动。>', '使用远古魔力，重新启动传送道标。', '', '', '', '', '', 0);
