INSERT INTO `quest_template_locale` VALUES (43906, 'itIT', 'Incubo di Smeraldo: Caduta di Cenarius', 'Sconfiggi Cenarius nell\'Incubo di Smeraldo.', 'Anche se sappiamo che l\'Incubo è capace di dipanare il confine tra realtà e sogno, non c\'è dubbio che la minaccia sia ormai incombente. I nostri stessi alleati sono caduti vittima dell\'oscurità.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43906, 'koKR', '에메랄드의 악몽: 세나리우스의 몰락', '에메랄드의 악몽에 있는 세나리우스를 처치해야 합니다.', '악몽이 현실과 꿈의 경계를 뒤틀고 있다는 걸 알아냈어도, 여전히 위험한 상황입니다. 우리의 아군이 어둠에 침식당하고 있습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43906, 'ptBR', 'O Pesadelo Esmeralda: A queda de Cenarius', 'Derrote Cenarius no Pesadelo Esmeralda.', 'Sabemos que o Pesadelo torce os limites entre realidade e sonho, e não há dúvidas sobre a ameaça sobre nós. Nossos próprios aliados caíram na escuridão.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43906, 'ruRU', 'Изумрудный Кошмар: падение Кенария', 'Победите Кенария в Изумрудном Кошмаре.', 'Кошмар искажает границу между реальностью и вымыслом, и от него определенно исходит угроза. Наших союзников поглотила тьма.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43906, 'zhCN', '翡翠梦魇：塞纳留斯的堕落', '击败翡翠梦魇的塞纳留斯。', '我们都知道，梦魇会扭曲现实和梦境之间的边界，毫无疑问，这对我们造成威胁。我们的盟友已经堕入了黑暗。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43906, 'zhTW', '翡翠夢魘：塞納留斯的殞落', '在翡翠夢魘中擊敗塞納留斯。', '我們既然知道夢魘會扭曲現實與夢境的界限，相信我們的威脅也已經浮出水面。我們的盟友已經墮落到黑暗深淵。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43907, 'deDE', 'Der Smaragdgrüne Alptraum: Xavius', 'Besiegt Xavius im Smaragdgrünen Alptraum.', 'Die Zeit ist gekommen, gegen die Ursache des Alptraums vorzugehen und das Gleichgewicht im Smaragdgrünen Traum wiederherzustellen. Kämpft Euch in das Herz des Alptraums vor und stellt Euch dem Alptraumfürsten Xavius.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43907, 'esES', 'Pesadilla Esmeralda: Xavius', 'Derrota a Xavius en la Pesadilla Esmeralda.', 'Ha llegado la hora de enfrentarse a la fuente de la Pesadilla y restaurar el equilibrio en el Sueño Esmeralda. Ábrete paso hasta el corazón de la Pesadilla y enfréntate al Señor de la Pesadilla, Xavius.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43907, 'esMX', 'La Pesadilla esmeralda: Xavius', 'Derrota a Xavius en la Pesadilla esmeralda.', 'Llegó la hora de atacar a la fuente de la Pesadilla y restaurar el equilibrio del Sueño Esmeralda. Ve a luchar al corazón de la Pesadilla y enfréntate al Señor de la Pesadilla Xavius.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43907, 'frFR', 'Cauchemar d’émeraude : Xavius', 'Triomphez de Xavius dans le Cauchemar d’émeraude.', 'Le temps est venu d’attaquer le Cauchemar à la racine et de restaurer l’équilibre du Rêve d’émeraude. Taillez-vous un chemin jusqu’au cœur du Cauchemar, puis affrontez son seigneur Xavius.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43907, 'itIT', 'Incubo di Smeraldo: Xavius', 'Sconfiggi Xavius nell\'Incubo di Smeraldo.', 'È giunto il momento di trovare la sorgente dell\'Incubo e ripristinare l\'equilibrio nel Sogno di Smeraldo. Avventurati nel cuore dell\'Incubo e affronta il Signore dell\'Incubo Xavius.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43907, 'koKR', '에메랄드의 악몽: 자비우스', '에메랄드의 악몽에 있는 자비우스를 처치해야 합니다.', '악몽의 근원과 맞서고 에메랄드의 꿈에 균형을 되찾아야 할 때가 왔습니다. 악몽의 한가운데로 들어가 악몽의 군주, 자비우스와 맞서 싸우십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43907, 'ptBR', 'O Pesadelo Esmeralda: Xavius', 'Derrote Xavius no Pesadelo Esmeralda.', 'É chegada a hora de lidar com a fonte do Pesadelo e restaurar o equilíbrio ao Sonho Esmeralda. Batalhe para dentro do coração do Pesadelo e enfrente o senhor do pesadelo Xavius.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43907, 'ruRU', 'Изумрудный Кошмар: Ксавий', 'Победите Ксавия в Изумрудном Кошмаре.', 'Пришло время уничтожить причину Кошмара и восстановить равновесие в Изумрудном Сне. Пробейся в само Сердце Кошмара и сразись с Владыкой Кошмара Ксавием.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43907, 'zhCN', '翡翠梦魇：萨维斯', '击败翡翠梦魇的萨维斯。', '是时候清除梦魇的根源并让翡翠梦境重归平衡了。深入梦魇之心，并战胜梦魇之王萨维斯。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43907, 'zhTW', '翡翠夢魘：薩維斯', '在翡翠夢魘中擊敗薩維斯。', '是時候解決夢魘的源頭了，以便讓翡翠夢境恢復平衡。一路殺進夢魘的中心，迎戰夢魘之王薩維斯。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43908, 'deDE', 'Der Smaragdgrüne Alptraum: Der Fall von Cenarius', 'Besiegt Cenarius im Smaragdgrünen Alptraum.', 'Wir wissen zwar, dass der Alptraum die Grenzen zwischen Realität und Traum verschwimmen lässt, aber die Bedrohung ist für uns ganz real. Unsere eigenen Verbündeten sind der Dunkelheit verfallen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (43908, 'esES', 'Pesadilla Esmeralda: La caída de Cenarius', 'Derrota a Cenarius en la Pesadilla Esmeralda.', 'Mientras la Pesadilla siga distorsionando los límites entre la realidad y el sueño, no hay duda de que será una amenaza para nosotros. Nuestros propios aliados han caído en la oscuridad.', '', '', '', '', '', '', 0);
