INSERT INTO `quest_template_locale` VALUES (44159, 'esES', 'Un vale para Marin', 'Entrega el vale de cámara a Marin Tragonublo.', 'Tienes que entregar este vale de cámara a Marin Tragonublo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44159, 'esMX', 'Un boleto para Marin', 'Entrégale el boleto a Marin Tragonublo.', 'Este boleto para la Bóveda debe ser entregado a Marin Tragonublo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44159, 'frFR', 'Un ticket pour Marin', 'Apportez le Ticket du caveau à Marin Brouillecaboche.', 'Vous devriez apporter ce ticket du caveau à Marin Brouillecaboche.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44159, 'itIT', 'Un biglietto per Marin', 'Consegna il Biglietto delle Segrete a Marin Granstrippo.', 'Questo Biglietto delle Segrete va consegnato a Marin Granstrippo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44159, 'koKR', '마린을 위한 표', '마린 노겐포저에게 금고 입장권을 가져가야 합니다.', '이 금고 입장권을 마린 노겐포저에게 가져가야 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44159, 'ptBR', 'Um cupom para Marin', 'Entregue o Cupom da Câmara a Marin Nublacuca.', 'Este Cupom da Câmara deve ser entregue a Marin Nublacuca.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44159, 'ruRU', 'Ваучер для Гогельмогеля', 'Принесите ваучер хранилища Синю Гогельмогелю.', 'Этот ваучер хранилища нужно доставить Синю Гогельмогелю.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44159, 'zhCN', '马林的宝库赠品券', '将宝库赠品券交给马林·诺格弗格。', '这张宝库赠品券应当交到马林·诺格弗格手中。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44159, 'zhTW', '馬林的領物券', '將保險庫領物券交給馬林‧諾格弗格。', '這張保險庫領物券應該交給馬林‧諾格弗格。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44161, 'deDE', 'Was Gaardoun so braucht', 'Benutzt die Späherkarte und schließt die Mission \"Was Gaardoun so braucht\" ab.', 'Schlachtenfürst Gaardoun hat angeboten, weitere zerschlagene Krieger für die Sache anzuwerben. Er benötigt vorher aber ein paar Dinge. Er erwähnte so etwas wie das richtige Werkzeug für die Aufgabe, oder sowas.\n\nEs wird Zeit, unsere frischgebackenen Champions auf die Probe zu stellen. Ich bin sicher, es wird eine aufreibende Mission.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44161, 'esES', 'Lo que necesita Gaardoun', 'Usa el mapa de exploración y completa la misión \"Lo que necesita Gaardoun\".', 'El señor de la batalla Gaardoun se ha ofrecido a reclutar más guerreros Tábidos para la causa. Solo necesita algunas cosas. Comentó algo sobre la herramienta adecuada para el trabajo, o algo así.\n\nEs hora de probar a tus nuevos campeones. Esta misión va a ser agotadora, seguro.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44161, 'esMX', 'Cosas necesarias para Gaardoun', 'Usa el mapa de exploración y completa la misión \"Cosas necesarias para Gaardoun\".', 'El señor de la batalla Gaardoun ofreció reclutar más guerreros quebrados para la causa. Solo necesita algunas cosas primero. Dijo algo acerca de la herramienta correcta para el trabajo, o algo así.\n\nEs hora de poner a prueba a nuestros campeones nuevos. De seguro será una misión extenuante.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44161, 'frFR', 'Ce dont Gaardoun a besoin', 'Utilisez la Carte de reconnaissance et accomplissez la mission « Ce dont Gaardoun a besoin ».', 'Le seigneur de bataille Gaardoun a proposé de recruter d’autres guerriers Roués pour notre cause. Mais pour cela, il lui faudrait « deux-trois petites choses ». Les bons outils pour le travail à effectuer, à l’en croire…\n\nIl va être temps de mettre vos nouveaux champions à l’épreuve. Cette mission s’annonce des plus difficiles, j’ai l’impression.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44161, 'itIT', 'I desideri di Gaardoun', 'Usa la Mappa d\'Esplorazione e completa la missione \"I desideri di Gaardoun\".', 'Il Signore della Battaglia Gaardoun si è offerto di reclutare altri guerrieri Corrotti per la causa. Ha solo bisogno di alcuni oggetti, prima. Parla di qualcosa come \"lo strumento giusto per il lavoro da fare\", o qualcosa del genere.\n\nÈ ora di verificare la bravura dei tuoi nuovi campioni... e di sicuro sarà una missione estenuante.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44161, 'koKR', '가르둔에게 필요한 것', '정찰 지도를 사용해 \"가르둔에게 필요한 것\" 임무를 완료해야 합니다.', '전투군주 가르둔이 우리 목표를 위해 뒤틀린 전사들을 더 많이 모집하겠다고 했습니다. 다만 우선 필요한 게 몇 가지 있다고 하더군요. 임무에 알맞은 도구가 따로 있을 거라나요?\n\n새롭게 모집한 용사들을 시험해볼 시간이 됐습니다. 아마 쉽지 않은 임무일 거예요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44161, 'ptBR', 'Coisas que Gaardoun precisa', 'Use o Mapa de Patrulha e conclua a missão \"Pedido de Gaardoun\".', 'O senhor da batalha Gaardoun ofereceu recrutar mais guerreiros degradados para a causa. Ele só precisa de algumas coisas antes. Ele disse algo sobre a ferramenta certa para o trabalho, ou algo assim.\n\nÉ hora de testar seus novos campeões. Tenho certeza de que será uma missão exaustiva.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44161, 'ruRU', 'Список Гаардуна', 'Воспользуйтесь тактической картой и выполните задание \"Список Гаардуна\".', 'Военачальник Гаардун предложил завербовать побольше сломленных воинов для нашего дела. Но вначале ему кое-что нужно. Он что-то говорил о подходящем инструменте.\n\nПора испытать новых защитников в деле. Думаю, задание будет непростым.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44161, 'zhCN', '加尔顿的需求', '使用侦察地图，完成“加尔顿的需求”任务。', '战争领主加尔顿主动请缨，要为我们的事业招募更多的破碎者战士。首先，他需要一些东西。他提到要有合适的工具才能事半功倍之类的话。\n\n是时候派出你的新勇士接受考验了。我敢肯定，这次的任务会非常辛苦。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44161, 'zhTW', '蓋爾道恩需要的東西', '使用偵察地圖，完成「蓋爾道恩需要的東西」任務。', '戰鬥領主蓋爾道恩願意協助我們招募更多的破碎者，一同對抗燃燒軍團。但是他得先拿到幾樣東西，什麼正確的工具之類的。\n\n你新招募的勇士正好可以派上用場。我相信這個任務一定很有挑戰性。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44164, 'deDE', 'Ein brennender Pfad durch die Zeit', 'Schließt 5 Zeitwanderungsdungeons ab.', 'Der gleißende Stern wirft sein Licht auf das Land und lässt die Erde verdorren und ganze Landschaften versengen. Es ist ein gnadenloser, fast schon bösartiger Stern. Unter seinem Einfluss eröffnen sich neue Wege – zu weit entfernten Orten und sogar zu weit entfernten Zeiten. Kein Weiser ist in der Lage, die Bedeutung dieses Zeichens vollständig zu verstehen, aber das Glück ist mit den Mutigen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44164, 'esES', 'Una senda ardiente a través del tiempo', 'Completa 5 mazmorras de Paseo en el tiempo.', 'La estrella llameante brilla en el horizonte, abrasando la tierra y secando el paisaje. La estrella es despiadada, casi malévola. Mientras se mantenga su influjo pueden abrirse nuevos senderos que conducen hacia lugares lejanos, e incluso hacia tiempos lejanos. No hay sabiduría capaz de comprender la naturaleza de esta señal, pero la fortuna favorece a los osados.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44164, 'esMX', 'Un camino ardiente por el tiempo', 'Completa 5 calabozos de cronoviaje.', 'La estrella ardiente brilla sobre la tierra, arrasando el suelo y quemando el paisaje. Esta estrella es implacable, casi malévola. Mientras tenga influencia, se podrán abrir nuevos caminos a lugares lejanos e incluso a tiempos distantes. Ningún sabio comprende realmente la naturaleza de esta señal, pero la fortuna favorece a los valientes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44164, 'frFR', 'Un passage brûlant dans les limbes du temps', 'Terminez 5 donjons des Marcheurs du temps.', 'L’étoile ardente éclaire le monde, brûlant la terre et desséchant le paysage. Cette étoile est implacable, presque malveillante même. Tant que son influence perdure, de nouveaux chemins vers des lieux éloignés dans l’espace ou dans le temps peuvent s’ouvrir. Aucun sage ne comprend réellement la nature de ce signe, mais la chance sourit aux audacieux.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44164, 'itIT', 'Una strada infuocata attraverso il tempo', 'Completa 5 Viaggi nel Tempo.', 'La stella fiammeggiante risplende, bruciando la terra e rendendo secco il paesaggio. Questa stella è spietata, quasi maligna. Nonostante la sua influenza, nuove strade possono aprirsi, verso luoghi e anche tempi distanti. Nessun saggio comprende la natura di questo segno, ma la fortuna aiuta gli audaci...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44164, 'koKR', '시간 속의 불타는 길', '시간여행 던전 5개를 완료해야 합니다.', '불타는 별이 이 땅 위에서 빛나며 대지를 불태우고, 할퀴고 있습니다. 이 별은 잔인합니다. 악의가 가득하다고 표현해도 될 정도입니다. 별의 영향력이 지속되는 동안 새로운 길이 열릴 겁니다. 먼 곳, 심지어 먼 시간에 이르는 길이. 어떤 현자도 이 징조의 진정한 의미를 이해할 수는 없겠지요. 그러나 행운의 여신은 대담한 자를 좋아하는 법입니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44164, 'ptBR', 'Uma trilha incandescente através do tempo', 'Complete 5 Masmorras de Caminhada Temporal.', 'A estrela ardente brilha sobre a terra, abrasando o chão e calcinando a paisagem. A estrela é inclemente, quase malévola. Enquanto sua influência perdurar, novos caminhos poderão se abrir, para lugares, talvez até tempos longínquos. Nenhum sábio compreende de fato a natureza deste sinal, mas a sorte favorece os audazes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44164, 'ruRU', 'Пылающий путь сквозь время', 'Пройдите 5 подземелий в режиме путешествия во времени.', 'Пылающая звезда, что недавно воцарилась на небосводе, иссушает землю. Кажется, она задалась целью выжечь под собой все живое. Пока ее влияние в этом мире сильно, перед тобою могут открыться новые дороги – в иные края и даже в другие времена. Даже мудрецы пока не могут понять природы этого предзнаменования, но смельчакам наверняка улыбнется удача.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44164, 'zhCN', '燃烧的时光之路', '完成5个时空漫游地下城。', '燃烧之星照耀着大地，使土地布满焦痕，四处都是干枯的景象。那是一颗残忍而恶毒的星星。在它的影响持续期间，新的道路将被打开，通往遥远的空间甚至时间。没有哪位贤者真正了解这一天象的涵义，但英勇的行为总会得到幸运的眷顾。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44164, 'zhTW', '穿越時間的燃燒之路', '完成5次時光漫遊地城冒險', '熾炎之星照耀大地，燒灼土壤、烘烤地面。這是一枚無情之星，幾近惡意。在它的影響之下，可能會開啟通往遠方、甚至前往其他時間的新道路。賢者們無法瞭解這個徵兆，但好運往往眷顧英勇之士。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44166, 'deDE', 'Ein gefrorener Weg durch die Zeit', 'Schließt 5 Zeitwanderungsdungeons ab.', 'Der kalte Stern wirft sein frostiges Licht auf das Land und selbst die Schatten ziehen sich zurück. Es ist ein Stern wie kein zweiter, der alle Wärme und Leben entzieht. Unter seinem Einfluss eröffnen sich neue Wege – zu weit entfernten Orten und sogar zu weit entfernten Zeiten. Kein Weiser ist in der Lage, die Bedeutung dieses Zeichens vollständig zu verstehen, aber das Glück ist mit den Mutigen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44166, 'esES', 'Una senda helada a través del tiempo', 'Completa 5 mazmorras de Paseo en el tiempo.', 'La estrella helada brilla en el horizonte con una fría luz que hace que hasta los espíritus se refugien en las sombras. Esta estrella, a diferencia de las demás, absorbe la calidez y la vida. Mientras se mantenga su influjo pueden abrirse nuevos senderos que conducen hacia lugares lejanos, e incluso hacia tiempos lejanos. No hay sabiduría capaz de comprender la naturaleza de esta señal, pero la fortuna favorece a los osados.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44166, 'esMX', 'Un camino helado por el tiempo', 'Completa 5 calabozos de cronoviaje.', 'La estrella fría brilla sobre la tierra con su gélida luz e incluso los espíritus se esconden en las sombras. Esta estrella es distinta a todas las otras del cielo, pues absorbe el calor y la vida. Mientras tenga influencia, se podrán abrir nuevos caminos a lugares lejanos e incluso a tiempos distantes. Ningún sabio comprende realmente la naturaleza de esta señal, pero la fortuna favorece a los valientes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44166, 'frFR', 'Un passage glacial dans les limbes du temps', 'Terminez 5 donjons des Marcheurs du temps.', 'L’étoile glaciale éclaire le monde de sa lumière froide, en repoussant même les esprits dans les ombres. Cette étoile ne ressemble à aucune autre dans le ciel : elle absorbe la chaleur et la vie. Tant que son influence perdure, de nouveaux chemins vers des lieux éloignés dans l’espace ou dans le temps peuvent s’ouvrir. Aucun sage ne comprend réellement la nature de ce signe, mais la chance sourit aux audacieux.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44166, 'itIT', 'Una strada bloccata nel tempo', 'Completa 5 Viaggi nel Tempo.', 'La stella del gelo risplende su questa terra, e la sua luce è così fredda che persino gli spiriti si ritirano nelle ombre. Questa stella è differente da ogni altra nel cielo: indebolisce il calore e la vita. Nonostante la sua influenza, nuove strade possono aprirsi, verso luoghi e anche tempi distanti. Nessun saggio comprende la natura di questo segno, ma la fortuna aiuta gli audaci...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44166, 'koKR', '시간 속의 얼어붙은 길', '시간여행 던전 5개를 완료해야 합니다.', '얼어붙은 별이 이 땅 위에 빛나며 그 차가운 빛으로 영혼마저 어둠 속으로 쫓아내고 있습니다. 이 별은 저 하늘에 있는 다른 별들과는 달라, 온기와 생명을 빼앗아 가지요. 별의 영향력이 지속되는 동안 새로운 길이 열릴 것입니다. 먼 곳, 심지어 먼 시간에 이르는 길이. 어떤 현자도 이 징조의 진정한 의미를 이해할 수는 없겠지요. 그러나 행운의 여신은 대담한 자를 좋아하는 법입니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44166, 'ptBR', 'Uma trilha congelada através do tempo', 'Complete 5 Masmorras de Caminhada Temporal.', 'A estrela gélida reluz sobre a terra com sua luz fria, e até os espíritos se refugiam nas sombras. Esta estrela é diferente de todas as outras no céu, pois suga calor e vida. Enquanto sua influência perdurar, novos caminhos poderão se abrir, para lugares e talvez até tempos longínquos. Nenhum sábio compreende de fato a natureza deste sinal, mas a sorte favorece os audazes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44166, 'ruRU', 'Замерзший путь сквозь время', 'Пройдите 5 подземелий в режиме путешествия во времени.', 'Ледяная звезда льет на землю холодный свет, от которого даже духи спешат укрыться в тени. Другой такой звезды на небе нет; кажется, она высасывает из земли все ее тепло – а с ним и жизнь. Пока ее влияние в этом мире сильно, перед тобою могут открыться новые дороги – в иные края и даже в другие времена. Даже мудрецы пока не могут понять природы этого предзнаменования, но смельчакам наверняка улыбнется удача.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44166, 'zhCN', '冰冻的时光之路', '完成5个时空漫游地下城。', '寒冷之星用它冰冷的光芒照耀着大地，就连灵魂都退缩到了暗影之中。这颗星星与天空中其他的星星完全不同，它会吸取热量与生命。在它的影响持续期间，新的道路将被打开，通往遥远的空间与甚至时间。没有哪位贤者真正了解这一天象的涵义，但英勇的行为总会得到幸运的眷顾。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44166, 'zhTW', '穿越時間的冰封之路', '完成5次時光漫遊地城冒險', '冰凍之星以冷光照耀大地，將靈魂逼回暗影之中。它不同於天上的任何星體，只會吸取溫暖和生命。在它的影響之下，可能會開啟通往遠方、甚至前往其他時間的新道路。賢者們無法瞭解這個徵兆，但好運往往眷顧英勇之士。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44167, 'deDE', 'Ein verwüsteter Pfad durch die Zeit', 'Schließt 5 Zeitwanderungsdungeons ab.', 'Der Stern des Zerstörers bedeckt das Land mit seinem durchdringenden Licht, sodass sich selbst die Geister in die Schatten zurückziehen. Dieser Stern ist wie kein anderer am Himmel. Er offenbart selbst die Schwächen der furchtlosesten Helden. Solange er am Himmel steht, können sich neue Pfade zu fernen Orten oder sogar Zeiten eröffnen. Die Gelehrten sind ratlos, was die Natur dieses Zeichens betrifft, doch das Glück belohnt bekanntlich die Mutigen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44167, 'esES', 'Una senda en el tiempo fragmentada', 'Completa 5 mazmorras de Paseo en el tiempo.', 'La estrella del Destructor ilumina la tierra con una luz tan penetrante que hasta los espíritus se resguardan en las sombras. Esta estrella es única en los cielos; su luz llega a revelar las debilidades de los héroes más osados. Mientras ejerce su influencia, pueden abrirse nuevos caminos a lugares e incluso tiempos remotos. Ningún sabio alcanza a comprender la naturaleza de esta señal, pero la fortuna sonríe a los atrevidos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44167, 'esMX', 'Un camino destrozado por el tiempo', 'Completa 5 calabozos de cronoviaje.', 'La estrella del Destructor brilla sobre la tierra con su penetrante luz e incluso los espíritus se esconden en las sombras. Esta estrella es distinta a todas las otras del cielo, ya que pone al descubierto las flaquezas de incluso los héroes más intrépidos. Mientras tenga influencia, se podrán abrir nuevos caminos a lugares lejanos e incluso a tiempos distantes. Ningún sabio comprende realmente la naturaleza de esta señal, pero la fortuna favorece a los valientes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44167, 'frFR', 'Un passage brisé dans les limbes du temps', 'Terminez 5 donjons des Marcheurs du temps.', 'L’étoile du Destructeur éclaire le monde de sa lumière transperçante, en repoussant même les esprits dans les ombres. Cette étoile ne ressemble à aucune autre dans le ciel : elle met à nu les faiblesses des héros même les plus intrépides. Tant que son influence perdure, de nouveaux chemins vers des lieux éloignés dans l’espace ou dans le temps peuvent s’ouvrir. Aucun sage ne comprend réellement la nature de ce signe, mais la chance sourit aux audacieux.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44167, 'itIT', 'Una strada distrutta nel tempo', 'Completa 5 Viaggi nel Tempo.', 'La stella del Distruttore risplende su questa terra, e la sua luce è così fredda che persino gli spiriti si ritirano nelle ombre. Questa stella è differente da ogni altra nel cielo: indebolisce persino il più coraggioso degli eroi. Nonostante la sua influenza, nuove strade possono aprirsi, verso luoghi e anche tempi distanti. Nessun saggio comprende davvero la natura di questo segno, ma la fortuna aiuta gli audaci...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44167, 'koKR', '시간 속의 으스러진 길', '시간여행 던전 5개를 완료해야 합니다.', '파괴자의 별이 이 땅 위에 빛나며 그 날카로운 빛으로 영혼마저 어둠 속으로 쫓아내고 있습니다. 이 별은 저 하늘에 있는 다른 별들과는 달라, 가장 용맹한 영웅이라도 그 약점을 낱낱이 드러내 버리지요. 별의 영향력이 지속되는 동안 새로운 길이 열릴 것입니다. 먼 곳, 심지어 먼 시간에 이르는 길이. 어떤 현자도 이 징조의 진정한 의미를 이해할 수는 없겠지요. 그러나 행운의 여신은 대담한 자를 좋아하는 법입니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44167, 'ptBR', 'Uma trilha destroçada através do tempo', 'Complete 5 Masmorras de Caminhada Temporal.', 'A estrela do Destruidor brilha sobre a terra com uma luz poderosa que faz com que até os espíritos se escondam nas sombras. A estrela é diferente de todas as outras no céu e expõe as fraquezas até dos heróis mais bravos. Enquanto sua influência permanecer, novos caminhos podem se abrir, tanto para lugares quanto para tempos distantes. Nenhum sábio compreende realmente a natureza deste sinal, mas a sorte favorece os ousados.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44167, 'ruRU', 'Разрушенный путь сквозь время', 'Пройдите 5 подземелий в режиме путешествия во времени.', 'Звезда Разрушителя льет на землю всепроникающий свет, от которого даже духи спешат укрыться в тени. Другой такой звезды на небе нет; кажется, она способна указать слабости даже величайших из героев. Пока ее влияние в этом мире сильно, перед тобою могут открыться новые дороги – в иные края и даже в другие времена. Даже мудрецы пока не могут понять природы этого предзнаменования, но смельчакам наверняка улыбнется удача.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44167, 'zhCN', '崩裂的时光之路', '完成5个时空漫游地下城。', '毁灭者之星的光芒照耀着大地，就连灵魂都退缩到了暗影之中。这颗星星与天空中其他的星星完全不同，它可以揭示人们的弱点，哪怕是最勇敢的英雄也不例外。在它的影响持续期间，新的道路将被打开，通往遥远的空间甚至时间。没有哪位贤者真正了解这一天象的涵义，但英勇的行为总会得到幸运的眷顾。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44167, 'zhTW', '破碎的時光之徑', '完成5次時光漫遊地城冒險', '破壞者之星以強光照耀大地，將靈魂逼回暗影之中。它不同於天上的任何星體，即便是最強大的英雄，也能讓他們的弱點暴露無遺。在它的影響之下，可能會開啟通往遠方、甚至前往其他時間的新道路。賢者們無法瞭解這個徵兆，但好運往往眷顧英勇之士。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44171, 'deDE', 'Abgesandter des Krieges', 'Schließt 4 Dungeons auf dem Schwierigkeitsgrad \'Mythisch\' ab.', 'Wenn das Zeichen des Kriegers am Nachthimmel leuchtet, ist das Schicksal den Wagemutigen hold. Mächtige Feinde können noch größere Belohnungen erbringen. Begebt Euch tief in die Höhlen und Festungen, um Euch als würdig zu erweisen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44171, 'esES', 'Emisario de guerra', 'Completa 4 mazmorras en dificultad mítica.', 'Cuando la señal del guerrero brilla en el cielo nocturno, la fortuna sonríe a los valientes. En los enemigos más poderosos se encuentran las mayores recompensas. Lánzate a las cavernas y fortalezas y demuestra tu valía.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44171, 'esMX', 'Emisario de guerra', 'Completa 4 calabozos en dificultad mítica.', 'Cuando el signo del guerrero brilla en el cielo nocturno, la fortuna favorece a los audaces. Los enemigos poderosos pueden producir recompensas mayores. Aventúrate a las cavernas y fortalezas para probar tu valor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44171, 'frFR', 'L’émissaire de guerre', 'Terminez 4 donjons en mode mythique.', 'Lorsque le signe du guerrier brille dans le ciel nocturne, la chance sourit aux audacieux. De puissants ennemis vous rapporteront des récompenses à la hauteur du danger. Aventurez-vous au cœur des grottes et des forteresses pour prouver votre valeur.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44171, 'itIT', 'Emissario di guerra', 'Completa 4 spedizioni in modalità Mitica.', 'Quando il segno del guerriero splende nel cielo notturno, la fortuna aiuta gli audaci. Potenti nemici potrebbero portare ricompense ancora maggiori. Avventurati nelle caverne e nelle roccaforti per dimostrare il tuo valore.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44171, 'koKR', '전쟁의 사절', '신화 난이도 던전 4개를 완료해야 합니다.', '밤하늘에서 전사의 상징이 빛나면, 행운의 여신은 대담한 자를 더욱 총애하지요. 강력한 적들은 또한 더욱 강력한 보상을 의미하는 법. 악의 동굴과 요새 깊숙한 곳으로 나아가 당신의 가치를 증명하십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44171, 'ptBR', 'Emissário da guerra', 'Complete 4 masmorras na dificuldade Mítica.', 'Quando o sinal do guerreiro reluz no céu noturno, a sorte favorece os ousados. Inimigos poderosos podem conceder recompensas ainda maiores. Aventure-se em cavernas e fortalezas para provar seu valor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44171, 'ruRU', 'Посланник войны', 'Пройдите 4 подземелья в эпохальном режиме.', 'Когда в ночном небе сияет знак воина, удача благоволит отважным. Сражения с самыми опасными противниками могут принести самые великие награды. Отправляйся вглубь пещер и каменных крепостей и докажи свою доблесть.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44171, 'zhCN', '战争的使者', '完成4个史诗难度的地下城。', '当战士的天象在夜空中闪耀之时，幸运将眷顾勇敢者。强大的敌人可能会带来更丰厚的奖赏。深入洞穴和要塞，证明你的价值。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44171, 'zhTW', '征戰使節', '在傳奇難度下完成4次地城冒險', '當戰士之兆在夜空中閃耀，表示命運之神正對無懼的勇者微笑。敵人越是強大，獎賞就越豐厚，深入龍潭虎穴，證明你的價值吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44172, 'deDE', 'Die Arena ruft', 'Gewinnt 10 Arenageplänkel.', 'Diese Sternenkonstellation ist ein düsteres Omen. Sie ruft Blutlust hervor und zwingt Krieger zur Teilnahme an tödlichen Nahkämpfen. Die Arenameister verdienen gut dieser Tage. Vermutlich wissen sie nicht einmal, warum so viele Krieger in ihren Arenen antreten wollen. Neben der fiebrigen Spannung erwarten alle, die mutig oder töricht genug sind, sich diesen Arenakämpfen zu stellen, große Belohnungen...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44172, 'esES', 'La arena aguarda', 'Gana 10 batallas de escaramuzas de arena.', 'La alineación de las estrellas es una señal nefasta que estimula el ansia de sangre y llama a los guerreros a combatir a muerte en espacios reducidos. Los maestros de arena obtienen buenos beneficios durante estos días, y probablemente no entienden por qué sus gradas están a rebosar. Entre tanta emoción, grandes recompensas aguardan a aquellos que sean lo bastante valientes o insensatos como para aventurarse en la arena...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44172, 'esMX', 'El llamado de la arena', 'Gana 10 batallas de refriega de la arena.', 'Esta alineación de las estrellas es una señal terrible que agudiza la sed de sangre y llama a los guerreros a luchar mano a mano hasta la muerte. Los maestros de la arena tienen muchas ganancias durante estos días, probablemente sin comprender por qué sus galerías están tan llenas. En medio de la emoción, grandes recompensas esperan a aquellos suficientemente valientes, o insensatos, como para aventurarse en la arena...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44172, 'frFR', 'L’appel de l’arène', 'Remportez 10 escarmouches en arène.', 'Cet alignement des étoiles n’est pas de bon augure. Il attise la soif de sang et incite les guerriers à se battre jusqu’à la mort en combat rapproché. Les maîtres d’arène font d’excellents bénéfices ces jours-là, et sûrement sans comprendre pourquoi leurs tribunes sont pleines à craquer. Au milieu de cette agitation, ceux qui sont assez braves ou idiots pour se risquer dans l’arène peuvent obtenir de magnifiques récompenses…', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44172, 'itIT', 'La chiamata dell\'Arena', 'Vinci 10 Schermaglie in Arena.', 'Questo allineamento delle stelle è un segno infausto: la brama di sangue sta crescendo e i guerrieri vengono chiamati a combattere fino alla morte in scontri ravvicinati. I Signori dell\'Arena stanno guadagnando moltissimo in questi giorni, probabilmente senza capire perché le loro tribune stiano straripando. Tra l\'emozione generale, grandi ricompense attendono i combattenti più coraggiosi, nonché i più spericolati, che si avventurano nell\'arena...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44172, 'koKR', '투기장의 부름', '투기장 연습 전투에서 10번 승리해야 합니다.', '별이 끔찍한 형태로 정렬되며 피의 욕망을 불러일으키고, 좁은 공간에서 벌어지는 죽음의 전투로 전사들을 부르고 있습니다. 투기장 사무관들은 요사이 상당한 이윤을 남기면서도 어째서 관중이 이토록 밀려드는지 아마 이해하지 못할 겁니다. 그 흥분의 도가니 안에는 엄청난 보상이 준비되어 있습니다. 투기장에 스스로 발을 들인 용감한, 혹은 무모한 자에게 주어질 보상이...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44172, 'ptBR', 'A arena chama', 'Vença 10 Escaramuças da Arena.', 'O alinhamento das estrelas é um sinal calamitoso, que atiça a sede de sangue e convoca os guerreiros a lutar até a morte, face a face com o inimigo. Os mestres de arena veem seus lucros dispararem nesses dias, provavelmente sem compreender por que as arquibancadas ficam apinhadas. Em meio à agitação, grandes recompensas aguardam aqueles que são bravos — ou imprudentes — o bastante para se aventurar na arena...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44172, 'ruRU', 'Зов арены', 'Одержите победу в 10 стычках на арене.', 'Нынешнее расположение звезд на небе – дурное предзнаменование. В воинах просыпается безудержная жажда крови, и они буквально рвутся в смертельную рукопашную схватку. Золото течет в карманы повелителей арены рекой, хотя вряд ли они понимают истинную причину происходящего. Невиданные награды ждут смельчаков или глупцов, которые отважатся выйти в эти дни на арену...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44172, 'zhCN', '竞技场的召唤', '在竞技场练习赛中获得10场胜利。', '星辰的排列显示出了可怕的天象，刺激着人们心中嗜血的渴望，呼唤着战士到战场上与敌人短兵相接。在这样的日子里，竞技场高手们总是获利颇丰，他们可能也不明白为什么竞技场比赛总是场场爆满。在这激动人心的时刻，竞技场中会有丰厚的奖励等待着那些勇敢的，或者说莽撞的人……', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44172, 'zhTW', '競技場的呼喚', '贏得10場競技場練習戰。', '星辰排列成厄運之兆，翻攪起人們心中的嗜血慾望，呼喚戰士與敵人展開絕命死鬥。競技場大師在這些日子獲得大筆收入，他們可能根本沒搞懂錢財滾滾而來的真正理由。但是在這陣熱潮之中，只要夠大膽或笨到踏入競技場，就有巨大獎賞在等候…', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44173, 'deDE', 'Ein Aufruf zur Schlacht', 'Gewinnt 4 Schlachtfeldpartien.', 'Das Sternbild des Kriegsherren beherrscht den Himmel und bringt allen Strategen Glück. Einige sagen, dieses Sternbild schärft die Instinkte von Kommandanten so weit, dass es Wahrsagerei gleicht. Wenn Ihr je daran gedacht habt, Euch auf dem Schlachtfeld zu beweisen, ist jetzt die richtige Zeit dafür.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44173, 'esES', 'Llamada a la batalla', 'Gana 4 encuentros de campos de batalla.', 'La estrella favorita de los señores de la guerra y los generales domina el cielo, concediendo la buena fortuna a los estrategas por todas partes. Hay quien dice que la estrella de la batalla afina los instintos de los comandantes hasta un punto casi profético. Si pensabas ponerte a prueba alguna vez en el campo de batalla, ahora es el momento de hacerlo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44173, 'esMX', 'Un llamado a la batalla', 'Gana 4 encuentros de campo de batalla.', 'La estrella favorita de los señores de la guerra y los generales domina el cielo, ofrece buena fortuna a los estrategas sin importar dónde estén. Algunos dicen que la estrella de batalla agudiza los instintos de los comandantes al punto de convertirlos casi en profecía. Si planeas alguna vez probar tu valor en el campo de batalla, ahora es el momento.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44173, 'frFR', 'L’appel au combat', 'Remportez 4 Champs de bataille.', 'L’étoile préférée des seigneurs de guerre et des généraux domine le ciel et porte chance aux stratèges de tous les horizons. D’aucuns prétendent que l’étoile de bataille aiguise l’instinct des commandants presque au point d’en faire des prophètes. Si jamais vous envisagiez de faire vos preuves sur le champ de bataille, c’est le moment ou jamais.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44173, 'itIT', 'Chiamata alla battaglia', 'Vinci 4 campi di battaglia.', 'La stella preferita dei signori della guerra e dei generali domina il cielo, benedicendo gli strateghi sparsi nel mondo. Alcuni dicono che la stella della battaglia affini l\'istinto dei comandanti al punto da renderli quasi dei profeti. Se desideri metterti alla prova sul campo di battaglia, è il momento giusto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44173, 'koKR', '전장의 부름', '전장에서 4번 승리해야 합니다.', '지휘관과 장군들이 가장 좋아하는 별이 하늘을 지배하며, 온누리의 전술가들에게 행운을 안겨 주고 있습니다. 사람들은 전투의 별이 사령관의 직감을 더욱 날카롭게 다듬어 거의 예언에 가깝게 만든다고들 하지요. 만약 전장에서 자신의 기량을 시험해볼 생각이 있다면, 지금이 바로 그럴 때입니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44173, 'ptBR', 'Chamado à batalha', 'Vença 4 partidas nos Campos de Batalha.', 'A estrela favorita dos senhores da guerra e generais domina o firmamento, concedendo boa sorte a estrategistas de todos os cantos. Alguns dizem que a estrela da batalha aprimora os instintos dos comandantes a um patamar quase profético. Se você já cogitou se testar no campo de batalha, a hora é agora.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44173, 'ruRU', 'Призыв к бою', 'Одержите победу на 4 полях боя.', 'На небе воцарилась боевая звезда, особенно почитаемая генералами и полководцами, ибо она считается покровительницей тактиков. Говорят, под ее влиянием опытные военачальники обретают чуть ли не дар предвидения. Так что если ты собираешься испытать свои силы на поле боя, то сейчас для этого самое подходящее время.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44173, 'zhCN', '战斗的呼唤', '在战场中获得4场胜利。', '将军与战争领主的幸运星主宰了天空，护佑着各地的战术家。有人说战争之星可以强化指挥官的本能，让他们拥有接近先知的能力。如果你曾经打算在战场上检验自己的水平，现在就是最好的时机了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44173, 'zhTW', '戰鬥的呼喚', '贏得4場戰場比試。', '這枚星體在天空閃閃發亮，深受督軍與將領的喜愛，並為各地的謀士帶來好運。有人說，戰鬥之星會強化指揮官的本領，達到近乎傳奇的境界。如果你想考驗自己身處戰場的本事，現在正是好時機。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44174, 'deDE', 'Haustierchampion', 'Besiegt 5 Spieler über \'Kampf finden\' mit einer Gruppe aus Haustieren auf Stufe 25.', 'Dies ist eine wahrhaft ungewöhnliche Sternenkonstellation. Sie ist dafür verantwortlich, dass die kleinsten Kreaturen ungewöhnliche Fähigkeiten entwickeln. Keiner unserer Weisen kann dieses Phänomen erklären, aber für geschickte Zähmer ist dies eine perfekte Gelegenheit, ihre Fähigkeiten gegen andere auf die Probe zu stellen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44174, 'esES', 'El mejor', 'Derrota a 5 jugadores mediante Encontrar duelo con un equipo de mascotas de nivel 25.', 'Esta alineación de las estrellas es realmente extraña y provoca que las criaturas más pequeñas obtengan aptitudes antinaturales. Ninguno de nuestros sabios puede explicar el fenómeno, pero los domadores astutos tienen mucho que ganar, especialmente al probar sus habilidades contra sus iguales.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44174, 'esMX', 'El mejor', 'Derrota a 5 jugadores mediante Encontrar duelo con un equipo de mascotas de nivel 25.', 'Esta alineación de estrellas es realmente extraña, causa que las más pequeñas criaturas ganen capacidades antinaturales. Ninguno de nuestros sabios puede explicar el fenómeno, pero los domadores astutos tienen mucho que ganar, especialmente al probar sus habilidades contra sus pares.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44174, 'frFR', 'La crème de la crème', 'Battez 5 personnages-joueurs en JcJ en utilisant la fonction Trouver un combat avec une équipe de mascottes de niveau 25.', 'Cet alignement des étoiles est des plus étranges. Il confère aux plus minuscules des créatures des capacités surnaturelles. Aucun de nos sages n’est en mesure d’expliquer le phénomène, mais les dompteurs rusés ont beaucoup à y gagner, surtout en mettant à l’épreuve leurs compétences face à leurs pairs.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44174, 'itIT', 'I migliori', 'Sconfiggi 5 giocatori utilizzando \"Trova scontro\" con una squadra composta solo da mascotte di livello 25.', 'Questo allineamento delle stelle è davvero strano e sta fornendo capacità sovrannaturali anche alle creature più piccole. Nessuno dei nostri saggi è in grado di spiegare questo fenomeno, ma gli allenatori più astuti possono imparare molto, specialmente mettendo alla prova le loro abilità contro i loro pari.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44174, 'koKR', '최고의 애완동물', '대전 찾기를 이용하여 25 레벨 애완동물 팀으로 플레이어 간 대전에서 5번 승리해야 합니다.', '별의 정렬은 참으로 신비로워, 가장 자그마한 생명체에게 초자연적인 능력을 주기도 하지요. 어떤 현자도 이 현상을 설명할 수는 없으나, 뛰어난 조련사라면 자신과 비슷한 상대와 겨루어 얻을 것이 많을 것입니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44174, 'ptBR', 'O melhor', 'Derrote 5 Jogadores usando Encontrar Batalha com uma equipe de mascotes de nível 25.', 'O alinhamento das estrelas é deveras curioso, pois concede mesmo à menor das criaturas habilidades sobrenaturais. Nenhum de nossos sábios é capaz de explicar o fenômeno, mas domadores habilidosos têm muito a ganhar, especialmente ao testar suas habilidades em relação à de seus pares.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44174, 'ruRU', 'Лучшие из лучших', 'Воспользуйтесь системой поиска боев и победите 5 игроков. Все питомцы из вашей команды должны быть 25-го уровня.', 'Нынешнее расположение звезд на небе очень странно. Самые крохотные создания вдруг непонятно как обрели невероятные таланты. Никто из наших мудрецов не может объяснить этот феномен, но умелые дрессировщики совершенно точно смогут извлечь большую выгоду из этой ситуации – особенно померившись силами со своими товарищами по ремеслу.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44174, 'zhCN', '强中自有强中手', '通过搜寻战斗功能，以全队25级宠物打败5名玩家。', '这次星辰的排列真的非常奇怪，它们使得最微小的生物也有了不寻常的天资。我们的贤者无法解释这一现象，不过精明的训练师会把握这个机会，尤其是借这段时间与其他同好切磋技艺。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44174, 'zhTW', '箇中翹楚', '帶著由25級寵物組成的隊伍，透過對戰搜尋功能打倒5位玩家。', '這種星象十分怪異，它讓最小的生物顯現出異於平常的習性。沒有一位賢者能夠解釋這種現象，但是聰明的訓練師可以從中獲益良多，尤其是和同伴對戰、考驗技巧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44175, 'deDE', 'Die Welt wartet', 'Schließt 20 Weltquests auf den Verheerten Inseln ab.', 'Wenn das Zeichen des Gesandten am Nachthimmel leuchtet, festigen die Sterne ihre bestehenden Bande und knüpfen neue. Freundschaft wird nicht leichtfertig vergeben und nur durch Taten wird Treue bewiesen, aber Vertrauen stellt sich in diesen Tagen schneller ein. Bereist die Verheerten Inseln und helft, wo Ihr könnt, um Euch als würdig zu erweisen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44175, 'esES', 'El mundo aguarda', 'Completa 20 misiones del mundo en las Islas Abruptas.', 'Cuando la señal del emisario brilla en el cielo nocturno, las estrellas fortalecen los vínculos existentes y crean otros nuevos. La amistad no se gana fácilmente y la lealtad se demostrará con los hechos, pero la confianza se consigue ahora con más facilidad. Adéntrate en las Islas Abruptas y ofrece tu ayuda donde puedas para probar tu valía.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44175, 'esMX', 'El Mundo espera', 'Completa 20 misiones de mundo en las Islas Quebradas.', 'Cuando la señal del emisario brilla en el cielo nocturno, las estrellas fortalecen los vínculos existentes y forjan unos nuevos. La amistad no se gana fácilmente y las acciones prueban la lealtad, pero es más fácil ganar la confianza durante estos días. Aventúrate a las Islas Quebradas y ayuda donde puedas para probar tu valor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44175, 'frFR', 'Le monde vous attend', 'Accomplissez 20 expéditions sur les Îles Brisées.', 'Quand le signe de l’émissaire brille dans le ciel nocturne, les étoiles renforcent les liens existants et en tissent de nouveaux. L’amitié reste difficile à gagner, et les exploits permettent de prouver sa loyauté, mais la confiance s’accorde plus facilement ces jours-là. Aventurez-vous sur les îles Brisées et rendez-vous utile afin de prouver votre valeur.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44175, 'itIT', 'Il mondo attende', 'Completa 20 missioni mondiali sulle Isole Disperse.', 'Quando il simbolo dell\'emissario brilla nel cielo notturno, le stelle rafforzano i legami esistenti e ne creano di nuovi. L\'amicizia non è facile da ottenere e le tue gesta saranno prova della tua lealtà, ma la fiducia si ottiene più facilmente di questi tempi. Avventurati sulle Isole Disperse e fornisci aiuto a chi ne ha bisogno per provare il tuo valore.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44175, 'koKR', '세상이 기다린다', '부서진 섬에서 전역 퀘스트 20개를 완료해야 합니다.', '밤하늘에서 사절의 상징이 빛나면, 별들은 연결을 더욱 굳건히 하고 새로운 연결을 만들지요. 우정은 쉽게 얻을 수 있는 것이 아니며, 행동으로 충실함을 증명해야 하는 법. 그러나 요즘같은 시기에는 믿음을 보다 선뜻 얻을 수 있을 것입니다. 부서진 섬을 누비며 도움을 제공하여, 당신의 가치를 증명하십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44175, 'ptBR', 'O mundo aguarda', 'Conclua 20 Missões Mundiais nas Ilhas Partidas.', 'Quando o sinal do emissário reluz no céu noturno, as estrelas estreitam os laços existentes e forjam novos. A amizade é uma conquista custosa, e a lealdade se comprova por atos, mas a confiança chega mais facilmente hoje em dia. Aventure-se nas Ilhas Partidas e ajude aqueles que puder, para mostrar seu valor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44175, 'ruRU', 'Мир ждет', 'Выполните 20 локальных заданий на Расколотых островах.', 'Когда на небе появляется знак посланника, звезды помогают нам укреплять старые связи и создавать новые. Настоящая дружба и теперь не дается легко; преданность по-прежнему нужно подтверждать делами, но заслужить доверие в эти дни будет проще обычного. Отправляйся же на Расколотые острова и покажи, чего ты стоишь.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44175, 'zhCN', '世界在等待', '在破碎群岛完成20个世界任务。', '当使者的象征在夜空中闪烁时，星辰将会强化已有的羁绊，并会创建新的联系。想要获取友谊并不容易，你需要通过自己的行为来证明忠诚，但在这样的日子，信任会更容易建立。在破碎群岛上游历一番，尽量四处提供帮助，以证明你的价值。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44175, 'zhTW', '世界任務正等著你', '完成20個破碎群島的世界任務。', '當特使之兆在夜空中閃耀時，星辰會強化人們現有的牽絆，並鑄造出新的連繫。雖然友誼無法輕易締結，得仰賴一點一滴的功績來證明彼此的忠誠，但在這些日子裡，這樣的機會多的是。踏上破碎群島，對需要你幫助的人伸出援手，證明你的價值。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44176, 'deDE', 'Die Annehmlichkeiten eines Zuhauses', 'Bringt die Banktruhe des Reisenden nach Shal\'Aran.', 'Diese Truhe könnte uns gute Dienste leisten. Bringt sie nach Shal\'Aran.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44176, 'esES', 'Las comodidades de casa', 'Lleva el cofre bancario del viajero a Shal\'Aran.', 'Este cofre podría ser muy útil para nuestra campaña. Llévalo a Shal\'Aran.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44176, 'esMX', 'La comodidad del hogar', 'Lleva los fondos bancarios del viajero a Shal\'Aran.', 'Este cofre puede resultar muy útil para nuestra campaña. Llévalo de vuelta a Shal\'aran.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44176, 'frFR', 'Proche toutes commodités', 'Apportez le Coffre de banque de voyageur à Shal’Aran.', 'Ce coffre pourrait s’avérer fort utile dans le cadre de notre campagne. Emportez-le à Shal’Aran.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44176, 'itIT', 'Le comodità di casa', 'Porta lo Scrigno Deposito del Viaggiatore a Shal\'aran.', 'Questa cassa potrebbe dimostrarsi utile nei tuoi sforzi bellici. Riportala a Shal\'aran.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44176, 'koKR', '편리한 물건', '여행자의 은행 상자를 샬아란으로 가져가야 합니다.', '이 상자는 우리에게 매우 유용할 것 같습니다. 샬아란으로 가져가는 게 좋겠습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44176, 'ptBR', 'As conveniências do lar', 'Leve o Baú Bancário do Viajante para Shal\'Aran.', 'Esse baú seria muito útil para os nossos esforços. Leve ele de volta para Shal\'Aran.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44176, 'ruRU', 'Домашние радости', 'Отнесите банковский сундук путешественника в Шал\'Аран.', 'Этот сундук может нам очень пригодиться. Отнеси его назад в Шал\'Аран.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44176, 'zhCN', '家的便利', '把旅行者的银柜带到沙尔艾兰。', '这个箱子对我们的事业很有帮助。将箱子带回沙尔艾兰。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44176, 'zhTW', '如魚得水', '將旅人銀行箱帶到沙亞蘭。', '這個箱子對我們非常有用。將箱子帶回沙亞蘭。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44177, 'deDE', 'Dunkle Geheimnisse und krumme Geschäfte', 'Sprecht mit Lord Rabenholdt.', 'Was auch immer Amber Kearnen gewusst hat, Mathias Shaw wollte nicht, dass jemand davon erfährt...$b$bWirklich ein dreckiges Geschäft, den Roten Säbel zu so \'nem Preis anzuheuern, bloß um jemanden aus der eigenen Mannschaft zu töten!$b$bIhr macht Euch besser auf den Weg zurück zur Halle der Schatten und erzählt Lord Rabenholdt, was wir hier in der Citrinbucht rausgefunden haben.$b$bWas auch immer der SI:7 plant, dahinter steckt mehr, als wir vermutet hatten...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44177, 'esES', 'Secretos oscuros y tratos sospechosos', 'Habla con lord Ravenholdt.', 'Mathias Shaw no quería que saliera a la luz lo que Amber Kearnen sabía...$b$bSí, aquí algo huele a fosfatina... ¡Contratar a La Hoja Roja a semejante precio solo para dar caza y matar a uno de los suyos!$b$bMás vale que regreses a la Morada de las Sombras e informes a lord Ravenholdt sobre nuestro hallazgo en Bahía de Cuarzo.$b$bNo sé qué trama el IV:7, pero va más allá de lo que habíamos imaginado...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44177, 'esMX', 'Secretos oscuros y tratos sombríos', 'Habla con Lord Ravenholdt.', 'Sea lo que fuere lo que sabía Amber Keearnen, Mathias Shaw no quería que salga a la luz...$b$bOye, eh\'te eh\' un negocio bien sucio, contratar a la Espada Carmesí a ese precio... ¡y para cazah\' y matah\' a uno de loh\' propioh\'!$b$bSerá mejoh\' que vayah\' al Salón de las Sombrah\' y le digah\' a Lord Ravenholdt lo que encontramoh\' aquí en la Bahía Citrino.$b$bSea lo que fuere que trama la IV:7, irá máh\' allá de lo que pensábamoh\'...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44177, 'frFR', 'Sombres secrets et accords véreux', 'Parlez au Seigneur Ravenholdt.', 'Je sais pas ce qu’Amber Kearnen savait, mais Mathias Shaw voulait pas que ça s’ébruite…$b$bQuelle sale histoire… payer la Lame rouge un prix pareil… pour traquer et zigouiller une des leurs !$b$bVous feriez bien d’retourner à la salle des Ombres et d’informer le seigneur Ravenholdt de ce qu’on a trouvé à la baie de Citrine.$b$bJ’sais pas ce que mijote le SI:7, mais ce qui est sûr, c’est que ça va encore plus loin que ce qu’on imaginait…', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44177, 'itIT', 'Segreti foschi e accordi loschi', 'Parla con Ser Corvolesto.', 'Qualunque cosa sapesse Amber Kearnen, Mathias Shaw non voleva che venisse divulgata...$b$bSì, è un affare losco. Assoldare i Lama Rossa per questo prezzo... e solo per uccidere uno dei loro!$b$bFaresti meglio a tornare alla Dimora delle Ombre e dire a Ser Corvolesto cos\'abbiamo scoperto alla Baia di Citrino.$b$bQualunque cosa stia facendo l\'IR:7, si è spinta troppo oltre...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44177, 'koKR', '추악한 비밀과 더러운 거래', '라벤홀트 경과 대화해야 합니다.', '앰버 켈넌이 뭘 알고 있었든, 마티아스 쇼는 그걸 퍼뜨리고 싶지 않았던 모양이군...$b$b아, 정말 추악한 짓이잖소. 그런 대가를 치르고 붉은 칼날단을 고용해서... 자기들 요원을 추적해 살해하다니!$b$b그림자의 전당으로 돌아가 라벤홀트 경에게 여기 황수정 만에서 알아낸 사실을 전해주시오.$b$bSI:7이 무슨 일을 꾸미고 있든, 우리가 생각했던 것보다 더 심각할지도 모르오...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44177, 'ptBR', 'Segredos obscuros e negócios sombrios', 'Fale com o Lorde Corvoforte.', 'O que quer que Amber Kearnen sabia, Mathias Shaw não queria que vazasse...$b$bAye, é um trabalhinho sujo ter que contratar a Lâmina Vermelha por esse preço só pra caçar e matar um deles!$b$bÉ melhor tu voltar pro Salão das Sombras e avisar ao Lorde Corvoforte do que a gente encontrou aqui na Baía Citrina.$b$bSeja lá o que a AVIN esteja planejando, vai ser pior do que a gente tinha imaginado...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44177, 'ruRU', 'Темные секреты и сомнительные сделки', 'Поговорите с лордом Черным Вороном.', 'Эмбер Кирнен знала нечто такое, что Матиас Шоу хотел сохранить в секрете...$b$bКакая гнусность, отвалить столько денег Красным Клинкам, чтобы убить собственного агента!$b$bДавай-ка быстро дуй в Оплот Теней, сообщи лорду Черному Ворону, что мы обнаружили в Цитриновой бухте.$b$bУж не знаю, что там замышляет ШРУ, но дело явно очень серьезное...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44177, 'zhCN', '黑暗的秘密和不可告人的交易', '与拉文霍德公爵对话。', '不管安玻·吉尔妮知道了什么，马迪亚斯·肖尔都不想让她泄露出去……$b$b没错，这绝对是一笔肮脏的交易，用这样的高价雇佣深红之剑……竟然只是为了去追捕和刺杀他们的自己人！$b$b你最好返回暗影大厅，将我们在黄金海湾的发现告诉拉文霍德公爵。$b$b不管军情七处有什么阴谋，都比我们之前想象的更为严重……', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44177, 'zhTW', '黑暗秘密，沆瀣一氣', '與拉文霍德領主交談。', '琥珀‧吉爾妮一定發現了什麼，馬迪亞斯‧肖爾才想封她的嘴…$b$b這案情確實不單純，竟然出這種價錢…只為獵殺自己人！$b$b你最好趕快回到潛影之廳，告訴拉文霍德領主我們在黃晶海灣的發現。$b$b不管軍情七處在打什麼算盤，事情絕對比我們原本想像的還嚴重。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44178, 'deDE', 'Ein besonders mächtiger Trank', 'Stehlt Liebestrank Nr. 6 aus dem persönlichen Tresor von Gallywix.', 'Ich habe einen Vorschlag für Euch, $n!$b$bEs heißt, Handelsprinz Gallywix hätte vor Kurzem einen unbezahlbaren und einzigartigen Liebestrank erworben. Es gibt nur einen Goblin, der so etwas nachbrauen könnte... mich! Besorgt mir diesen Trank und ich mache uns zwei so reich, dass alle Könige grün vor Neid werden!$b$bDas Lustschloss befindet sich in Azshara und wird seit Ausbruch des Krieges streng bewacht. Gallywix lässt nur sein eigenes Personal hinein, aber ich kenne eins der Mädchen, die dort arbeiten!$b$bFindet sie und sie führt Euch zum Tresor!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44178, 'esES', 'Una poción particularmente potente', 'Roba la poción de amor n.º 6 de la caja fuerte personal de Gallywix.', '¡Tengo una propuesta para ti, $n!$b$bDicen por ahí que el príncipe mercante Gallywix acaba de adquirir una valiosa y única poción de amor. Solo existe un goblin que sepa cómo reproducirla. ¡Lo tienes delante! ¡Entrégame esa poción y nuestra fortuna superará a la de los reyes!$b$bEl palacete se encuentra en Azshara y lleva en máxima alerta desde que estalló la guerra. Gallywix solo permite el acceso a su personal, ¡pero conozco a una chica que está dentro!$b$b¡Ve a buscarla y te conducirá hasta la caja fuerte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44178, 'esMX', 'Una poción particularmente potente', 'Roba la Poción de amor #6 de la caja fuerte de Gallywix.', '¡Tengo una propuesta para ti, $n!$b$bSe dice que el Príncipe mercante Gallywix adquirió recientemente una poción de amor de valor incalculable y única en su especie. Sólo hay un goblin que puede replicarla y ¡lo estás mirando! Tráeme esa poción y yo puedo hacernos más ricos que los reyes!$b$bEl palacio del placer se encuentra en Azshara y ha estado en alerta máxima desde que comenzó la guerra. Gallywix no está dejando entrar a nadie excepto a sus empleados personales, ¡pero conozco a una niña que está adentro!$b$b ¡Encuéntrala y ella te llevará a la caja fuerte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44178, 'frFR', 'Une potion particulièrement puissante', 'Volez le Philtre d’amour n°6 dans le coffre personnel de Gallywix.', 'J’ai une proposition à vous faire, $n !$b$bIl paraît que le prince marchand Gallywix a récemment fait l’acquisition d’un philtre d’amour unique en son genre et valant son pesant d’or. Il n’y a qu’un seul gobelin capable de le reproduire et vous l’avez devant vous ! Allez me chercher ce philtre et nous deviendrons tous deux riches comme Crésus !$b$bLe palais des Plaisirs se situe en Azshara et il est en état d’alerte maximum depuis que la guerre a commencé. Gallywix ne laisse entrer personne sauf ses domestiques personnels, mais je connais une fille qui travaille là-bas !$b$bTrouvez-la et elle vous conduira au coffre !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44178, 'itIT', 'Una pozione particolarmente potente', 'Ruba la Pozione d\'Amore N° 6 dalla cassaforte personale di Gallywix.', 'Ho una proposta per te, $n!$b$bSi dice che il Principe del Commercio Gallywix abbia di recente acquistato un\'unica e preziosissima pozione d\'amore. C\'è solo un Goblin al mondo capace di replicarne la ricetta, e lo stai guardando ora! Portami quella pozione e ti assicuro che diventeremo entrambi ricchi come re!$b$bAnche se c\'è una cosa che devi sapere prima... il Palazzo dei Piaceri è ad Azshara, in massima allerta dall\'inizio della guerra. Gallywix non fa entrare nessuno, eccetto il personale di servizio. Però conosco una ragazza al suo interno!$b$bTrovala e ti condurrà alla cassaforte!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44178, 'koKR', '아주 특별하고 강력한 물약', '갤리윅스의 개인 금고에서 사랑의 묘약 6번을 훔쳐야 합니다.', '$n! 내가 제안 하나 할게.$b$b소문에 무역왕 갤리윅스가 최근 아주 특별한 사랑의 묘약을 손에 넣었다더군. 그걸 복제할 수 있는 고블린이 딱 하나 있는데... 그게 바로 지금 자네 앞에 있는 이 몸이시라고! 그 묘약을 나한테 가져오면 우린 왕들보다 더 떵떵거리는 부자가 될 수 있을 거야!$b$b아방궁은 아즈샤라에 있는데, 전쟁이 시작된 후 그곳 경계가 부쩍 삼엄해졌어. 갤리윅스는 개인적으로 부리는 부하가 아니면 절대로 내부로 들어오게 하지 않아. 하지만 내가 아방궁 안에서 일하는 여자애를 하나 알고 있어!$b$b그 아이를 찾으면, 금고까지 안내해줄 거야!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44178, 'ptBR', 'Uma poção particularmente potente', 'Roube a Poção do Amor nº 6 do cofre pessoal de Gallywix.', 'Tenho uma proposta pra tu, $n!$b$bDizem por aí que o Príncipe Mercador Gallywix comprou por esses dias uma poção do amor inestimável. Só tem um goblin que pode fazer outra igual, e você tá olhando pra ele! Pega aquela poção pra que a gente possa ficar mais rico que o rei!$b$bO palacete fica em Azshara e tá em estado de alerta desde que a guerra começou. Gallywix não vai deixar ninguém entrar, a menos que seja seu pessoal. Mas eu conheço uma moça por aquelas bandas!$b$bEncontre ela e ela vai te dar o caminho das pedras!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44178, 'ruRU', 'Особенно мощное зелье', 'Украдите любовное зелье №6 из личного сейфа Галливикса.', '$n, у меня для тебя предложение!$b$bХодят слухи, что у торгового принца Галливикса недавно появилось уникальное, бесценное любовное зелье. На свете есть лишь один гоблин, который способен его повторить, и этот гоблин перед тобой! Добудь мне это зелье, и мы с тобой озолотимся!$b$bДворец удовольствий Галливикса находится в Азшаре, и с начала войны он под усиленной охраной. Галливикс никого не пускает внутрь, кроме личной прислуги, но как раз среди прислуги-то у меня есть знакомая девчонка!$b$bНайди ее, и она проведет тебя к сейфу!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44178, 'zhCN', '威力超强的药水', '从加里维克斯的私人保险箱中偷取6号爱情药水。', '我有个任务给你，$n！$b$b据说贸易大王加里维克斯最近获得了一瓶价值连城、独一无二的爱情药水。只有一个地精可以复制这种药水，远在天边近在眼前！将那瓶药水带来，我会让我们比国王更富有！$b$b忘忧宫就在艾萨拉里面。自从战争开始之后，那里就进入了高度戒备状态。除了他的私人员工之外，加里维克斯不允许任何人进入。但我认识一个内部人员！$b$b找到她，她会帮你找到保险箱的！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44178, 'zhTW', '特別強效的藥水', '從加里維克斯的私人保險箱偷取愛情秘藥6號。', '我有個提案，$n！$b$b街頭流傳貿易親王加里維克斯最近入手一款無價、獨一無二的藥水。全世界只有一位哥布林能複製…就是在下我！去把藥水給我弄來，包準我們兩個都能躺著過下半輩子！$b$b他的歡樂宮殿在艾薩拉，自從戰爭開始就一直處於高警戒狀態。除了加里維克斯的私人員工，誰都不許進去。但我認識裡頭的一個女孩！$b$b找到她，她就能帶你找到保險箱！', '', '', '', '', '', '', 0);
