INSERT INTO `quest_template_locale` VALUES (44195, 'esES', 'Pedidos de armamento', 'Haz un pedido de un cargamento de armamento de campeón y otro de equipo de campeón.', 'Puedo conseguirte los mejores precios en equipo... ligeramente usado.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44195, 'esMX', 'Pedidos de armamentos', 'Haz un pedido de armamentos y equipo de campeón.', 'Puedo conseguirte buenos precios para equipo... apenas... usado.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44195, 'frFR', 'Des commandes d’armement', 'Passez commande d’une cargaison d’équipement et d’armement de champion.', 'Je peux vous avoir les meilleurs prix sur de l’équipement déjà porté…', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44195, 'itIT', 'Ordini per equipaggiamenti', 'Richiedi un incarico di lavoro per gli armamenti e l\'equipaggiamento da campione.', 'Posso procurarti il miglior prezzo su alcuni equipaggiamenti... quasi nuovi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44195, 'koKR', '무장 주문', '용사의 무장과 장비를 주문해야 합니다.', '사용감은 조금 있지만... 장비를 좋은 가격에 드릴 수 있습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44195, 'ptBR', 'Ordens de serviço para armamentos', 'Faça uma ordem de serviço para um carregamento de Equipamentos e armamentos de Campeão.', 'Consigo para você os melhores negócios em equipamentos... levemente... usados.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44195, 'ruRU', 'Заказ на вооружение', 'Разместите заказ на вооружение и снаряжение для защитников.', 'Я могу предложить тебе лучшие цены на оружие, слегка... бывшее в употреблении.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44195, 'zhCN', '武器订单', '下达订单，订购一批勇士武器和装备。', '我能以最优惠的价格给你弄到一些稍微用过……几次的……装备。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44195, 'zhTW', '武裝訂單', '下一張勇士武裝或裝備的訂單。', '這裡有一批，算是…二手…的裝備，你如果有興趣可以打折。', '', '', '', '', '', '', 0);
