INSERT INTO `quest_template_locale` VALUES (44222, 'ruRU', 'Мощь валь\'кир', 'Соберите силу валь\'кир и выпустите ее, чтобы выполнить локальное задание.', 'В час нужды валь\'киры способны концентрировать свою силу в чистейшую разрушительную форму. Сейчас настало именно такое время.\n\nПо твоему запросу мы направим нашу силу в рог, который будет отдан тебе. Ты сможешь высвободить его мощь для выполнения особенно важного задания.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44222, 'zhCN', '瓦格里的力量', '汇聚并释放瓦格里的力量，完成一个世界任务。', '在危急关头，瓦格里会把最强大的力量汇聚成一种纯粹的毁灭性武器。而眼下就是这样的非常时刻。\n\n一旦收到指示，我们会把力量导入一个号角之中，然后把它交给你。释放它的力量，完成一项意义非凡的任务。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44222, 'zhTW', '華爾琪的力量', '加強華爾琪的力量，然後用這股力量完成一個世界任務。', '在危急時刻，華爾琪可以將力量集中成一個純粹的毀滅武器。現在正是時候。\n\n一旦收到指示，我們就會把力量導入號角，然後把號角交給你。只要使用號角，就可以釋放力量，完成有價值的任務。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44223, 'deDE', 'Waffen für Champions', 'Erteilt einen Arbeitsauftrag für Championwaffen und -ausrüstung.', 'Wir brauchen Waffen und Ausrüstung für unsere Champions.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44223, 'esES', 'Armamento de campeón', 'Haz un pedido de armamento y equipo de campeón.', 'Necesitaremos armamento y equipo para nuestros campeones.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44223, 'esMX', 'Armamentos del campeón', 'Haz un pedido de armamentos y equipo de campeón.', 'Necesitaremos armamentos y equipo para nuestros campeones.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44223, 'frFR', 'Armement de champion', 'Passez une commande d’équipement et d’armement de champion.', 'Nous allons avoir besoin d’armement et d’équipement pour nos champions.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44223, 'itIT', 'Armamenti da Campione', 'Richiedi un incarico di lavoro per gli armamenti e l\'equipaggiamento da campione.', 'Ci serviranno armi ed equipaggiamenti per i nostri campioni.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44223, 'koKR', '용사의 무장', '용사의 무장과 장비를 주문해야 합니다.', '용사들이 사용할 무장과 장비가 필요합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44223, 'ptBR', 'Armamentos de campeão', 'Faça uma ordem de serviço para equipamento e armamentos de Campeão.', 'Vamos precisar de equipamentos e armamentos para nossos campeões.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44223, 'ruRU', 'Вооружение для защитников', 'Разместите заказ на вооружение и снаряжение для защитников.', 'Нам потребуется вооружение и снаряжение для наших защитников.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44223, 'zhCN', '勇士武器', '下达订单，订购一批勇士武器和装备。', '我们需要武器和装备来武装我们的勇士。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44223, 'zhTW', '勇士武裝', '下一張勇士武裝或裝備的訂單。', '我們的勇士需要武裝和裝備。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44224, 'deDE', 'Unser entfesselter Zorn', 'Kanalisiert die Macht des Hammers und entfesselt sie dann, um eine Weltquest abzuschließen.', 'In Zeiten größter Not können wir unsere Stärke zu einer reinen und sehr zerstörerischen Form vereinen. Dies ist solch eine Zeit.\n\nAuf Euer Wort werden wir unsere Kraft in einen Gegenstand kanalisieren, den wir Euch dann übergeben. Wenn Ihr seine Macht entfesselt, könnt Ihr eine wichtige Aufgabe abschließen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44224, 'esES', 'Liberar nuestra ira', 'Canaliza el poder del martillo y luego desátalo para completar una misión del mundo.', 'En tiempos de gran necesidad, podemos reunir nuestras fuerzas más poderosas en una forma pura y destructiva. Este es uno de esos momentos.\n\nCuando se nos pida, canalizaremos nuestro poder en un objeto que te proporcionaremos. Úsalo para desatar ese poder y completar una misión de gran valor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44224, 'esMX', 'Desatando nuestra cólera', 'Canaliza el poder del Martillo y luego libéralo para completar una misión de mundo.', 'En tiempos de gran necesidad, podemos congregar nuestras más poderosas fuerzas en una forma pura y destructiva. Este es uno de estos momentos.\n\nCuando lo pidas, ellos canalizarán nuestro poder a un objeto que te impartiremos. Úsalo para liberar ese poder, y habrás logrado una misión de gran valor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44224, 'frFR', 'Le courroux déchaîné', 'Canalisez la puissance du marteau et libérez-la pour mener à bien une expédition.', 'En cas de nécessité absolue, il nous est possible de rassembler nos forces en une entité destructrice dans sa forme la plus pure. Et c’est ce qu’exige la situation actuelle.\n\nSi vous le souhaitez, nous canaliserons cette énergie dans un objet que nous vous remettrons ensuite. Utilisez-le afin de libérer la puissance nécessaire pour mener à bien une quête de la plus haute importance.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44224, 'itIT', 'Scatenare la rabbia', 'Incanala il potere del Martello, quindi scatenalo per completare una missione mondiale.', 'Nei tempi di grande necessità, possiamo spingere i più grandi dei nostri soldati verso una forma di pura distruzione. Questo è uno di quei tempi.\n\nSe verrà richiesto, incanaleremo questo potere in un oggetto che ti affideremo. Usalo per scatenare questo potere e completare una missione di grande valore.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44224, 'koKR', '분노를 방출하라', '망치의 힘을 집중시킨 다음, 이 힘을 방출하여 전역 퀘스트를 완료해야 합니다.', '심각한 위기를 맞이했을 때, 우리는 순수하고도 파괴적인 하나의 형태에 가장 강력한 힘을 쏟아부을 수 있습니다. 지금이 바로 그런 시기지요.\n\n부탁만 하면, 우리가 특정 물건에 힘을 집중시킬 겁니다. 그리고 그 물건을 당신에게 전해드리겠습니다. 이것을 사용해 힘을 방출하여 중대한 임무를 완료하십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44224, 'ptBR', 'Liberando nossa ira', 'Canalize o poder do Martelo e, então, libere-o para concluir uma Missão Mundial.', 'Em momentos de grande necessidade, nós focamos nossas forças mais poderosas em um item puro e destrutivo. Estamos em um momento desses.\n\nA pedido, nós canalizaremos nosso poder em um item, e daremos ele a você. Use ele para liberar o poder e concluir uma missão muito valiosa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44224, 'ruRU', 'Обрушим свой гнев', 'Вберите силу Молота, а затем выпустите ее, чтобы выполнить локальное задание.', 'В час нужды мы можем сконцентрировать свою силу в чистейшую разрушительную форму. Сейчас настало именно такое время.\n\nПо твоему запросу мы направим нашу силу в предмет, который будет отдан тебе. Ты сможешь высвободить его мощь для выполнения особенно важного задания.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44224, 'zhCN', '释放怒火', '引导并释放邪能之槌的力量，以完成一个世界任务。', '在危急关头，我们会把最强大的力量汇聚成一种纯粹的毁灭性武器。而眼下就是这样的非常时刻。\n\n一旦收到指示，我们会把力量导入一个物品之中，然后把它交给你。释放它的力量，完成一项意义非凡的任务。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44224, 'zhTW', '釋放怒火', '引導魔錘號的力量，然後用這股力量完成一個世界任務。', '在危急時刻，組織可以把最強大的力量集合成一個純粹的毀滅武器。現在正是時候。\n\n一旦收到指示，我們就會把力量導入某個物品，然後把物品交給你。只要使用這個物品，就可以釋放力量，完成有價值的任務。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44226, 'deDE', 'Das Schicksal ist uns wohlgesonnen', 'Erteilt einen Arbeitsauftrag für ein Siegel des verheerten Schicksals.', 'Wir können ein wertvolles Siegel beschaffen, das Euch im Krieg gegen die Legion gute Dienste leisten wird. Wenn Ihr dieses Siegel im rechten Moment einsetzt, erhaltet Ihr eine zweite Chance, Euer Schicksal zu erfüllen.\n\nSollen wir das Siegel für Euch beschaffen?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44226, 'esES', 'El destino nos sonríe', 'Haz un pedido de sello de destino alterado.', 'Podemos facilitarte un objeto valioso que te ayudará en nuestra guerra contra la Legión. Este sello se puede utilizar en los momentos más adecuados para gozar de una nueva oportunidad de cara al destino.\n\n¿Te proporcionamos uno?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44226, 'esMX', 'El destino está de nuestro lado', 'Haz un pedido de un Sello del Destino quebrado.', 'Podemos conseguir un objeto valioso que te ayudará en la guerra contra la Legión. Este sello se puede usar en los momentos más oportuno para tener otra oportunidad con el destino. \n\n¿Quieres que te consigamos uno?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44226, 'frFR', 'La faveur du destin', 'Passez une commande de Sceau du destin brisé.', 'Nous sommes à même de vous fournir un objet de grande valeur pour vous aider dans votre campagne contre la Légion. Utilisé à bon escient, ce sceau vous donnera une autre chance d’accomplir votre destinée.\n\nEn voulez-vous un ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44226, 'itIT', 'Il destino è con noi', 'Assegna un incarico di lavoro per un Sigillo del Destino Spezzato.', 'Siamo in grado di ottenere un oggetto prezioso per la nostra guerra contro la Legione. Questo sigillo può essere usato nel momento più opportuno per dare un\'altra possibilità al destino.\n\nDesideri che te ne procuriamo uno?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44226, 'koKR', '운명이 우리를 사랑하사', '부서진 운명의 인장을 주문해야 합니다.', '저희는 군단과의 전쟁에서 도움이 될 중요한 물건을 확보할 수 있습니다. 그 인장을 적절한 시기에 사용하면 운명을 바꿀 수도 있지요.\n\n혹시 하나 필요하십니까?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44226, 'ptBR', 'O destino nos favorece', 'Faça uma ordem de serviço para um Selo do Destino Interrompido.', 'Podemos obter um item valioso para ajudar você na nossa guerra contra a Legião. Este selo pode ser usado nos momentos mais oportunos para conceder uma nova chance ao destino. \n\nDevemos providenciar um para você?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44226, 'ruRU', 'Судьба благоволит нам', 'Разместите заказ на печать сломанной судьбы.', 'Мы можем добыть для тебя ценный предмет, чтобы помочь в борьбе с Легионом. Эту печать ты сможешь применить в самый трудный час, чтобы судьба подарила тебе еще один шанс. \n\nЧто скажешь? Она тебе нужна?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44226, 'zhCN', '命运垂青', '下达一份破碎命运印记订单。', '我们可以找到一件宝物，它可以帮助你对抗燃烧军团。这个封印能在最关键的时刻给你一次改变命运的机会。\n\n要我们帮你去找吗？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44226, 'zhTW', '命運寵兒', '下一張破碎命運徽印的訂單。', '為了幫你對抗燃燒軍團，我們可以取得一種珍貴的物品，只要在關鍵時刻使用這個徽印，你就可以重新挑戰命運。\n\n要不要我們設法幫你取得？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44227, 'deDE', 'Unser entfesselter Zorn', 'Beginnt mit dem Ritual zur Versklavung der Höllenbestie und entfesselt sie dann, um eine Weltquest abzuschließen.', 'In Zeiten größter Not können wir unsere Stärke zu einer reinen und sehr zerstörerischen Form vereinen. Dies ist solch eine Zeit.\n\nAuf Euer Wort werden wir mit einem Ritual beginnen, das eine mächtige Höllenbestie versklavt und in ein dämonisches Phylakterium bannt. Dieses Phylakterium übergeben wir dann an Euch. Wenn Ihr seine Macht entfesselt, könnt Ihr eine wichtige Aufgabe abschließen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44227, 'esES', 'Liberar nuestra ira', 'Comienza el ritual para esclavizar al infernal y luego desátalo para completar una misión del mundo.', 'En tiempos de gran necesidad, podemos reunir nuestras fuerzas más poderosas en una forma pura y destructiva. Este es uno de esos momentos.\n\nCuando se nos pida, comenzaremos el ritual para esclavizar a un poderoso infernal en una filacteria demoníaca que te proporcionaremos. Úsala para desatar ese poder y completar una misión de gran valor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44227, 'esMX', 'Desatando nuestra cólera', 'Comienza el ritual para esclavizar al infernal y luego libéralo para completar una misión de mundo.', 'En tiempos de gran necesidad, podemos congregar nuestras más poderosas fuerzas en una forma pura y destructiva. Este es uno de estos momentos.\n\nCuando lo pidas, comenzaremos el ritual para esclavizar a un poderoso infernal en una filacteria demoníaca, que te entregaremos. Úsala para liberar ese poder y habrás logrado una misión de gran valor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44227, 'frFR', 'Le courroux déchaîné', 'Commencez le rituel pour asservir l’infernal, puis libérez-le pour mener à bien une expédition.', 'En cas de nécessité absolue, il nous est possible de rassembler nos forces en une entité destructrice dans sa forme la plus pure. Et c’est ce qu’exige la situation actuelle.\n\nSi vous le souhaitez, nous entamerons le rituel permettant d’emprisonner un infernal dans un phylactère démoniaque, que nous vous remettrons ensuite. Utilisez-le afin de libérer la puissance nécessaire pour mener à bien une quête de la plus haute importance.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44227, 'itIT', 'Scatenare la rabbia', 'Inizia il rituale per asservire l\'Infernale, quindi scatenalo per completare una missione mondiale.', 'Nei tempi di grande necessità, possiamo spronare le nostre forze migliori verso una forma di pura distruzione. Questo è uno di quei tempi.\n\nSe verrà richiesto, inizieremo un rituale per asservire un potente Infernale in un Filatterio Demoniaco, che ti affideremo. Usalo per scatenare quel potere e completare una missione di grande valore.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44227, 'koKR', '힘을 방출하라', '지옥불정령을 조종할 의식을 시작하고, 이 힘으로 전역 퀘스트를 완료해야 합니다.', '심각한 위기를 맞이했을 때, 우리는 순수하고도 파괴적인 하나의 형태에 가장 강력한 힘을 쏟아부을 수 있네. 지금이 바로 그런 시기지.\n\n부탁만 하면, 우리가 악마의 성물함에 강력한 지옥불정령이 속박되도록 의식을 시작할 걸세. 그리고 그 성물함을 그대에게 전해주겠네. 이것을 사용해 힘을 방출하여 중대한 임무를 완료하게.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44227, 'ptBR', 'Liberando nossa ira', 'Inicie o ritual para escravizar o infernal e, então, libere-o para concluir uma Missão Mundial.', 'Em momentos de grande necessidade, nós focamos nossas forças mais poderosas em um item puro e destrutivo. Este é um desses momentos.\n\nA pedido, nós iniciaremos o ritual para escravizar um Infernal poderoso em um Filactério Demoníaco, e daremos ele a você. Use ele para liberar o poder e concluir uma missão muito valiosa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44227, 'ruRU', 'Обрушим свой гнев', 'Начните ритуал порабощения инфернала, а затем выпустите его, чтобы выполнить локальное задание.', 'В час нужды мы можем сконцентрировать свою силу в чистейшую разрушительную форму. Сейчас настало именно такое время.\n\nПо твоему запросу мы начнем ритуал порабощения могущественного инфернала и заключения его в демоническую филактерию. Этот предмет будет отдан тебе. Ты сможешь высвободить его мощь для выполнения особенно важного задания.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44227, 'zhCN', '释放怒火', '开始奴役地狱火的仪式，然后释放它，以完成一个世界任务。', '在危急关头，我们可以把最强大的力量汇聚成一种毁灭性的纯粹形态。而眼下就是这样的非常时刻。\n\n一旦收到指示，我们会开始仪式，将强大的地狱火奴役在魔化护命匣里，然后把它交给你。释放它的力量，完成一项意义非凡的任务。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44227, 'zhTW', '釋放怒火', '進行儀式來奴役煉獄火，然後用這股力量完成一個世界任務。', '在危急時刻，我們可以將最強大的力量集中成一個純粹的毀滅武器。現在正是時候。\n\n一旦收到指示，我們會開始儀式，將強大的煉獄火奴役在惡魔骨匣中，接著我們會把這物品交給你。釋放它的力量，發揮它最大的價值吧。\n', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44228, 'deDE', 'Waffen für Champions', 'Erteilt einen Arbeitsauftrag für Championwaffen und -ausrüstung.', 'Seht Ihr Lurr? Groß und stark, aber nicht sehr schlau. Nicht so wie Murr. Murr kann Lurr sagen, er soll tolle Sachen für Euch bauen. Sachen für Eure Champions zum Draufhauen.\n\nAber zuerst habt Ihr etwas für Murr?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44228, 'esES', 'Armamento de campeón', 'Haz un pedido de armamento y equipo de campeón.', '¿Tú ver a Lurr? Grande y fuerte, pero no listo, no como Murr. Murr poder hacer que Lurr hacer cosas buenas para ti, cosas para que tus campeones y pegar buenas tundas.\n\nPero antes, ¿tú dar algo a Murr?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44228, 'esMX', 'Armamentos del campeón', 'Haz un pedido de armamentos y equipamiento para campeones.', '¿Tú ver Lurr? Él ser fuerte e inteligente, no como Murr. Murr poder hacer Lurr construir cosas buenas para ti, cosas para tus campeones usar y ser más golpeadores.\n\nPrimero, ¿dar algo para Murr?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44228, 'frFR', 'Armement de champion', 'Passez une commande d’équipement et d’armement de champion.', 'Toi voir Lurr ? Lui grand et costaud, mais abruti, pas comme Murr. Murr dire à Lurr de fabriquer bonnes choses pour toi, des choses pour tes champions, pour être forts.\n\nD’abord, toi donner quelque chose à Murr ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44228, 'itIT', 'Armamenti da Campione', 'Richiedi un incarico di lavoro per gli armamenti e l\'equipaggiamento da campione.', 'Tu vede Lurr? Lui grande e forte, ma non intelligente come Murr. Murr può far costruire a Lurr cose buone per te, cose per i tuoi campioni da indossare per spaccare meglio.\n\nPrima tu dai qualcosa a Murr?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44228, 'koKR', '용사의 무장', '용사의 무장과 장비를 주문해야 합니다.', '러르 봤어? 덩치 크고 힘도 세지만, 머르처럼 똑똑하지 않아. 머르는 러르 시켜서 친구에게 좋은 물건 만들어 줄 수 있어. 용사가 입을 거, 부술 때 쓸 거, 그런 거.\n\n먼저 머르에게 뭔가 줘 봐야지?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44228, 'ptBR', 'Armamentos de campeão', 'Faça uma ordem de serviço para equipamento e armamentos de Campeão.', 'Vê Lurr? Ele é grande, forte, mas burrinho. Murr é inteligente. Murr pode fazer Lurr construir coisas legais pra você, coisas pros campeões usarem e serem mais esmagadores. \n\nPrimeiro, você dá uma coisa pra Murr?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44228, 'ruRU', 'Вооружение для защитников', 'Разместите заказ на вооружение и снаряжение для защитников.', 'Видишь Лурра? Он большой и сильный, но не такой умный, как Мурр. Мурр может заставить Лурра сделать для тебя хорошие вещи, чтобы твои защитники их надели и рубились еще лучше. \n\nНо сперва дашь что-то Мурру?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44228, 'zhCN', '勇士武器', '下达订单，订购一批勇士武器和装备。', '看到鲁尔了吗？他又高又壮，但不聪明，不像穆尔。穆尔能让鲁尔给你造些好东西，你的勇士穿上之后砸东西更方便。\n\n但是首先，你得给穆尔一点东西？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44228, 'zhTW', '勇士武裝', '下一張勇士武裝或裝備的訂單。', '你看嚕爾，他很壯，很大隻，但腦子不太好。穆爾就不同啦。穆爾可以讓嚕爾幫你做好東西，讓你的勇士穿了好棒棒。\n\n但你得先給穆爾一些好東西。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44229, 'deDE', 'Waffen für Champions', 'Erteilt einen Arbeitsauftrag für Championwaffen und -ausrüstung.', 'Wir brauchen Waffen und Ausrüstung für unsere Champions.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44229, 'esES', 'Armamento de campeón', 'Haz un pedido de armamento y equipo de campeón.', 'Necesitaremos armamento y equipo para nuestros campeones.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44229, 'esMX', 'Armamentos del campeón', 'Haz un pedido de armamentos y equipamiento para campeones.', 'Necesitaremos armamentos y equipo para nuestros campeones.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44229, 'frFR', 'Armement de champion', 'Passez une commande d’équipement et d’armement de champion.', 'Nous allons avoir besoin d’armement et d’équipement pour nos champions.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44229, 'itIT', 'Armamenti da Campione', 'Richiedi un incarico di lavoro per gli armamenti e l\'equipaggiamento da campione.', 'Ci serviranno armi ed equipaggiamenti per i nostri campioni.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44229, 'koKR', '용사의 무장', '용사의 무장과 장비를 주문해야 합니다.', '용사들이 사용할 무기와 장비가 필요합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44229, 'ptBR', 'Armamentos de campeão', 'Faça uma ordem de serviço para equipamento e armamentos de Campeão.', 'Vamos precisar de equipamentos e armamentos para nossos campeões.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44229, 'ruRU', 'Вооружение для защитников', 'Разместите заказ на вооружение и снаряжение для защитников.', 'Нам потребуется вооружение и снаряжение для наших защитников.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44229, 'zhCN', '勇士武器', '下达订单，订购一批勇士武器和装备。', '我们需要武器和装备来武装我们的勇士。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44229, 'zhTW', '勇士武裝', '下一張勇士武裝或裝備的訂單。', '我們的勇士需要武裝和裝備。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44230, 'deDE', 'Möge uns das Schicksal segnen', 'Erteilt einen Arbeitsauftrag für ein Siegel des verheerten Schicksals.', 'Wir können ein wertvolles Siegel beschaffen, das Euch im Krieg gegen die Legion gute Dienste leisten wird. Wenn Ihr dieses Siegel im rechten Moment einsetzt, erhaltet Ihr eine zweite Chance, Euer Schicksal zu erfüllen.\n\nSollen wir das Siegel für Euch beschaffen?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44230, 'esES', 'Los destinos nos bendicen', 'Haz un pedido de sello de destino alterado.', 'Podemos facilitarte un objeto valioso que te ayudará en nuestra guerra contra la Legión. Este sello se puede utilizar en los momentos más adecuados para gozar de una nueva oportunidad de cara al destino.\n\n¿Te proporcionamos uno?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44230, 'esMX', 'Que el destino nos bendiga', 'Haz un pedido de un Sello del Destino quebrado.', 'Podemos conseguir un objeto valioso que te ayudará en la guerra contra la Legión. Este sello se puede usar en los momentos más oportunos para tener otra oportunidad con el destino. \n\n¿Quieres que te consigamos uno?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44230, 'frFR', 'Le destin nous bénit', 'Passez une commande de Sceau du destin brisé.', 'Nous sommes à même de vous fournir un objet de grande valeur pour vous aider dans votre campagne contre la Légion. Utilisé à bon escient, ce sceau vous donnera une autre chance d’accomplir votre destinée.\n\nEn voulez-vous un ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44230, 'itIT', 'Il destino ci benedice', 'Assegna un incarico di lavoro per un Sigillo del Destino Spezzato.', 'Siamo in grado di ottenere un oggetto prezioso per la nostra guerra contro la Legione. Questo sigillo può essere usato nel momento più opportuno per dare un\'altra possibilità al destino.\n\nDesideri che te ne procuriamo uno?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44230, 'koKR', '운명의 축복', '부서진 운명의 인장을 주문해야 합니다.', '저희는 군단과의 전쟁에서 도움이 될 중요한 물건을 확보할 수 있습니다. 그 인장을 적절한 시기에 사용하면 운명을 바꿀 수도 있지요.\n\n혹시 하나 필요하십니까?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44230, 'ptBR', 'O destino nos abençoa', 'Faça uma ordem de serviço para um Selo do Destino Interrompido.', 'Podemos obter um item valioso para ajudar você na nossa guerra contra a Legião. Este selo pode ser usado nos momentos mais oportunos para conceder uma nova chance ao destino. \n\nDevemos providenciar um para você?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44230, 'ruRU', 'Судьба благословляет нас', 'Разместите заказ на печать сломанной судьбы.', 'Мы можем добыть для тебя ценный предмет, чтобы помочь в борьбе с Легионом. Эту печать ты сможешь применить в самый трудный час, чтобы судьба подарила тебе еще один шанс. \n\nЧто скажешь? Она тебе нужна?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44230, 'zhCN', '命运祝福我们', '下达一份破碎命运印记订单。', '我们可以找到一件宝物，它可以帮助你对抗燃烧军团。这个封印能在最关键的时刻给你一次改变命运的机会。\n\n要我们帮你去找吗？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44230, 'zhTW', '命運恩佑', '下一張破碎命運徽印的訂單。', '為了幫你對抗燃燒軍團，我們可以取得一種珍貴的物品，只要在關鍵時刻使用這個徽印，你就可以重新挑戰命運。\n\n要不要我們設法幫你取得？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44231, 'deDE', 'Waffen für Champions', 'Erteilt einen Arbeitsauftrag für Championwaffen und -ausrüstung.', 'Die meisten Leute gucken eine Leiche an und denken: \"Was für ein stinkender Haufen.\" Ich bin da ganz anders. Ich sehe ein wunderschönes Geschenk mit vielen wundervollen Geheimnissen.\n\nUnd zum richtigen Preis teile ich diese Geheimnisse mit Euch.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44231, 'esES', 'Armamento de campeón', 'Haz un pedido de armamento y equipo de campeón.', 'Hoy día la gente ve un cadáver y piensa que no es más que un montón de casquería, pero yo no, desde luego. Yo veo un pequeño regalo lleno de maravillosos secretos.\n\nPor un módico precio, también compartiré esos secretos contigo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44231, 'esMX', 'Armamentos del campeón', 'Haz un pedido de armamentos y equipo de campeón.', 'La gente de hoy ve un cadáver y piensa que es una fedorenta pila de porquería, pero yo no soy así. Yo lo veo como un pequeño regalo que contiene maravillosos secretos.\n\nPor un precio justo, te los contaré.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44231, 'frFR', 'Armement de champion', 'Passez une commande d’équipement et d’armement de champion.', 'De nos jours, les gens considèrent qu’un corps sans vie n’est rien de plus qu’un tas malodorant de matière organique en décomposition. Mais pas moi, ça non ! J’y vois un petit cadeau qui recèle de merveilleux secrets.\n\nEt moyennant finance, je veux bien partager ces secrets avec vous.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44231, 'itIT', 'Armamenti da Campione', 'Richiedi un incarico di lavoro per gli armamenti e l\'equipaggiamento da campione.', 'La gente di questi tempi vede i corpi dei morti solo come una puzzolente massa di melma, ma io no, te lo assicuro. Io li vedo come un regalo pieno di splendidi segreti.\n\nPer il giusto prezzo, condividerò con te quei segreti.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44231, 'koKR', '용사의 무장', '용사의 무장과 장비를 주문해야 합니다.', '요즘 사람들은 사체를 그저 냄새나는 덩어리 정도로 생각하지만, 나는 절대 그렇지 않소. 난 사체를 놀라운 비밀이 담긴 작은 선물이라고 생각하지.\n\n적당한 금액만 지급하면, 당신에게도 그 비밀을 알려드리겠소.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44231, 'ptBR', 'Armamentos de campeão', 'Faça uma ordem de serviço para equipamento e armamentos de Campeão.', 'As pessoas hoje em dia veem um corpo e pensam que é um monte de gosma fedorenta. Mas eu, não. Pra mim, é um presentinho que esconde segredos maravilhosos. \n\nA um preço, posso compartilhar esses segredos com você também.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44231, 'ruRU', 'Вооружение для защитников', 'Разместите заказ на вооружение и снаряжение для защитников.', 'Люди нынче смотрят на труп и видят вонючую груду гниющего мяса. А я – нет. Я вижу маленький подарок, таящий в себе чудесные секреты. \n\nЗа некоторую плату я поделюсь этими секретами и с тобой.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44231, 'zhCN', '勇士武器', '下达订单，订购一批勇士武器和装备。', '现在的人啊，看到一具尸体只觉得那是一堆散发着恶臭的烂肉。可我和他们不一样。我把它看作一件包含着奇妙秘密的小礼物。\n\n价格合适的话，我可以把其中的秘密分享给你。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44231, 'zhTW', '勇士武裝', '下一張勇士武裝或裝備的訂單。', '現在的人看到死屍只覺得又髒又臭。我可不會。死屍對我而言就像禮物盒，裝滿了驚喜。\n\n我可以和你分享這些驚喜，但你得付錢。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44232, 'deDE', 'Der Hain gibt', 'Nehmt ein Geschenk von den Traumwebern an.', '$n, Eure Weisheit und Führungsqualitäten sind eine Inspiration für uns alle!\n\nDie Traumweber möchten Euch eine komplette Rüstung für den Kampf gegen die Brennende Legion zur Verfügung stellen. Erweitert Euren Einfluss auf den Rest der Verheerten Inseln, dann ist der Sieg eines Tages unser.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44232, 'esES', 'La Arboleda proveerá', 'Acepta un regalo de los Tejesueños.', '¡$n, tu sabiduría y tu liderazgo nos inspiran a todos un gran asombro!\n\nLos Tejesueños están dispuestos a proporcionarte una armadura completa para ayudarte en la batalla contra la Legión Ardiente. Sigue expandiendo tu influencia por las Islas Abruptas y puede que aún logremos la victoria.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44232, 'esMX', 'La Arboleda es rica', 'Acepta un regalo de los tejesueños.', '¡$n, tu sabiduría y liderazgo te han hecho merecer el respeto de todos nosotros!\n\nLos tejesueños están preparados para proporcionarte un conjunto completo de armadura con el fin de ayudarte en la pelea contra la Legión Ardiente. Sigue expandiendo tu influencia en las Islas Quebradas y quizás podramos alcanzar la victoria.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44232, 'frFR', 'Le bosquet prodigue', 'Acceptez un cadeau de la part des Tisse-Rêves.', '$n, votre sagesse et vos qualités de $gmeneur:meneuse; nous inspirent le plus grand respect.\n\nLes Tisse-Rêves sont prêts à vous fournir un ensemble d’armure complet pour vous aider à combattre la Légion ardente. Continuez à étendre votre influence sur les îles Brisées, et nous pourrions bien finir par l’emporter.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44232, 'itIT', 'Frutti della terra', 'Accetta il dono dei Plasmasogni.', '$n, la tua saggezza e la tua guida ispirano tutti noi!\n\nI Plasmasogni sono pronti a fornirti un\'armatura per aiutarti nel combattimento contro la Legione Infuocata. Continua a espandere la tua influenza sulle Isole Disperse e giungeremo alla vittoria!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44232, 'koKR', '숲이 주는 것', '몽술사가 주는 선물을 받아야 합니다.', '$n 님, 우리는 모두 당신의 지혜와 통솔력에 탄복했어요!\n\n불타는 군단에 맞설 수 있도록 우리 몽술사는 방어구를 지원해 드릴게요. 부서진 섬에서 세력을 더 확장해 나간다면 우리가 승리를 거머쥘 수 있을 거예요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44232, 'ptBR', 'O bosque provê', 'Aceite um presente do Tece-sonhos.', '$n, sua sabedoria e liderança nos inspira!\n\nOs Tece-sonhos estão preparados para fornecer a você uma armadura completa para ajudar na luta contra a Legião Ardente. Continue a expandir a sua influência nas Ilhas Partidas e talvez consigamos alcançar a vitória.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44232, 'ruRU', 'Дары Рощи', 'Примите дар Ткачей Снов.', '$n, твоя мудрость и талант вдохновляют нас!\n\nТкачи Снов готовы обеспечить тебе полный набор снаряжения для битвы с Пылающим Легионом. Продолжай расширять свое влияние на Расколотых островах, и мы еще сможем одержать победу.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44232, 'zhCN', '林地的馈赠', '接受织梦者的礼物。', '$n，你的智慧和领导才能激励了我们所有人！\n\n织梦者准备为你提供全套装备来协助你对抗燃烧军团。继续在破碎群岛上扩展你的影响力吧，要不了多久我们就会取得胜利。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44232, 'zhTW', '林地供給', '接受織夢者的禮物。', '$n，你的智慧和領導能力令人欽佩！\n\n為了對抗燃燒軍團，織夢者已經幫你準備好全副武裝。繼續擴張你在破碎群島的勢力，我們就可能勝利。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44233, 'deDE', 'Dies ist Euer Pfad', 'Nehmt ein Geschenk vom Unsichtbaren Pfad an.', '$n, Eure Kraft und Führungsqualitäten sind eine Inspiration für uns alle!\n\nDer Unsichtbare Pfad möchte Euch eine komplette Rüstung für den Kampf gegen die Brennende Legion zur Verfügung stellen. Erweitert Euren Einfluss auf den Rest der Verheerten Inseln, dann ist der Sieg eines Tages unser.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44233, 'esES', 'Por el buen camino', 'Acepta un regalo de la Senda Oculta.', '¡$n, tu fuerza y tu liderazgo nos inspiran a todos un gran asombro!\n\nLa Senda Oculta está dispuesta a proporcionarte una armadura completa para ayudarte en la batalla contra la Legión Ardiente. Sigue expandiendo tu influencia por las Islas Abruptas y puede que aún logremos la victoria.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44233, 'esMX', 'Por aquí', 'Acepta un regalo de la Senda Oculta.', '¡$n, tu fuerza y liderazgo te han hecho merecer el respeto de todos nosotros!\n\nLa Senda Oculta está preparada para proporcionarte un conjunto completo de armadura con el fin de ayudarte en la pelea contra la Legión Ardiente. Sigue expandiendo tu influencia en las Islas Quebradas y quizás podramos alcanzar la victoria.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44233, 'frFR', 'J’irai où tu iras', 'Acceptez un cadeau de la part du Sentier invisible.', '$n, votre force et vos qualités de $gmeneur:meneuse; nous inspirent le plus grand respect.\n\nLe Sentier invisible est prêt à vous fournir un ensemble d’armure complet pour vous aider à combattre la Légion ardente. Continuez à étendre votre influence sur les îles Brisées, et nous pourrions bien finir par l’emporter.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44233, 'itIT', 'Il sentiero da seguire', 'Accetta il dono del Sentiero Invisibile.', '$n, la tua forza e la tua guida ispirano tutti noi!\n\nIl Sentiero Invisibile è pronto a fornirti un\'armatura per aiutarti nel combattimento contro la Legione Infuocata. Continua a espandere la tua influenza sulle Isole Disperse e giungeremo alla vittoria!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44233, 'koKR', '이 길을 따라', '드러나지 않은 길이 주는 선물을 받아야 합니다.', '$n, 우리는 모두 그대의 힘과 통솔력에 탄복했다오!\n\n불타는 군단에 맞설 수 있도록 드러나지 않은 길에서 방어구를 지원해 드리겠소. 부서진 섬에서 세력을 더 확장해 나간다면 우리가 승리를 거머쥘 수 있을 거요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44233, 'ptBR', 'Por aqui', 'Aceite um presente do Caminho Oculto.', '$n, sua força e liderança nos inspira!\n\nO Caminho Oculto está preparado para fornecer a você uma armadura completa para ajudar na luta contra a Legião Ardente. Continue a expandir a sua influência nas Ilhas Partidas e talvez consigamos alcançar a vitória.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44233, 'ruRU', 'Следуй своему пути', 'Примите дар Незримого пути.', '$n, твой ум и сила вдохновляют всех нас!\n\nНезримый путь готов обеспечить тебя полным комплектом снаряжения для битвы с Пылающим Легионом. Продолжай расширять свое влияние на Расколотых островах, и мы еще сможем одержать победу.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44233, 'zhCN', '践行此道', '接受隐秘通途的礼物。', '$n，你的力量和领导才能激励了我们所有人！\n\n隐秘通途准备为你提供全套装备来协助你对抗燃烧军团。继续在破碎群岛上扩展你的影响力吧，要不了多久我们就会取得胜利。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44233, 'zhTW', '追隨此道', '接受隱獵團的禮物。', '$n，你的力量和領導能力令人欽佩！\n\n為了對抗燃燒軍團，隱獵團已經幫你準備好全副武裝。繼續擴張你在破碎群島的勢力，我們就可能勝利。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44234, 'deDE', 'Unser entfesselter Zorn', 'Kanalisiert die Macht des Frostwyrms und entfesselt sie dann, um eine Weltquest abzuschließen.', 'In Zeiten größter Not können wir unsere Stärke zu einer reinen und sehr zerstörerischen Form vereinen. Dies ist solch eine Zeit.\n\nAuf Euer Wort werden wir unsere Kraft in einen Gegenstand kanalisieren, den wir Euch dann übergeben. Wenn Ihr seine Macht entfesselt, könnt Ihr eine wichtige Aufgabe abschließen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44234, 'esES', 'Liberar nuestra ira', 'Canaliza el poder de la vermis de escarcha y luego desátalo para completar una misión del mundo.', 'En tiempos de gran necesidad, podemos reunir nuestras fuerzas más poderosas en una forma pura y destructiva. Este es uno de esos momentos.\n\nCuando se nos pida, canalizaremos nuestro poder en un objeto que te proporcionaremos. Úsalo para desatar ese poder y completar una misión de gran valor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44234, 'esMX', 'Desatando nuestra cólera', 'Canaliza el poder del Vermis de escarcha y luego libéralo para completar una misión de mundo.', 'En tiempos de gran necesidad, podemos reunir a nuestras fuerzas más poderosas en una forma pura y destructiva. Este es uno de estos momentos.\n\nCuando lo pidas, ellos canalizarán nuestro poder a un objeto que te entregaremos. Úsalo para liberar ese poder, y habrás completado una valiosa misión.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44234, 'frFR', 'Le courroux déchaîné', 'Canalisez la puissance du wyrm de givre et libérez-la pour mener à bien une expédition.', 'En cas de nécessité absolue, il nous est possible de rassembler nos forces en une entité destructrice dans sa forme la plus pure. Et c’est ce qu’exige la situation actuelle.\n\nSi vous le souhaitez, nous canaliserons cette énergie dans un objet que nous vous remettrons ensuite. Utilisez-le afin de libérer la puissance nécessaire pour mener à bien une quête de la plus haute importance.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44234, 'itIT', 'Scatenare la rabbia', 'Incanala il potere del Dragone del Gelo, quindi scatenalo per completare una missione mondiale.', 'Nei tempi di grande necessità, possiamo spingere i più grandi dei nostri soldati verso una forma di pura distruzione. Questo è uno di quei tempi.\n\nSe verrà richiesto, incanaleremo questo potere in un oggetto che ti affideremo. Usalo per scatenare questo potere e completare una missione di grande valore.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44234, 'koKR', '힘을 방출하라', '서리고룡의 힘을 집중시킨 다음, 이 힘을 방출하여 전역 퀘스트를 완료해야 합니다.', '심각한 위기를 맞이했을 때, 우리는 순수하고도 파괴적인 하나의 형태에 가장 강력한 힘을 쏟아부을 수 있습니다. 지금이 바로 그런 시기지요.\n\n부탁만 하면, 우리가 특정 물건에 힘을 집중시킬 겁니다. 그리고 그 물건을 당신에게 전해드리겠습니다. 이것을 사용해 힘을 방출하여 중대한 임무를 완료하십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44234, 'ptBR', 'Liberando nossa ira', 'Canalize o poder da Serpe Gélida e, então, libere-o para concluir uma Missão Mundial.', 'Em momentos de grande necessidade, nós focamos nossas forças mais poderosas em um item puro e destrutivo. Estamos em um momento desses.\n\nA pedido, nós canalizaremos nosso poder em um item, e daremos ele a você. Use ele para liberar o poder e concluir uma missão muito valiosa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44234, 'ruRU', 'Обрушим свой гнев', 'Вберите силу ледяного змея, а затем выпустите ее, чтобы выполнить локальное задание.', 'В час нужды мы можем сконцентрировать свою силу в чистейшую разрушительную форму. Сейчас настало именно такое время.\n\nПо твоему запросу мы направим нашу силу в предмет, который будет отдан тебе. Ты сможешь высвободить его мощь для выполнения особенно важного задания.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44234, 'zhCN', '释放怒火', '引导冰霜巨龙之力并释放它，完成一个世界任务。', '在危急关头，我们会把最强大的力量汇聚成一种纯粹的毁灭性武器。而眼下就是这样的非常时刻。\n\n一旦收到指示，我们会把力量导入一个物品之中，然后把它交给你。释放它的力量，完成一项意义非凡的任务。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44234, 'zhTW', '釋放怒火', '引導冰霜巨龍的力量，然後用這股力量完成一個世界任務。', '在危急時刻，黯刃騎士團可以將最強大的力量集合成一個純粹的毀滅武器。現在正是時候。\n\n一旦收到指示，我們就會把力量導入物品中，然後把物品交給你。只要使用這個物品，就可以釋放力量，完成有價值的任務。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44235, 'deDE', 'Essenz des Schicksals', 'Erteilt einen Arbeitsauftrag für ein Siegel des verheerten Schicksals.', 'Wir können ein wertvolles Siegel beschaffen, das Euch im Krieg gegen die Legion gute Dienste leisten wird. Wenn Ihr dieses Siegel im rechten Moment einsetzt, erhaltet Ihr eine zweite Chance, Euer Schicksal zu erfüllen.\n\nSollen wir das Siegel für Euch beschaffen?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44235, 'esES', 'Esencia de destino', 'Haz un pedido de sello de destino alterado.', 'Podemos facilitarte un objeto valioso que te ayudará en nuestra guerra contra la Legión. Este sello se puede utilizar en los momentos más adecuados para gozar de una nueva oportunidad de cara al destino.\n\n¿Te proporcionamos uno?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44235, 'esMX', 'Esencia del destino', 'Haz un pedido de un Sello del Destino quebrado.', 'Podemos conseguir un objeto valioso que te ayudará en la guerra contra la Legión. Este sello se puede usar en los momentos más oportunos para tener otra oportunidad con el destino. \n\n¿Quieres que te consigamos uno?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44235, 'frFR', 'L’essence du destin', 'Passez une commande de Sceau du destin brisé.', 'Nous sommes à même de vous fournir un objet de grande valeur pour vous aider dans votre campagne contre la Légion. Utilisé à bon escient, ce sceau vous donnera une autre chance d’accomplir votre destinée.\n\nEn voulez-vous un ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44235, 'itIT', 'Essenza del destino', 'Assegna un incarico di lavoro per un Sigillo del Destino Spezzato.', 'Siamo in grado di ottenere un oggetto prezioso per la nostra guerra contro la Legione. Questo sigillo può essere usato nel momento più opportuno per dare un\'altra possibilità al destino.\n\nDesideri che te ne procuriamo uno?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44235, 'koKR', '운명의 정수', '부서진 운명의 인장을 주문해야 합니다.', '저희는 군단과의 전쟁에서 도움이 될 중요한 물건을 확보할 수 있습니다. 그 인장을 적절한 시기에 사용하면 운명을 바꿀 수도 있지요.\n\n혹시 하나 필요하십니까?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44235, 'ptBR', 'Essência do destino', 'Faça uma ordem de serviço para um Selo do Destino Interrompido.', 'Podemos obter um item valioso para ajudar você na nossa guerra contra a Legião. Este selo pode ser usado nos momentos mais oportunos para conceder uma nova chance ao destino. \n\nDevemos providenciar um para você?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44235, 'ruRU', 'Сущность судьбы', 'Разместите заказ на печать сломанной судьбы.', 'Мы можем добыть для тебя ценный предмет, чтобы помочь в борьбе с Легионом. Эту печать ты сможешь применить в самый трудный час, чтобы судьба подарила тебе еще один шанс. \n\nЧто скажешь? Она тебе нужна?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44235, 'zhCN', '命运的精华', '下达一份破碎命运印记订单。', '我们可以找到一件宝物，它可以帮助你对抗燃烧军团。这个封印能在最关键的时刻给你一次改变命运的机会。\n\n要我们帮你去找吗？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44235, 'zhTW', '命運之華', '下一張破碎命運徽印的訂單。', '為了幫你對抗燃燒軍團，我們可以取得一種珍貴的物品，只要在關鍵時刻使用這個徽印，你就可以重新挑戰命運。\n\n要不要我們設法幫你取得？', '', '', '', '', '', '', 0);
