INSERT INTO `quest_template_locale` VALUES (44236, 'itIT', 'Essenza del destino', 'Assegna un incarico di lavoro per creare un Sigillo del Destino.', 'Siamo riusciti a procurare un oggetto di grande valore per aiutarti nella guerra contro la Legione. Questo sigillo può essere usato nei momenti opportuni per avere un\'altra possibilità.\n\nNe vuoi uno?', '', '', '', '', '', '', 0);
