INSERT INTO `quest_template_locale` VALUES (44237, 'deDE', 'Urtume des Krieges', 'Beginnt mit der Rekrutierung eines Urtums des Krieges.', 'Der Setzling ist eindeutig aufgeregt und verlangt nach Eurer Aufmerksamkeit. Vielleicht solltet Ihr ihm helfen?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44237, 'esES', 'Ancianos de la guerra', 'Empieza a reclutar a un anciano de la guerra.', 'El pimpollo está muy nervioso y requiere tu atención. ¿Quieres ayudarle?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44237, 'esMX', 'Veteranos de guerra', 'Comienza por recultar a un Anciano de la Guerra.', 'El retoño está muy emocionado y quiere que le prestes atención. ¿Crees que puedes ayudarlo?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44237, 'frFR', 'Les anciens de la guerre', 'Commencez à recruter un Ancien de la guerre.', 'L’arbrisseau est manifestement très enthousiaste et essaie d’attirer votre attention. Peut-être devriez-vous l’aider ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44237, 'itIT', 'Antichi della Guerra', 'Inizia a reclutare un Antico della Guerra.', '<L\'arboscello è agitato e cerca la tua attenzione. Forse dovresti aiutarlo?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44237, 'koKR', '전쟁의 고대정령', '전쟁의 고대정령을 영입해야 합니다.', '묘목이 아주 들뜬 상태이며, 당신의 관심을 받고 싶어 합니다. 그를 도와야 할 것 같습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44237, 'ptBR', 'Ancientes da guerra', 'Inicie o recrutamento de um Anciente da Guerra', 'O broto está claramente animado e quer a sua atenção. De repente, seria interessante ajudá-lo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44237, 'ruRU', 'Древа войны', 'Начните выращивать древо войны.', 'Саженец явно чем-то взволнован и хочет привлечь ваше внимание. Вероятно, вам стоит ему помочь.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44237, 'zhCN', '战争古树', '招募一棵战争古树。', '那个树苗显然非常兴奋，想要吸引你的注意。也许你应该去帮他一把？', '', '', '', '', '', '', 0);
