INSERT INTO `quest_template_locale` VALUES (44239, 'koKR', '천신의 메아리', '천신의 메아리를 영입해야 합니다.', '티아닐리가 아주 흥분한 상태이며, 당신의 관심을 얻고 싶어 합니다. 그를 도와야 할 것 같습니다.', '', '', '', '', '', '', 0);
