INSERT INTO `quest_template_locale` VALUES (44240, 'deDE', 'Orange ist das neue Violett', 'Nehmt ein Geschenk von den Magiern der Tirisgarde an.', '$n, Eure Intelligenz und Führungsqualitäten sind eine Inspiration für uns alle!\n\nDie Tirisgarde möchte Euch eine komplette Rüstung für den Kampf gegen die Brennende Legion zur Verfügung stellen. Erweitert Euren Einfluss auf den Rest der Verheerten Inseln, dann ist der Sieg eines Tages unser.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44240, 'esES', 'El naranja es el nuevo morado', 'Acepta un regalo de los magi de la Guardia de Tirisfal.', '¡$n, tu inteligencia y tu liderazgo nos inspiran a todos un gran asombro!\n\nLa Guardia de Tirisfal está dispuesta a proporcionarte una armadura completa para ayudarte en la batalla contra la Legión Ardiente. Sigue expandiendo tu influencia por las Islas Abruptas y puede que aún logremos la victoria.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44240, 'esMX', 'El naranja es el nuevo violeta', 'Acepta un regalo del mágico de Tirisgarde.', '¡$n, tu inteligencia y liderazgo te han hecho merecer el respeto de todos nosotros!\n\nTirisgarde está preparado para proporcionarte un conjunto completo de armadura con el fin de ayudarte en la pelea contra la Legión Ardiente. Sigue expandiendo tu influencia en las Islas Quebradas y quizás podramos alcanzar la victoria.', '', '', '', '', '', '', 0);
