INSERT INTO `quest_template_locale` VALUES (44240, 'koKR', '티리스가드의 선물', '티리스가드의 마법사가 주는 선물을 받아야 합니다.', '$n 님, 우리는 모두 당신의 지혜와 통솔력에 탄복했습니다!\n\n불타는 군단에 맞설 수 있도록 우리 티리스가드가 방어구를 지원해 드리겠습니다. 부서진 섬에서 세력을 더 확장해 나간다면 우리가 승리를 거머쥘 수 있을 겁니다.', '', '', '', '', '', '', 0);
