INSERT INTO `quest_template_locale` VALUES (44263, 'koKR', '비전로: 비전로로', '비전로에서 조언자 반드로스를 처치해야 합니다.', '비전로는 우리 생각보다 훨씬 더 광대하고, 많은 위험이 도사리고 있습니다. 도시를 점령하는 유일한 방법은 지하부터 처리하는 것입니다.\n\n비전로 내부로 들어가 모든 적을 처치해야 합니다.', '', '', '', '', '', '', 0);
