INSERT INTO `quest_template_locale` VALUES (44268, 'deDE', 'Hallen der Tapferkeit: Odyns Herausforderung', 'Bezwingt Odyn in den Hallen der Tapferkeit.', 'Odyn selbst hat eine Nachricht geschickt. Er und seine Krieger warten in den Hallen der Tapferkeit auf würdige Anwärter.\n\nBezwingt die Champions in den Hallen der Tapferkeit und der Ruhm ist Euer.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44268, 'esES', 'Cámaras del Valor: El desafío de Odyn', 'Derrota a Odyn en las Cámaras del Valor.', 'El mismísimo Odyn envía un mensaje. Él y sus guerreros esperan en las Cámaras del Valor a que lleguen contendientes dignos.\n\nDerrota a los campeones de las Cámaras del Valor y consigue la gloria.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44268, 'esMX', 'Salones del Valor: El desafío de Odyn', 'Derrota a Odyn en los Salones del Valor.', 'El mismísimo Odyn nos envía un mensaje. Él y sus guerreros esperan a los contendientes dignos dentro de los Salones del Valor.\n\nDerrota a los campeones de los Salones del Valor y obtén la gloria.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44268, 'frFR', 'Salles des Valeureux : le défi d’Odyn', 'Venez à bout d’Odyn dans les Salles des Valeureux.', 'Ce message provient d’Odyn en personne. Ses guerriers et lui-même ont hâte de croiser le fer avec de preux concurrents.\n\nTerrassez les champions des salles des Valeureux et couvrez-vous de gloire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44268, 'itIT', 'Sale del Valore: La sfida di Odyn', 'Sconfiggi Odyn nelle Sale del Valore.', 'Odyn stesso ha inviato un messaggio. Lui e i suoi guerrieri attendono contendenti onorevoli alle Sale del Valore.\n\nSconfiggi i campioni delle Sale del Valore e raggiungi la gloria.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44268, 'koKR', '용맹의 전당: 오딘의 도전', '용맹의 전당에서 오딘을 꺾어야 합니다.', '오딘이 직접 말을 전해왔습니다. 오딘과 그의 전사들이 용맹의 전당에서 상대할 만한 경쟁자를 기다린다는 것이었습니다.\n\n용맹의 전당에서 용사들을 무찔러 승리를 거두십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44268, 'ptBR', 'Salões da Bravura: Desafio de Odyn', 'Derrote Odyn nos Salões da Bravura.', 'O próprio Odyn manda uma mensagem. Ele e seus guerreiros esperam por competidores dignos dentro dos Salões da Bravura.\n\nDerrote os campeões dos Salões da Bravura, e clame a glória.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44268, 'ruRU', 'Чертоги Доблести: испытание Одина', 'Победите Одина в Чертогах Доблести.', 'Послание отправил сам Один. Вместе со своими воинами он ждет достойных соперников в Чертогах Доблести.\n\nОдержите победу в Чертогах Доблести, чтобы снискать славу.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44268, 'zhCN', '英灵殿：奥丁的挑战', '击败英灵殿中的奥丁。', '奥丁亲自发来了一则消息。他和他的战士们正在英灵殿里等待有资格的挑战者出现。\n\n击败英灵殿的勇士，夺得荣耀。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44268, 'zhTW', '英靈殿：歐丁的挑戰', '前往英靈殿擊敗歐丁。', '歐丁捎來消息。他和他的勇士正在英靈殿等待有資格的競爭者前去挑戰。\n\n擊敗英靈殿的勇士，奪得榮耀。', '', '', '', '', '', '', 0);
