INSERT INTO `quest_template_locale` VALUES (44275, 'koKR', '별의 궁정: 별의 궁정으로', '별의 궁정에서 조언가 멜란드루스를 처치해야 합니다.', '파티하기 딱 좋은 날씨지요?\n\n대마법학자는 군단이 주변을 순찰하는 것에 대한 귀족들의 불안을 잠재우기 위해 파티에 모습을 드러낼지도 모릅니다. 별의 궁정으로 가보십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44275, 'ptBR', 'Pátio das Estrelas: Pátio das Estrelas adentro', 'Derrote o Conselheiro Melandrus no Pátio das Estrelas.', 'O clima está perfeito para uma festa, não é mesmo?\n\nViaje para o Pátio das Estrelas, onde a grã-magistra em pessoa pode aparecer dentre os nobres para reafirmar a necessidade de todas as patrulhas da Legião.', '', '', '', '', '', '', 0);
