INSERT INTO `quest_template_locale` VALUES (44275, 'zhCN', '群星庭院：进入群星庭院', '击败群星庭院的顾问麦兰杜斯。', '这样的天气最适合开派对了，不是吗？\n\n前往群星庭院，大魔导师可能会在那里现身，试图让贵族们认同燃烧军团巡逻的必要性。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44275, 'zhTW', '眾星之廷：進入眾星之廷', '在眾星之廷擊敗諫言者梅朗卓斯。', '今天的天氣最適合開派對了，不是嗎？\n\n前往眾星之廷，大博學者可能會親自現身，說服貴族認同燃燒軍團巡邏兵。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44276, 'deDE', 'Rabenwehr: Der Herr der Rabenwehr', 'Bezwingt Lord Kur\'talos Rabenkrone in der Rabenwehr.', 'Während unsere Champions die Überreste der Rabenwehr untersuchten, gerieten sie in einen Hinterhalt! Der Herr der Rabenwehr hat sich offenbar mit der Legion zusammengetan.\n\nWir brauchen Eure Hilfe, um seine immer größer werdende Armee zu besiegen und ihn zur Strecke zu bringen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44276, 'esES', 'Torreón Grajo Negro: El señor del Torreón Grajo Negro', 'Derrota a lord Kur\'talos Cresta Cuervo en el Torreón Grajo Negro.', '¡Mientras investigaban los restos del Torreón Grajo Negro, nuestros campeones cayeron en una emboscada! Parece que el señor del Torreón se ha aliado con la Legión.\n\nNecesitamos tu ayuda para derrotar a su creciente ejército y acabar con él.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44276, 'esMX', 'Fuerte Torre Oscura: El señor del Fuerte Torre Oscura', 'Derrota a Lord Kur\'talos Cresta Cuervo en el Fuerte Torre Oscura.', '¡Nuestros campeones sufrieron una emboscada mientras investigaban los restos del Fuerte Torre Oscura! Parece ser que el lord del Fuerte Torre Oscura se alió con la Legión.\n\nNecesitamos tu ayuda para derrotar a su ejército que crece y acabar con él.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44276, 'frFR', 'Bastion du Freux : le seigneur du bastion du Freux', 'Triomphez du Seigneur Kur’talos Corvaltus au Bastion du Freux.', 'En inspectant les décombres du bastion du Freux, nos champions sont tombés dans une embuscade ! Apparemment, le seigneur du bastion s’est allié avec la Légion.\n\nNous avons besoin de votre aide pour vaincre son armée toujours plus imposante, avant de l’éliminer lui.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44276, 'itIT', 'Forte Corvonero: Il Signore di Forte Corvonero', 'Sconfiggi Ser Kur\'talos Crinocorvo al Forte Corvonero.', 'Durante un\'indagine sulle macerie di Forte Corvonero, i nostri Campioni sono caduti in un\'imboscata! Il Signore del Forte sembra aver stretto un\'alleanza con la Legione.\n\nAbbiamo bisogno del tuo aiuto per sconfiggere il suo esercito, una volta per tutte.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44276, 'koKR', '검은 떼까마귀 요새: 검은 떼까마귀 요새의 군주', '검은 떼까마귀 요새에 있는 군주 쿠르탈로스 레이븐크레스트를 꺾어야 합니다.', '우리 용사가 검은 떼까마귀 요새 폐허를 조사하던 중, 적의 습격을 받았습니다! 그 요새의 군주가 군단과 동맹을 맺은 것 같습니다.\n\n성장하는 그의 군대를 섬멸하고 그를 쓰러뜨리려면 당신의 도움이 필요합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44276, 'ptBR', 'Castelo Corvo Negro: O Senhor do Castelo Corvo Negro', 'Derrote o Lorde Kur\'talos Cristacorvo no Castelo Corvo Negro.', 'Enquanto investigavam as ruínas do Castelo Corvo Negro, nossos campeões foram emboscados! Parece que o próprio Lorde do Castelo se aliou à Legião.\n\nPrecisamos da sua ajuda para derrotar o crescente exército do Lorde, e acabar com ele.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44276, 'ruRU', 'Крепость Черной Ладьи: владыка крепости', 'Победите лорда Кур\'талоса Гребня Ворона в крепости Черной Ладьи.', 'Наши защитники попали в засаду, когда осматривали крепость Черной Ладьи! Похоже, хозяин крепости перешел на сторону Легиона.\n\nНам нужна твоя помощь, чтобы победить растущую армию Черной Ладьи и уничтожить ее командира.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44276, 'zhCN', '黑鸦堡垒：黑鸦堡垒领主', '击败黑鸦堡垒的库塔洛斯·拉文凯斯。', '我们的勇士在调查黑鸦堡垒废墟的时候遭到了伏击！黑鸦领主似乎已经和燃烧军团勾结到了一起。\n\n我们需要你协助我们击败他不断壮大的军队并将他消灭。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44276, 'zhTW', '玄鴉堡：玄鴉堡之主', '前往玄鴉堡擊敗克塔羅斯‧黑羽堡主。', '在調查玄鴉堡的遺跡時，我們的勇士遇到埋伏！玄鴉堡堡主似乎已經與燃燒軍團結盟。\n\n我們需要你的協助，擊潰他日益壯大的軍隊，並且擊敗他本人。', '', '', '', '', '', '', 0);
