INSERT INTO `quest_template_locale` VALUES (44306, 'frFR', 'Bénédictions de l’ordre', 'Demandez la bénédiction de l’ordre', 'Bien que cela nécessite du temps et des ressources, nous pouvons bien évidemment bénir vos champions. Ainsi, leurs chances de succès au cours de leurs aventures seront nettement améliorées.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44306, 'itIT', 'Benedizioni dell\'Ordine', 'Richiedi una Benedizione dell\'Ordine.', 'Anche se richiede tempo e risorse, possiamo benedire i tuoi campioni. Con queste benedizioni, le loro possibilità di riuscire nelle loro missioni sono molto più alte.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44306, 'koKR', '연맹의 축복', '연맹의 축복을 요청해야 합니다.', '시간과 자원이 필요하지만, 기꺼이 당신의 용사들에게 축복을 내려 드리겠습니다. 이 축복을 받으면 그들의 모험이 성공할 가능성도 상당히 커질 겁니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44306, 'ptBR', 'Bênçãos da ordem', 'Peça uma Bênção da Ordem.', 'Apesar de levar tempo e recursos, somos capazes de, é claro, abençoar os seus campeões. Com essa benção, o sucesso deles nas aventuras aumentarão bastante.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44306, 'ruRU', 'Благословения ордена', 'Попросите благословение ордена.', 'Мы можем благословить твоих защитников, но для этого потребуются ресурсы и время. С нашим благословением они станут намного удачливее в своих приключениях.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44306, 'zhCN', '团体的祝福', '请求团体的祝福。', '尽管这需要耗费时间和资源，但我们可以为你的勇士们赋予祝福。有了祝福之力，他们成功完成任务的几率将大大提高。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44306, 'zhTW', '牧道會的祝福', '向牧道會請求祝福。', '雖然會消耗時間和資源，但我們當然可以祝福你麾下的勇士。有了我們的祝福，他們的冒險旅途也會更順利些。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44310, 'deDE', 'Dreifache Macht', 'Sagt Meryl Bescheid, wenn Ihr einem weiteren Artefakt nachgehen wollt.', 'Wir haben Informationen über das letzte Artefakt, nach dem Ihr vielleicht suchen solltet, wenn Ihr vorhabt, noch andere Magiearten anzuwenden.\n\nHier sind alle Einzelheiten. Lasst mich wissen, wie Ihr vorgehen wollt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44310, 'esES', 'El triple de poder', 'Informa a Meryl si quieres ir a por otro artefacto.', 'Tenemos información sobre un último artefacto que podrías buscar, si es que quieres utilizar otros tipos de magia.\n\nAquí tienes todos los detalles. Dime cómo te gustaría proceder.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44310, 'esMX', 'Tres veces más poder', 'Dile a Meryl si quieres ir a buscar otro artefacto.', 'Tenemos información de un último artefacto que podrías ir a buscar, en caso de que tengas que utilizar otros tipos de magia.\n\nAquí están todos los detalles. Dime cómo quieres proceder.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44310, 'frFR', 'Trois fois plus de puissance', 'Si vous souhaitez partir en quête d’une autre arme prodigieuse, informez-en Meryl.', 'Nous détenons des informations concernant une dernière arme prodigieuse que vous pourriez acquérir, si cela vous tente d’essayer d’autres types de magie.\n\nToutes les précisions sont là. Dites-moi ce que vous souhaitez faire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44310, 'itIT', 'Triplicare il potere', 'Fai sapere a Meryl se vuoi recuperare un altro Artefatto.', 'Abbiamo informazioni su un ultimo artefatto che potresti ricercare, se deciderai di tessere altri tipi di magia.\n\nHo qui tutti i dettagli, fammi sapere come vuoi procedere.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44310, 'koKR', '강력한 힘', '메릴에게 또 다른 유물을 찾고 싶다고 말해야 합니다.', '우리에게 마지막 유물에 대한 정보가 있네. 다른 형태의 마법을 다뤄볼 의향이 있다면 유용한 정보가 될 걸세.\n\n모든 정보는 여기 있네. 어떻게 하고 싶은가?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44310, 'ptBR', 'O triplo do poder', 'Avise Meryl se quiser buscar outro artefato.', 'Temos informações sobre um último artefato que você pode procurar, se quiser manipular outros tipos de magia.\n\nTodos os detalhes estão aqui. Diga-me como quer prosseguir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44310, 'ruRU', 'Тройное могущество', 'Сообщите Мерилу свое решение относительно артефакта.', 'У нас есть сведения о последнем артефакте из тех, что могут тебя заинтересовать, если вдруг ты хочешь овладеть другими видами магии.\n\nВсе подробности здесь. Когда примешь решение, скажи мне.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44310, 'zhCN', '三倍力量', '告诉梅瑞尔你是否要去寻找下一件神器。', '如果你想探寻其他类型的魔法力量的话，我们得到了你所要寻找的最后一件神器的情报。\n\n详情都在这里。告诉我你准备怎么做。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44310, 'zhTW', '三重法力', '告訴莫爾你是否想再去找另一件神兵武器。', '如果你有興趣嘗試其他法術，我們總算獲得關於最後一件神兵武器的情報。\n\n所有的資料都在這裡。請告訴我你的決定。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44337, 'deDE', 'Die Göttin wache über Euch', 'Trefft Erzmagier Khadgar in Dalaran.', 'Ich habe womöglich einen \"Schlüssel\" gefunden, um das Herz des Lichts aufzuschließen, $n.$B$BIch blätterte durch einige uralte kosmologische Folianten, als ich über eine Passage stolperte, die besagte, dass der höchste Naaru möglicherweise während der großen Ordnung von Licht und Schatten von Elune selbst geschaffen wurde.$B$BWenn Elune Xe\'ra tatsächlich erschaffen hat, dann würde es einleuchten, dass wir mit den Tränen von Elune die Geheimnisse des Herzens des Lichts erschließen können.$B$BEs ist zwar weit hergeholt, aber wir haben nichts mehr zu verlieren.$B$BTrefft mich in Dalaran. Ich werde die Tränen von Elune für Euch vorbereiten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44337, 'esES', 'Que la diosa te proteja.', 'Reúnete con el archimago Khadgar en Dalaran.', 'Puede que haya descubierto una \"llave\" para abrir el Corazón de la Luz, $n.$b$bEstaba repasando unos tomos antiguos sobre cosmología cuando me topé con un pasaje que indicaba que la naaru primigenia podría haber sido creación de Elune durante el gran ordenamiento de la Luz y las Sombras.$b$bSi Elune creó a Xe\'ra, resulta razonable que podamos usar las Lágrimas de Elune para acceder a los secretos del Corazón de la Luz.$b$bEs probar por probar, pero no tenemos nada que perder.$b$bReúnete conmigo en Dalaran. Tendré las Lágrimas de Elune listas para ti.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44337, 'esMX', 'Que la diosa te proteja', 'Reúnete con el Archimago Khadgar en Dalaran.', 'Puede que haya encontrado una \"llave\" para abrir el Corazón de la Luz, $n.$B$BEstaba revisando unos escritos antiguos acerca de cosmología y me topé con un capítulo que indica que la naaru suprema puede haber sido creada por Elune durante la gran ordenación de la Luz y las Sombras.$B$BSi Elune efectivamente creó a Xe\'ra, entonces es lógico que podamos usar las Lágrimas de Elune para descubrir los secretos del Corazón de la Luz.$B$BEs arriesgado, pero no tenemos nada que perder.$B$BReunámonos en Dalaran. Yo prepararé las Lágrimas de Elune para ti.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44337, 'frFR', 'La Déesse vous protège', 'Allez voir l’Archimage Khadgar à Dalaran.', 'Je pense avoir trouvé une « clé » permettant de déverrouiller le Cœur de la Lumière, $n.$B$BJe feuilletais d’anciens grimoires consacrés à la cosmologie quand j’ai découvert un passage indiquant que le Premier Naaru pourrait avoir été créé par Élune lors du grand ordonnancement de l’Ombre et de la Lumière.$B$BSi Élune a bel et bien façonné Xe’ra, il n’est pas impossible que ses Larmes puissent nous aider à percer les secrets du Cœur de la Lumière.$B$BLa piste est mince, je vous l’accorde, mais après tout, nous n’avons plus rien à perdre.$B$BRejoignez-moi à Dalaran. Je vais vous préparer les Larmes d’Élune.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44337, 'itIT', 'Che la dea vegli su di te.', 'Incontra l\'Arcimago Khadgar a Dalaran.', 'Potrei aver trovato una \"chiave\" per aprire il Cuore della Luce, $n.$B$BSfogliando degli antichi tomi di cosmologia mi sono imbattuto in un passaggio secondo cui il Naaru Primordiale potrebbe esser stato creato da Elune quando divise Luce e Ombra.$B$BSe è stata Elune a creare Xe\'ra, è ragionevole pensare che potremo usare le Lacrime di Elune per sbloccare i segreti del Cuore della Luce.$B$BÈ un azzardo, ma non abbiamo nient\'altro da perdere.$B$BIncontriamoci a Dalaran. Preparerò le Lacrime di Elune per il tuo ritorno.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44337, 'koKR', '여신의 가호가 있기를', '달라란에 있는 대마법사 카드가를 만나야 합니다.', '내가 빛의 심장을 여는 \"열쇠\"를 발견했는지도 모르겠소, $n.$B$B고대 우주론을 설명하는 책들을 살펴보던 중이었는데, 우연히 시초의 나루가 빛과 어둠의 질서가 잡히는 과정에서 엘룬의 손에 만들어졌음을 나타낸 문구를 발견했소.$B$B엘룬이 실제로 제라를 만들었다면, 엘룬의 눈물을 사용하여 빛의 심장에 담긴 비밀을 밝혀낼 수 있을지도 모르오.$B$B가능성이 높진 않지만, 어차피 잃을 건 없으니...$B$B달라란에서 다시 만납시다. 엘룬의 눈물을 준비해 놓겠소.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44337, 'ptBR', 'Que a deusa zele por você', 'Encontre-se com o Arquimago Hadggar em Dalaran.', 'Acho que encontrei uma \"chave\" para destrancar o Coração da Luz, $n.$B$BEu estava lendo alguns tomos sobre cosmologia ancestral quando me deparei com uma passagem que indica que o primeiro naaru pode ter sido criado por Eluna durante o ordenamento da Luz e Sombra.$B$BSe Eluna de fato criou Xi\'ra, então é provável que possamos usar as Lágrimas de Eluna para destrancar os segredos do Coração da Luz.$B$BPode não dar certo, mas não temos nada a perder.$B$BEncontre-me em Dalaran. Eu prepararei as Lágrimas de Eluna para você.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44337, 'ruRU', 'Богиня благоволит тебе', 'Встретьтесь с верховным магом Кадгаром в Даларане.', 'Кажется, я нашел своеобразный ключ к Сердцу Света, $n.$B$BИзучая древние книги по космологии, я наткнулся на упоминание того, что первый наару, возможно, был создан Элуной во время великого распределения Света и Тьмы.$B$BЕсли Зе\'ра действительно была создана Элуной, значит, с помощью Слез Элуны мы могли бы разгадать тайны Сердца Света.$B$BЭто будет непросто, но терять нам все равно нечего.$B$BВстретимся в Даларане. Я приготовлю Слезы Элуны.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44337, 'zhCN', '女神护佑着你', '去达拉然找大法师卡德加。', '我可能找到了解开圣光之心的“钥匙”，$n。$B$B我之前在查阅一些宇宙学古代典籍时，发现了让我非常困惑一段文字。这段文字暗示，始祖纳鲁是由艾露恩在光暗大定序时创造出来的。$B$B如果确实是艾露恩创造了泽拉，那么我们也许就能用艾露恩之泪去解开圣光之心的秘密。$B$B这是一次豪赌，但我们已经没什么可失去的了。$B$B来达拉然找我吧。我会准备好艾露恩之泪。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44337, 'zhTW', '願女神保佑你', '到達拉然與大法師卡德加見面。', '我說不定找到了打開聖光之心的「鑰匙」了，$n。$B$B當時我正在翻閱幾本古代宇宙論的秘典，赫然發現在其中一個段落中寫道原始的那魯可能就是在光與暗誕生之際，由伊露恩親手所創。$B$B如果瑟拉真的是伊露恩一手所造，那麼我們應該可以用伊露恩之淚來打開聖光之心。$B$B這是一場賭注，不過試試看也沒有損失，$B$B到達拉然來找我，我會幫你準備好伊露恩之淚。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44338, 'deDE', 'Die Göttin wache über Euch', 'Bergt die Tränen von Elune aus Val\'sharah.', 'Ich habe womöglich einen \"Schlüssel\" gefunden, um das Herz des Lichts aufzuschließen, $n.$B$BIch blätterte durch einige uralte kosmologische Folianten, als ich über eine Passage stolperte, die besagte, dass der höchste Naaru möglicherweise während der großen Ordnung von Licht und Schatten von Elune selbst geschaffen wurde.$B$BWenn Elune Xe\'ra tatsächlich erschaffen hat, dann würde es einleuchten, dass wir mit den Tränen von Elune die Geheimnisse des Herzens des Lichts erschließen können.$B$BJetzt liegt es an Euch, die Tränen von Elune aus Val\'sharah zu beschaffen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44338, 'esES', 'Que la diosa te proteja.', 'Recupera las Lágrimas de Elune en Val\'sharah.', 'Puede que haya descubierto una \"llave\" para abrir el Corazón de la Luz, $n.$b$bEstaba repasando unos tomos antiguos sobre cosmología cuando me topé con un pasaje que indicaba que la naaru primigenia podría haber sido creación de Elune durante el gran ordenamiento de la Luz y las Sombras.$b$bSi Elune creó a Xe\'ra, resulta razonable que podamos usar las Lágrimas de Elune para acceder a los secretos del Corazón de la Luz.$b$bAhora te corresponde a ti recuperar las Lágrimas de Elune en Val\'sharah.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44338, 'esMX', 'Que la diosa te proteja', 'Recupera las Lágrimas de Elune de Val\'sharah.', 'Puede que haya encontrado una \"llave\" para abrir el Corazón de la Luz, $n.$B$BEstaba revisando unos escritos antiguos acerca de cosmología y me topé con un capítulo que indica que la naaru suprema puede haber sido creada por Elune durante la gran ordenación de la Luz y las Sombras.$B$BSi Elune efectivamente creó a Xe\'ra, entonces es lógico que podamos usar las Lágrimas de Elune para descubrir los secretos del Corazón de la Luz.$B$BAhora es tu responsabilidad recuperar Las Lágrimas de Elune de Val\'sharah.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44338, 'frFR', 'La Déesse vous protège', 'Récupérez les Larmes d’Élune en Val’sharah.', 'Je pense avoir trouvé une « clé » permettant de déverrouiller le Cœur de la Lumière, $n.$B$BJe feuilletais d’anciens grimoires consacrés à la cosmologie quand j’ai découvert un passage indiquant que le premier Naaru aurait été créé par Élune lors du grand ordonnancement de l’Ombre et de la Lumière.$B$BSi Élune a bel et bien façonné Xe’ra, selon toute logique, nous devrions pouvoir utiliser ses larmes pour percer les secrets du Cœur de la Lumière.$B$BÀ charge pour vous de récupérer les Larmes d’Élune en Val’sharah.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44338, 'itIT', 'Che la dea vegli su di te.', 'Recupera le Lacrime di Elune a Val\'sharah.', 'Potrei aver trovato una \"chiave\" per aprire il Cuore della Luce, $n.$B$BSfogliando degli antichi tomi di cosmologia mi sono imbattuto in un passaggio secondo cui il Naaru Primordiale potrebbe esser stato creato da Elune quando divise Luce e Ombra.$B$BSe è stata Elune a creare Xe\'ra, è ragionevole pensare che potremo usare le Lacrime di Elune per sbloccare i segreti del Cuore della Luce.$B$BOra è tuo compito recuperare le Lacrime di Elune a Val\'sharah.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44338, 'koKR', '여신의 가호가 있기를', '발샤라에서 엘룬의 눈물을 찾아내야 합니다.', '내가 빛의 심장을 여는 \"열쇠\"를 발견했는지도 모르겠소, $n.$B$B고대 우주론을 설명하는 책들을 살펴보던 중이었는데, 우연히 시초의 나루가 빛과 어둠의 질서가 잡히는 과정에서 엘룬의 손에 만들어졌음을 나타낸 문구를 발견했소.$B$B엘룬이 실제로 제라를 만들었다면, 엘룬의 눈물을 사용하여 빛의 심장에 담긴 비밀을 밝혀낼 수 있을지도 모르오.$B$B이제 발샤라에서 당신이 엘룬의 눈물을 찾아내는 일만 남았소.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44338, 'ptBR', 'Que a deusa zele por você', 'Recupere as Lágrimas de Eluna em Val\'sharah.', 'Acho que encontrei uma \"chave\" para destrancar o Coração da Luz, $n.$B$BEu estava lendo alguns tomos sobre cosmologia ancestral quando me deparei com uma passagem que indica que o primeiro naaru pode ter sido criado por Eluna durante o ordenamento da Luz e Sombra.$B$BSe Eluna de fato criou Xi\'ra, então é provável que possamos usar as Lágrimas de Eluna para destrancar os segredos do Coração da Luz.$B$BAgora cabe a você recuperar as Lágrimas de Eluna em Val\'sharah.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44338, 'ruRU', 'Богиня благоволит тебе', 'Добудьте Слезы Элуны в Валь\'шаре.', 'Кажется, я нашел своеобразный ключ к Сердцу Света, $n.$B$BИзучая древние книги по космологии, я наткнулся на упоминание того, что первый наару, возможно, был создан Элуной во время великого распределения Света и Тьмы.$B$BЕсли Зе\'ра действительно была создана Элуной, значит, с помощью Слез Элуны мы могли бы разгадать тайны Сердца Света.$B$BИ добыть Слезы Элуны в Валь\'шаре предстоит тебе.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44338, 'zhCN', '女神护佑着你', '从瓦尔莎拉找回艾露恩之泪。', '我可能找到了解开圣光之心的“钥匙”，$n。$B$B我之前在查阅一些宇宙学古代典籍时，发现了让我非常困惑一段文字。这段文字暗示，始祖纳鲁是由艾露恩在光暗大定序时创造出来的。$B$B如果确实是艾露恩创造了泽拉，那么我们也许就能用艾露恩之泪去解开圣光之心的秘密。$B$B现在我们得靠你去从瓦尔莎拉取回艾露恩之泪了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44338, 'zhTW', '願女神保佑你', '從維爾薩拉取回伊露恩之淚。', '我說不定找到了打開聖光之心的「鑰匙」了，$n。$B$B當時我正在翻閱幾本古代宇宙論的秘典，赫然發現在其中一個段落中寫道原始的那魯可能就是在光和暗誕生之際，由伊露恩親手所創。$B$B如果瑟拉真的是伊露恩一手所造，那麼我們應該可以用伊露恩之淚來打開聖光之心。$B$B那麼接下來，從維爾薩拉取回伊露恩之淚的任務就交給你了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44366, 'deDE', 'Ein letztes Abenteuer', 'Sagt Emmarel Bescheid, wenn Ihr einem weiteren Artefakt nachgehen wollt.', 'Es gibt noch einen letzten Weg, den Ihr vielleicht gehen solltet... noch eine Waffe, die Ihr vielleicht führen solltet.$B$BWenn Ihr diese suchen wollt, müsst Ihr nur Bescheid geben.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44366, 'esES', 'Una última aventura', 'Informa a Emmarel si quieres ir a por otro artefacto.', 'Aún te queda una última senda por recorrer, otra arma que podrías empuñar.$b$bSi quieres ir en su busca, no tienes más que decirlo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44366, 'esMX', 'Una última aventura', 'Habla con Emmarel si quieres ir a buscar otro artefacto.', 'Aún falta un camino que podrías recorrer y un arma más por conseguir.$B$BSi quieres ir a buscarla, tan solo dilo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44366, 'frFR', 'Une dernière aventure', 'Si vous souhaitez partir en quête d’une autre arme prodigieuse, informez-en Emmarel.', 'Une dernière voie s’ouvre à vous ; une dernière arme que vous pourriez brandir.$B$BSi vous souhaitez vous lancer à sa recherche, vous n’avez qu’un mot à dire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44366, 'itIT', 'Un\'ultima avventura', 'Fai sapere a Emmarel se vuoi recuperare un altro Artefatto.', 'C\'è ancora un sentiero per te da percorrere... un\'altra arma che potresti impugnare.$B$BSe vuoi andare a cercarla, devi solo dirlo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44366, 'koKR', '최후의 모험', '에마렐에게 또 다른 유물을 찾고 싶다고 말해야 합니다.', '아직 가보지 않은 길이 하나 남아있습니다. 획득할 수 있는 무기가 하나 남아있지요.$B$B찾고자 한다면 주저 없이 말씀해주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44366, 'ptBR', 'Uma última aventura', 'Avise Emmarel se quiser buscar outro artefato.', 'Ainda há um último caminho que você ainda não trilhou - mais uma arma que você pode empunhar.$B$BSe você quiser buscá-lo, basta dar a ordem.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44366, 'ruRU', 'Последнее приключение', 'Сообщите Эммарель свое решение относительно артефакта.', 'Для тебя есть еще один неторный путь – еще одно оружие, которое может стать твоим.$B$BЕсли хочешь отправиться на его поиски, скажи только слово.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44366, 'zhCN', '最后一次冒险', '告诉伊墨瑞尔你是否要去追寻下一件神器。', '还有最后一条你可以践行的道路——又一件你可以挥舞的武器。$B$B如果你希望追寻它，就说一声。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44366, 'zhTW', '最後的冒險', '告訴艾瑪芮爾你是否想再去找另一件神兵武器。', '你還有另外一種選擇，另外一件武器。$B$B如果你還有興趣就說一聲。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44370, 'deDE', 'Euer Arsenal vervollständigen', 'Sagt Tyrosus Bescheid, wenn Ihr einem weiteren Artefakt nachgehen wollt.', 'Hochlord, da draußen ist immer noch eine letzte Waffe, die Euch im Kampf gegen die Legion helfen würde.$B$BWenn es Euch recht ist, würde ich gerne mit Euch unsere neuesten Informationen darüber durchgehen, wo sie sich befinden könnte.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44370, 'esES', 'Completar tu arsenal', 'Informa a Neófitus si quieres ir a por otro artefacto.', '$GAlto Señor:Alta Señora;, queda una última arma que te ayudaría contra la Legión.$b$bSi quieres, para mí sería un placer repasar nuestros últimos informes sobre su paradero.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44370, 'esMX', 'Completando tu arsenal', 'Dile a Tyrosus que quieres ir a buscar otro artefacto.', '$GAlto señor:Alta señora;, aún hay una última arma que te podría ayudar contra la Legión.$B$BSi quieres, con gusto averiguo en donde se encuentra.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44370, 'frFR', 'Enrichir l’arsenal', 'Si vous souhaitez partir en quête d’une autre arme prodigieuse, informez-en Tyrosus.', 'Généralissime, il reste une dernière arme qui vous aiderait à combattre la Légion.$B$BSi vous le souhaitez, je peux vous faire part des dernières informations en date sur le lieu où elle se trouverait.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44370, 'itIT', 'Completare l\'arsenale', 'Fai sapere a Tyrosus se vuoi recuperare un altro Artefatto.', '$gGran Signore:Gran Signora;, là fuori c\'è un\'ultima arma che potrebbe esserti d\'aiuto contro la Legione.$B$BSe la cosa ti interessa, sarei felice di comunicarti le informazioni che ho a proposito di dove si trova.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44370, 'koKR', '무기고 완성하기', '티로서스에게 또 다른 유물을 찾고 싶다고 말해야 합니다.', '대영주님, 군단과의 전투에 힘을 보태줄 만한 무기가 하나 남아 있습니다.$B$B원하신다면 기꺼이 그 무기의 최근 행방에 관해 알아보겠습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44370, 'ptBR', 'Completando o arsenal', 'Avise Tyrosus se quiser buscar outro artefato.', '$gGrão-lorde:Grã-senhora;, ainda existe uma última arma que poderia ajudar você contra a Legião.$B$BSe você assim desejar, ficaria feliz em ir buscar informações recentes sobre o paradeiro dela.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44370, 'ruRU', 'Полный арсенал', 'Сообщите Тироссу свое решение относительно артефакта.', '$gВерховный лорд:Верховная леди;, есть еще одно оружие, способное помочь тебе в борьбе с Легионом.$B$BЕсли желаешь, я буду рад поделиться тем, что нам известно о его местонахождении.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44370, 'zhCN', '集齐你的武器', '告诉泰罗索斯你是否要去寻找下一件神器。', '大领主，还有一件武器可以帮助你对抗燃烧军团。$B$B如果你有兴趣的话，我很乐意为你重新研读最新的情报，找出它的所在。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44370, 'zhTW', '完整的神兵收藏', '告訴泰羅索斯你是否想再去找另一件神兵武器。', '大領主，還有一件武器可以幫你對抗燃燒軍團。$B$B如果你想要這件神兵武器，我們可以討論一下目前所擁有的情報。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44375, 'deDE', 'Die letzte Klinge', 'Sagt Jorach Bescheid, wenn Ihr einem weiteren Artefakt nachgehen wollt.', 'Wie Ihr Euch sicherlich erinnert, haben wir den letzten Satz unserer Pläne zurückgehalten, eine Waffe unglaublicher Macht zu beschaffen.$B$BIhr will Euch natürlich nicht unter Druck setzen, aber wenn Ihr dieser Waffe nachgehen wollt, sagt mir einfach Bescheid.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44375, 'esES', 'La última hoja', 'Informa a Jorach si quieres ir a por otro artefacto.', 'Seguro que recuerdas que conservamos los últimos planos para conseguir un arma de inmenso poder.$b$bNo hay obligación ninguna, pero si quieres ir a por ella, no tienes más que decírmelo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44375, 'esMX', 'La última espada', 'Habla con Jorach si quieres ir a buscar otro artefacto.', 'Como bien recordarás, retuvimos nuestros últimos planes para obtener un arma de inmenso poder.$B$BNo te quiero presionar, pero si quieres ir a buscarla, tan solo avísame.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44375, 'frFR', 'La dernière lame', 'Si vous souhaitez partir en quête d’une autre arme prodigieuse, informez-en Jorach.', 'Vous vous souvenez certainement que nous avons conservé nos derniers plans visant à obtenir une arme d’une puissance colossale.$B$BJe ne vous mets pas la pression, évidemment, mais si vous souhaitez vous la procurer, faites-moi signe.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44375, 'itIT', 'L\'ultima lama', 'Fai sapere a Jorach se vuoi recuperare un altro Artefatto.', 'Come sono certo tu ricordi, abbiamo ancora i piani per il recupero di un\'altra arma di immenso potere.$B$BNon voglio metterti pressione, ovviamente, ma se decidi di andare a cercarla, fammelo sapere.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44375, 'koKR', '마지막 칼날', '조라크에게 또 다른 유물을 찾고 싶다고 말해야 합니다.', '기억하시겠지만 강대한 힘을 가진 무기를 얻을 마지막 계획이 아직 남아 있습니다.$B$B부담 가지실 필요는 없지만, 실행하고 싶다면 말씀해주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44375, 'ptBR', 'A última lâmina', 'Avise Jorach se quiser buscar outro artefato.', 'Como sei que você se lembra, mantive nosso conjunto final de planos para obter uma arma de imenso poder.$B$BSem pressão, é claro. Mas, se você quiser procurar ela, me avise.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44375, 'ruRU', 'Последние клинки', 'Сообщите Джораху свое решение относительно артефакта.', 'Как ты помнишь, у нас остается еще один план, как нам получить оружие с огромной мощью.$B$BЯ не настаиваю, конечно, но если ты желаешь начать действовать – только скажи.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44375, 'zhCN', '最终之刃', '如果要去寻找下一件神器，就去告诉乔拉齐。', '你一定还记得，我们还保存着取得强力武器的最后一套计划。$B$B当然，我不是催你，但如果你想要寻找它的话，就告诉我吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44375, 'zhTW', '最後的神兵', '告訴喬拉齊你是否想再去找另一件神兵武器。', '我想你應該記得，我們還握有找到最後一件神兵武器的情報。$B$B當然你可以放棄，但如果你還想要的話，就告訴我一聲。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44379, 'deDE', 'Auf der Jagd nach Macht', 'Sagt Altruis Bescheid, wenn Ihr einem weiteren Artefakt nachgehen wollt.', 'Es gibt noch ein Paar Klingen in erreichbarer Nähe, die Euch im Kampf gegen die Legion helfen würden.$B$BWenn Ihr ihnen nachgehen wollt, sagt mir einfach Bescheid.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44379, 'esES', 'En busca de poder', 'Informa a Altruis si quieres ir a por otro artefacto.', 'Quedan un par de hojas a nuestro alcance que te serían de gran ayuda contra la Legión.$b$bSi quieres ir a por ellas, dímelo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44379, 'esMX', 'En busca de poder', 'Habla con Altruis si quieres ir a buscar otro artefacto.', 'Hay otro par de espadas a nuestro alcance que servirían para tu lucha contra la Legión.$B$BSi quieres ir a buscarlas, tan solo dímelo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44379, 'frFR', 'En quête de pouvoir', 'Si vous souhaitez partir en quête d’une autre arme prodigieuse, informez-en Altruis.', 'Une paire de lames pouvant vous aider à combattre la Légion demeure encore à notre portée.$B$BSi vous souhaitez partir en quête de telles armes, vous n’avez qu’un mot à dire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44379, 'itIT', 'A caccia di potere', 'Fai sapere ad Altruis se vuoi recuperare un altro Artefatto.', 'Ci sono ancora un paio di lame che possiamo ottenere e che ti aiuterebbero contro la Legione.$B$BSe vuoi andare a cercarle, devi solo farmelo sapere.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44379, 'koKR', '힘을 추적하다', '알트루이스에게 다른 유물을 추적하고 싶다고 알려야 합니다.', '군단과의 전쟁에서 도움이 될 전투검 한 쌍이 아직 남아 있다. 우리가 손에 넣을 가능성도 남아 있고.$B$B다음 전투검을 또 추적하고 싶다면, 내게 얘기해라.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44379, 'ptBR', 'Em busca de poder', 'Avise Altruis se quiser buscar outro artefato.', 'Há mais um par de lâminas que ainda podemos obter e que seria de grande ajuda contra a Legião.$B$BSe você quiser ir atrás delas, me informe.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44379, 'ruRU', 'В погоне за силой', 'Сообщите Алтруису свое решение относительно артефакта.', 'Есть еще одно оружие – парные клинки, которые могли бы помочь тебе в борьбе с Легионом.$B$BЕсли хочешь отправиться на их поиски, только скажи.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44379, 'zhCN', '追寻力量', '告诉奥图里斯你是否要去寻找下一件神器。', '就在我们触手可及的地方，还有一对足以帮你对抗燃烧军团的战刃。$B$B如果你想要找到它们的话，就告诉我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44379, 'zhTW', '追尋力量', '告訴奧翠司你是否想再去找另一件神兵武器。', '還有一對兵刃應該能幫你對抗燃燒軍團。$B$B如果你想去找，請讓我知道。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44383, 'deDE', 'Auf der Jagd nach Macht', 'Sagt Kayn Bescheid, wenn Ihr einem weiteren Artefakt nachgehen wollt.', 'Es gibt immer noch ein Paar Klingen, die Euch bei den bevorstehenden Kämpfen von großem Nutzen sein könnten.$B$BIch kann Euch darüber ins Bild setzen, was wir wissen, wenn Ihr ihnen nachgehen wollt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44383, 'esES', 'En busca de poder', 'Informa a Kayn si quieres ir a por otro artefacto.', 'Aún quedan un par de hojas que te serían muy provechosas en las batallas que están por venir.$b$bSi quieres ir a por ellas, puedo informarte de lo que sabemos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44383, 'esMX', 'En busca de poder', 'Habla con Kayn si quieres ir a buscar otro artefacto.', 'Aún hay otro par de espadas que serían de gran utilidad en las batallas que se avecinan.$B$BTe puedo contar lo que sabemos si quieres ir a buscarlas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44383, 'frFR', 'En quête de pouvoir', 'Si vous souhaitez partir en quête d’une autre arme prodigieuse, informez-en Kayn.', 'Il reste encore une paire de lames qui pourraient grandement vous servir dans les combats à venir.$B$BSi vous souhaitez partir à leur recherche, je peux partager avec vous les informations que nous avons à leur sujet.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44383, 'itIT', 'A caccia di potere', 'Fai sapere a Kayn se vuoi recuperare un altro Artefatto.', 'Ci sono ancora un paio di lame che potrebbero esserti di grande aiuto nelle battaglie a venire.$B$BPosso raccontarti quello che sappiamo, se deciderai di andarle a cercare.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44383, 'koKR', '힘을 추적하다', '카인에게 다른 유물을 추적하고 싶다고 알려야 합니다.', '앞으로의 전투에서 그대에게 큰 도움이 될 전투검 한 쌍이 아직 남아 있소.$B$B그대가 다음 전투검을 추적하고 싶다면, 내가 필요한 정보를 알려주겠소.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44383, 'ptBR', 'Em busca de poder', 'Avise Kayn se quiser buscar outro artefato.', 'Ainda existe outro par de lâminas que poderiam ser de grande ajuda nas batalhas que estão por vir.$B$BPosso ajudar você com o que sabemos, se você quiser ir atrás delas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44383, 'ruRU', 'В погоне за силой', 'Сообщите Кайну свое решение относительно артефакта.', 'Есть еще одна пара клинков, которые могли бы помочь тебе в предстоящих битвах.$B$BЕсли хочешь отправиться на их поиски, я расскажу тебе все, что нам известно.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44383, 'zhCN', '追寻力量', '告诉凯恩你是否要去寻找下一件神器。', '还有一对战刃，若是在你手上，它们同样能在之后的战斗中派上大用场。$B$B如果你想要找到它们的话，我能为你汇总下手头的信息。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44383, 'zhTW', '追尋力量', '告訴凱恩你是否想再去找另一件神兵武器。', '還有一件兵器對接下來的戰役或許有幫助。$B$B如果你打算去找這對戰刃，我可以跟你報告目前已經蒐集到的情報。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44400, 'deDE', 'Sturm auf die Violette Festung: Fiasko in der Festung', 'Schlagt den Sturm auf die Violette Festung zurück.', 'Schon wieder! Es ist schon wieder geschehen!\n\nNatürlich steckt die Legion dieses Mal hinter dem Gefängnisausbruch, aber ich habe ja allen im Vorfeld schon gesagt, dass es keine gute Idee ist, einige der gefährlichsten Wesen Azeroths in einem Gefängnis mitten in unserer Stadt unterzubringen!\n\nIch nehme an, wir können uns später noch über eine Verlegung unterhalten. Jetzt müssen wir uns erst einmal um ein wenig... Befriedung kümmern.\n\nKönnt Ihr helfen?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44400, 'esES', 'Asalto al Bastión Violeta: Dolor morado', 'Repele el asalto a El Bastión Violeta.', '¡Otra vez! ¡Ha vuelto a pasar!\n\nSí, es cierto que la Legión es la responsable de la fuga en esta ocasión, pero ya les dije que era mala idea encerrar a varios de los seres más peligrosos de Azeroth en una prisión dentro de nuestra ciudad.\n\nYa hablaremos más adelante de reubicarla, me imagino. Ahora mismo tenemos que... centrarnos en la pacificación.\n\n¿Podrías colaborar?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44400, 'esMX', 'Ataque al Bastión Violeta: Dolor púrpura', 'Repele el ataque al Bastión Violeta.', '¡Otra vez! ¡Ocurrió otra vez!\n\nSí, esta vez fue la Legión la que montó la fuga de la prisión, ¡pero yo *les dije* que no era buena idea encerrar a las criaturas más peligrosas de Azeroth en nuestra ciudad!\n\nSupongo que más adelante podremos hablar de cambiarlos de lugar, ¿no? En este momento, lo más importante es... pacificar un poco las cosas.\n\n¿Nos darías una mano?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44400, 'frFR', 'Assaut sur le fort Pourpre : la douleur pourpre', 'Repoussez l’Assaut sur le Fort Pourpre.', 'Non, mais je rêve ! Voilà que ça recommence !\n\nD’accord, cette fois, l’évasion a été organisée par la Légion, mais je leur avais bien dit que c’était une mauvaise idée d’incarcérer les plus dangereux individus d’Azeroth dans l’enceinte de notre ville !\n\nBref, nous pourrons réfléchir à un endroit plus adapté quand le calme sera revenu… Pour l’heure, il nous faut rétablir l’ordre.\n\nPourriez-vous nous y aider ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44400, 'itIT', 'Assalto alla Fortezza Violacea: Dolore purpureo', 'Respingi l\'Assalto alla Fortezza Violacea.', 'Di nuovo! È successo di nuovo!\n\nNaturalmente, questa volta è stata la Legione a causare tutto questo, ma io l\'avevo detto che era una pessima idea tenere le creature più pericolose di Azeroth rinchiuse in una prigione nel bel mezzo della nostra città!\n\nParleremo dopo di eventuali spostamenti, al momento dobbiamo pensare a... sistemare un po\' le cose qui.\n\nPuoi aiutarmi?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44400, 'koKR', '보랏빛 요새 침공: 보랏빛 고통', '보랏빛 요새 침공을 저지해야 합니다.', '또! 또 일어났어요!\n\n네, 이번에 탈옥을 일으킨 건 군단이에요. 하지만 전 아제로스에서 가장 위험한 존재들을 우리 도시 안의 감옥에 가둬 두는 게 좋은 생각이 아니라고 수도 없이 얘기했다고요!\n\n이전은 나중에 얘기해 봐야 할 것 같아요. 지금은... 처리해야 할 게 너무 많으니까요.\n\n도와주시겠어요?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44400, 'ptBR', 'Ataque ao Castelo Violeta: viva a dor', 'Reprima o ataque ao Castelo Violeta.', 'De novo! Aconteceu de novo!\n\nClaro, foi a Legião que encenou a fuga da prisão dessa vez, mas eu *avisei* a eles que era uma péssima ideia manter alguns dos seres mais perigosos de Azeroth em uma prisão dentro da nossa cidade!\n\nPodemos conversar sobre realocação mais tarde, creio eu. Agora, temos que fazer uma... pacificação.\n\nVocê pode ajudar?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44400, 'ruRU', 'Штурм Аметистовой крепости: проблема цвета аметиста', 'Отразите штурм Аметистовой крепости.', 'Опять! Опять!\n\nДа, на этот раз во всем повинен Легион, но я всегда говорила: неразумно держать самых опасных существ Азерота прямо в городе!\n\nТюрьму нужно отсюда перенести, но об этом потом. Сейчас нам надо кое-кого... успокоить.\n\nПоможешь?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44400, 'zhCN', '突袭紫罗兰监狱：紫色的痛', '击退对紫罗兰监狱的突袭。', '又来！又发生了这种事情！\n\n没错，这回是军团策划了越狱，但我*告诉*过他们，在城市监狱里囚禁几个艾泽拉斯最危险的生物，实在是糟糕透顶的主意！\n\n迁址的问题待会儿我们再谈。现在嘛，我们有一些……安抚工作要做。\n\n你能帮个忙吗？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44400, 'zhTW', '紫羅蘭堡之襲：紫羅蘭暴動', '鎮壓紫羅蘭堡的暴動。', '又來了！悲劇重演！\n\n沒錯，這次的監獄暴動是燃燒軍團所策劃，不過我「早就」跟他們說過，把艾澤拉斯最危險的生物關在城裡的監獄，實在是蠢到極點！\n\n我們之後再來討論移監的事。現在，當務之急是平定暴動。\n\n你能幫忙嗎？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44401, 'deDE', 'Eine Waffe für jede Gelegenheit', 'Sagt Herzog Lankral Bescheid, wenn Ihr einem weiteren Artefakt nachgehen wollt.', 'Unser Verbündeter, der Lichkönig, hat angeboten, Euch einen Hinweis auf eine letzte Waffe zukommen zu lassen, wenn Ihr Eure Offensivoptionen maximieren wollt.\n\nSagt mir Bescheid, wie Ihr weiter vorgehen wollt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44401, 'esES', 'Un arma para cada ocasión', 'Informa al duque Lankral si quieres ir a por otro artefacto.', 'Nuestro aliado, el Rey Exánime, se ha ofrecido a ponerte sobre la pista de una última arma, por si deseas maximizar nuestras opciones ofensivas.\n\nYa me dirás qué quieres hacer.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44401, 'esMX', 'Un arma para cada ocasión', 'Habla con el duque Lankral si quieres ir a buscar otro artefacto.', 'Nuestro aliado, el Rey Exánime, ofreció darte una pista acerca de una última arma en caso de que quieras maximizar nuestras opciones defensivas.\n\nAvísame en caso de que quieras proceder.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44401, 'frFR', 'Une arme pour chaque occasion', 'Si vous souhaitez partir en quête d’une autre arme prodigieuse, informez-en le duc Lankral.', 'Si vous désirez optimiser vos capacités d’attaque, notre allié le roi-liche a offert de vous mettre sur la piste d’une dernière arme.\n\nDites-moi ce que vous souhaitez faire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44401, 'itIT', 'Un\'arma per ogni occasione', 'Fai sapere al Duca Lankral se vuoi recuperare un altro Artefatto.', 'Il nostro alleato, il Re dei Lich, si è offerto di fornirci degli indizi per un\'ultima arma che dovresti recuperare per massimizzare le opzioni della nostra offensiva.\n\nFammi sapere come vuoi procedere.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44401, 'koKR', '모든 경우를 대비한 무기', '공작 란크랄에게 또 다른 유물을 찾고 싶다고 말해야 합니다.', '우리 아군인 리치 왕이 지금 지닌 무기로는 혹시 부족하지 않을까 염려하여, 마지막 무기에 관한 단서를 주었소.\n\n어떻게 하고 싶은지 알려주시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44401, 'ptBR', 'Uma arma para toda ocasião', 'Avise Duque Lankral se quiser buscar outro artefato.', 'Nosso aliado, o Lich Rei, ofereceu a você uma pista para uma arma definitiva, caso você deseje maximizar nossas opções de ataque.\n\nMe informe sobre como deseja proceder.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44401, 'ruRU', 'Оружие на все случаи нежизни', 'Сообщите герцогу Ланкралу свое решение относительно артефакта.', 'Наш союзник Король-лич предложил указать тебе, где искать последнее оружие, которое прибавит нам вариантов наступательных действий.\n\nСообщи мне о своих дальнейших планах.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44401, 'zhCN', '全能神兵', '告诉兰克拉尔公爵你是否要去寻找下一件神器。', '我们的盟友巫妖王向我们提供了一些线索，可以找到最后一件兵器，让我们的进攻选择更加多元化。\n\n告诉我你的想法吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44401, 'zhTW', '萬全的準備', '告訴蘭克洛公爵你是否想再去找另一件神兵武器。', '你是否想再取得最後一件神兵武器，更進一步強化我們的戰力？我們的盟友巫妖王又提供了一些線索。\n\n請告訴我你的決定。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44406, 'deDE', 'Aggras Anleitung', 'Sucht Aggra in der Sturmaugenwarte im Herz von Azeroth auf.', '$ct, habt Ihr schon die Schamanin Aggra kennengelernt? Sie entstammt einer zerschmetterten Welt und lernte von den Mag\'harorcs der Scherbenwelt alles über die Gesetze der Elemente. Am Vorabend des Kataklysmus nahm sie es auf sich, Thralls Schamanenausbildung abzuschließen. Unsere jüngeren Initianden haben großes Glück, dass sie unter uns weilt, um sie anzuleiten.$b$bWenn Ihr noch ein zweites Artefakt suchen wollt, kann Aggra Euch helfen. Sie bildet neue Rekruten über uns in der Sturmaugenwarte aus.', '', 'Aggralan, bekannt als Aggra.', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44406, 'esES', 'El consejo de Aggra', 'Busca a Aggra en el Mirador Ojo de la Tormenta dentro del Corazón de Azeroth.', '$ct, ¿conoces a la chamán Aggra? Nacida en un mundo destrozado, aprendió las artes de los elementos con los orcos Mag\'har, en Terrallende. En la víspera del Cataclismo, se propuso completar el entrenamiento chamánico de Thrall. Nuestros iniciados más jóvenes tienen suerte de que la tengamos entre nosotros para orientarlos.$b$bSi quieres buscar un segundo artefacto, Aggra puede ayudarte. Está instruyendo a nuevos reclutas ahí arriba, en el Mirador Ojo de la Tormenta.', '', 'Aggralan, conocida como Aggra.', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44406, 'esMX', 'La guía de Aggra', 'Busca a Aggra en el Mirador del Ojo del huracán, en el interior del Corazón de Azeroth.', '$ct, ¿conoces a la chamán Aggra? Nacida en un mundo en ruinas, ella aprendió el arte de los elementos entre los orcos Mag\'har de Terrallende. En vísperas del Cataclismo, decidió completar el entrenamiento chamán de Thrall por su propia cuenta. Nuestros iniciados más jóvenes tienen suerte de tenerla entre nosotros para guiarlos.$b$bSi quieres buscar un segundo artefacto, Aggra podrá ayudarte. Está entrenando a los nuevos reclutas sobre nosotros en el Mirador del Ojo del huracán.', '', 'Aggralan, conocida como Aggra.', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44406, 'frFR', 'Les enseignements d’Aggra', 'Allez voir Aggra au Surplomb de l’Œil de la tempête, au Cœur d’Azeroth.', '$ct, avez-vous rencontré la chamane Aggra ? Née dans un monde dévasté, elle a appris à communier avec les éléments dans la communauté des Orcs mag’har d’Outreterre. À la veille du Cataclysme, elle a décidé d’achever la formation chamanique de Thrall. Nos plus jeunes initiés ont une chance inouïe de pouvoir bénéficier de ses enseignements.$b$bSi vous souhaitez partir en quête d’une deuxième arme prodigieuse, Aggra pourra vous y aider. Elle forme de nouvelles recrues juste au-dessus de nous, au surplomb de l’Œil de la tempête.', '', 'Aggralan, connue sous le nom d’Aggra.', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44406, 'itIT', 'Il consiglio di Aggra', 'Cerca Aggra al Promontorio Occhio della Tempesta nel Cuore di Azeroth.', '$Ct, hai già conosciuto la Sciamana Aggra? Nata su di un mondo spezzato, ha appreso le vie degli elementi tra gli Orchi Mag\'har delle Terre Esterne. Alla vigilia del Cataclisma, si è assunta la responsabilità di completare l\'addestramento da Sciamano di Thrall. I nostri giovani iniziati sono fortunati ad averla come guida.$b$bSe desideri andare alla ricerca di un secondo artefatto, Aggra può aiutarti. Adesso sta addestrando le nuove reclute sopra di noi, al Promontorio Occhio della Tempesta.', '', 'Aggralan, conosciuta come Aggra.', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44406, 'koKR', '아그라의 지도', '아제로스의 심장에 있는 폭풍의 눈 전망대에서 아그라를 찾아야 합니다.', '$ct 님, 아그라라는 주술사를 만나보셨습니까? 산산조각난 세상에서 태어난 아그라는 아웃랜드의 마그하르 오크 사이에서 원소에 관해 배웠습니다. 그리고 대격변이 일어나기 직전, 스랄의 주술 훈련을 마쳤지요. 그런 주술사의 가르침을 받을 수 있다니, 우리 젊은 신참들은 운이 좋은 셈입니다.$b$b두 번째 유물을 찾고 싶다면 아그라가 도움이 될 겁니다. 지금 위쪽에 있는 폭풍의 눈 전망대에서 신병들을 훈련시키고 있습니다.', '', '아그라라고 알려져 있는 아그랄란입니다.', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44406, 'ptBR', 'Orientação de Aggra', 'Procure por Aggra no Mirante do Olho da Tormenta, no Coração de Azeroth.', '$ct, você conheceu a xamã Aggra? Nascida de um mundo estilhaçado, ela aprendeu o caminho dos elementos com os orcs Mag\'har de Terralém. Na véspera do Cataclismo, ela tomou para si a tarefa de concluir o treinamento xamanístico de Thrall. Nossos iniciados mais jovens tem sorte em tê-la entre nós para guiá-los.$b$bSe você deseja procurar um segundo artefato, Aggra pode ajudar você. Ela está treinando novos recrutas acima de nós, no Mirante do Olho da Tormenta.', '', 'Aggralan, conhecida como Aggra.', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44406, 'ruRU', 'Наставление Аггры', 'Найдите Аггру на утесе Глаза Бури в Сердце Азерота.', '$ct, ты $gзнаком:знакома; с шаманкой Аггрой? Аггра – дитя расколотого мира, она постигала пути стихий среди маг\'харов – орков Запределья. Накануне Катаклизма она завершила свое обучение у Тралла. Нашим молодым шаманам, которые только начинают свой путь, очень повезло иметь такую наставницу, как она.$b$bЕсли ты хочешь отправиться на поиски второго артефакта, Аггра может тебе помочь. Она сейчас тренирует новобранцев на утесе Глаза Бури.', '', 'Аггралан, известная как Аггра.', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44406, 'zhCN', '阿格娜的引导', '在艾泽拉斯之心的风暴之眼瞭望台找到阿格娜。', '$ct，你见过萨满阿格娜了吗？她出生在外域这个破碎的世界，并从那里的玛格汉兽人处学会了元素之道。在大灾变的前夕，她亲自帮助萨尔完成了萨满训练。年轻的新兵们能有她作为导师真是太幸运了。$b$b如果你想找到第二件神器，阿格娜一定能帮到你。她正在我们上方的风暴之眼瞭望台训练新兵。', '', '阿格娜兰，大家叫她阿格娜。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44406, 'zhTW', '阿格拉的指引', '前往艾澤拉斯之心，在風暴之眼瞰臺和阿格拉交談。', '$ct，你見過阿格拉嗎？她出生於破碎的外域，在瑪格哈獸人之中研習元素的法則。大災變前夕，她親自協助索爾完成薩滿訓練。我們的初學者有幸獲得她的教導。$b$b如果你在找第二件神兵武器，阿格拉應該幫得上忙。她正在風暴之眼瞰臺訓練新人。', '', '阿格拉蘭，又稱阿格拉。', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44407, 'deDE', 'Die dritte Legende', 'Sprecht mit Alonsus Faol, um einem dritten Artefakt nachzugehen.', 'Ihr habt nicht nur eines, sondern gleich ZWEI mächtige Artefakte, die Ihr gegen die Legion einsetzen könnt. Aber könnt Ihr Euch vorstellen, wie die Welt aussähe, wenn Ihr drei hättet? Denkt doch, was Ihr in einer solchen Welt alles tun könntet!\n\nSollen wir uns die Möglichkeiten einmal ansehen?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44407, 'esES', 'La tercera leyenda', 'Habla con Alonsus Faol para buscar un tercer artefacto.', 'No tienes uno, sino DOS poderosos artefactos para usarlos contra la Legión. ¿Te imaginas si tuvieras tres? ¡Piensa en todo lo que podrías hacer!\n\n¿Quieres que repasemos las posibilidades?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44407, 'esMX', 'La tercera leyenda', 'Habla con Alonsus Faol para ir a buscar un tercer artefacto.', 'No tienes uno, sino DOS poderosos artefactos para usar contra la Legión. ¿Pero te imaginas un mundo en el que puedas tener tres? ¡Piensa en lo que podrías hacer en un mundo así!\n\n¿Revisemos las posibilidades?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44407, 'frFR', 'La troisième légende', 'Parlez à Alonsus Faol pour acquérir une troisième arme prodigieuse.', 'Vous disposez non pas d’une, mais de DEUX puissantes armes prodigieuses pour combattre la Légion. Imaginez que vous puissiez en manier trois ! Pensez à tout ce que vous pourriez accomplir en ce monde !\n\nSouhaitez-vous étudier les différentes options qui s’offrent à vous ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44407, 'itIT', 'La terza leggenda', 'Parla con Alonsus Faol per recuperare un terzo Artefatto.', 'Possiedi non uno, bensì DUE potenti artefatti da usare contro la Legione. Riesci a immaginare un mondo dove ne hai tre? Pensa cosa potresti fare in un simile mondo!\n\nChe ne dici di esplorare questa possibilità?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44407, 'koKR', '세 번째 전설', '세 번째 유물을 추적하려면 알론서스 파올과 이야기해야 합니다.', '자네, 군단에 대항해 사용할 수 있는 유물을 한 개도 아니고 두 개나 가지고 있군! 하지만 세 개가 있다면 어떨까? 그런 상황이 되면 무슨 일을 할 수 있을지 생각해보게!\n\n그걸 실현해 보는 건 어떻겠나?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44407, 'ptBR', 'A terceira lenda', 'Fale com Alonso Faol para buscar um terceiro artefato.', 'Você tem não um, mas DOIS artefatos poderosos para usar contra a Legião. Mas, você consegue imaginar um mundo onde você pudesse ter três? Pense no que você poderia fazer!\n\nDevemos estudar as possibilidades?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44407, 'ruRU', 'Последняя легенда', 'Поговорите с Алонсием Фаолом, чтобы приступить к поискам третьего артефакта.', 'У тебя уже целых ДВА могущественных артефакта, которые можно использовать против Легиона. Только представь, что будет, когда их станет три! Сколько всего ты $gсмог:смогла; бы совершить!\n\nМожет, нам стоит заняться поисками?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44407, 'zhCN', '第三个传奇', '和阿隆索斯·法奥对话来追寻第三件神器。', '你用来对抗燃烧军团的神器已经不是一件，而是两件了！但是你有没有想过拿到第三件？想象一下吧，那样的话你能用它做些什么！\n\n我们来探讨一下吧？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44407, 'zhTW', '第三個傳說', '與阿隆索斯‧法奧交談，討論第三件神兵武器。', '你已經有神兵武器可以對抗燃燒軍團了，而且不是一件，是兩件！但你想像得到如果你有三件的話，世界會變得多不一樣嗎？你能做好多好多事！\n\n要不要試試看？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44411, 'deDE', 'Merkwürdig summender Kristall', 'Bringt den merkwürdig summenden Kristall zu Khadgar in Dalaran.', '<Ihr legt den Kristall an Euer Ohr und hört das unharmonische Stimmengewirr von Hunderten Stimmen, die gleichzeitig sprechen. Vielleicht weiß Khadgar mehr über diesen merkwürdigen Edelstein.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44411, 'esES', 'Extraño cristal zumbante', 'Lleva el extraño cristal zumbante a Khadgar en Dalaran.', '<Te acercas el cristal a la oreja y oyes un bullicio disonante de cientos de voces que hablan a la vez. Quizás Khadgar sepa más sobre esta extraña gema.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44411, 'esMX', 'Cristal zumbante extraño', 'Llévale el extraño cristal zumbante a Khadgar en Dalaran.', '<Acercas el cristal a tu oído y escuchas el disonante estruendo de cientos de voces hablando a la vez. Quizás Khadgar sepa más acerca de esta extraña gema.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44411, 'frFR', 'Étrange cristal bourdonnant', 'Apportez l’Étrange cristal bourdonnant à Khadgar, à Dalaran.', '<En approchant le cristal de votre oreille, vous entendez la clameur discordante de centaines de voix qui parlent en même temps. Peut-être Khadgar pourra-t-il vous en dire plus au sujet de cette étrange gemme.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44411, 'itIT', 'Cristallo Ronzante Strano', 'Porta il Cristallo Ronzante Strano a Khadgar nella città di Dalaran.', '<Porti il cristallo all\'orecchio e senti il frastuono disarmonico di centinaia di voci che parlano contemporaneamente. Forse Khadgar sa qualcosa su questa strana gemma.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44411, 'koKR', '이상하게 윙윙거리는 수정', '달라란에 있는 카드가에게 이상하게 윙윙거리는 수정을 가져가야 합니다.', '<수정을 귀에 대자 수백 가지 목소리가 한꺼번에 말하는 듯한 조화롭지 않은 소리가 들려 옵니다. 카드가라면 이 이상한 보석에 대해 알고 있을지도 모릅니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44411, 'ptBR', 'Estranho Cristal Murmurante', 'Leve o Estranho Cristal Murmurante para Hadggar em Dalaran.', '<Você leva o cristal perto ao seu ouvido e escuta a balbúrdia desornenada de centenas de vozes falando ao mesmo tempo. Talvez Hadggar saiba mais sobre essa gema estranha.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44411, 'ruRU', 'Странный гудящий кристалл', 'Отнесите странный гудящий кристалл Кадгару в Даларан.', '<Вы подносите кристалл к уху и слышите беспорядочный гвалт сотен голосов. Возможно, Кадгар знает что-то об этом странном камне.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44411, 'zhCN', '诡异的嗡鸣水晶', '把诡异的嗡鸣水晶带给达拉然的卡德加。', '<你把水晶放在耳边，只听到一阵杂乱的喧闹，仿佛有上百个声音同时在说话>。也许卡德加会知道些有关这个奇怪宝石的事情。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44411, 'zhTW', '詭異的嗡鳴水晶', '把詭異的嗡鳴水晶拿給達拉然的卡德加', '<你把水晶拿到耳際，聽見數以百計的不諧和人聲同時講話的聲音。也許卡德加會對這顆奇怪的寶石有更多了解。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44412, 'deDE', 'Überall Gegner', 'Vernichtet die geheimen Infiltratoren in Dalaran.', 'Moment mal, $n. Während ich Eure Kugel untersuche, könnt Ihr doch etwas für mich untersuchen, oder?$b$bIch vermute schon seit Langem, dass Dalaran infiltriert wird. Aber nach einer peinlichen Begegnung mit der örtlichen Floristin will ich lieber sichergehen, dass meine These stimmt.$b$bHabt Ihr bemerkt, dass es einige Bürger gibt, die etwas zu weltlich aussehen? Beinahe unmenschlich? Spione, die finsteren Meistern dienen, sind unter uns und warten nur darauf, dass wir ihnen den Rücken kehren. Merzt sie aus, aber versucht das ohne viel Aufhebens zu tun.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44412, 'esES', 'Enemigos por todas partes', 'Derrota a las fuerzas infiltradas ocultas en Dalaran.', 'Un momento, $n. Mientras echo un ojo a este orbe, ¿por qué no investigas una cosa por mí?$b$bLlevo mucho tiempo sospechando de cierta infiltración de Dalaran pero, tras un bochornoso encuentro con un florista local, he dejado de lado mis acusaciones hasta tenerlo más claro.$b$b¿No te has fijado en que hay algunos ciudadanos que parecen demasiado mundanos? ¿Casi a un nivel inhumano? Espías que sirven a maestros oscuros observan y aguardan hasta que les volvamos la espalda. Hay que borrarlos del mapa, pero procura no armar revuelo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44412, 'esMX', 'Enemigos por todas partes', 'Derrota a los infiltrados ocultos en Dalaran.', 'Un momento, $n. Mientras reviso esta orbe tuya, ¿por qué no vas a buscar algo por mí?$b$bHace tiempo sospecho de una infiltración en Dalaran, pero tras un vergonzoso encuentro con un florista local, contuve mis acusaciones hasta cerciorarme por completo.$b$b¿Notaste si algunos ciudadanos lucían demasiado mundanos? ¿Casi inhumanos? Espías, sirvientes de maestros oscuros, que observan y esperan a que estemos desprevenidos. Deshazte de ellos, pero hazlo con cautela.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44412, 'frFR', 'Des ennemis partout', 'Éliminez les espions infiltrés à Dalaran.', 'Mais j’y pense, $n… Pendant que j’examine votre orbe, vous pourriez en profiter pour vous rendre utile.\n\nJe soupçonne depuis longtemps Dalaran d’être infiltrée, mais depuis une altercation quelque peu embarrassante avec un fleuriste local, je n’ose porter d’accusations sans certitude absolue.$b$bVous n’avez pas remarqué que certains citoyens font tout pour passer inaperçus ? Un peu trop pour être honnêtes, si vous voulez mon avis. Des espions au service de puissances ténébreuses nous surveillent. Elles attendent que nous baissions notre garde. Éliminez ces agents infiltrés, mais tâchez de rester $gdiscret:discrète;.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44412, 'itIT', 'Nemici ovunque', 'Sconfiggi gli infiltratori nascosti a Dalaran.', 'Un secondo solo, $n. Mentre mi occupo di questo tuo globo, perché non fai qualcosa per me?$b$bDa tempo sospetto che la Legione si sia infiltrata a Dalaran, ma dopo un imbarazzante disguido con una fioraia locale ho lasciato perdere le mie accuse finché non ne sarò completamente certo.$b$bHai per caso notato dei cittadini tra di noi che sembrano troppo normali? Quasi in modo irreale? Spie di padroni oscuri, che guardano e aspettano finché non voltiamo le spalle... Occupatene tu, in silenzio.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44412, 'koKR', '사방이 적', '달라란에 있는 숨은 침입자들을 처치해야 합니다.', '잠깐 기다리시오, $n. 내가 당신이 가져온 보주를 살펴보는 동안, 가서 다른 일을 좀 해 주면 안 되겠소?$b$b난 꽤 오랫동안 달라란에 첩자가 침입했다고 의심해 왔지만, 이곳 꽃집 상인과 부끄러운 일을 겪은 후에는 누구도 쉽사리 의심할 수가 없었소. 하지만 이젠 확신이 생겼소.$b$b우리 중에 너무 평범해 보이는 시민들이 있다는 걸 눈치챘소? 거의 비인간적으로 평범한 자들이오. 어둠의 주인들을 섬기는 첩자들이, 우릴 지켜보며 우리가 등을 돌리기를 기다리고 있는 거겠지. 놈들을 없애주시오. 조용히 처리하는 게 좋겠소.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44412, 'ptBR', 'Inimigos por toda parte', 'Derrote os infiltradores escondidos em Dalaran.', 'Espere um pouco, $n. Enquanto eu verifico o seu orbe, por que você não verifica algo pra mim?$b$bHá muito tempo eu suspeito de uma infiltração em Dalaran, mas depois de um encontro constrangedor com um florista local, eu retive minhas acusações até que eu tivesse certeza absoluta.$b$bVocê já percebeu cidadãos entre nós que parecem ser mundanos demais? Quase desumanamente mundanos? Espiões, servindo mestres sombrios, observe e espere até que estejamos de costas. Elimine-os, mas tente não fazer muito barulho.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44412, 'ruRU', 'Враги повсюду', 'Одолейте тайных агентов в Даларане.', 'Постой-ка, $n. Пока я изучаю этот артефакт, не окажешь ли ты мне услугу?$b$bУ меня давно уже было подозрение, что в Даларан проникли лазутчики, но после неприятной сцены с местной торговкой цветами я пока решил воздержаться от обвинений, если у меня не будет неопровержимых доказательств.$b$bТак вот, ты не $gобращал:обращала; внимания, что некоторые жители ведут себя как-то подозрительно? Как будто и на себя не похожи? Шпионы, служащие своим темным владыкам, уже следят за нами и только и ждут момента для удара. Нужно от них избавиться – только без шума.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44412, 'zhCN', '敌人无处不在', '消灭潜伏在达拉然的渗透者。', '稍等一下，$n。在我帮你研究这个球体的时候，能否请你也帮我解决一些麻烦？$b$b我很久以前就怀疑达拉然被渗透了。但经过了和一位本地花匠的尴尬对质之后，我决定在能够完全肯定之前先保留我的指控。$b$b你有没有注意到，出现在我们身边的市民有点太过于平凡了？平凡得没有人味？他们是间谍，侍奉邪恶主子的间谍。他们在监视我们，等着我们露出背后的要害。铲除他们，但尽量别闹出大动静。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44412, 'zhTW', '危機四伏', '擊敗潛伏在達拉然裡的滲透者', '稍等一會，$n。在我研究你這顆寶珠的同時，幫我調查一些東西好嗎？$b$b我一直都懷疑在達拉然內有潛伏者，不過尷尬的是我上次指控了一個無辜的本地花藝師。從此以後我的態度就比較保守，除非我有十足的證據。$b$b你有注意到我們身邊的市民有誰行為怪異的嗎？舉止幾乎不像人類？間諜，為他們的黑暗主人效命，監視並等待我們露出破綻。肅清他們，不過盡量保持低調。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44414, 'deDE', 'Teufelsbrut von Lothros', 'Bezwingt Lothros.', 'Diese Teufelsbrut nimmt einfach kein Ende! Ich kann sie nicht schnell genug niedermähen, um an den Schreckenslord im Mausoleum zu gelangen.\n\nMeister Illidan, werdet Ihr mich begleiten, damit wir Lothros gemeinsam zur Strecke bringen können?', '', '', 'Lothros', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44414, 'esES', 'Entes viles de Lothros', 'Derrota a Lothros.', '¡Estos entes viles son una plaga! No puedo diezmarlos con suficiente rapidez para llegar a su Señor del Terror, dentro del mausoleo.\n\nMaestro Illidan, ¿vendrías conmigo para derrotar a Lothros en equipo?', '', '', 'Lothros', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44414, 'esMX', 'Apariciones viles de Lothros', 'Derrota a Lothros.', '¡Estas apariciones viles son innumerables! No puedo acabar con ellas lo suficientemente rápido como para que el Señor del Terror llegue al interior del mausoleo.\n\nMaestro Illidan, ¿me acompañas para acabar juntos con Lothros?', '', '', 'Lothros', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44414, 'frFR', 'L’engeance de Lothros', 'Terrassez Lothros.', 'Ces engeances gangrenées sont innombrables ! Je n’arrive pas à éclaircir leurs rangs assez rapidement pour atteindre le seigneur de l’effroi dans le mausolée.\n\nMaître Illidan, vous joindrez-vous à moi pour terrasser Lothros ?', '', '', 'Lothros', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44414, 'itIT', 'Vilprole di Lothros', 'Sconfiggi Lothros.', 'Questa Vilprole non ha mai fine! Non riesco a ucciderla abbastanza in fretta per arrivare al Signore del Terrore dentro il mausoleo.\n\nMentore Illidan, ti chiedo di venire con me per sconfiggere Lothros insieme.', '', '', 'Lothros', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44414, 'koKR', '로드로스의 지옥피조물', '로드로스를 처치해야 합니다.', '지옥피조물들이 끝이 없군요! 놈들을 빨리 처치해야 묘지 안에 있는 공포의 군주를 공격할 수 있는데, 도무지 줄어들질 않습니다.\n\n일리단 님, 저를 도와 로드로스를 처치해 주시겠습니까?', '', '', '로드로스', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44414, 'ptBR', 'Crias vis de Lothros', 'Derrote Lothros.', 'Estas crias vis são infinitas! Não consigo derrubá-las rápido o bastante para chegar até o senhor do medo no mausoléu.\n\nMestre Illidan, você vem comigo para que possamos derrotar Lothros juntos?', '', '', 'Lothros', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44414, 'ruRU', 'Порождения Скверны Лотроса', 'Победите Лотроса.', 'Этим порождениям Скверны просто нет конца! Я не успеваю с ними расправляться, чтобы добраться до повелителя ужаса, что засел в мавзолее.\n\nИллидан, ты поможешь мне сразить Лотроса?', '', '', 'Лотрос', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44414, 'zhCN', '洛瑟鲁斯的邪裔', '击败洛瑟鲁斯', '这些邪裔无穷无尽！不快点把他们解决的话，陵墓里这个恐惧魔王就要溜走了。\n\n伊利丹大人，你能和我一起来击败洛瑟鲁斯吗？', '', '', '洛瑟鲁斯', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44414, 'zhTW', '洛斯羅司的魔裔惡魔', '擊敗洛斯羅司', '這些魔裔惡魔根本殺不完！我沒辦法快速解決他們，在陵寢中殺出一條通往驚懼領主的血路。\n\n伊利丹大人，你願意與我同行，合力擊敗洛斯羅司嗎？', '', '', '洛斯羅司', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44415, 'deDE', 'Die rote Axt', 'Schließt die instabilen Portale und macht Euch auf den Weg zu Broxigar dem Roten.', 'Broxigar der Rote ist auf der Brücke in Bedrängnis. Ich bin mir sicher, dass er uns niemals um Hilfe bitten würde, aber ich glaube, er könnte jetzt etwas Unterstützung vertragen.\n\nWenn Ihr entscheidet, unserem Verbündeten zur Hilfe zu eilen, würde ich Euch gern begleiten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44415, 'esES', 'El hacha roja', 'Destruye los portales inestables y ábrete paso hasta Broxigar el Rojo.', 'Broxigar el Rojo está acorralado en el puente. No sé si admitiría que necesita nuestra ayuda, pero me parece lo suyo tenderle la mano.\n\nSi quieres colaborar con nuestro aliado, sería un honor para mí acompañarte.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44415, 'esMX', 'El hacha roja', 'Cierra los portales inestables y dirígete hacia Broxigar el Rojo.', 'Broxigar el Rojo está rodeado en el puente. No creo que admita que necesita ayuda, pero me parece apropiado darle una mano.\n\nSi quieres ayudar a nuestro aliado, sería un honor para mi acompañarte.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44415, 'frFR', 'La hache rouge', 'Détruisez les Portails instables et frayez-vous un chemin jusqu’à Broxigar le Rouge.', 'Broxigar le Rouge est acculé sur le pont. Je ne pense pas qu’il soit du genre à appeler à l’aide, pourtant nous nous devons de nous porter à son secours.\n\nSi vous souhaitez aider notre allié, je vous demande humblement la permission de vous accompagner.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44415, 'itIT', 'L\'Ascia Rossa', 'Chiudi i Portali Instabili e raggiungi Broxigar il Rosso.', 'Broxigar il Rosso è stretto all\'angolo sul ponte. Non credo ammetterebbe mai di aver bisogno del nostro aiuto, ma è giusto accorrere in suo soccorso.\n\nSe desideri aiutare il nostro alleato, sarò onorato di accompagnarti in questo incarico.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44415, 'koKR', '붉은 도끼', '불안정한 차원문을 닫고 붉은 브록시가르에게 가야 합니다.', '붉은 브록시가르가 다리에서 포위됐습니다. 그는 도움이 필요하다고 인정하지 않을 게 분명하지만, 도와주는 게 좋겠습니다.\n\n그를 도우실 계획이라면, 저도 함께 가서 조금이나마 돕고 싶습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44415, 'ptBR', 'O machado vermelho', 'Feche os Portais Instáveis e chegue até Broxigar, o Vermelho.', 'Broxigar, o Vermelho, está encurralado na ponte. Não sei ao certo se ele vai admitir que precisa da nossa ajuda, mas sinto que o mais adequado é oferecer-lhe ajuda.\n\nSe você quiser ajudar nosso aliado, eu humildemente acompanharia você nesta empreitada.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44415, 'ruRU', 'Рыжий топор', 'Закройте неустойчивые порталы и пробейтесь к Броксигару Рыжему.', 'Броксигар Рыжий в одиночку сражается с врагами на мосту. Готов биться об заклад, он нипочем не признается, что наша помощь ему бы не помешала – но это не повод ее не оказать.\n\nЕсли ты решишь помочь нашему товарищу, для меня будет честью последовать за тобой.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44415, 'zhCN', '赤斧', '关闭不稳定的传送门，并找到“赤斧”布洛克斯加。', '“赤斧”布洛克斯加在桥上被包围了。不知道他愿不愿意拉下脸来求救，但我想我们一定得去帮他一把。\n\n如果你想协助我们的盟友，我将追随你一同奋战。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44415, 'zhTW', '火紅戰斧', '關閉不穩定的傳送門，去找火紅的布洛克斯。', '布洛克斯在橋上被包圍了，我不知道他會不會承認需要幫忙，不過我想最好還是出手去幫他一下。\n\n如果你決定要協助我們的盟友，我很樂意與你同行。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44416, 'deDE', 'Jäger der Nacht', 'Bezwingt Malvingeroth.', 'Was für ein glorreicher Kampf!\n\nUnd wenn ich mich nicht täusche, wird es gleich noch etwas tödlicher.\n\nDie Legion setzt nämlich gern solches Schlachtvieh ein, um den Weg für größere Gegner frei zu machen.\n\nMeine Axt ist scharf und bereit.', '', '', 'Malvingeroth', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44416, 'esES', 'Cazador de la noche', 'Derrota a Malvingeroth.', '¡Qué batalla más gloriosa!\n\nSi no me equivoco, estará al caer algo un poco más letal.\n\nLa Legión suele usar forraje de ese tipo con el objetivo de preparar el camino para un enemigo más digno.\n\nMi hacha está afilada y lista.', '', '', 'Malvingeroth', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44416, 'esMX', 'Cazador de la noche', 'Derrota a Malvingeroth', '¡Verdaderamente una batalla gloriosa!\n\nSi estoy en lo cierto, podemos esperar algo más letal en cualquier momento.\n\nLa Legión suele mandar enemigos así para prepararse para algo más peligroso.\n\nMi hacha está afilada y lista.', '', '', 'Malvingeroth', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44416, 'frFR', 'Chasseur de la nuit', 'Terrassez Malvingeroth.', 'Une glorieuse bataille s’il en est !\n\nMais sauf erreur de ma part, le pire reste à venir.\n\nLa Légion déploie souvent sa piétaille en premier pour préparer la voie à un combattant plus redoutable.\n\nMa hache est affûtée et prête à chanter.', '', '', 'Malvingeroth', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44416, 'itIT', 'Cacciatore della notte', 'Sconfiggi Malvingeroth.', 'Una gloriosa battaglia!\n\nSe non sbaglio, ci dobbiamo aspettare qualcosa di peggio da un momento all\'altro.\n\nLa Legione spesso usa carne da cannone di questo genere per aprire la strada ad avversari più forti.\n\nLa mia ascia è pronta e affilata.', '', '', 'Malvingeroth', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44416, 'koKR', '밤의 사냥꾼', '말빈게로스를 처치해야 합니다.', '정말로 영광스러운 전투로군!\n\n내 생각이 맞다면, 언제라도 더 끔찍한 존재가 나타날 수 있네.\n\n군단은 이렇게 약한 졸개들을 풀어 놓고는 더 가치 있는 적이 나타나기 전까지 전장을 달구는 걸 좋아하니까 말이야.\n\n내 날카로운 도끼도 준비를 마쳤네.', '', '', '말빈게로스', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44416, 'ptBR', 'Caçador da noite', 'Derrote Malvingeroth', 'Uma batalha realmente gloriosa!\n\nSe eu estiver certo, podemos esperar algo um pouco mais mortal a qualquer momento.\n\nA Legião costuma usar trastes como esses para preparar o caminho para um inimigo mais digno.\n\nMeu machado está pronto e afiado.', '', '', 'Malvingeroth', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44416, 'ruRU', 'Ночной охотник', 'Победите Малвингерота', 'Воистину славная битва!\n\nЕсли предчувствие меня не обманывает, с минуты на минуту здесь объявится кое-кто посильнее.\n\nЛегион часто использует всякую шваль как пушечное мясо, чтобы измотать врага перед тем, как выставить против него более грозных бойцов.\n\nМой топор жаждет битвы!', '', '', 'Малвингерот', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44416, 'zhCN', '暗夜猎手', '击败马文格罗斯', '真是一次辉煌的战斗！\n\n如果我没猜错的话，我们得随时注意，准备好面对一些更致命的敌人。\n\n军团经常用这些东西当做炮灰，来为更强大的敌人铺路。\n\n我的斧子已经准备好斩杀敌人了。', '', '', '马文格罗斯', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44416, 'zhTW', '暗夜狩獵者', '擊敗瑪維戈洛司', '真是場精彩的戰鬥！\n\n如果我沒猜錯，我們隨時都可能遇到更致命的東西。\n\n燃燒軍團經常用這種砲灰為更厲害的敵人鋪路。\n\n我的斧頭已經磨好，隨時都可以開戰。', '', '', '瑪維戈洛司', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44417, 'deDE', 'Noch eine Legende', 'Sprecht mit Odyn, um nach einem weiteren Artefakt zu suchen.', 'Zwei sind gut, aber drei wären besser. Ihr solltet Eurem Arsenal noch ein Artefakt hinzufügen.\n\nWas meint Ihr?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44417, 'esES', 'Otra leyenda más', 'Habla con Odyn para ir a por otro artefacto.', 'Con dos está bien, pero tres es mejor aún. Deberías ir a por otro artefacto para tu armería.\n\n¿Qué me dices?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44417, 'esMX', 'Una leyenda más', 'Habla con Odyn para ir a buscar otro artefacto.', 'Dos es bueno, pero tres es mejor. Deberías ir a buscar otro artefacto para tu arsenal.\n\n¿Qué dices?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44417, 'frFR', 'Une légende de plus', 'Parlez à Odyn pour acquérir une autre arme prodigieuse.', 'Deux, c’est bien, mais trois, ce serait beaucoup mieux. Vous devriez vous mettre en quête d’une autre arme prodigieuse à ajouter à votre armurerie.\n\nQu’en dites-vous ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44417, 'itIT', 'Un\'altra leggenda', 'Parla con Odyn per iniziare la ricerca di un altro Artefatto.', 'Due è buono, ma tre è ancora meglio. Dovresti andare alla ricerca di un altro artefatto da aggiungere alla tua armeria.\n\nChe ne dici?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44417, 'koKR', '또 하나의 전설', '또 다른 유물을 추적하려면 오딘과 이야기해야 합니다.', '두 개도 괜찮지만 세 개라면 더 든든하겠지. 무기고를 채울 또 다른 유물을 찾아보는 게 좋을 것 같다.\n\n네 생각은 어떠냐?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44417, 'ptBR', 'Mais uma lenda', 'Fale com Odyn para buscar outro artefato.', 'Dois é bom, mas três seria melhor. Você deveria buscar outro artefato para adicionar ao seu arsenal.\n\nO que me diz?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44417, 'ruRU', 'И еще одна легенда', 'Поговорите с Одином, чтобы отправиться на поиски еще одного артефакта.', 'Два артефакта – хорошо, но три – еще лучше. Тебе не помешало бы добыть еще одно легендарное оружие для своего арсенала.\n\nЧто скажешь?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44417, 'zhCN', '又一个传奇', '和奥丁对话来追寻另一件神器。', '两件很不错，但三件当然更好。你应该再去找来一件神器放进你的武器库里。\n\n你怎么说？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44417, 'zhTW', '再現傳奇', '告訴歐丁你想要第三件神兵武器。', '兩件很好，三件就更好了。你應該去找第三件神兵武器。\n\n你說呢？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44421, 'deDE', 'Anormale Anomalien', 'Verwendet Khadgars Kristall, um die Anomalien in Dalaran zu neutralisieren.', 'Dalaran hierher zu bewegen, sollte nur eine vorübergehende Maßnahme sein, aber wir müssen vielleicht früher als erwartet weiterziehen.$b$bDas Teleportieren einer Stadt durch die Welt ist keine einfache Aufgabe. Die Menge der dafür erforderlichen Energien kann zu... unerwarteten Nebenwirkungen führen.$b$bDie Arkananomalien in der Stadt sorgen für Störungen. Allein das könnte den Erfolg einer weiteren Teleportation gefährden.$b$bWir können nicht warten, bis sie sich von selbst auflösen, $n. Ich habe hier ein Gerät, mit dem Ihr diese Aufgabe etwas beschleunigen könnt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44421, 'esES', 'Anomalías anómalas', 'Usa el cristal de Khadgar para neutralizar anomalías en Dalaran.', 'Se suponía que traer Dalaran a esta ubicación era una medida temporal, pero igual hay que largarse antes de lo que pensábamos.$b$bTrasladar una ciudad al otro lado del mundo es toda una gesta. La magnitud de la energía requerida podría originar... efectos colaterales inesperados.$b$bUnas anomalías arcanas están sembrando el caos por la ciudad. Su presencia podría desestabilizar cualquier intento de traslación.$b$bNo tenemos tiempo de esperar a que se disipen solas, $n. Tengo un dispositivo que ayudará a agilizar las cosas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44421, 'esMX', 'Anomalías anómalas', 'Usa el cristal de Khadgar para neutralizar las anomalías en Dalaran.', 'Mover Dalaran a esta ubicación se hizo como medida preventiva, pero puede que tengamos que partir antes de lo esperado.$b$bTrasladar una ciudad por el mundo no es nada fácil. La cantidad de energías involucradas podría conducir a... efectos secundarios inesperados.$b$bLas anomalías arcanas están causando estragos por toda la ciudad. Su presencia podría desestabilizar cualquier intento de trasladarla otra vez.$b$bNo tenemos mucho tiempo para dejar que se disipen por sí mismas, $n. Tengo un aparato que ayudará a acelerar las cosas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44421, 'frFR', 'Anomalies anormales', 'Utilisez le cristal de Khadgar pour neutraliser des Anomalies dans Dalaran.', 'Nous avons toujours su que Dalaran n’était pas destinée à rester ici éternellement, mais nous pourrions bien avoir à repartir plus rapidement que prévu.$b$bLe transfert d’une cité à l’autre bout du monde n’est pas une mince affaire. La manipulation d’énergies d’une telle magnitude peut entraîner… des effets secondaires inattendus.$b$bDes anomalies arcaniques nous causent bien des soucis aux quatre coins de la cité, et leur simple présence pourrait perturber toute autre tentative de transfert.$b$bNous n’avons pas le temps de les laisser se dissiper naturellement, $n. Il se trouve justement que je possède un appareil pouvant accélérer le processus.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44421, 'itIT', 'Anomalie anomale', 'Usa il cristallo di Khadgar per neutralizzare le anomalie a Dalaran.', 'Spostare Dalaran qui doveva essere una soluzione temporanea sin dall\'inizio, ma dovremo partire prima del previsto.$b$bTraslare una città da una parte all\'altra del mondo non è facile. La magnitudine delle energie coinvolte può avere... effetti collaterali inaspettati.$b$bAnomalie arcane stanno causando un po\' di disagio a Dalaran. La loro presenza può destabilizzare i tentativi di traslare nuovamente la città.$b$bNon abbiamo tempo per lasciare che si disperdano naturalmente, ma ho uno strumento che può aiutare a velocizzare il processo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44421, 'koKR', '기이한 변형체들', '카드가의 수정을 사용하여 달라란에 있는 변형물을 무력화시켜야 합니다.', '달라란이 여기로 옮겨 온 것은 원래 임시방편이었지만, 생각했던 것보다 더 빨리 떠나야 할 수도 있소.$b$b점멸로 도시 전체를 세계 반대편으로 이동시키는 건 쉬운 일이 아니오. 막대한 마력이 사용되는 일이기에... 예상치 못한 부작용도 생기게 마련이지.$b$b비전 변형물이 도시 전체에서 날뛰기 시작했소. 그것들이 마력을 불안정하게 하여 다시 점멸하기가 쉽지 않다오.$b$b놈들이 소멸될 때까지 기다릴 순 없소, $n. 당신을 도와줄 장치가 하나 있소.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44421, 'ptBR', 'Anomalias anômalas', 'Use o cristal de Hadggar para neutralizar as anomalias em Dalaran.', 'A movimentação de Dalaran para essa localidade deveria ser uma medida temporária, mas talvez partamos antes do esperado.$b$bLampejar uma cidade pelo mundo não é tarefa fácil. A magnitude das energias envolvidas podem levar a efeitos colaterais inesperados.$b$bAnomalias arcanas estão causando problemas pela cidade. A presença delas poderia desestabilizar qualquer tentativa de lampejar novamente.$b$bNão tempos tempo para deixar que eles se dissipem por conta própria, $n. Tenho um dispositivo que vai ajudar a agilizar as coisas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44421, 'ruRU', 'Аномальные аномалии', 'Нейтрализуйте аномалии в Даларане с помощью кристалла Кадгара.', 'Перемещение Даларана сюда планировалось как временная мера, и здесь мы не задержимся, но, возможно, отправляться дальше придется совсем скоро.$b$bПеренести через полмира целый город – задачка не из легких. В этом процессе будет задействовано огромное количество энергии, что может вызвать... неожиданные побочные эффекты.$b$bВ городе были замечены чародейские аномалии. Их присутствие может повлиять на телепортацию самым непредсказуемым образом.$b$bУ нас нет времени ждать, пока они рассеются сами, $n, но у меня есть устройство, которое ускорит этот процесс.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44421, 'zhCN', '畸形畸体', '使用卡德加的水晶中和达拉然的畸体。', '在我们原本的计划中，把达拉然移到现在的位置只是暂时的手段，但现在我们需要提前离开了。$b$b让一座城市瞬间移动到新的位置可不是轻松的工作。这其中需要调动的能量之大……可能会导致意料之外的副作用。$b$b奥术畸体正在在城市的各个角落造成意外事件，这样下一次传送就无法正常进行。$b$b我们没有时间让它们自生自灭了，$n。我有一台仪器，可以用来加速它们消亡的过程。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44421, 'zhTW', '不協調的異常體', '使用卡德加的水晶消滅達拉然的異常體。', '將達拉然移動到這個位置，原本只是臨時措施，但我們可能需要提早離開。$b$b將一座城市瞬移到世界的其他地方可是件大事。其中牽涉到的能量強度可能會造成…出乎意料的副作用。$b$b秘法異常體正在城市各地搞破壞。它們的存在可能會使瞬移出現不穩定的情況。$b$b我們沒有時間等它們自行消散，$n。我有一台裝置可以加快它們的消散速度。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44424, 'deDE', 'Drei Pfade, drei Waffen', 'Wählt ein drittes Artefakt, nach dem Ihr suchen wollt.', 'Wir haben geforscht und einen neuen Hinweis auf ein weiteres nützliches Artefakt gefunden.\n\nHier, lasst mich Euch zeigen, was ich gefunden habe. Wenn es Euch interessiert, dann lasst es mich einfach wissen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44424, 'esES', 'Tres caminos, tres armas', 'Elige un tercer artefacto que buscar.', 'Hemos investigado y descubierto una pista sobre otro artefacto que podría resultarte útil.\n\nVen, voy a mostrarte mi hallazgo. No dudes en decirme si te interesa o no.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44424, 'esMX', 'Tres caminos, tres armas', 'Elige un tercer artefacto que buscar.', 'Buscamos y encontramos una pista de otro artefacto que te podría interesar.\n\nPermíteme mostrártelo. Si te interesa, tan solo avísame.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44424, 'frFR', 'Trois chemins, trois armes', 'Choisissez la troisième arme prodigieuse que vous souhaitez vous procurer.', 'Nous sommes sur la piste d’une autre arme prodigieuse qui pourrait vous être utile.\n\nTenez, voilà ce que j’ai découvert. Si jamais ça vous intéresse, vous n’avez qu’à me le dire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44424, 'itIT', 'Tre sentieri, tre armi', 'Scegli un terzo Artefatto da cercare.', 'Abbiamo cercato, e trovato, un indizio su un altro artefatto che potresti trovare utile.\n\nEcco, lascia che ti mostri cos\'abbiamo trovato. Se la cosa ti interessa, devi solo farmelo sapere.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44424, 'koKR', '세 갈래의 길, 세 가지 무기', '세 번째로 찾을 유물을 선택해야 합니다.', '자네에게 도움이 될만한 또 다른 유물에 대해 새로운 단서를 찾았다네.\n\n자, 여기 내가 찾은 것을 보게나. 마음에 드는 것이 있다면 내게 알려주게.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44424, 'ptBR', 'Três caminhos, três armas', 'Escolha um terceiro artefato para buscar.', 'Procuramos e encontramos uma pista para outro artefato que você pode achar útil.\n\nAqui, deixa eu te mostrar o que eu encontrei. Se tiver interesse, me avise.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44424, 'ruRU', 'Три пути, три орудия', 'Выберите третий артефакт для поисков.', 'Мы нашли кое-что, что сможет привести тебя к еще одному артефакту.\n\nВот, позволь показать тебе. Если это тебя заинтересует, просто скажи мне.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44424, 'zhCN', '三途三修', '选择你要追寻的第三件神器。', '我们在搜索中找到了一条线索，指向另一件你可能用得上的神器。\n\n来，让我给介绍一下我的发现吧。如果你对它感兴趣，就直接告诉我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44424, 'zhTW', '三條道路，三件武器', '請選擇要尋找的第三件神兵武器', '我們找了很久，終於找到另一件神兵武器的線索。或許對你有幫助。\n\n你看，這就是我所找到的資料，我們可以深入討論一下。如果你有興趣的話，就讓我知道。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44431, 'deDE', 'Noch mehr alte Waffen', 'Wählt ein drittes Artefakt, nach dem Ihr suchen wollt.', 'Ihr habt große Erfolge gegen unsere Feinde errungen, aber Ihr solltet auf alles vorbereitet sein, was sie auf Euch loslassen.$b$bIhr wäret gut beraten, darüber nachzudenken, noch eine uralte Waffe zu besorgen, die uns in diesem Kampf weiterhelfen könnte.$b$bLasst mich wissen, wie Ihr verfahren wollt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44431, 'esES', 'Más armas de antaño', 'Elige un tercer artefacto que buscar.', 'Has cosechado una gran victoria contra nuestros enemigos, pero deberías prepararte para cualquier desafío que te pongan por delante.$b$bHarías bien en plantearte obtener otra de las armas de antaño para ayudar en nuestro conflicto.$b$bYa me dirás qué quieres hacer.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44431, 'esMX', 'Más armas antiguas', 'Elige un tercer artefacto que buscar.', 'Te ha ido muy bien contra nuestros enemigos, pero deberías estar $Glisto:lista; para cualquier desafío que pongan en tu camino.$b$bSería sabio que consideres obtener otra arma antigua para ayudar en este conflicto.$b$bDime cómo quieres proceder.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44431, 'frFR', 'D’autres armes du temps jadis', 'Choisissez la troisième arme prodigieuse que vous souhaitez vous procurer.', 'Vous avez rencontré un franc succès face à nos ennemis, mais il vous faut désormais vous préparer à relever tous les obstacles qu’ils placeront sur votre route.$b$bVous devriez songer à chercher une autre arme prodigieuse pour nous aider dans ce conflit.$b$bDites-moi comment vous souhaitez procéder.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44431, 'itIT', 'Altre armi del passato', 'Scegli un terzo Artefatto da cercare.', 'I tuoi successi contro i nostri nemici sono stati grandi, ma devi prepararti per qualsiasi sfida ci attenda.$b$bSarebbe saggio se ti procurassi un\'altra delle antiche armi che ci aiuti nel conflitto a venire.$b$bFammi sapere quando vorrai procedere.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44431, 'koKR', '또 다른 고대 무기', '세 번째로 찾을 유물을 선택해야 합니다.', '큰 승리를 거두었지만, 적들의 다음 계략에 대비해야 합니다.$b$b다음 전투에 도움될 만한 다른 고대 무기도 구해놓는 게 좋을 것 같습니다.$b$b어떻게 할지 결정되면 알려주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44431, 'ptBR', 'Mais armas de outrora', 'Escolha um terceiro artefato para buscar.', 'Você obteve incrível sucesso contra nossos inimigos, mas precisa estar pront$go:a; para qualquer desafio que surgir em seu caminho.$b$bAconselho você a considerar obter uma outra arma de outrora para ajudar em nosso conflito.$b$bDiga-me como quer prosseguir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44431, 'ruRU', 'Еще больше древнего оружия', 'Выберите третий артефакт для поисков.', 'Пока что ты победоносно сражаешься с врагами, но лучше заранее подготовиться ко всему, что они могут на тебя обрушить.$b$bТебе неплохо бы обдумать, каким еще древним оружием пополнить свой арсенал. Это увеличит наши шансы на победу.$b$bСообщи мне о своем выборе.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44431, 'zhCN', '更多的远古武器', '选择你要追寻的第三件神器。', '在对抗燃烧军团的战争中，你的表现相当出色，但是你应该准备好迎接今后的各种挑战。$b$b你应该考虑一下获取另一件远古神器，让你在战斗中占据更大的优势。$b$b告诉我你有什么打算。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44431, 'zhTW', '更多古老的武器', '選擇要尋找的第三件神兵武器。', '在面對燃燒軍團的這場戰役中，你表現得相當不錯，但你應該預先準備好面對各種挑戰。$b$b我認為再找一件神兵武器，對你和我們的戰況都有幫助。$b$b讓我知道你打算怎麼做。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44437, 'deDE', 'Legion Dungeon Reward Quest', '', '', '', '', '', '', '', '', 0);
