INSERT INTO `quest_template_locale` VALUES (44800, 'koKR', '마가타에게 반기 들기', '마가타 그림토템과 대화하고, 죽음돌을 선견자 노분도에게 전달해야 합니다.', '왜 멍하니 서 있나? 어서 죽음돌을 나에게 주게.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44800, 'ptBR', 'Contra a vontade de Magatha', 'Fale com Magatha Temível Totem e leve a Pedra da Ruína para o Clarividente Nobambo.', 'E então? O que você está esperando? Entregue a Pedra da Ruína.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44800, 'ruRU', 'Против воли Магаты', 'Поговорите с Магатой Зловещий Тотем, а затем отнесите Камень Рока предсказателю Нобундо.', 'Ну? Чего ты ждешь? Дай мне Камень Рока.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44800, 'zhCN', '违背玛加萨的意志', '与玛加萨·恐怖图腾谈一谈，然后把末日石交给预言者努波顿。', '嗯？你还在等什么？快把末日石交出来。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44800, 'zhTW', '違背瑪加薩的意願', '與瑪加薩‧恐怖圖騰交談，再把末日石帶給先知諾柏多。', '嗯？你還等什麼？把末日石交出來啊。', '', '', '', '', '', '', 0);
