INSERT INTO `quest_template_locale` VALUES (44862, 'itIT', 'Armare i ribelli', 'Parla con Victoire a Meredil.', 'La ribellione degli Esuli Oscuri sta crescendo, ma non combattono una vera guerra da secoli.$b$bVictoire e Silgryn stanno facendo il loro meglio per addestrare i nuovi arrivati, ma ci farebbe comodo una mano esperta.$b$bVai da loro e mostragli alcune tecniche di combattimento che hai appreso durante i tuoi viaggi. Abbiamo bisogno che quegli Esuli Oscuri siano pronti alla battaglia quando verrà il momento.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44862, 'koKR', '반군 무장', '메레딜에 있는 빅트와와 대화해야 합니다.', '나이트폴른 저항군의 세력이 커지고 있습니다. 하지만 그들은 지난 수 세기 동안 진짜 전쟁에서 싸워 본 적이 없지요.$b$b빅트와와 실그린이 최선을 다해 신병들을 훈련시키고 있지만, 아무래도 전투에 익숙한 이의 도움이 필요할 것 같습니다.$b$b그들을 찾아가 지금까지의 모험에서 그대가 체득한 솜씨를 보여 주세요. 때가 되었을 때, 나이트폴른들이 전투에 뛰어들 준비가 되어 있기를 바랍니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44862, 'ptBR', 'Armas ao rebeldes', 'Fale com Victória em Meredil.', 'A quantidade de noctívoros rebeldes só aumenta, mas a última guerra em que lutaram foi eras atrás.$b$bVictória e Silgryn estão fazendo de tudo para treinar os recrutas, mas alguém experiente seria de grande ajuda.$b$bMostre a elas as técnicas de combate que você aprendeu ao longo da sua jornada. A gente precisa dos noctívoros prontos para a batalha quando a hora chegar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44862, 'ruRU', 'Вооружение повстанцев', 'Поговорите с Викторией в Мередиле.', 'Мятеж помраченных привлекает все больше горожан в свои ряды, но проблема в том, что им не приходилось воевать на протяжении многих столетий.$b$bВиктория с Силгрином делают все возможное, чтобы как следует обучить вновь прибывших, и им точно не помешает помощь.$b$bСтупай к ним и продемонстрируй боевые навыки, которые ты $gприобрел:приобрела; в своих странствиях. Нам нужно, чтобы эти помраченные были полностью готовы к бою, когда пробьет час.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44862, 'zhCN', '武装义军', '在梅瑞戴尔与维多利亚谈一谈。', '堕夜精灵起义军的人数越来越多，但是他们大多久疏战阵。$b$b维多利亚和希尔格林都在尽全力训练新人，但还需要一个老手帮忙。$b$b去他们那里，向他们展示一下你在旅途中学到的战斗技巧。我们得让那些堕夜精灵能为即将到来的战争做好准备。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44862, 'zhTW', '武裝反抗軍', '在麥瑞迪爾和薇可朵交談。', '夜落精靈的反抗規模越來越龐大，但這些人大都沒有經歷過真正的戰爭。$b$b薇可朵和西爾格林已經盡力訓練新兵，但急需經驗豐富的人出手相助。$b$b去找他們，讓他們見識一下你在旅途中學到的戰鬥技巧。那些夜落精靈必須在開戰之前做好準備。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44863, 'deDE', 'Sich Luft verschaffen', 'Sucht für Lunelli nach einem Kardinalhimmelskern, einem Kardinalsturmkern und einem Kardinalwindkern. Bringt die Steine zu ihr nach Shattrath, wenn Ihr sie alle zusammen habt.', 'Um das Wissen für die Herstellung uralter Waffen wiederzuerlangen, benötigt man zunächst uralte Gaben.$b$bWenn Ihr lernen möchtet, wie man luftgeschmiedete Waffen herstellt, müsst Ihr Euch den Wesen stellen, die aus jener geboren wurden.$b$bBringt mir ihre Kerne. Dann werde ich Euch das Wissen zuteilwerden lassen, das Ihr sucht.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44863, 'esES', 'Purificación del aire', 'Consigue un núcleo celeste cárdeno, un núcleo de tormenta cárdeno y un núcleo de viento cárdeno para Lunelli. Cuando tengas los tres, llévaselos a Shattrath.', 'Las armas antiguas requieren ofrendas antiguas para recuperar los conocimientos para construirlas.$B$BSi quieres aprender a forjar armas a partir del aire, primero tienes que luchar contra los seres que nacen del él.$B$BTráeme sus núcleos y te daré el conocimiento que buscas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44863, 'esMX', 'A depurar el aire', 'Consigue un núcleo de cielo cárdeno, un Núcleo de tormenta cárdeno y un núcleo de viento cárdeno para Lunelli en Shattrath. Llévaselos cuando tengas los tres.', 'Las armas antiguas requieren de ofrendas ancestrales para recobrar el conocimiento de cómo construirlas.$b$bSi deseas aprender cómo se fabricaban las armas forjadas con aire, primero tendrás que luchar contra seres que nacieron del aire.$b$bTráeme sus núcleos y te obsequiaré con el conocimiento que tanto buscas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44863, 'frFR', 'L’air d’avoir deux airs', 'Récupérez un Noyau de ciel cardinal, un Noyau de tempête cardinale et un Noyau de vent cardinal pour Lunelli. Apportez-les-lui à Shattrath quand ils seront tous trois en votre possession.', 'Les armes anciennes nécessitent des offrandes anciennes pour retrouver les connaissances permettant de les recréer.$b$bSi vous voulez découvrir comment sont réalisées les armes forgées d’air, vous allez d’abord devoir affronter des créatures qui en sont faites.$b$bApportez-moi leur noyau et je vous transmettrai ce savoir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44863, 'itIT', 'Armi dell\'aria', 'Ottieni un Nucleo Cardinale di Cielo, un Nucleo Cardinale di Tempesta e un Nucleo Cardinale di Vento, quindi riporta questi oggetti a Lunelli nella città di Shattrath.', 'Per riscoprire la conoscenza necessaria a forgiare le armi antiche, bisogna fare offerte altrettanto antiche.$b$bSe desideri apprendere come vengono forgiate le armi dall\'aria, dovrai prima affrontare esseri nati da essa.$b$bPortami i loro nuclei e ti donerò la conoscenza che cerchi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44863, 'koKR', '공기 정화', '하늘의 중핵, 폭풍의 중핵, 바람의 중핵을 모은 후 샤트라스에 있는 루넬리에게 가야 합니다.', '고대의 무기를 제작하는 법을 알기 위해서는 고대의 공물이 필요합니다.$b$b바람으로 벼려진 무기를 만드는 법을 알고 싶다면, 먼저 바람에서 태어난 존재와 싸워야 해요.$b$b그들의 핵을 가져오시면 당신이 찾는 지식을 드릴게요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44863, 'ptBR', 'A purificação do ar', 'Recolha um Núcleo do Céu Cardinal, um Núcleo de Tempestade Cardinal e um Núcleo de Vento Cardinal. Depois que tiver obtido os três, entregue-os a Lunelli em Shattrath.', 'Para restaurar as técnicas de criação das armas ancestrais é preciso fazer oferendas ancestrais.$b$bSe quiser aprender a forjar armas a partir do ar, primeiro terá que enfrentar as criaturas nativas desse elemento.$b$bTraga-me o núcleo delas, e eu lhe darei o conhecimento que você busca.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44863, 'ruRU', 'Очищение воздуха', 'Раздобудьте для Лунелли основные сущности неба, бури и ветра. Когда все три сущности будут у вас, отнесите их ей в Шаттрат.', 'Для обретения знания о древнем оружии потребуется древнее подношение.$b$bЕсли ты желаешь знать, как изготавливать оружие, сотворенное из воздуха, тебе сперва нужно будет сразиться с порожденными этой стихией созданиями.$b$bПринеси мне их сущности, и тогда я поделюсь с тобой этим знанием.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44863, 'zhCN', '净化空气', '为露妮丽找到一个鲜红的天空核心，一个鲜红的风暴核心和一个鲜红的风之核心。集齐三个核心后返回沙塔斯向她复命。', '要想重新掌握打造远古武器的技术，就需要远古的祭品。$b$b如果你想了解如何使用空气铸造武器，那就需要先与从空气的产物交战。$b$b把它们的核心带给我，我就会把你寻求的知识赐予你。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44863, 'zhTW', '風行草偃', '幫露奈黎找到緋紅天空核心、緋紅風暴核心和緋紅風之核心。全部找到以後把東西帶回撒塔斯交給她。', '上古武器需要上古祭品，才能重新獲知打造武器的方法。$b$b如果你想知道風鑄武器是怎麼製造的，就必須先與風之生物交戰。$b$b把它們的核心帶給我，我就會把你想要的知識傳授給你。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44865, 'deDE', 'Butler von Berühmtheiten ', 'Deckt den Tisch und beschwört Moroes.', 'Eine gespenstische Dinnerparty steht an, alles ist bereit für das Festessen und die Dinnergäste.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44865, 'esES', 'Mayordomo de los grandes', 'Prepara la mesa y llama a Moroes.', 'Nos aguarda una cena fantasmal. Prepárate para el banquete y los invitados.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44865, 'esMX', 'Mayordomo para los grandes', 'Pon la mesa y llama a Moroes.', 'Una noche de cena fantasmal aguarda, lista para sus invitados.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44865, 'frFR', 'Le majordome des grands de ce monde', 'Mettez la table et invoquez Moroes.', 'Un grand dîner spectral attend ! Il faut dresser la table, les invités ne tarderont plus.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44865, 'itIT', 'Maggiordomo di classe', 'Apparecchia il tavolo e chiama Moroes.', 'Una cena spettrale attende, pronta ad accogliere i suoi invitati.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44865, 'koKR', '집사의 잠재력', '식탁을 차리고 모로스를 소환해야 합니다.', '진수성찬과 손님들이 함께하는 유령의 저녁 만찬이 기다립니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44865, 'ptBR', 'Mordomo aos grandes', 'Ponha a mesa e chame Tristão.', 'Um jantar fantasmagórico aguarda, é só botar a mesa e esperar os convivas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44865, 'ruRU', 'Дворецкий на века', 'Накройте стол и призовите Мороуза.', 'Предстоит небывалый званый ужин, готовьте угощение для гостей.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44865, 'zhCN', '了不起的管家', '铺好桌子，然后召唤莫罗斯。', '幽灵宴会准备停当，只等桌子铺好，宾客来访。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44865, 'zhTW', '偉人的管家', '備好桌，然後呼喚摩洛斯。', '有場幽靈晚宴正等著，只差美食與賓客尚未抵達。', '', '', '', '', '', '', 0);
