INSERT INTO `quest_template_locale` VALUES (44868, 'deDE', 'Prüfung der Tapferkeit: Odyns Urteil', 'Bezwingt Helya im Schlachtzug \"Die Prüfung der Tapferkeit\".', 'Ihr habt Euch als würdig erwiesen, Helya herauszufordern. Mein Segen gewährt Euch die Macht, sie zu besiegen.', '', '', 'Helya', '', 'Odyn', '', 0);
INSERT INTO `quest_template_locale` VALUES (44868, 'esES', 'Prueba del Valor: El juicio de Odyn', 'Derrota a Helya en la banda de la Prueba del Valor.', 'Has demostrado ser $gdigno:digna; de desafiar a Helya. Ahora, y con mi bendición, el poder para derrotarla es tuyo.', '', '', 'Helya', '', 'Odyn', '', 0);
INSERT INTO `quest_template_locale` VALUES (44868, 'esMX', 'Prueba de valor: El juicio de Odyn', 'Derrota a Helya en la banda Reto del valor.', 'Demostraste ser $Gdigno:digna; de retar a Helya. Ahora con mi bendición, el poder para derrotarla es tuyo.', '', '', 'Helya', '', 'Odyn', '', 0);
INSERT INTO `quest_template_locale` VALUES (44868, 'frFR', 'Jugement des Valeureux : le jugement d’Odyn', 'Venez à bout de Helya dans le raid du Jugement des Valeureux.', 'Vous avez prouvé que vous êtes de taille à défier Helya. Je vous octroie ma bénédiction, vous avez maintenant le pouvoir de la vaincre.', '', '', 'Helya', '', 'Odyn', '', 0);
INSERT INTO `quest_template_locale` VALUES (44868, 'itIT', 'Prova del Valore: Il giudizio di Odyn', 'Sconfiggi Helya nell\'incursione \"Prova del Valore\".', 'Ti sei $gdimostrato degno:dimostrata degna; di sfidare Helya. Ora, con la mia benedizione, hai il potere di sconfiggerla.', '', '', 'Helya', '', 'Odyn', '', 0);
INSERT INTO `quest_template_locale` VALUES (44868, 'koKR', '용맹의 시험: 오딘의 심판', '용맹의 시험 공격대에서 헬리아를 처치해야 합니다.', '너는 헬리아에게 도전할 자격이 있음을 증명했다. 이제 내 축복과 함께, 그녀를 물리칠 힘은 네 것이다.', '', '', '헬리아', '', '오딘', '', 0);
INSERT INTO `quest_template_locale` VALUES (44868, 'ptBR', 'Provação da Bravura: Julgamento de Odyn', 'Derrote Helya no raide Provação da Bravura.', 'Você provou-se dign$Go:a; de desafiar Helya. Agora, com a minha bênção, o poder para derrotá-la é seu.', '', '', 'Helya', '', 'Odyn', '', 0);
INSERT INTO `quest_template_locale` VALUES (44868, 'ruRU', 'Испытание доблести: вердикт Одина', 'Победите Хелию в рейде \"Испытание доблести\".', 'Я вижу, что ты $gдостоин:достойна; бросить вызов Хелии. С моим благословением ты сможешь победить ее.', '', '', 'Хелия', '', 'Один', '', 0);
INSERT INTO `quest_template_locale` VALUES (44868, 'zhCN', '勇气试炼：奥丁的审判', '在勇气试炼团队副本中击败海拉。', '你证明了自己有实力挑战海拉。有了我的祝福，你就有了击败她的力量。', '', '', '海拉', '', '奥丁', '', 0);
INSERT INTO `quest_template_locale` VALUES (44868, 'zhTW', '勇氣試煉：歐丁的審判', '在勇氣試煉團隊副本中擊敗黑爾雅。', '你證明了自己有資格挑戰黑爾雅。有了我的祝福，你現在擁有擊敗她的力量了。', '', '', '黑爾雅', '', '歐丁', '', 0);
INSERT INTO `quest_template_locale` VALUES (44869, 'deDE', 'Krallenkrawall', 'Benutzt das Portal auf dem Traumpfad, um Euch mit Thisalee am Schrein von Aviana auf dem Hyjal zu treffen.', 'Ich habe gerade eine Nachricht erhalten, dass die Brennende Legion den Schrein von Aviana angreift! Während die Druiden der Kralle sich hier Zeit lassen, um ihre Verteidigung zu planen und Strategien zu entwickeln, werden unsere Brüder und Schwestern an Ort und Stelle abgeschlachtet.$b$bIch kann nicht untätig sein, während mein Heimatschrein in Gefahr ist. Bitte, kommt mit mir, um mein Volk zu retten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44869, 'esES', 'Terror de la Garfa', 'Utiliza el portal del Camino del Sueño para reunirte con Thisalee en el Santuario de Aviana, en el Monte Hyjal.', '¡Me acaban de informar de que la Legión Ardiente está atacando el Santuario de Aviana! Mientras aquí los druidas de la Garfa se toman su tiempo para planificar su defensa, nuestros hermanos y hermanas están siendo masacrados.$B$BNo puedo quedarme de brazos cruzados mientras mi santuario está en peligro. Ven conmigo a salvar a mi gente, por favor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44869, 'esMX', 'El terror de la Garfa', 'Usa el portal de la Senda de los Sueños para encontrarte con Thisalee en el Santuario de Aviana en el Monte Hyjal.', '¡Acabo de saber que la Legión Ardiente está atacando el Santuario de Aviana! Mientras los druidas de la Garfa se toman demasiado tiempo para montar sus defensas y planear estrategias, la Legión está masacrando a nuestros hermanos y nuestras hermanas.$b$bNo puedo permanecer de brazos cruzados mientras mi santuario de origen corre peligro. Te ruego que me acompañes a salvar a mi gente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44869, 'frFR', 'Terreur de la Serre', 'Utilisez le portail du Chemin du Rêve pour rejoindre Thisalee au Sanctuaire d’Aviana, sur le Mont Hyjal.', 'Je viens d’apprendre que la Légion ardente a attaqué le sanctuaire d’Aviana ! Pendant que les druides de la Serre préparent tranquillement leurs défenses ici, nos frères et nos sœurs se font massacrer !$b$bIl est hors de question que je reste là les bras ballants, pendant que mon sanctuaire est en péril ! Je vous en supplie, venez m’aider à protéger mon peuple.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44869, 'itIT', 'Artigli in pericolo', 'Usa il portale nel Metasogno per incontrare Thisalee al Sacrario di Aviana presso il Monte Hyjal.', 'Mi è appena giunta notizia che il Sacrario di Aviana è sotto attacco da parte della Legione Infuocata! Mentre i Druidi dell\'Artiglio qui stanno pianificando strategie e difese, i nostri fratelli e le nostre sorelle vengono massacrati sul posto.$b$bNon posso restarmene con le mani in mano mentre il Sacrario, la mia casa, è in pericolo. Ti prego, vieni con me a salvare la mia gente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44869, 'koKR', '갈퀴발톱의 공포', '꿈길에 있는 차원문을 사용하여 하이잘 산에 있는 아비아나의 제단에서 티살리 크로우를 만나야 합니다.', '불타는 군단이 아비아나의 제단을 공격하고 있다는 소식을 들었습니다! 여기 갈퀴발톱의 드루이드들이 방어 전략을 구상하는 동안에도 우리 형제자매들은 그곳에서 살육당하고 있을 겁니다.$b$b제 추억이 어린 제단이 위험에 처한 상황에서 가만히 있을 순 없습니다. 부디 저와 함께 제 동포들을 구해주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44869, 'ptBR', 'Terror da Garra', 'Use o portal da Estrada Onírica para encontrar Tessália no Santuário de Aviana, que fica no Monte Hyjal.', 'Acabo de saber que o Santuário de Aviana está sendo atacado pela Legião Ardente! Enquanto os Druidas do Gadanho aqui estão planejando a estratégia de defesa deles, nossos irmãos e irmãs estão sendo massacrados.$b$bNão posso ficar de braços cruzados enquanto meu santuário natal está em perigo. Por favor, venha comigo para salvar meu povo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44869, 'ruRU', 'Бич пернатых', 'Пройдите через портал Пути Снов и встретьтесь с Тизали у святилища Авианы на горе Хиджал.', 'Мне только что сообщили, что Пылающий Легион напал на святилище Авианы! Моих братьев и сестер убивают, пока друиды-вороны планируют да обдумывают тактику защиты.$b$bЯ не могу сидеть сложа руки, когда мой дом в опасности. Прошу, помоги мне спасти близких.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44869, 'zhCN', '利爪恐慌', '使用梦境之路的传送门，前往海加尔山的艾维娜圣殿找希萨莉。', '我刚刚得知，燃烧军团正在攻打艾维娜圣殿！而利爪德鲁伊却在这里慢吞吞地策划着防御战略，眼看着我们的兄弟姐妹惨遭屠戮。$b$b我不能坐视被我视为家园的圣殿化为火海。求求你，跟我一起去拯救我的人民。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44869, 'zhTW', '利爪恐懼', '使用翡翠夢途的傳送門，到海加爾山的艾維娜聖壇與黎沙莉會面。', '我剛接獲訊息，艾維娜聖壇正受到燃燒軍團攻擊！猛禽德魯伊還在這裡花時間策劃如何防禦，我們的兄弟姊妹卻慘遭屠殺。$b$b我無法袖手旁觀，我家鄉的聖壇正陷入危境。拜託，請跟我一起拯救我的同胞。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44870, 'deDE', 'Zu stopfende Mäuler', 'Sprecht mit Arkanistin Valtrois in Shal\'Aran.', 'Wie Ihr sehen könnt, haben wir viele Flüchtlinge retten können. Aber leider eröffnet sich dadurch ein neues Problem.$b$bDie Frucht des Arcan\'dor ist unsere Rettung, aber er trägt nicht genug Früchte, um alle zu heilen. Wir müssen noch eine Weile auf das Mana zurückgreifen, um die Flüchtlinge zu versorgen.$b$bValtrois glaubt, eine Lösung für das Problem gefunden zu haben. Bitte geht zu ihr und besprecht ihre Pläne mit ihr.', 'Sprecht mit Arkanistin Valtrois in Shal\'Aran.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44870, 'esES', 'Bocas que alimentar', 'Habla con la arcanista Valtrois en Shal\'Aran.', 'Como puedes ver, hemos rescatado a muchos refugiados. Por desgracia, esto ha sacado a la luz un nuevo problema.$b$bLa fruta de Arcan\'dor es nuestra salvación, pero no produce lo bastante como para curarlos a todos. Tendremos que confiar un poco más en el maná para ayudar a los refugiados.$b$bValtrois cree tener la solución. Habla con ella y averigua cuáles son sus planes.', 'Habla con la arcanista Valtrois en Shal\'Aran.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44870, 'esMX', 'Bocas que alimentar', 'Habla con la arcanista Valtrois en Shal\'aran.', 'Como ves, rescatamos a muchos refugiados, pero por desgracia, ahora tenemos un nuevo problema.$b$bLa fruta de Arcan\'dor es nuestra salvación, pero no tenemos suficiente para curarlos a todos. Debemos depender del maná un poco más para ayudar a los refugiados.$b$bValtrois cree que podría tener la solución. Ve y habla con ella para descubrir qué piensa hacer.', 'Habla con la arcanista Valtrois en Shal\'aran.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44870, 'frFR', 'Des bouches à nourrir', 'Parlez à l’Arcaniste Valtrois à Shal’Aran.', 'Nous avons secouru de nombreux réfugiés, comme vous pouvez le constater. Malheureusement, leur nombre croissant soulève de nouveaux problèmes.$b$bLes fruits de l’arcan’dor sont notre planche de salut, mais sa production actuelle ne suffit pas à couvrir la demande. Faute de mieux, nous sommes donc contraints de recourir encore et toujours au mana pour faire tenir les réfugiés.$b$bValtrois pense avoir trouvé une solution. Allez lui parler pour apprendre ce qu’elle compte faire exactement.', 'Parler à l’Arcaniste Valtrois à Shal’Aran', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44870, 'itIT', 'Bocche da sfamare', 'Parla con l\'Arcanista Valtrois a Shal\'aran.', 'Abbiamo salvato molti rifugiati, come puoi vedere, ma sfortunatamente nel farlo abbiamo portato alla luce un nuovo problema.$b$bIl Frutto dell\'Arcan\'dor è la nostra salvezza, ma non ce n\'è abbastanza per curare tutti quanti. Per guidare i rifugiati al sicuro, dobbiamo fare affidamento sul mana ancora un po\'.$b$bValtrois pensa di avere una soluzione. Va\' da lei e cerca di capire cos\'ha intenzione di fare.', 'Parla con l\'Arcanista Valtrois a Shal\'aran.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44870, 'koKR', '보급품이 필요해', '샬아란에 있는 비전술사 발트와와 대화해야 합니다.', '보시다시피 우린 아주 많은 피난민을 구출해냈어요. 불행히도 그로 인해 다른 문제가 생겨났지만요.$b$b아르칸도르의 열매가 우리의 구원이 될 예정이었지만, 지금으로써는 모든 이들을 치유할 만큼 충분한 열매를 맺지 못해요. 밀려오는 피난민들을 보호하려면 조금 더 마나에 의존해야 할 것 같아요.$b$b발트와가 뭔가 해결책이 있다고 하더군요. 가서 이야기를 나누고, 어떻게 할 생각인지 알아봐 주세요.', '샬아란에 있는 비전술사 발트와와 대화해야 합니다.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44870, 'ptBR', 'Bocas para alimentar', 'Fale com a Arcanista Valtrois em Shal\'Aran.', 'Como pode ver, resgatamos muitos refugiados. Infelizmente, isso nos trouxe outro problema.$b$bO fruto da arcan\'dor é a nossa salvação, mas não temos o suficiente para curar todo mundo. Teremos que continuar usando mana para cuidar dos refugiados um pouco mais.$b$bValtrois acredita ter encontrado uma saída. Por favor, converse com ela para saber do que se trata.', 'Fale com a Arcanista Valtrois em Shal\'Aran.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44870, 'ruRU', 'Голодные рты', 'Поговорите с чародейкой Вальтруа в Шал\'Аране.', 'Как видишь, мы спасли много беженцев. К сожалению, это породило новую проблему.$b$bПлоды аркан\'дора – наше спасение, но их недостаточно, чтобы исцелить всех. Еще некоторое время нам будет нужна мана, чтобы беженцы продержались.$b$bВальтруа думает, что нашла выход. Пожалуйста, поговори с ней и узнай ее планы.', 'Поговорите с чародейкой Вальтруа в Шал\'Аране.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44870, 'zhCN', '嗷嗷待哺', '在沙尔艾兰与奥术师瓦尔托伊谈一谈。', '如你所见，我们救了许多难民。不幸的是，这导致了一个新问题。$b$b阿坎多尔的果实是我们的救赎，但它现在结的果子数量不足以治愈所有人。为了保障所有难民的生活，我们还得继续依赖魔力一小段时间。$b$b瓦尔托伊说她可能有办法。去跟她谈谈，看看她有什么计划。', '在沙尔艾兰与奥术师瓦尔托伊谈一谈。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44870, 'zhTW', '滿足所有人', '在沙亞蘭與秘法師薇兒塔交談。', '如你所見，我們救了許多難民，但也因此浮現新的問題。$b$b我們得靠奧肯多爾果實才能獲救，但目前長出來的果實數量不夠分給每個人。在這段時期裡，還是需要繼續仰賴魔法來解救這些難民。$b$b薇兒塔認為她有解決的辦法。去找她討論一下，看看她想怎麼做。', '在沙亞蘭與秘法師薇兒塔交談。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44873, 'deDE', 'Oculeth ex Machina', 'Sprecht mit Oculeth und lasst Euch nach Azsuna teleportieren.', 'Ich habe einen Plan, Shal\'Aran zu versorgen.$b$bJetzt, wo ich mich nicht mehr nach Mana verzehre, konnte ich die Leylinien in anderen Gegenden studieren. Ich habe einen Manawirbel in den Leylinien von Azsuna ausgemacht. Mir steht eine Menge Magie zur Verfügung, aber wir müssen die Leylinie persönlich aufsuchen, um die Energie korrekt nach Shal\'Aran umzulenken.$b$bOculeth hat uns einen Transport zugesagt, aber ich brauche jemanden, der mir dabei hilft. Wollt Ihr mich vielleicht begleiten?', 'Sprecht mit Oculeth und lasst Euch nach Azsuna teleportieren.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44873, 'esES', 'Oculeth ex machina', 'Habla con Oculeth y teletranspórtate hasta Azsuna.', 'Tengo un plan para alimentar a Shal\'Aran.$b$bDesde que no sufro la necesidad de maná, he tenido tiempo para estudiar las líneas Ley de nuevas regiones. He detectado un remolino de maná en las líneas Ley de Azsuna. Tengo mucha magia a mi disposición, pero debemos visitar la línea Ley en persona para redirigir adecuadamente la energía hacia Shal\'Aran.$b$bOculeth se ha ofrecido amablemente a teletransportarnos hasta allí, pero necesitaría un ayudante. ¿Te importaría acompañarme?', 'Habla con Oculeth y teletranspórtate hasta Azsuna.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44873, 'esMX', 'Oculeth Ex Machina', 'Habla con Oculeth y teletranspórtate a Azsuna.', 'Tengo un plan para alimentar Shal\'Aran.$b$bAhora que ya no estoy sediento de maná, pude estudiar más las Líneas Ley y detecté un torbellino de maná en las Líneas Ley de Azsuna. Tengo magia de sobra a mi disposición, pero debemos visitar la Línea Ley en persona para poder redirigir la energía a Shal\'Aran.$b$bOculeth se ofreció a teletransportarnos allí, pero me vendría bien algo de ayuda. ¿Vendrás conmigo?', 'Habla con Oculeth y teletranspórtate a Azsuna.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44873, 'frFR', 'Oculeth ex machina', 'Parlez à Oculeth et téléportez-vous sur Azsuna.', 'J’ai un plan pour assurer la subsistance de Shal’Aran.$b$bAprès m’être affranchie de mon addiction au mana, j’ai eu le loisir d’étudier les lignes telluriques de nouvelles régions. Cela m’a permis de détecter un tourbillon d’énergie au cœur d’Azsuna. Ce ne sont pas les ressources magiques qui me manquent, mais il me faut inspecter les lieux en personne afin de rediriger correctement l’énergie vers Shal’Aran.$b$bOculeth a gentiment proposé de nous téléporter sur place, mais j’aurais bien besoin d’$gun assistant:une assistante;. Ça vous tente ?', 'Parler à Oculeth et téléportez-vous sur Azsuna', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44873, 'itIT', 'Oculeth ex machina', 'Parla con Oculeth e teletrasportati ad Azsuna.', 'Ho un piano per aiutare Shal\'aran.$b$bOra che non sono più distratta dalla sete di mana, ho avuto il tempo di studiare meglio le linee di faglia, rilevando un piccolo vortice di mana in quelle presenti ad Azsuna. Ho molta magia a mia disposizione, ma dobbiamo raggiungere la linea di faglia di persona per reindirizzare nel modo giusto le energie a Shal\'aran.$b$bOculeth ha gentilmente offerto il suo supporto, ma un assistente potrebbe farmi davvero comodo. Ti va di unirti a me?', 'Parla con Oculeth e teletrasportati ad Azsuna.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44873, 'koKR', '오큘레스와 기계의 혼', '오큘레스와 대화하고 아즈스나로 순간이동해야 합니다.', '샬아란의 모두에게 마나를 공급할 생각이 있어요.$b$b이제 제가 마나에 대한 굶주림을 느끼지 않아도 되니, 다른 지역의 지맥을 연구할 시간이 생겼거든요. 그런데 아즈스나의 지맥에서 마나의 회오리를 감지했어요. 제가 쓸 수 있는 마법은 꽤 많지만, 그 마력을 적절히 샬아란으로 유도하려면 직접 그곳을 찾아가는 수밖에 없죠.$b$b오큘레스가 그곳으로 순간이동시켜주겠다고 했는데, 절 도와주실 분이 필요합니다. 함께 가시겠어요?', '오큘레스와 대화하고 아즈스나로 순간이동해야 합니다.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44873, 'ptBR', 'Oculeth Ex Machina', 'Fale com Oculeth e teleporte-se para Azsuna.', 'Acho que sei como alimentar Shal\'Aran.$b$bAgora que não sinto mais sede de mana, pude me dedicar ao estudo das linhas do meridiano. Detectei um turbilhão de mana nas linhas de Azsuna. Eu tenho muita magia ao meu dispor, mas temos que ir até lá pessoalmente para redirecionar a energia para Shal\'Aran.$b$bOculeth se ofereceu para me teleportar até o local, mas acho que vou precisar de $Gum:uma; ajudante. Poderia me acompanhar?', 'Fale com Oculeth e teleporte-se para Azsuna.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44873, 'ruRU', 'Окулет из машины', 'Поговорите с Окулетом и телепортируйтесь в Азсуну.', 'Я знаю, как накормить Шал\'Аран.$b$bТеперь я не испытываю жажды маны – а значит, имею больше времени, чтобы исследовать силовые линии. В силовых линиях Азсуны я обнаружила небольшое завихрение маны. С магией у меня проблем не будет, но мы должны лично отправиться к силовой линии, чтобы перенаправить ее энергию в Шал\'Аран.$b$bОкулет любезно предложил телепортировать нас туда, однако мне пригодится помощь. Присоединишься ко мне?', 'Поговорите с Окулетом и телепортируйтесь в Азсуну.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44873, 'zhCN', '救星欧库勒斯', '与欧库勒斯谈一谈，然后传送到阿苏纳。', '我有办法养活沙尔艾兰的难民。$b$b现在我已经不再缺少魔力，就有更多时间来研究其他地区的魔网了。我探测出阿苏纳的魔网有处魔法涡流。我虽然掌握着不少魔法，但我们还得亲自去看看那里的魔网，才能把能量转移到沙尔艾兰。$b$b欧库勒斯人很好，愿意把我们传送过去，可我还需要个帮手。你愿意跟我一起去吗？', '与欧库勒斯谈一谈，然后传送到阿苏纳。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44873, 'zhTW', '救星歐庫雷斯', '與歐庫雷斯交談並傳送到艾蘇納。', '我的計畫可以餵養整個沙亞蘭。$b$b自從我不再因為缺少法力而時時感到飢渴以後，就有餘裕去研究新地區的地脈。我偵測到艾蘇納的地脈有充沛的魔法。我知道該怎麼使用魔法，但得親自靠近地脈才能把能量正確地導向沙亞蘭。$b$b歐庫雷斯已經主動表示願意把我們傳送過去，但我需要助手。你願意來幫忙嗎？', '與歐庫雷斯交談並傳送到艾蘇納。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44874, 'deDE', 'Nichts für Leyen', 'Helft Arkanistin Valtrois beim Platzieren des Leylinienableiters.', 'Zurück zur eigentlichen Aufgabe!$b$bNehmt diese Leylinienableiter. Wir können den Fluss durch die Resonanzenergien richtig ausrichten, doch dafür müsst Ihr sie an ganz bestimmten Orten präzise platzieren. Ich zeige Euch wo.$b$bEure Freundin hat sich einverstanden erklärt, uns zu... \"überwachen\". Wir können nur hoffen, dass sie uns nicht in die Quere kommt und die Arbeit den Profis überlässt.', 'Helft Arkanistin Valtrois beim Platzieren des Leylinienableiters.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44874, 'esES', 'Increíbley', 'Ayuda a la arcanista Valtrois a colocar las espitas de línea Ley.', '¡Volvamos a lo nuestro!$b$bCoge estas espitas de línea Ley. Podemos usar las energías resonantes para redirigir la circulación como es debido, pero hay que colocarlas en lugares precisos. Te enseñaré dónde.$b$bTu amiga ha accedido a... \"supervisarnos\". Esperemos que no estorbe mientras los profesionales nos ocupamos de esto.', 'Ayuda a la arcanista Valtrois a colocar las espitas de línea Ley.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44874, 'esMX', 'Increíbley', 'Ayuda a la arcanista Valtrois a colocar los toques de Líneas Ley.', '¡Sigamos con el trabajo!$b$bToma estos toques de Líneas Ley. Podemos usar las energías resonantes para redirigir el flujo, pero debemos colocarlas en ubicaciones precisas. Te mostraré dónde.$b$bTu amiga aceptó \"supervisarnos\". Esperemos que se quede al margen mientras los profesionales se hacen cargo.', 'Ayuda a la arcanista Valtrois a colocar los toques de Líneas Ley.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44874, 'frFR', 'Tellurifiant', 'Aidez l’Arcaniste Valtrois à poser les Vannes de ligne tellurique.', 'Revenons à nos moutons !$b$bPrenez ces vannes de ligne tellurique. Nous allons utiliser les énergies résonnantes pour rediriger le flux, mais il faut pour cela poser les vannes à des endroits bien précis. Je vais vous montrer où.$b$bVotre amie a accepté de nous... « superviser ». J’espère seulement qu’elle aura le bon goût de rester en retrait pendant que les professionnels font leur travail.', 'Aider l’Arcaniste Valtrois à poser les Vannes de ligne tellurique', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44874, 'itIT', 'Stupefagliente!', 'Aiuta l\'Arcanista Valtrois a piazzare le Valvole delle Linee di Faglia.', 'Torniamo al lavoro!$b$bPrendi queste Valvole delle Linee di Faglia. Possiamo usare le energie risonanti per reindirizzare in modo corretto il flusso, ma devono essere posizionate nei punti giusti. Ti mostrerò dove.$b$bLa tua amica ha accettato di \"supervisionare\" il nostro lavoro. Speriamo solo che non si intrometta troppo mentre i professionisti sono al lavoro.', 'Aiuta l\'Arcanista Valtrois a piazzare le Valvole delle Linee di Faglia.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44874, 'koKR', '지맥이 놀라워', '비전술사 발트와를 도와 지맥 꼭지를 설치해야 합니다.', '이제 시급한 일을 해야죠!$b$b이 지맥 꼭지를 받아 주세요. 공명하는 마력을 사용하여 지맥 마력의 흐름을 바꿀 수는 있지만, 정확한 지점에 설치해야 해요. 어디인지는 제가 알려드릴게요.$b$b그대의 친구도 우릴... \"감독\"해 주겠다고 했어요. 전문가들이 제대로 일을 처리하는 동안, 그녀가 방해나 되지 않기를 빌어 보자고요.', '비전술사 발트와를 도와 지맥 꼭지를 설치해야 합니다.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44874, 'ptBR', 'Alinhamento meridional', 'Ajude a Arcanista Valtrois a posicionar os Drenos de Linha de Meridiano.', 'Vamos logo ao que interessa!$b$bPegue esses drenos de linha de meridiano. Podemos usar as energias ressonantes para redirecionar o fluxo, mas temos que colocá-los em pontos específicos. Venha, eu vou lhe mostrar.$b$bA sua amiga concordou em nos... \"supervisionar\". Espero que ela não atrapalhe o trabalho dos profissionais.', 'Ajude a Arcanista Valtrois a posicionar os Drenos de Linha de Meridiano.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44874, 'ruRU', 'Полная нелинейность', 'Помогите чародейке Вальтруа разместить отводы на силовой линии.', 'Но вернемся к более насущной проблеме.$b$bВозьми эти отводы. Мы используем энергию магического резонанса, чтобы перенаправить поток, но их необходимо разместить в четко определенных местах. Я покажу, где именно.$b$bТвоя союзница согласилась... \"присмотреть\" за нами. Остается надеяться, что она не будет путаться под ногами, пока профессионалы занимаются делом.', 'Помогите чародейке Вальтруа разместить отводы на силовой линии.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44874, 'zhCN', '魔网之力', '协助奥术师瓦尔托伊放置魔网共振器。', '言归正传！$b$b你拿着这些魔网共振器。我们可以使用共振能量准确地改变魔网流动的方向，但前提是共振器的摆放位置要精准。我会把位置指给你看。$b$b你的朋友同意来……“监督”我们。但愿我这个专业人士在干活的时候，她不要来添乱。', '协助奥术师瓦尔托伊放置魔网共振器。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44874, 'zhTW', '扭轉地脈', '協助秘法師薇兒塔放置地脈導流器。', '來吧，開始工作了！$b$b拿著這些地脈導流器。我們可以利用共鳴能量來引導脈流，但是必須精確地放在對的位置。我會跟你說要放在哪裡。$b$b你的朋友說會…「監督」這個過程。希望她不會在專家工作時干涉太多。', '協助秘法師薇兒塔放置地脈導流器。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44875, 'deDE', 'Alles im Fluss', 'Beschützt Arkanistin Valtrois, während sie das Ritual abschließt.', 'Es ist vollbracht, $n! Nur noch ein kleiner Schritt und Shal\'Aran wird so viel Mana erhalten, wie es nur brauchen kann.$b$bDurch den besseren Zugriff auf die Leylinie da unten kann ich das Energievorkommen so lösen, dass die Energie reibungslos durchfließt. Aufgrund des chaotischen Zustands dieser Leylinie werde ich all meine Konzentration benötigen, um die Energie umzulenken.$b$bIch muss Euch warnen, dass ich dadurch Aufmerksamkeit auf uns lenken werde. Ihr müsst mich beschützen, während ich die Prozedur beende.$b$bSagt mir Bescheid, wenn Ihr bereit seid.', 'Beschützt Arkanistin Valtrois, während sie das Ritual abschließt.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44875, 'esES', 'El arte de fluir', 'Protege a la arcanista Valtrois hasta que complete el ritual.', '¡Listo, $n! Tan solo un paso más y Shal\'Aran tendrá todo el maná que necesite.$b$bAhora que tengo un mejor acceso a la línea Ley inferior, podré soltar el pozo para que fluya. Dado el caótico estado de esta línea Ley, tendré que concentrarme al máximo para redirigir la energía.$b$bTe advierto que llamaremos la atención al hacerlo, así que tendrás que protegerme hasta que complete el proceso.$b$bAvísame cuando estés $glisto:lista;.', 'Protege a la arcanista Valtrois hasta que complete el ritual.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44875, 'esMX', 'El arte fluido', 'Protege a la arcanista Valtrois mientras completa el ritual.', '¡Terminamos, $n! Ya solo nos queda un paso y Shal\'Aran tendrá el maná que necesita.$b$bAhora que tengo mejor acceso a la Línea Ley que hay bajo nosotros, puedo romper el embalse para que fluya como debe. Debido al caótico estado de esta Línea Ley, requerirá toda mi concentración para redirigir la energía.$b$bDebo advertirte que atraeremos mucha atención mientras lo hago, así que debes protegerme mientras completo el proceso.$b$bAvísame cuando quieras comenzar.', 'Protege a la arcanista Valtrois mientras completa el ritual.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44875, 'frFR', 'Maîtrise des flux', 'Protégez l’Arcaniste Valtrois pendant qu’elle accomplit le rituel.', 'Mission accomplie, $n ! Cela dit, il nous reste encore un détail à régler avant que Shal’Aran ne dispose de stocks de mana inépuisables.$b$bMaintenant que j’ai accès à la ligne tellurique sous-jacente, je vais pouvoir morceler le bassin pour permettre à l’énergie de circuler librement. Compte tenu de la nature chaotique de cette ligne, il va me falloir une bonne dose de concentration pour rediriger le flux.$b$bJ’aime autant vous prévenir, cette opération risque d’attirer l’attention. Vous devrez donc me protéger pendant que je m’attellerai à la tâche.$b$bPrévenez-moi quand vous serez $gprêt:prête;.', 'Protéger l’Arcaniste Valtrois pendant qu’elle accomplit le rituel', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44875, 'itIT', 'L\'arte dei flussi', 'Proteggi l\'Arcanista Valtrois mentre completa il rituale.', 'Tutto pronto, $n! Ci resta solo un\'altra cosa da fare, e poi Shal\'aran avrà accesso a tutto il mana di cui ha bisogno.$b$bOra che ho un accesso più diretto alla linea di faglia sottostante, posso rompere la sorgente e permettere che le energie fluiscano senza ostacoli. Considerando la situazione caotica in cui si trova la linea di faglia, dovrò usare tutta la mia concentrazione per reindirizzare l\'energia.$b$bNel farlo, però, attireremo sicuramente l\'attenzione di qualcuno, quindi avrò bisogno della tua protezione per completare il tutto.$b$bParla con me quando sarai $gpronto:pronta;.', 'Proteggi l\'Arcanista Valtrois mentre completa il rituale.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44875, 'koKR', '흐름의 미학', '비전술사 발트와가 의식을 수행하는 동안 보호해야 합니다.', '다 됐어요, $n 님! 이제 샬아란에 필요한 마나를 모두 공급하기까지 딱 한 가지만 남았다고요.$b$b이제 아래쪽의 지맥에 접근할 수 있으니, 마력의 웅덩이를 파괴해서 마력이 더 잘 흐르게 할 수도 있어요. 이 지맥은 지금 상태가 좀 엉망이라, 마력을 이전하려면 제 온 신경을 집중해야 할 것 같긴 해요.$b$b제가 의식을 치르는 동안 주위의 이목을 끌게 될 거예요. 제가 모든 과정을 마칠 때까지 그대가 보호해 주시면 좋겠어요.$b$b준비되면 말씀해 주세요.', '비전술사 발트와가 의식을 수행하는 동안 보호해야 합니다.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44875, 'ptBR', 'A arte da fluidez', 'Proteja a Arcanista Valtrois enquanto ela termina o ritual.', 'Tudo pronto, $n! Só mais uma etapa, e Shal\'Aran terá todo o mana de que precisa.$b$bAgora que tenho mais acesso à linha de meridiano, posso romper o reservatório para que a energia flua livremente. Dada a condição caótica dessa linha, precisarei de concentração total para redirecionar a energia.$b$bAviso logo que chamaremos atenção durante o ritual. Você terá que me proteger até que tudo esteja terminado.$b$bFale comigo quando estiver $Gpronto:pronta;.', 'Proteja a Arcanista Valtrois enquanto ela termina o ritual.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44875, 'ruRU', 'Искусство управления потоком', 'Защищайте чародейку Вальтруа, пока она проводит ритуал.', 'Все готово, $n! Остался всего один шаг, чтобы полностью обеспечить Шал\'Аран маной.$b$bТеперь у меня есть доступ к силовой линии, и я смогу устранить преграду, мешающую потоку энергии. Эта силовая линия сейчас в полном беспорядке, поэтому, мне нельзя отвлекаться, пока я буду направлять энергию.$b$bДолжна предупредить тебя, что наша операция не останется незамеченной. Тебе придется защищать меня, пока я занята этим сложным процессом.$b$bСкажи, когда будешь $gготов:готова;.', 'Защитите чародейку Вальтруа и дайте ей завершить ритуал.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44875, 'zhCN', '上善若水', '在奥术师瓦尔托伊举行仪式期间保护她。', '都搞定了，$n！只差最后一步，沙尔艾兰就可以尽情享用魔力了。$b$b现在，我对魔网的掌握能力变强了，可以震散这个池子，让魔网能量畅通无阻地流过。可这片魔网的状态十分混乱，我得全神贯注，才能让能量转向。$b$b我得警告你，这么做肯定闹出很大的动静。我进行仪式时，你得保护我。$b$b你准备好了就跟我说一声。', '在奥术师瓦尔托伊进行仪式期间保护她。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44875, 'zhTW', '流動的藝術', '在秘法師薇兒塔完成儀式時保護她的安全。', '完成了，$n！只剩下最後一個步驟，沙亞蘭就再也不會缺乏法力了。$b$b現在可以直探下方的地脈，我就能撥動遲滯的能量，讓它們正常流動。但由於這條地脈的狀況相當紊亂，我需要全神貫注才能導正它的能量。$b$b先警告你，我的儀式一定會吸引到某些東西的注意，你得好好保護我。$b$b要是你準備好了，就跟我說一聲。', '在秘法師薇兒塔完成儀式時保護她的安全。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44876, 'deDE', 'Ein Tanz mit Drachen', 'Sprecht mit Stellagosa über einen Flug zurück nach Shal\'Aran.', 'Ich bin sehr beeindruckt, $n.$b$bIhr und Eure Freundin konntet eine Leylinie instand setzen, obwohl Ihr noch nicht einmal Drachen seid!$b$bAber Ihr scheint davon erschöpft zu sein. Erlaubt mir, Euch nach Hause zu bringen.', 'Sprecht mit Stellagosa über einen Flug zurück nach Shal\'Aran.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44876, 'esES', 'Danza de dragones', 'Habla con Stellagosa para volver a Shal\'Aran.', 'Debo admitir que me has impresionado, $n.$b$bHabéis conseguido restablecer la energía de la línea Ley ¡y ni siquiera sois dragones!$b$bPero esta terrible experiencia parece haberos dejado al borde de la extenuación. Permitidme que os ayude a volver a vuestro hogar.', 'Habla con Stellagosa para volver a Shal\'Aran.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44876, 'esMX', 'Danza con dragones', 'Habla con Stellagosa para volver al Shal\'Aran.', 'Reconozco que me impresionas, $n.$b$b¡Tu amiga y tú pudieron reparar una Línea Ley y ni siquiera son dragones!$b$bParecen agotados, permítanme ayudarlos a regresar a casa.', 'Habla con Stellagosa para volver al Shal\'Aran.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44876, 'frFR', 'Danse avec les dragons', 'Parlez à Stellagosa pour regagner Shal’Aran.', 'Je dois avouer que je suis impressionnée, $n.$b$bVotre amie et vous avez réussi à réhabiliter cette ligne tellurique, une véritable prouesse pour qui n’est pas un dragon !$b$bVous m’avez l’air $gépuisés:épuisées;. Laissez-moi vous aider à rentrer.', 'Parler à Stellagosa pour regagner Shal’Aran', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44876, 'itIT', 'La danza dei draghi', 'Sali in groppa a Stellagosa per tornare a Shal\'aran.', 'Devo dire che sono impressionata, $n.$b$bTu e la tua amica siete $gstati:state; in grado di riparare una linea di faglia, e non siete nemmeno dei draghi!$b$bSembrate $gentrambi:entrambe; parecchio $gesausti:esauste; da quest\'impresa. Permettimi di riaccompagnarvi a casa.', 'Parla con Stellagosa per tornare a Shal\'aran.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44876, 'koKR', '용과의 춤', '스텔라고사와 대화하여 샬아란으로 돌아가야 합니다.', '솔직히 놀라웠다, $n.$b$b너와 네 친구가 지맥을 수리하다니. 너희는 용도 아닌데 말이야!$b$b둘 다 많이 지쳤겠구나. 내가 돌아가는 일을 도와 주겠다.', '스텔라고사와 대화하여 샬아란으로 돌아가야 합니다.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44876, 'ptBR', 'Dança com dragões', 'Fale com Stellagosa para voltar a Shal\'Aran.', 'Devo dizer que estou impressionada, $n.$b$bMesmo sem ser dragões, vocês conseguiram corrigir uma linha de meridiano!$b$bVocês me parecem exaustos. Permita-me levá-los de volta.', 'Fale com Stellagosa para voltar a Shal\'Aran.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44876, 'ruRU', 'Танец с драконами', 'Попросите Стеллагосу отнести вас в Шал\'Аран.', 'Если честно, ты меня приятно $gудивил:удивила;, $n.$b$bВам вдвоем удалось восстановить силовую линию, а ведь вы даже не драконы!$b$bКажется, такое испытание вас утомило. Позвольте помочь вам добраться домой.', 'Попросите Стеллагосу отнести вас в Шал\'Аран.', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44876, 'zhCN', '与龙共舞', '与丝黛拉苟萨交谈并飞回沙尔艾兰。', '老实说我很惊讶，$n。$b$b你和你的朋友都不是巨龙，居然也修复了一片魔网！$b$b你们看来也筋疲力竭了。我来送你们回家吧。', '与丝黛拉苟萨交谈并飞回沙尔艾兰。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44876, 'zhTW', '與龍共舞', '與斯黛拉苟莎交談，返回沙亞蘭。', '我不得不說，你真的讓我刮目相看，$n。$b$b你和你朋友都不是龍，可是卻有本事解決地脈的問題！$b$b你們兩個看起來都累了，請讓我送你們回去吧。', '與斯黛拉苟莎交談，返回沙亞蘭。', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44877, 'deDE', 'Angriff auf den Nistplatz', 'Tötet $1oa Invasoren der Legion.', 'Himmelsfürst Schattenhain hat lange Zeit die Verteidigung des Schreins angeführt, aber er scheint langsam die Hoffnung zu verlieren. Er braucht ganz einfach etwas Feuerschutz!$b$bLasst uns diese Dämonen ausschalten und den Angriff zurückwerfen. Sie werden eine tödliche Überraschung erleben!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44877, 'esES', 'Ataque al nido', 'Mata a $1oa invasores de la Legión.', 'El señor del cielo Frondagrís ha estado dirigiendo la defensa del santuario, pero creo que empieza a perder la esperanza. ¡Está claro que necesita el respaldo de una potencia de fuego mayor!$B$BEliminemos a esos demonios y rechacemos el ataque. ¡Ni lo verán venir!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44877, 'esMX', 'Ataque sobre el Nidal', 'Mata a $1oa invasores de la Legión.', 'El Señor del cielo Crecesombra es quien se encarga de la defensa del santuario, pero creo que está empezando a perder la esperanza. ¡Sin dudas necesita un poco más de apoyo!$b$bVamos a encargarnos de estos demonios y a frenar sus embates. ¡No se lo esperan!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44877, 'frFR', 'Attaque sur le perchoir', 'Tuez $1oa envahisseurs de la Légion.', 'Le seigneur du ciel Sylvebrume dirige la défense du sanctuaire, mais je crois qu’il commence à perdre espoir. Il a besoin de renforts musclés, et vite !$b$bAllons éliminer ces démons et repousser leur attaque. On peut les prendre par surprise !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44877, 'itIT', 'Attacco al nido', 'Uccidi $1oa invasori della Legione.', 'Il Signore dei Cieli Boscombroso ha guidato la difesa del sacrario, ma penso che stia cominciando a perdere la speranza. Ha sicuramente bisogno di supporto!$b$bLiberiamoci di questi demoni e respingiamo l\'attacco. Non ci vedranno nemmeno arrivare!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44877, 'koKR', '공격받는 보금자리', '군단 침략자를 $1oa명 처치해야 합니다.', '하늘군주 셰이드그로브가 제단의 방어를 이끌고 있습니다만, 어느새 희망을 잃기 시작한 것 같습니다. 그를 지원할 화력이 필요합니다!$b$b악마들을 쓰러트리고 공세를 막아내야 합니다. 놈들을 순식간에 없애버립시다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44877, 'ptBR', 'Ataque ao Galinheiro', 'Mate $1oa invasores da Legião.', 'O senhor celeste Bosquescuro está liderando a defesa do santuário, mas acho que está começando a perder as esperanças. Ele precisa de mais poder de fogo ao seu lado!$b$bVamos eliminar esses demônios e rechaçar o ataque. Eles nem vão saber o que os atingiu!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44877, 'ruRU', 'Нападение на гнездо', 'Убейте $1oa |4захватчика:захватчиков:захватчиков; из Легиона.', 'Повелитель небес Тенистая Роща руководит обороной святилища, но мне кажется, что у него начинают опускаться руки. Думаю, ему не помешает поддержка.$b$bРазберемся с этими демонами и отобьем атаку!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44877, 'zhCN', '进击的利爪', '消灭$1oa个军团入侵者。', '啸天者沙德格弗一直在指挥圣殿的防御，但是我觉得他正在丧失信心。显然他需要更多的火力支援！$b$b我们去把这些恶魔干掉，击溃他们的攻势，打他们个措手不及！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44877, 'zhTW', '直搗黃龍', '殺死$1oa個燃燒軍團入侵者。', '傲天者影林領軍堅守聖壇，但他可能也漸漸感到絕望了。顯然，他需要更多火力支援！$b$b讓我們來收拾這些惡魔，瓦解它們的攻勢。它們永遠料想不到！', '', '', '', '', '', '', 0);
