INSERT INTO `quest_template_locale` VALUES (44943, 'esMX', 'En garras del destino', '', '', '', 'El makrura más audaz junto al mar.', 'Garrasus', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44943, 'frFR', 'Pince-moi, je rêve !', '', '', '', 'Le plus insatiable des makruras marins.', 'Pinsatiable', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44943, 'itIT', 'Ma che roba!', '', '', '', 'Il Makrura più audace dei mari.', 'Chelishus', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44943, 'koKR', '끔찍한 발톱', '', '', '', '바닷가에서 가장 대담무쌍한 마크루라입니다.', '무쌍발톱', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44943, 'ptBR', 'Segurando uma garra pesada!', '', '', '', 'Os makruras mais audazes de toda a costa.', 'Garrastóteles', '', '', '', 0);
