INSERT INTO `quest_template_locale` VALUES (44943, 'zhCN', '克劳德舒斯的利爪', '', '', '', '海边最狂暴的龙虾人。', '克劳德舒斯', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44943, 'zhTW', '我要爪狂啦！', '', '', '', '海邊最威猛的龍蝦人。', '克勞戴休斯', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44944, 'deDE', 'Aura der Ungewissheit', 'Helft Khadgar, die Geheimnisse hinter den Fragmenten zu enträtseln.', 'Noch kennen wir zwar nicht die Absichten der Legion, doch wenn wir Eure Funde untersucht haben, werden wir dahinterkommen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44944, 'esES', 'Aura de incertidumbre', 'Ayuda a Khadgar a desentrañar el misterio que esconden estos trozos.', 'Puede que aún no sepamos qué trama la Legión, pero, tras analizar tus hallazgos, no tardaremos en averiguarlo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44944, 'esMX', 'Aura de incertidumbre', 'Ayuda a Khadgar a resolver el misterio de los fragmentos.', 'No conocemos aún las intenciones de la Legión, pero, tras analizar tus hallazgos, pronto lo haremos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44944, 'frFR', 'Une aura d’incertitude', 'Aidez Khadgar à percer le mystère des fragments.', 'Certes, nous ignorons encore ce que trame la Légion, mais nous ne devrions pas tarder à le découvrir en analysant vos découvertes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44944, 'itIT', 'Aura di incertezza', 'Aiuta Khadgar a svelare il mistero dietro ai frammenti.', 'Forse non conosciamo ancora le intenzioni della Legione, ma dopo aver analizzato ciò che hai trovato le scopriremo presto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44944, 'koKR', '불확실성의 오라', '카드가를 도와 파편 이면의 수수께끼를 풀어야 합니다.', '아직 군단의 의도가 뭔지는 모르지만, 당신이 찾아낸 것들을 분석하면 곧 알게 될 거요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44944, 'ptBR', 'Aura de incerteza', 'Ajude Hadggar a desvendar o mistério por trás dos fragmentos.', 'Podemos não saber os planos da Legião ainda, mas isso vai mudar assim que analisarmos as suas descobertas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44944, 'ruRU', 'Аура неизвестности', 'Помогите Кадгару раскрыть тайну фрагментов.', 'Мы пока не знаем, что замышляет Легион, но благодаря твоим находкам быстро во всем разберемся.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44944, 'zhCN', '云山雾罩', '帮助卡德加解开碎片隐藏的秘密。', '虽然我们目前还不知道军团的意图，但在分析过你的发现后，应该就能水落石出了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44944, 'zhTW', '狐疑氛圍', '協助卡德加解開碎片隱藏的秘密。', '我們或許目前還不知道燃燒軍團的意圖，但是只要分析你的發現，應該很快就能得知。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44948, 'deDE', 'Schneefederaufzucht', 'Pflegt Eurer Schneefederjunges bis zum Erreichen von Stufe 25.', '<Euer Schneefederjunges sieht Euch ängstlich an. Es verlässt sich wohl auf Eure Hilfe, um zu überleben und groß zu werden.>', '', 'Krächz!', 'Schneefederjunges', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44948, 'esES', 'Criar a tu Plumanívea', 'Cría a tu prole de Plumanívea hasta el nivel 25.', '<Tu prole de Plumanívea te mira con timidez. Parece esperar que la ayudes a sobrevivir y crecer.>', '', '¡Erk!', 'Prole de Plumanívea', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44948, 'esMX', 'Crianza del Plumanívea', 'Lleva a tu prole Plumanívea al nivel 25.', '<Tu prole Plumanívea te mira con timidez. Parece querer tu ayuda para sobrevivir y crecer.>', '', '¡Erk!', 'Prole Plumanívea', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44948, 'frFR', 'Élever votre plume-neige', 'Faites progresser votre Nouveau-né plume-neige jusqu’au niveau 25.', '<Votre nouveau-né plume-neige vous regarde timidement. On dirait que vous allez devoir l’aider à survivre et grandir.>', '', 'Erk !', 'Nouveau-né plume-neige', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44948, 'itIT', 'Far crescere il Piumanevosa', 'Porta il tuo Cucciolo di Piumanevosa al livello 25.', '<Il tuo Cucciolo di Piumanevosa ti guarda timidamente. Sembra che voglia che lo aiuti a sopravvivere e crescere.>', '', 'Erk!', 'Cucciolo di Piumanevosa', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44948, 'koKR', '서리깃 팔코사우루스 키우기', '새끼 서리깃 팔코사우루스를 25 레벨까지 키워야 합니다.', '<조심스레 당신을 쳐다보는 새끼 서리깃 팔코사우루스. 자기가 무사히 성장할 수 있게 도와 달라는 눈빛입니다.>', '', '아록!', '새끼 서리깃 팔코사우루스', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44948, 'ptBR', 'A criação do Pena Nevada', 'Crie o Filhote de Pena Nevada até ele atingir o nível 25.', '<O filhote de Pena Nevada olha para você com timidez. Parece que espera a sua ajuda para crescer e sobreviver.>', '', 'Erk!', 'Filhote de Pena Nevada', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44948, 'ruRU', 'Воспитание снегоспин', 'Повысьте уровень вашего детеныша снегоспина до 25-го.', '<Ваш детеныш снегоспина робко смотрит на вас. Похоже, он надеется, что вы защитите его и поможете ему вырасти большим и сильным.>', '', 'Чирик!', 'Детеныш снегоспина', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44948, 'zhCN', '雪羽雏龙饲养员', '把你的雪羽雏龙提升到25级。', '<你的雪羽雏龙怯生生地望向你，似乎希望你能帮助它活下来，并健康长大。>', '', '啾！', '雪羽雏龙', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44948, 'zhTW', '培育你的雪羽小龍隼', '將你的雪羽小龍隼提升到等級25。', '<你的雪羽小龍隼畏縮地看著你。看樣子你要負責撫育牠長大了。>', '', '咿！', '雪羽小龍隼', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44949, 'deDE', 'Der Geruch von Menschen', 'Bringt Euer Schneefederjunges zur Güldenen Rose in Sturmwind.', '<Ihr habt bemerkt, dass Euer Schneefederjunges immer, wenn Menschen in der Nähe sind, zittert. Anscheinend hat es große Angst vor ihnen. Vielleicht solltet Ihr es irgendwo hinbringen, wo es eine Menge Menschen gibt, damit es sich an den Geruch gewöhnen kann.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44949, 'esES', 'El olor de los humanos', 'Lleva a tu prole de Plumanívea a La Rosa Áurea, en Ventormenta.', '<Has notado que tu prole de Plumanívea tiembla cuando hay humanos cerca. Parece tenerles autentico pánico. Quizás podrías llevarla a algún lugar donde haya muchos, para que se acostumbre al olor.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44949, 'esMX', 'El olor a humano', 'Lleva a tu prole Plumanívea a La Rosa Áurea en Ventormenta.', '<Notas que tu prole Plumanívea se agita cuando hay humanos cerca. Parece que se siente aterrorizada. Tal vez puedas llevarla a un sitio con muchos humanos para que se acostumbre al olor.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44949, 'frFR', 'L’odeur des Humains', 'Amenez votre Nouveau-né plume-neige à la Rose dorée, à Hurlevent.', '<Vous avez remarqué que votre nouveau-né plume-neige est pris de tremblements chaque fois qu’il aperçoit des Humains. Leur seule vue semble le terrifier. Peut-être serait-il bon de le mettre en présence d’un grand nombre d’Humains afin qu’il s’habitue à leur odeur ?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44949, 'itIT', 'L\'odore degli Umani', 'Porta il tuo Cucciolo di Piumanevosa alla Rosa Dorata nella città di Roccavento.', '<Hai notato che il tuo Cucciolo di Piumanevosa trema quando ci sono degli Umani nelle vicinanze. Sembra che ne sia terrorizzato. Forse potresti portarlo in un posto pieno di Umani, in modo che si abitui al loro odore.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44949, 'koKR', '인간의 냄새', '새끼 서리깃 팔코사우루스를 스톰윈드에 있는 황금 장미 여관으로 데려가야 합니다.', '<당신은 인간이 주위에 다가올 때마다 새끼 서리깃 팔코사우루스가 몸을 떠는 걸 알아챘습니다. 인간이 엄청 무서운가 봅니다. 새끼를 인간이 많은 곳으로 데려가 보는 건 어떨까요? 냄새에 익숙해지도록 말이죠...>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44949, 'ptBR', 'O cheiro dos humanos', 'Leve o Filhote de Pena Nevada à Rosa Dourada, que fica em Ventobravo.', '<Você repara que o filhote de Pena Nevada treme sempre que tem gente por perto. Parece que ele tem pavor de gente. De repente, se você levá-lo a um lugar muito movimentado, ele acaba se acostumando com o cheiro de pessoas.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44949, 'ruRU', 'Запах людей', 'Отведите вашего детеныша снегоспина в \"Позолоченную розу\" в Штормграде.', '<Вы обратили внимание, что ваш детеныш снегоспина начинает дрожать от страха всякий раз, когда поблизости оказываются люди. Похоже, он их до жути боится. Вероятно, стоит отвести его куда-нибудь, где полно людей, чтобы он к ним попривык.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44949, 'zhCN', '人类的气味', '带着你的雪羽雏龙前往暴风城的镶金玫瑰旅店。', '<你发现只要有人类靠近，这只雪羽雏龙就会瑟瑟发抖。看来它真的很怕人类。或许你能把它带到人类聚集的地方，让它习惯这种气味。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44949, 'zhTW', '人類的氣味', '帶你的雪羽小龍隼到暴風城的鑲金玫瑰。', '<你發現雪羽小龍隼只要附近有人類就會顫抖。看起來是嚇壞了。或許帶牠到人類密集的地方，可以讓牠習慣人類的氣味。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44950, 'deDE', 'Jagdlektion: Nordfalkeneulen', 'Lasst Euer Schneefederjunges 3 Nordfalkeneulen am Hochberg bezwingen.', '<Euer Schneefederjunges scheint ganz aufgeregt zu sein, wenn Ihr Euch anderen Kleintieren nähert. Dies gilt insbesondere für die Falkeneulen vom Hochberg. Es plustert sein Gefieder auf und krächzt laut, sobald eine in der Nähe ist. Anscheinend verspürt Euer Junges einen angeborenen Jagdinstinkt.> \n\n<Ihr müsst wohl mit Eurem Falkosaurier auf die Jagd gehen.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44950, 'esES', 'Lección de caza: lechuzas gavilanas del norte', 'Derrota a 3 lechuzas gavilanas del norte en Monte Alto con tu prole de Plumanívea.', '<Tu prole de Plumanívea parece alterarse cuando estáis cerca de otras criaturas, sobre todo si son lechuzas gavilanas de Monte Alto. Se ahueca el plumaje y grazna con fuerza cuando hay una cerca. Es como una necesidad depredadora primaria.>\n\n<Parece que tendrás que sacar a tu falcosaurio de caza.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44950, 'esMX', 'Lección de cacería: lechuzas gavilanas del norte', 'Derrota a 3 lechuzas gavilanas del norte en Altamontaña con tu prole Plumanívea.', '<Tu prole Plumanívea luce agitada cuando estás cerca de otras alimañas, especialmente las lechuzas gavilanas de Altamontaña. Hincha el plumaje y chilla con fuerza cuando hay una cerca. Parece sentir una necesidad primaria de depredación.> \n\n<Todo indica que vas a tener que sacar tu falcosaurio de cacería.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44950, 'frFR', 'Leçon de chasse : épervières boréales', 'Terrassez 3 Épervières boréales à Haut-Roc avec votre Nouveau-né plume-neige.', '<Votre nouveau-né plume-neige semble avoir du mal à se contrôler en présence d’autres bestioles, en particulier lorsqu’il s’agit d’épervières boréales de Haut-Roc. Il hérisse son plumage et pousse de grands cris chaque fois qu’il aperçoit l’une de ces créatures, comme si son instinct de prédateur reprenait le dessus.>\n\n<À l’évidence, il va vous falloir emmener votre falcosaure à la chasse.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44950, 'itIT', 'Lezione di caccia: Gufi Reali del Nord', 'Sconfiggi 3 Gufi Reali del Nord ad Alto Monte con il tuo Cucciolo di Piumanevosa.', '<Il tuo Cucciolo di Piumanevosa sembra agitarsi in prossimità di altri animali, specialmente quando si trova vicino ai Gufi Reali di Alto Monte. Gli si gonfiano le piume e comincia a gracchiare ogni volta che uno di essi è nelle vicinanze. Sembra avere un istinto primordiale che lo porta a cacciarli.>\n\n<Dovrai portarlo a caccia.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44950, 'koKR', '사냥 수업: 북쪽 매올빼미', '새끼 서리깃 팔코사우루스와 함께 높은산에 있는 북쪽 매올빼미 3마리를 꺾어야 합니다.', '<당신이 다른 생물체, 특히 높은산의 매올빼미에 가까이 갈 때마다 새끼 서리깃 팔코사우루스가 흥분하는 것 같습니다. 주위에 매올빼미가 있으면 항상 깃털을 바짝 세우고 깍깍 웁니다. 포식 본능을 갖고 있는 모양입니다.>\n\n<새끼 팔코사우루스를 데리고 사냥을 해야 할 것 같습니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44950, 'ptBR', 'Lição de caça: Falcorujas do Norte', 'Derrote 3 Falcorujas do Norte na Alta Montanha com o seu Filhote de Pena Nevada.', '<O Filhote de Pena Nevada fica agitado quando você está perto de outros bichos, principalmente das falcorujas da Alta Montanha. Ele eriça as plumas e guincha alto sempre que tem algum por perto. Deve ser o instinto primitivo de predador.>\n\n<Pelo jeito, você vai ter que levar o seu falcossauro para caçar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44950, 'ruRU', 'Уроки охоты: северные ястребиные совы', 'Победите трех северных ястребиных сов в Крутогорье с вашим детенышем снегоспина.', '<Ваш детеныш снегоспина явно возбуждается, когда поблизости оказываются другие зверьки, в особенности северные ястребиные совы Крутогорья. Он взъерошивает перья и начинает громко чирикать всякий раз, когда где-то поблизости оказывается ястребиная сова. Судя по всему, в нем проявляется инстинкт хищника.> \n\n<Похоже, вам придется вывести вашего соколозавра на охоту.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44950, 'zhCN', '狩猎课：北地鹰喙猫头鹰', '在至高岭用你的雪羽雏龙击败3只北地鹰喙猫头鹰。', '<你一走近其他小动物，尤其是至高岭的鹰喙猫头鹰，你的雪羽雏龙就会激动起来。它会竖起羽毛，大声嘶鸣。看来它已经展露出原始的掠食本能。>\n\n<看来你得把自己的隼龙带出去狩猎了。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44950, 'zhTW', '狩獵訓練：北地猛鴞', '派雪羽小龍隼在高嶺擊敗3隻北地猛鴞。', '<你的雪羽小龍隼在你靠近其他動物時就開始激動，看到高嶺的北地猛鴞時尤其如此。有猛鴞在附近時小龍隼就會豎起身上的羽毛，嘎嘎大叫。看來這是牠的掠食本能。> \n\n<看來你得帶龍隼出去狩獵了。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44952, 'deDE', 'Schwarzfelstiefen: Juwel der Tiefen', 'Begebt Euch in die Schwarzfelstiefen und beschafft Euch einen Kern von Bael\'Gar, einen Kern von Roccor, eine Essenz von Incendius und eine Essenz von Magmus. Sobald Ihr alle vier Materialien zusammen habt, müsst Ihr sie in der Schwarze Schmiede am tiefsten Punkt der Schwarzfelstiefen in der Nähe des Eingangs zum Geschmolzenen Kern miteinander kombinieren. Bringt dann den Schwarzfelsrubin zu Wixxrak in der Ewigen Warte.', 'Willkommen in der Ewigen Warte. Habt Ihr vielleicht Interesse an einer neuen Waffe?$b$bIhr müsst wissen, ich verkaufe hier in meinem bescheidenen Laden nicht nur Gliedmaßenvernichtungswaffen. Auch mit Juwelen kenne ich mich aus.$b$bVielleicht können wir beide dahingehend ja ins Geschäft kommen, denn es gibt da eine Art Edelstein, auf die ich schon lange ein Auge geworfen habe. Ich habe ein paar außergewöhnliche Schmiedepläne in meinem Sortiment, die Euch vielleicht interessieren und die ich Euch als Gegenleistung für Eure... äh... Hilfe anbieten könnte.$b$bNun zu den Einzelheiten...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44952, 'esES', 'Profundidades de Roca Negra: Joya de las profundidades', 'Ve a Profundidades de Roca Negra y obtén un núcleo de Bael\'Gar, un núcleo de Roccor, una esencia de Incendius y una esencia de Magmus. Cuando tengas los cuatro objetos, combínalos en La Forja Negra, cerca de la entrada a Núcleo de Magma, en el fondo de Profundidades de Roca Negra, y llévale el rubí Roca Negra a Wixxrak en Vista Eterna.', 'Te doy la bienvenida a Vista Eterna. ¿Te interesa un arma nueva?$b$bBueno, no solo me dedico a vender armas de desmembramiento masivo en la tienda. También me gustan las joyas poco comunes.$b$bIgual podemos llegar a un acuerdo para encontrar una joya a la que le tengo echado el ojo desde hace tiempo. Tengo planos de herrería bastante valiosos en caso de que te interesen a cambio de tus servicios.$b$bPasemos a los detalles...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44952, 'esMX', 'Profundidades de Roca Negra: Joya de las profundidades', 'Viaja a las Profundidades de Roca Negra y consigue un núcleo de Bael\'Gar, un núcleo de Roccor, una esencia de Incendius y una esencia de Magmus. Una vez que tengas esos cuatro objetos, combínalos en la Forja Negra cerca de la entrada del Núcleo de Magma, al fondo de las Profundidades de Roca Negra, y llévale el rubí de Roca Negra a Wixxrak en Vista Eterna.', '$gBienvenido:Bienvenida; a Vista Eterna. ¿Puedo ofrecerte un arma nueva?$b$b¿Sabes? No solo vendo armas de desmembramiento masivo aquí en la tienda. También me gustan las joyas raras.$b$bQuizás podamos llegar a algún tipo de acuerdo para conseguir cierta joya que hace mucho que tengo en la mira. A cambio, te puedo ofrecer algunos diseños de herrería bastante buenos que podrían interesarte.$b$bAhora, pasemos a los detalles...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44952, 'frFR', 'Profondeurs de Rochenoire : le joyau des profondeurs', 'Descendez dans les Profondeurs de Rochenoire et obtenez un Noyau de Bael’gar, un Noyau de Roccor, une Essence d’Incendius et une Essence de Magmus. Ceci fait, combinez les quatre objets à la Forge noire, près de l’entrée du Cœur du Magma, au fond des Profondeurs de Rochenoire. Enfin, rapportez le Rubis de Rochenoire à Wixxrak, à Long-Guet.', 'Bienvenue à Long-Guet. Puis-je vous aiguiller sur le choix d’une nouvelle arme ?$b$bMais vous savez, je ne fais pas que vendre des armes de démembrement massif, dans ce magasin. Je fais aussi dans le joyau d’exception.$b$bPeut-être que vous et moi, on pourrait s’entendre. Ça fait longtemps que j’ai des vues sur certain caillou. Par ailleurs, j’ai de magnifiques plans de forge qui pourraient vous intéresser, vous savez, si vous me rapportez la pierre.$b$bBon, venons-en aux détails…', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44952, 'itIT', 'Sotterranei di Roccianera: Gioiello delle profondità', 'Raggiungi i Sotterranei di Roccianera e ottieni il Nucleo di Bael\'gar, il Nucleo di Roccor, l\'Essenza di Incendius e l\'Essenza di Magmus. Una volta ottenuti questi oggetti, combinali assieme utilizzando la Forgia Nera, che si trova presso l\'ingresso del Nucleo Ardente nelle profondità dei Sotterranei di Roccianera, quindi porta il Rubino di Roccianera a Wixxrak nel villaggio di Lungavista.', '$gBenvenuto:Benvenuta; a Lungavista. Ti potrebbe interessare una nuova arma?$b$bSai, non mi limito a vendere armi di smembramento di massa nel mio negozio. Ho anche una passione per i gioielli.$b$bForse potresti darmi una mano a recuperarne uno su cui ho messo gli occhi da molto tempo. Ho degli ottimi progetti di forgiatura che potrebbero interessarti, e sarei felice di condividerli in cambio dei tuoi servigi.$b$bOra, ecco i dettagli...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44952, 'koKR', '검은바위 나락: 나락의 보석', '검은바위 나락으로 가서 밸가르의 핵, 록코르의 핵, 인센디우스의 정수, 마그무스의 정수를 획득해야 합니다. 네 가지 아이템을 모두 획득한 후에는, 검은바위 나락 아래쪽의 화산 심장부 입구 근처에 있는 검은 제련소에서 이들을 모두 결합하고, 그렇게 만든 검은바위 루비를 눈망루 마을에 있는 윅스락에게 가져가야 합니다.', '눈망루 마을에 잘 왔어. 내가 새로운 무기 얘기 좀 해 줄까?$b$b알고 있겠지만, 난 여기 상점에서 대량 살상 무기만 파는 게 아니야. 희귀한 보석들도 함께 팔고 있다고.$b$b내가 꽤 오랫동안 눈독을 들이던 보석이 있는데, 그걸 조달하는 문제에 대해 얘기해 보자고. 내게 아주 괜찮은 대장기술 도면이 있으니, 자네가 힘을 써 주면 그에 대한 보답을 해 주지.$b$b자, 이제 자세한 얘기를 해 볼까...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44952, 'ptBR', 'Abismo Rocha Negra: joia das profundezas', 'Vá até o Abismo Rocha Negra e recolha um Núcleo de Bael\'gar, um Núcleo de Pétror, uma Essência de Incendius e uma Essência de Magmus. Uma vez em posse dos quatro itens, junte-os na Forja Negra, perto da entrada do Núcleo Derretido no fundo do Abismo Rocha Negra, e entregue o Rubi da Rocha Negra a Wixxrak em Visteterna.', 'Bem-$gvindo:vinda; a Visteterna. Você teria interesse em uma arma nova?$b$bSabe, eu não vendo só ferramentas de fazer chacina aqui na loja. Também gosto de joias raras.$b$bQuem sabe nós não fechamos um acordo, estou de olho em uma joia faz tempo. Eu tenho um punhado de instruções de ferraria interessantes que posso lhe passar em troca dos seus serviços, sabe?$b$bVamos discutir o assunto...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44952, 'ruRU', 'Глубины Черной горы: самоцвет Глубин', 'Отправляйтесь в Глубины Черной горы и раздобудьте сердце Бейл\'Гора, сердце Роккора, сущность Опалителя и сущность Магмуса. Собрав все четыре предмета, объедините их в Черной Кузне у входа в Огненные недра на самом дне Глубин Черной горы, после чего отнесите рубин Черной горы Виззраку в Круговзор.', 'Добро пожаловать в Круговзор. Быть может, я смогу заинтересовать тебя каким-то новым оружием?$b$bЯ-то вообще не только оружием массового расчленения приторговываю, а еще очень интересуюсь редкими драгоценными камнями.$b$bИ вот если бы ты $gпомог:помогла; мне раздобыть один камушек, на который я давно уже глаз положил, я бы променял тебе на него один очень ценный кузнечный чертеж – в благодарность за услугу, так сказать.$b$bТеперь к делу...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44952, 'zhCN', '黑石深渊：深渊宝石', '前往黑石深渊，获取贝尔加的核心，洛考尔的核心，伊森迪奥斯的精华，玛格姆斯的精华。获得全部四件物品后，在黑石深渊底部靠近熔火之心入口的黑熔炉处，将它们融合在一起，然后把黑岩红宝石带回永望镇交给维撒克。', '欢迎来到永望镇。要看看我们的新式武器吗？$b$b你瞧，我的店不止是出售大规模杀伤性武器那么简单。我也热爱珍贵的宝石。$b$b有一颗宝石我已经眼馋了很久了，也许你我可以达成某种珠宝采购协议。我这里有一些上佳的锻造设计图，可以作为你辛劳工作的报酬。$b$b现在，我们来说说细节……', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44952, 'zhTW', '玄鴉堡：深淵之珍', '前往黑石深淵並取得貝爾加之心、洛考爾之心、伊森迪奧斯的精華和瑪格姆斯的精華。取得以上四樣東西以後，到黑石深淵底部、熔火之心入口附近的黑熔爐將它們合而為一，並將合成的這顆黑石紅晶交給永望鎮的維撒克。', '歡迎來到永望鎮。不知道你對新武器有沒有興趣呢？$b$b你也知道，我在這間店裡不光是想賣能讓敵人死無全屍的武器，我也想賣稀有的珠寶。$b$b或許我們能想辦法拿到我一直很想要的珠寶。我有一些很棒的鍛造設計圖，你可能會有興趣，這就當作是給你的報酬。$b$b現在，我們來聊聊細節…', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44953, 'deDE', 'Verbündete am Hochberg', 'Schließt 5 Weltquests am Hochberg in Begleitung Eures Schneefederjungen ab.', '<Euer Schneefederjunges gibt ein tiefes, zwitscherndes Grummeln von sich, wenn Ihr Donnertotem dicht passiert.> \n\n<Die Hochbergstämme versuchten sofort, die Falkosaurier aus ihrem Territorium zu vertreiben. Euer Junges muss sie für eine Bedrohung für den Schwarm halten, und das Grummeln ist wahrscheinlich ein Versuch, Euch zu beschützen.>\n\n<Zeigt Eurem Falkosaurier, dass Ihr gemeinsam mit den Hochbergstämmen zusammenarbeiten könnt.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44953, 'esES', 'Aliados en Monte Alto', 'Completa 5 misiones del mundo en Monte Alto con tu prole de Plumanívea fuera.', '<Tu prole de Plumanívea castañetea y emite un gruñido profundo cuando pasáis cerca de Tótem del Trueno.>\n\n<Las tribus Monte Alto se pusieron a desalojar enseguida a los falcosaurios de su territorio. Debe de considerarlas una amenaza para la bandada y el gruñido es, probablemente, su forma de protegerte.>\n\n<Demuéstrale a tu falcosaurio que podéis trabajar junto a las tribus Monte Alto.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44953, 'esMX', 'Aliados en Altamontaña', 'Completa 5 misiones de mundo en Altamontaña con tu prole Plumanívea presente.', '<Tu prole Plumanívea emite un gruñido profundo y delator cuando pasas junto al Tótem del Trueno.> \n\n<Las tribus de Altamontaña intentaron de inmediato expulsar a los falcosaurios de su territorio. Debe de considerarlos una amenaza para la bandada y el gruñido puede ser su intento de protegerte.>\n\n<Muestra a tu falcosaurio que los dos pueden colaborar con las tribus de Altamontaña.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44953, 'frFR', 'Des alliés à Haut-Roc', 'Accomplissez 5 expéditions à Haut-Roc en vous faisant accompagner de votre Nouveau-né plume-neige.', '<Votre nouveau-né plume-neige pousse un grognement sourd quand vous passez près de Totem-du-Tonnerre.>\n\n<Les tribus de Haut-Roc ont immédiatement cherché à éradiquer les falcosaures dès leur apparition sur leur territoire. Il doit donc les considérer comme une menace et cherche sans doute à vous protéger en grognant.>\n\n<Montrez-lui qu’il est possible de collaborer avec les tribus de Haut-Roc.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44953, 'itIT', 'Alleati ad Alto Monte', 'Completa 5 missioni mondiali ad Alto Monte con il Cucciolo di Piumanevosa al tuo fianco.', '<Il tuo Cucciolo di Piumanevosa emette uno strano verso quando passi nei dintorni di Totem del Fulmine.>\n\n<Le tribù di Alto Monte hanno cominciato ad allontanare i falcosauri dal loro territorio. Probabilmente il tuo cucciolo li considera una minaccia e il verso che ha emesso è il suo modo di proteggerti.>\n\n<Mostragli che tu e lui potete lavorare assieme alle tribù di Alto Monte.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44953, 'koKR', '높은산의 친구들', '새끼 서리깃 팔코사우루스와 함께 높은산에서 전역 퀘스트 5개를 완료해야 합니다.', '<당신이 천둥 토템 주위를 지날 때마다 새끼 서리깃 팔코사우루스가 낮게 으르렁거립니다.>\n\n<높은산 부족은 팔코사우루스가 자신들의 영역을 침범하자 바로 내쫓으려 한 바 있었습니다. 새끼가 높은산 부족을 자기 종족의 적으로 간주하고, 당신을 구하려고 으르렁댔나 봅니다.>\n\n<팔코사우루스에게 높은산 부족과 조화롭게 지낼 수 있음을 보여 주십시오.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44953, 'ptBR', 'Aliados na Alta Montanha', 'Conclua 5 Missões Mundiais em Alta Montanha com o Filhote de Pena Nevada.', '<O Filhote de Pena Nevada solta um grunhido gutural quando você passa perto do Totem do Trovão.>\n\n<As Tribos da Alta Montanha expulsaram os falcossauros do seu território. O filhote deve considerá-las uma ameaça ao bando, e o grunhido deve ser uma forma de tentar proteger você.>\n\n<Mostre ao falcossauro que as Tribos da Alta Montanha estão do lado de vocês dois.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44953, 'ruRU', 'Союзники в Крутогорье', 'Выполните пять локальных заданий в Крутогорье со своим детенышем снегоспина.', '<Ваш детеныш снегоспина издает утробный рык каждый раз, когда вы с ним оказываетесь неподалеку от Громового Тотема.> \n\n<Племена Крутогорья активно гоняют соколозавров со своей территории. Очевидно, детеныш считает их угрозой для своей стаи и своим рыком пытается защитить вас.>\n\n<Продемонстрируйте вашему соколозавру, что с племенами Крутогорья вполне можно сосуществовать мирно.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44953, 'zhCN', '至高岭的盟友', '带着你的雪羽雏龙在至高岭完成5个世界任务。', '<你经过雷霆图腾时，雪羽雏龙发出一声深沉的咆哮。>\n\n<高岭诸族立刻开始驱赶隼龙，想将它赶出他们的地盘。你的雏龙可能是把他们当成了威胁种群的坏人，那声咆哮或许是为了保护你。>\n\n<用行动向你的隼龙证明，它可以跟高岭诸族和谐相处。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44953, 'zhTW', '高嶺的盟友', '帶著你的雪羽小龍隼在高嶺完成5個世界任務。', '<在你靠近雷霆圖騰時，你的雪羽小龍隼發出一聲低沉的吼叫。> \n\n<高嶺部族毫不遲疑地開始驅逐小龍隼。對小龍隼而言，高嶺部族是個威脅，牠發出低吼聲可能是為了要保護你。>\n\n<讓你的龍隼明白，你們和高嶺部族可以和平共處。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44954, 'deDE', 'Team Schneefeder', 'Schließt 3 Haustierkampfweltquests auf den Verheerten Inseln mit Eurem Schneefederjungen ab.', '<Euer Schneefederjunges sieht Euch an und stößt eine Art krächzendes Bellen aus. Es scheint etwas zusammen mit Euch unternehmen zu wollen!>\n\n<Damit Euer Junges etwas lernt, muss es vor dem Beginn eines Kampfs an Eurer Seite und das erste Haustier sein, mit dem Ihr in den Kampf zieht, und darf nicht im Kampf sterben.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44954, 'esES', 'Equipo con Plumanívea', 'Completa 3 misiones del mundo de duelos de mascotas en las Islas Abruptas con tu prole de Plumanívea.', '<Tu prole de Plumanívea te mira y emite una especie de ladrido graznado. ¡Parece que quiere hacer algo en equipo!>\n\n<Para aprender, tu prole debe estar a tu lado antes de que empieces un combate, debe ser tu primera mascota en entrar en combate y no puede morir en la refriega.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44954, 'esMX', 'Equipo Plumanívea', 'Completa 3 misiones de mundo de duelos de mascotas en las Islas Quebradas con tu Prole plumanívea.', '<Tu prole plumanívea te mira y lanza una especie de graznido ronco. ¡Es como si quisiera que hicieran algo juntos!>\n\n<Para aprender, tu prole tiene que estar a tu lado antes de que comience la batalla, tiene que ser la primera mascota en entrar a la lucha y no puede morir en la batalla.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44954, 'frFR', 'Au combat avec le plume-neige', 'Accomplissez 3 expéditions de combat de mascottes sur les îles Brisées avec votre Nouveau-né plume-neige.', '<Votre nouveau-né plume-neige vous regarde en poussant un cri proche de l’aboiement. Il semble avoir envie de faire quelque chose en équipe !>\n\n<Pour apprendre, votre nouveau-né doit être présent lorsque vous entamez un combat, doit être la première mascotte à combattre et ne doit pas mourir au cours de l’action.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44954, 'itIT', 'Fare squadra con il Piumanevosa', 'Completa 3 missioni mondiali degli scontri tra mascotte con il tuo Cucciolo di Piumanevosa sulle Isole Disperse.', '<Il tuo Cucciolo di Piumanevosa guarda verso di te e gracchia. Sembra voler fare qualcosa assieme a te!>\n\n<Per imparare, il tuo cucciolo dev\'essere al tuo fianco all\'inizio di uno scontro, dev\'essere il primo a entrare in battaglia e non deve morire.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44954, 'koKR', '서리깃 팀 결성', '새끼 서리깃 팔코사우루스와 함께 부서진 섬 애완동물 대전 전역 퀘스트 3개를 완료해야 합니다.', '<새끼 서리깃 팔코사우루스가 당신을 보며 깍깍 울어댑니다. 당신과 팀이 되어 무언가를 함께 해보고 싶나 봅니다!>\n\n<새끼 팔코사우루스를 가르치려면, 새끼가 전투 시작 전에 당신 곁에 있어야 하고, 가장 먼저 전투에 돌입하는 애완동물이어야 하며, 전투 중 죽지 않아야 합니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44954, 'ptBR', 'Equipe Pena Nevada', 'Conclua 3 Missões Mundiais de Batalha de Mascotes nas Ilhas Partidas com seu Filhote de Pena Nevada.', '<O filhote de pena nevada olha para você e solta um grunhido convidativo. Parece que ele quer viver uma aventura, mas em dupla!>\n\n<Para aprender, o seu filhote precisa estar presente quando você iniciar a batalha, ser sua primeira mascote a entrar em combate e permanecer vivo durante toda a luta.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44954, 'ruRU', 'В команде со снегоспином', 'Выполните 3 локальных задания по битвам питомцев вместе с вашим детенышем снегоспина на Расколотых островах.', '<Ваш детеныш снегоспина смотрит на вас и отрывисто чирикает. Похоже, ему скучно, и он хочет поразвлечься вместе с вами!>\n\n<Чтобы чему-то научиться, ваш детеныш должен быть рядом с вами к началу сражения, первым вступить в бой и выжить.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44954, 'zhCN', '雪羽小队', '在破碎群岛用你的雪羽雏龙赢得3个宠物对战世界任务。', '<你的雪羽雏龙看了看你，发出抗议的叫声。它似乎是想参加集体活动！>\n\n<为了有所领悟，开始战斗前，你的雏龙必须在你身边，必须是你的宠物里第一个进入战斗的，且不能在战斗中死亡。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44954, 'zhTW', '與雪羽團隊合作', '派你的雪羽小龍隼在破碎群島贏得3個寵物對戰世界任務。', '<你的雪羽小龍隼看著你，發出叫聲。看樣子牠想跟你並肩合作！>\n\n<為了觀摩學習，戰鬥時請務必帶著小龍隼一起出戰，將其安排為第一個進入戰鬥的寵物，並確保牠不會在戰鬥中死亡。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44955, 'deDE', 'Besucherin in Shal\'Aran', 'Trefft Euch mit der Fremden außerhalb von Shal\'Aran.', 'Jemand ist in Shal\'Aran eingetroffen, während Ihr in der Stadt wart. Sie hat unmissverständlich nach Euch gefragt.$b$bIch habe weder ihr Gesicht noch ihre Stimme erkennen können, aber sie schien mir aufrichtig und vertrauenswürdig zu sein.$b$bSie wartet draußen vor der Höhle. Sucht sie auf, um den Beweggrund ihres Kommens zu erfahren.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44955, 'esES', 'Visitante en Shal\'Aran', 'Reúnete con la desconocida en el exterior de Shal\'Aran.', 'Alguien vino a Shal\'Aran mientras estabas en la ciudad. Preguntó específicamente por ti.$b$bNo reconocí su rostro, ni su voz, pero parecía sincera y de fiar.$b$bEstá justo al salir de la caverna. Reúnete con ella y averigua qué la ha traído hasta aquí.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44955, 'esMX', 'Visitante en Shal\'Aran', 'Reúnete con la extraña afuera de Shal\'Aran.', 'Alguien vino a Shal\'Aran mientras estabas en la ciudad. Ella preguntó específicamente por ti.$b$bNo reconocí su cara, ni su voz, pero parecía de confianza y sincera.$b$bEstá afuera de la caverna. Reúnete con ella y descubre sus intenciones aquí.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44955, 'frFR', 'Une étrangère à Shal’Aran', 'Allez voir l’étrangère à l’extérieur de Shal’Aran.', 'Une femme s’est présentée à Shal’Aran pendant que vous étiez en ville. Elle a demandé à vous voir.$b$bJ’ignore qui elle est, mais elle m’a semblé honnête et digne de confiance.$b$bVous la trouverez à l’extérieur de la caverne. Allez à sa rencontre et découvrez ce qu’elle vient faire ici.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44955, 'itIT', 'Una visita a Shal\'aran', 'Incontra la straniera fuori da Shal\'aran.', 'Qualcuno è venuto a Shal\'aran mentre ti trovavi in città e ha chiesto espressamente di te.$b$bNon ho riconosciuto il suo volto e nemmeno la sua voce, ma sembrava sincera e affidabile.$b$bSi trova poco fuori dalla caverna. Va\' da lei e scopri cos\'ha da dire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44955, 'koKR', '샬아란의 방문객', '샬아란 외부에 있는 이방인을 만나야 합니다.', '당신이 성에 가 있는 동안 샬아란에 누군가 찾아왔어요. 당신을 만나고 싶다고 하더군요.$b$b얼굴도, 목소리도 제가 아는 이는 아니었지만, 왠지 진실하고 신뢰할 수 있는 사람이라는 느낌을 받았어요.$b$b그녀는 지금 동굴 바깥에서 기다리고 있어요. 그녀를 만나서 여기 무슨 일로 왔는지 알아보세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44955, 'ptBR', 'Visitante em Shal\'Aran', 'Fale com a desconhecida no lado de fora de Shal\'Aran.', 'Uma moça veio a Shal\'Aran à sua procura enquanto você estava na cidade.$b$bNão reconheci o rosto nem a voz dela, mas ela me pareceu ser séria e confiável.$b$bFale com ela do lado de fora da caverna e descubra o que está acontecendo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44955, 'ruRU', 'Гостья в Шал\'Аране', 'Поговорите с незнакомкой у входа в Шал\'Аран.', 'Кое-кто пожаловал в Шал\'Аран, пока ты $gбыл:была; в городе. Она хочет увидеться именно с тобой.$b$bНе знаю, кто она такая, но мне кажется, ей можно доверять.$b$bОна ждет тебя у входа в пещеру. Поговори с ней и выясни цель ее визита.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44955, 'zhCN', '沙尔艾兰的访客', '找到沙尔艾兰外面的陌生人。', '你在城里的时候，有个人来到了沙尔艾兰，指名要见你。$b$b虽然我没见过她，也不熟悉她的声音，但是她看起来非常真诚可靠。$b$b她就在洞外面。去见见她，搞清她来这里的目的吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44955, 'zhTW', '沙亞蘭的訪客', '在沙亞蘭外與陌生人碰面。', '你在城裡的時候，有個人來到了沙亞蘭。她專程來找你。$b$b我不認得她的臉，也對她的聲音沒印象，不過她似乎值得信賴而且很誠懇。$b$b她就在洞穴外頭。去跟她見面，了解她來這裡的目的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44956, 'deDE', 'Tödliche Beute', 'Schließt 1 seltene Eliteweltquest in Begleitung Eures Schneefederjungen ab.', '<Nachdem er Euch in letzter Zeit bei Haustierkämpfen beigestanden hat, scheint Euer Schneefederfalkosaurier deutlich stärker an Euren Kampffertigkeiten interessiert zu sein. Vielleicht kann er ja das ein oder andere lernen, wenn er Euch beim Kampf gegen einen mächtigen Gegner zusieht.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44956, 'esES', 'Presa letal', 'Completa 1 misión del mundo élite poco común con tu prole de Plumanívea fuera.', '<Después de trabajar en equipo en los últimos duelos de mascotas, tu falcosaurio Plumanívea parece mucho más interesado en tus capacidades de combate. Tal vez aprenda una o dos cosas al verte luchar con un oponente poderoso.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44956, 'esMX', 'Presa mortal', 'Completa 1 misión de mundo inusual de élite con tu prole Plumanívea presente.', '<Tras trabajar juntos en recientes batallas de mascotas, tu falcosaurio Plumanívea luce mucho más interesado en tus habilidades de combate. Podría aprender un par de cosas viéndote luchar contra un oponente poderoso.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44956, 'frFR', 'Une proie létale', 'Accomplissez 1 expédition élite rare en vous faisant accompagner de votre Nouveau-né plume-neige.', '<Les récents combats de mascottes de votre falcosaure plume-neige semblent avoir attisé son intérêt pour vos compétences martiales. Vous voir affronter un adversaire particulièrement puissant lui enseignerait sans doute une chose ou deux.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44956, 'itIT', 'Preda mortale', 'Completa 1 missione mondiale rara élite con il tuo Cucciolo di Piumanevosa.', '<Dopo aver fatto squadra con te negli ultimi scontri tra mascotte, il tuo falcosauro Piumanevosa sembra molto più interessato al combattimento. Potrebbe imparare parecchie cose vedendoti combattere contro un avversario potente.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44956, 'koKR', '치명적 먹잇감', '새끼 서리깃 팔코사우루스와 함께 희귀 정예 전역 퀘스트 1개를 완료해야 합니다.', '<최근 애완동물 대전을 함께 치른 뒤, 새끼 서리깃 팔코사우루스가 부쩍 당신의 전투 기술에 관심을 보이는 것 같습니다. 당신이 강력한 상대와 싸우는 걸 옆에서 구경하면 당신의 기술 한두 개를 배울 수 있을지도 모릅니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44956, 'ptBR', 'Presa perigosa', 'Conclua 1 missão mundial de elite rara com o Filhote de Pena Nevada.', '<Depois das batalhas de mascote, o Falcossauro Pena Nevada tem demonstrado muito mais interesse pelas suas habilidades de combate. Ele pode aprender uma coisinha ou outra observando você enfrentar um adversário poderoso.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44956, 'ruRU', 'Опасная добыча', 'Выполните одно редкое элитное локальное задание со своим детенышем снегоспина.', '<После участия в командных битвах питомцев вашего грифозавра-снегоспина намного сильнее заинтересовали ваши боевые навыки. Возможно, он сможет что-то у вас перенять, наблюдая за вашим боем с достаточно сильным противником.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44956, 'zhCN', '致命猎物', '带着你的雪羽雏龙完成1个稀有精英世界任务。', '<经过最近在宠物对战时的配合，你的雪羽隼龙似乎对你的战斗技能特别感兴趣。如果能让它旁观你是怎么对抗强敌的，它或许能从中领悟到些什么。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44956, 'zhTW', '致命獵物', '帶著你的雪羽小龍隼完成1個稀有精英世界任務。', '<在最近幾場寵物對戰中，你的雪羽小龍隼似乎對你的戰鬥技能更感興趣了。如果能觀看你和強敵戰鬥的話，或許牠能學到一些新的招式。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44957, 'deDE', 'Jagdlektion: Stachelschwanzbiber', 'Bezwingt 3 Stachelschwanzbiber am Hochberg mit Eurem Schneefederjungen.', '<Euer Schneefederjunges interessiert sich in letzter Zeit sehr für Biberspuren. Es muss eine neue Beute entdeckt haben.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44957, 'esES', 'Lección de caza: castor Colapincho', 'Derrota a 3 castores Colapincho en Monte Alto con tu prole de Plumanívea.', '<Últimamente, tu prole de Plumanívea no ha dejado de seguir huellas de castores. Debe de haber encontrado una nueva especie que cazar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44957, 'esMX', 'Lección de cacería: castor colaespina', 'Derrota a 3 castores colaespina en Altamontaña con tu prole Plumanívea.', '<Últimamente, tu prole Plumanívea tiene interés en seguir huellas de castor. Habrá encontrado una nueva especie a la que cazar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44957, 'frFR', 'Leçon de chasse : castors à queue piquante', 'Terrassez 3 Castors à queue piquante à Haut-Roc avec votre Nouveau-né plume-neige.', '<Votre nouveau-né plume-neige s’intéresse depuis quelque temps aux traces laissées par les castors. Il a dû découvrir une nouvelle espèce à laquelle s’attaquer.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44957, 'itIT', 'Lezione di caccia: Castori Codaculea', 'Sconfiggi 3 Castori Codaculea ad Alto Monte con il tuo Cucciolo di Piumanevosa.', '<Il tuo Cucciolo di Piumanevosa ultimamente è sempre intento a seguire le tracce dei castori. Deve aver trovato una nuova specie da cacciare.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44957, 'koKR', '사냥 수업: 가시꼬리 비버', '새끼 서리깃 팔코사우루스와 함께 높은산에 있는 가시꼬리 비버 3마리를 꺾어야 합니다.', '<새끼 서리깃 팔코사우루스가 최근 비버의 흔적을 추적하는 데 관심을 보이고 있습니다. 먹잇감으로 삼을 새로운 종을 발견한 모양입니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44957, 'ptBR', 'Lição de caça: Castor Caudespeto', 'Derrote 3 Castores Caudespeto na Alta Montanha com o Filhote de Pena Nevada.', '<O Filhote de Pena Nevada tem seguido rastros de castor ultimamente. Ele deve ter descoberto uma nova presa para caçar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44957, 'ruRU', 'Уроки охоты: шипохвостые бобры', 'Победите 3 шипохвостых бобров в Крутогорье со своим детенышем снегоспина.', '<Ваш детеныш снегоспина заинтересовался следами бобров. Видимо, он чует рядом что-то съедобное.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44957, 'zhCN', '狩猎课：针尾河狸', '用你的雪羽雏龙在至高岭击败3只针尾河狸。', '<你的雪羽雏龙最近迷上了捕猎河狸。它肯定是又发现了一种新猎物。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44957, 'zhTW', '狩獵訓練：刺尾海狸', '派雪羽小龍隼在高嶺擊敗3隻刺尾海狸。', '<你的雪羽小龍隼最近對於跟隨海狸的蹤跡很感興趣。牠一定是找到新的獵物種類了。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44958, 'deDE', 'Die unfreundliche Fraktion', 'Schließt 3 PvP-Weltquests in Begleitung Eures Schneefederjungen ab.', '<Als Ihr vor Kurzem an einem Mitglied der anderen Fraktion vorbeigegangen seid, ist Eure Schneefeder ihm furchtlos entgegengeeilt. Sie versteht wohl nicht, dass es sich um den Feind handelt. Ihr solltet es ihr besser beibringen, bevor etwas Schlimmes passiert.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44958, 'esES', 'La facción hostil', 'Completa 3 misiones del mundo de JcJ con tu prole de Plumanívea fuera.', '<Al pasar junto a la otra facción hace poco, tu Plumanívea se plantó delante de ellos sin temor. No parece entender que son el enemigo. Seguramente convenga enseñarle antes de que pase algo malo.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44958, 'esMX', 'La facción adversa', 'Completa 3 misiones de mundo en modo JcJ con tu prole Plumanívea presente.', '<Al pasar hace poco junto a la otra facción, tu Plumanívea de acercó a ellos sin miedo. No parece entender que son el enemigo. Probablemente sea mejor enseñárselo antes de que suceda algo malo.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44958, 'frFR', 'Fallacieuse faction', 'Accomplissez 3 expéditions JcJ en vous faisant accompagner de votre Nouveau-né plume-neige.', '<Votre plume-neige s’est récemment approché de certains représentants de l’autre faction sans la moindre méfiance. Il ne semble pas se rendre compte que ce sont ses ennemis. Mieux vaudrait le lui faire comprendre avant qu’un drame ne se produise.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44958, 'itIT', 'La fazione opposta', 'Completa 3 missioni mondiali PvP con il Cucciolo di Piumanevosa al tuo fianco.', '<Quando il tuo Piumanevosa si è imbattuto in membri della fazione opposta, ha camminato vicino a loro senza alcuna paura. Non sembra capire che sono dei nemici. Sarà meglio insegnarglielo, prima che accada qualcosa di brutto.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44958, 'koKR', '가까이 하기엔 너무 먼 진영', '새끼 서리깃 팔코사우루스와 함께 PvP 전역 퀘스트 3개를 완료해야 합니다.', '<최근 상대 진영의 병력을 지나친 적이 있었는데, 새끼 서리깃 팔코사우루스가 겁도 없이 그 병력 쪽으로 다가간 일이 있었습니다. 새끼는 그들이 적이라는 사실을 이해하지 못하는 것 같습니다. 행여나 큰일이 나기 전에 새끼에게 이 사실을 가르쳐야 합니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44958, 'ptBR', 'A facção hostil', 'Conclua 3 missões mundiais JxJ com o Filhote de Pena Nevada.', '<Ao passar pela facção contrária recentemente, seu Pena Nevada chegou pertinho deles, sem medo nenhum. Parece que o filhote não entende que eles são inimigos. É melhor ensinar antes que aconteça alguma coisa de ruim.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44958, 'ruRU', 'Недружественная фракция', 'Выполните 3 локальных PvP-задания со своим детенышем снегоспина.', '<Вы недавно проходили мимо представителя другой фракции, и ваш снегоспин безбоязненно подошел к нему. Похоже, он еще не умеет различать друзей и врагов. Нужно научить его этому, пока с ним не приключилось беды.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44958, 'zhCN', '仇人见面', '带着你的雪羽雏龙完成3个PvP世界任务。', '<最近，你路过对立阵营的地盘时，你的雪羽雏龙一点都不害怕，径直就朝他们走过去了。它大概还不明白，对立阵营的人都是敌人。你最好在它惹出麻烦前先教教它。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44958, 'zhTW', '不友善的陣營', '帶著你的雪羽小龍隼完成3個PvP世界任務。', '<最近遇到另一個陣營的成員時，你的雪羽小龍隼會直接走上前接近對方，一點都不害怕。牠似乎不明白對方是敵人。最好加以教導，以防萬一。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44959, 'deDE', 'Schneefederbande', 'Kauft einen winzigen Rucksack von Susana Averoy in Dalaran für Euer Schneefederjunges.', '<Euer Schneefederjunges wühlt ständig in Eurem Rucksack herum und bringt dort alles durcheinander. Es krächzt laut, wenn Ihr ihn ihm abnehmt, und stößt mit dem Kopf nach Euch. Vielleicht solltet Ihr ihm einen eigenen kleinen Rucksack spendieren?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44959, 'esES', 'Estrechar lazos con tu Plumanívea', 'Cómprale una mochila diminuta a Susana Averoy en Dalaran para tu prole de Plumanívea.', '<Tu prole de Plumanívea no deja de colarse en tu mochila para revolverlo todo. Grazna con fuerza cuando la sacas y te golpea con la cabeza. ¿Qué tal si le buscas una mochila diminuta para distraerla?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44959, 'esMX', 'Unión de Plumanívea', 'Compra un Paquete chiqui a Susana Averoy en Dalaran para tu prole Plumanívea.', '<Tu prole de Plumanívea sigue colándose en tu manada y causando problemas. Chilla fuerte cuando la sacas y te embiste con la cabeza. Tal vez puedas distraerla con una pequeña manada propia...>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44959, 'frFR', 'Jamais sans mon plume-neige', 'Achetez un Tout petit sac pour votre Nouveau-né plume-neige à Susana Averoy, à Dalaran.', '<Votre nouveau-né plume-neige ne cesse de fouiller dans votre sac et d’y semer la pagaille. Il pousse de grands cris quand vous l’ôtez et vous donne des coups de tête. Peut-être pourriez-vous détourner son attention en lui offrant un tout petit sac rien qu’à lui ?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44959, 'itIT', 'Legame con il Piumanevosa', 'Compra una Borsettina per il tuo Cucciolo di Piumanevosa da Susanna Averoy a Dalaran.', '<Il tuo Cucciolo di Piumanevosa continua a intrufolarsi nella tua borsa e a fare disastri. Quando lo tiri fuori, protesta vivacemente e continua a colpirti con la testa. Forse potresti distrarlo con un piccolo zainetto tutto suo?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44959, 'koKR', '서리깃과 친해지기', '달라란에 있는 수사나 에이버로이에게서 새끼 서리깃 팔코사우루스에게 줄 작은 꾸러미를 구입해야 합니다.', '<새끼 서리깃 팔코사우루스가 자꾸 당신의 꾸러미를 풀어헤쳐 엉망으로 만듭니다. 꾸러미를 치우면 또 시끄럽게 울면서 당신에게 박치기를 합니다. 새끼만의 작은 꾸러미를 준비해 주의를 돌려 보는 건 어떨까요?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44959, 'ptBR', 'Afeição Pena Nevada', 'Compre uma Bolsinha com Susana Averoy, em Dalaran, para o Filhote de Pena Nevada.', '<O Filhote de Pena Nevada fica entrando na sua bolsa e bagunçando tudo. Ele guincha bem alto ao ser tirado de dentro dela, e revida com uma cabeçada. De repente é o caso de dar uma bolsa pequena só para ele e assim distraí-lo.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44959, 'ruRU', 'Сближение со снегоспином', 'Купите крохотную сумочку у Сюзаны Аверой в Даларане для вашего детеныша снегоспина.', '<Детеныш снегоспина постоянно залезает в вашу сумку и переворачивает там все вверх дном. Когда вы его достаете, он громко возмущается и бодается. Может быть, ему стоит подарить маленькую сумочку?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44959, 'zhCN', '雪羽的羁绊', '前往达拉然寻找苏珊娜·阿维罗，为你的雪羽雏龙购买一个小包裹。', '<你的雪羽雏龙总是爱钻进你的背包里，把里面弄得乱糟糟。你把它揪出来，它还会大叫着用头撞你。也许你该给他弄个小包裹，分散一下它的注意力？>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44959, 'zhTW', '跟雪羽小龍隼培養感情', '從達拉然的蘇珊娜‧艾維羅那裡買一個小包裹給你的雪羽小龍隼。', '<你的雪羽小龍隼一直翻你的背包，弄得一片混亂。每次你把牠推開，牠就對你大聲嘶叫，並且用頭撞你。或許你可以給他一個小包裹，引開牠的注意力？>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44960, 'deDE', 'Jagdlektion: Schlickmuschelschlurfer', 'Bezwingt 3 Schlickmuschelschlurfer am Hochberg mit Eurem Schneefederjungen und 2 Flughaustieren.', '<Euer Schneefederjunges hat sich mit einigen der anderen fliegenden Haustiere angefreundet. Die Schar ist darauf aus, ein paar Schnecken am Hochberg zu jagen.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44960, 'esES', 'Lección de caza: caracola Valvalodo', 'Derrota a 3 caracolas Valvalodo en Monte Alto con tu prole de Plumanívea y 2 mascotas de la familia Volador.', '<Tu prole de Plumanívea se ha hecho amiga de otras mascotas voladoras. La bandada parece interesada en cazar unas cuantas caracolas de Monte Alto.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44960, 'esMX', 'Lección de cacería: caracola caparazón de barro', 'Derrota a 3 caracolas caparazón de barro en Altamontaña con tu prole Plumanívea y 2 mascotas voladoras.', '<Tu prole Plumanívea se hizo amiga de otras mascotas voladoras. La bandada parece interesada en cazar caracoles de Altamontaña.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44960, 'frFR', 'Leçon de chasse : conques bourbecoques', 'Terrassez 3 Conques bourbecoques à Haut-Roc avec votre Nouveau-né plume-neige et 2 Mascottes volantes.', '<Votre nouveau-né plume-neige s’est lié d’amitié avec certaines des autres mascottes volantes. Ces dernières semblent vouloir partir chasser des escargots de Haut-Roc.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44960, 'itIT', 'Lezione di caccia: Chiocciole Puntaguscio', 'Sconfiggi 3 Chiocciole Puntaguscio ad Alto Monte con il tuo Cucciolo di Piumanevosa e 2 mascotte Volanti.', '<Il tuo Cucciolo di Piumanevosa ha fatto amicizia con altre mascotte Volanti. Sembra che voglia cacciare alcune lumache di Alto Monte.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44960, 'koKR', '사냥 수업: 진흙껍질 고둥', '새끼 서리깃 팔코사우루스와 비행 애완동물 2마리와 함께 높은산에 있는 진흙껍질 고둥 3마리를 꺾어야 합니다.', '<새끼 서리깃 팔코사우루스가 다른 비행 애완동물 몇 마리와 친구가 되었습니다. 새끼와 친구들은 높은산의 달팽이 사냥에 관심이 있는 모양입니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44960, 'ptBR', 'Lição de caça: Concha Conchalama', 'Derrote 3 Conchas Conchalama na Alta Montanha com o Filhote de Pena Nevada e 2 mascotes do tipo Voador.', '<O Filhote de Pena Nevada fez amizade com outros bichos voadores. Parece que o bando está a fim de caçar lesmas na Alta Montanha.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44960, 'ruRU', 'Уроки охоты: грязнопанцирные улитки', 'Убейте 3 грязнопанцирных улиток вместе с вашим детенышем снегоспина и двумя летающими питомцами.', '<Ваш детеныш снегоспина подружился с другими летающими питомцами. Стая хочет поохотиться на улиток в Крутогорье.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44960, 'zhCN', '狩猎课：泥壳蜗牛', '用你的雪羽雏龙和2只飞行类宠物击败3只至高岭的泥壳蜗牛。', '<你的雪羽雏龙跟其他飞行宠物交上了朋友。它们好像对狩猎至高岭的蜗牛很有兴趣。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44960, 'zhTW', '狩獵訓練：泥殼海螺', '派你的雪羽小龍隼和2隻飛行寵物在高嶺擊敗3隻泥殼海螺。', '<你的雪羽小龍隼已經和其他飛行寵物變成朋友了。牠們似乎對獵捕高嶺的海螺很感興趣。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44961, 'deDE', 'Teamworkunterricht: Naraxas', 'Bezwingt Naraxas in Neltharions Hort am Hochberg in Begleitung Eures Schneefederjungen.', '<Trotz des Sieges ist es bedenklich, dass Euer Schneefederjunges seine Verbündeten im Kampf angekrächzt und nach ihnen gepickt hat. Vielleicht solltet Ihr ihm zeigen, wie gutes Teamwork funktioniert.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44961, 'esES', 'Lección de trabajo en equipo: Naraxas', 'Derrota a Naraxas en la Guarida de Neltharion en Monte Alto con tu prole de Plumanívea fuera.', '<Aunque la victoria fue vuestra, tu prole de Plumanívea graznó y picoteó a sus aliados en varias de las últimas contiendas. Darle un buen ejemplo de trabajo en equipo podría ayudar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44961, 'esMX', 'Lección de trabajo en equipo: Naraxas', 'Vence a Naraxas en la Guarida de Neltharion, en Altamontaña, con la prole Plumanívea presente.', '<Aunque venció, tu prole Plumanívea chilló y picoteó a sus aliados durante alguna de las últimas batallas. Mostrarle un buen ejemplo de trabajo en equipo podría ayudar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44961, 'frFR', 'Apprentissage de la coopération : Naraxas', 'Terrassez Naraxas dans le Repaire de Neltharion à Haut-Roc en vous faisant accompagner de votre Nouveau-né plume-neige.', '<Bien que victorieux, votre nouveau-né plume-neige a houspillé ses alliés et leur a donné des coups de bec au cours des derniers combats. Lui enseigner les rudiments de la coopération pourrait lui être profitable.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44961, 'itIT', 'Lezione sul gioco di squadra: Naraxas', 'Sconfiggi Naraxas nell\'Antro di Neltharion ad Alto Monte con il Cucciolo di Piumanevosa al tuo fianco.', '<Sebbene vittorioso, il tuo Cucciolo di Piumanevosa gracchiava e colpiva col becco alcuni dei suoi compagni durante alcuni degli ultimi combattimenti. Mostrargli un buon esempio di lavoro di squadra potrebbe risolvere il problema.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44961, 'koKR', '팀워크 수업: 나락사스', '새끼 서리깃 팔코사우루스와 함께 높은산에 있는 넬타리온의 둥지에서 나락사스를 처치해야 합니다.', '<지난 몇 번의 싸움에서 승리를 거두긴 했지만, 새끼 서리깃 팔코사우루스는 싸움 중에 소리를 지르며 부리로 동료를 공격하기도 했습니다. 팀워크가 어떤 것인지 본보기를 보여 주는 게 도움이 될 것 같습니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44961, 'ptBR', 'Lição de trabalho em equipe: Naraxas', 'Derrote Naraxas no Covil de Neltharion, que fica na Alta Montanha, com o Filhote de Pena Nevada.', '<Embora vitorioso, o Filhote de Pena Nevada hostilizou e bicou seus aliados nas últimas batalhas que enfrentou. Dê um bom exemplo de trabalho em equipe a ele.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44961, 'ruRU', 'Работа в команде: Нараксас', 'Победите Нараксас в Логове Нелтариона в Крутогорье со своим детенышем снегоспина.', '<После победы в некоторых сражениях ваш детеныш снегоспина кричал и клевал своих союзников. Нужно продемонстрировать ему преимущества слаженной командной игры.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44961, 'zhCN', '团队合作：纳拉萨斯', '带着你的雪羽雏龙，在至高岭击败奈萨里奥的巢穴中的纳拉萨斯。', '<在最近几次战斗中，虽然最终赢得了胜利，但你的雪羽雏龙总在战斗时冲它的盟友大叫，还啄它们。让它瞧瞧，团队合作应该是什么样子的，这样也许有帮助。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44961, 'zhTW', '團隊訓練：納拉薩斯', '帶著你的雪羽小龍隼在高嶺的奈薩里奧巢穴擊敗納拉薩斯。', '<雖然打贏了，但是你的雪羽小龍隼在先前幾場戰鬥中，對牠的夥伴又叫又啄。讓牠明白什麼叫做團隊合作應該會有所幫助。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44962, 'deDE', 'Jagdlektion: Korallenrückenmauschler', 'Bezwingt 3 Korallenrückenmauschler am Hochberg mit Eurem Schneefederjungen und 2 Elementarhaustieren.', '<Euer Schneefederjunges verhält sich misstrauisch gegenüber den Elementarhaustieren in Eurer Menagerie. Wenn es mit ihnen zurechtkommen soll, muss es lernen, an ihrer Seite zu kämpfen. Vielleicht hilft es, wenn Ihr gemeinsam eine neue Beute verfolgt.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44962, 'esES', 'Lección de caza: cigarra marina dorsocoral', 'Derrota a 3 cigarras marinas dorsocoral en Monte Alto con tu prole de Plumanívea y 2 mascotas de la familia Elemental.', '<Tu prole de Plumanívea parece especialmente recelosa de las mascotas elementales de tu reserva de mascotas. Tendrán que aprender a trabajar juntas si quieren llevarse bien. Podría venirles bien cazar juntas la nueva presa que ha empezado a acechar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44962, 'esMX', 'Lección de cacería: cigarra lomocoral', 'Derrota a 3 cigarras lomocoral en Altamontaña con tu prole Plumanívea y 2 mascotas elementales.', '<Tu prole Plumanívea tiene una cautela especial con los elementales de tu reserva de mascotas. Deben aprender a trabajar juntos. Una buena ocasión para aprender podría ser cazar juntos la nueva presa que tienen al acecho.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44962, 'frFR', 'Leçon de chasse : crabes violonistes corapaces', 'Terrassez 3 Crabes violonistes corapaces à Haut-Roc avec votre Nouveau-né plume-neige et 2 Mascottes élémentaires.', '<Votre nouveau-né plume-neige se méfie tout particulièrement des mascottes élémentaires de votre ménagerie. Ils vont pourtant tous devoir apprendre à collaborer. Profitez du fait qu’il s’intéresse à une nouvelle proie pour organiser une chasse commune.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44962, 'itIT', 'Lezione di caccia: Granchi Canterini Mantocorallino', 'Sconfiggi 3 Granchi Canterini Mantocorallino ad Alto Monte con il tuo Cucciolo di Piumanevosa e 2 mascotte Elementali.', '<Il tuo Cucciolo di Piumanevosa è particolarmente impaurito di fronte alle mascotte Elementali del tuo serraglio. In un modo o nell\'altro dovranno imparare ad andare d\'accordo. Cacciare insieme una nuova preda potrebbe essere una buona idea.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44962, 'koKR', '사냥 수업: 산호등 종종게', '새끼 서리깃 팔코사우루스와 정령 애완동물 2마리와 함께 높은산에 있는 산호등 종종게 3마리를 꺾어야 합니다.', '<새끼 서리깃 팔코사우루스가 당신의 애완동물 중 특히 정령 애완동물을 경계하는 것 같습니다. 서로 사이좋게 지내려면 협동하는 법을 배우는 게 좋겠습니다. 새끼가 요즘 쫓아다니기 시작한 새로운 먹잇감을 함께 사냥해 보는 건 어떨까요?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44962, 'ptBR', 'Lição de caça: Burlão Costacoral', 'Derrote 3 Burlões Costacoral na Alta Montanha com o Filhote de Pena Nevada e 2 mascotes Elementais.', '<O Filhote de Pena Nevada não se dá bem com suas mascotes elementais no viveiro. Eles vão ter que aprender a trabalhar em equipe para conviver bem. Se forem caçar juntos a presa que o falcossauro anda perseguindo, o problema deve se resolver.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44962, 'ruRU', 'Уроки охоты: коралловые крабы', 'Убейте 3 коралловых крабов вместе с вашим детенышем снегоспина и двумя питомцами-элементалями.', '<Ваш детеныш снегоспина очень настороженно относится к питомцам-элементалям в вашем зверинце. Нужно найти им общее занятие, чтобы они подружились. Нужно дать ему поохотиться в компании на новую жертву, которую он как раз начал выслеживать, и тогда, возможно, все получится.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44962, 'zhCN', '狩猎课：珊脊沙蟹', '用你的雪羽雏龙和2只元素类宠物击败至高岭的3只珊脊沙蟹。', '<你的雪羽雏龙对你的元素类宠物特别有戒心。要让它们和睦相处，就得让它们学会合作。让它们一起狩猎新猎物的话，或许就能达成这个目的。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44962, 'zhTW', '狩獵訓練：珊背招潮蟹', '派你的雪羽小龍隼和2隻元素生物在高嶺擊敗3隻珊背招潮蟹。', '<你的雪羽小龍隼似乎對你寵物園中的元素生物特別有戒心。牠們需要學習該如何合作才能和睦相處。一起獵殺牠最近喜歡的新獵物或許能改善這一點。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44964, 'deDE', 'Ich lasse das einfach mal hier', 'Platziert Energiedisruptoren bei den Energiequellen in Teloth\'aran, Kel\'balor und Elor\'shan in Suramar.', 'Valtrois hat einen Plan für die Energiebarriere des Sanktums ersonnen.$B$BWichtige Standorte innerhalb des Arkus in ganz Suramar bieten die dafür erforderliche Energie. Die Energiedisruptoren, die sie angefertigt hat, lösen gleichzeitig und aus der Ferne die Verbindungen und lassen die Barriere so zusammenbrechen.$b$bNehmt diese Geräte und installiert sie an den Leylinien. Kehrt dann hierhin zurück, damit der Überraschungsangriff auf das Sanktum beginnen kann.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44964, 'esES', 'Dejaré esto aquí', 'Coloca disruptores de energía en la fuentes de energía de Teloth\'aran, Kel\'balor y Elor\'shan, en Suramar.', 'Valtrois tiene un plan para la barrera de energía del Santuario.$b$bVarias ubicaciones clave de La Arquería de Suramar utilizan el poder necesario para mantenerla. Estos disruptores de energía de su creación romperán a distancia todos los vínculos a la vez al activarlos, lo que hará caer la barrera.$b$bToma estos dispositivos, colócalos en las líneas Ley y vuelve aquí para el ataque sorpresa al Santuario.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44964, 'esMX', 'Dejaré esto aquí', 'Coloca interruptores de energía en las fuentes de energía de Teloth\'aran, Kel\'balor y Elor\'shan en Suramar.', 'Valtrois tiene un plan para la barrera de energía del Sagrario.$B$BMúltiples ubicaciones claves del Arco en Suramar poseen el poder suficiente para llevarlo a cabo. Estos interruptores de energía que fabricó cortarán de forma remota todos los enlaces a la vez una vez activados, lo que hará que caiga la barrera.$b$bToma estos dispositivos e instálalos en las Líneas Ley. Luego regresa aquí para llevar a cabo el asalto sorpresa al Sagrario.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44964, 'frFR', 'Ni vu ni connu', 'Placez les Disrupteurs d’énergie sur les sources d’énergie de Teloth’aran, Kel’balor et Elor’shan, à Suramar.', 'Valtrois a un plan pour franchir la barrière d’énergie du sanctum.$B$BIl y a, aux quatre coins de Suramar, des points clés de l’Arcavia recelant la puissance nécessaire pour l’alimenter. Les disrupteurs d’énergie qu’elle a fabriqués interrompront le flux d’énergie quand vous les activerez, ce qui neutralisera la barrière.$b$bEmportez-les et installez-les sur les lignes telluriques, puis revenez ici pour l’attaque-surprise contre le sanctum.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44964, 'itIT', 'Questo lo lascio qui', 'Piazza i Disgregatori d\'Energia sulle fonti energetiche a Teloth\'aran, Kel\'balor e Elor\'shan a Suramar.', 'Valtrois ha un piano per abbattere la barriera energetica del Santuario.$B$BCi sono molti luoghi chiave dell\'Arcavia sparsi per tutta Suramar che contengono il potere necessario per attuarlo. I Disgregatori d\'Energia che ha creato spezzeranno simultaneamente i collegamenti, causando la caduta della barriera.$b$bPrendi questi dispositivi e installali sulle linee di faglia, poi torna qui per iniziare l\'assalto a sorpresa al Santuario.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44964, 'koKR', '그냥 두고 갈게요', '수라마르의 텔로스아란, 켈발로르, 엘로르샨에 마력 분열기를 설치해야 합니다.', '발트와가 성소의 마력 방벽에 대응할 계획을 세웠어요.$B$B수라마르 전역에서 비전로의 주요 지점들에는 그 마력 방벽을 유지하는 데 필요한 동력이 담겨 있어요. 그녀가 만든 이 마력 분열기를 사용하면, 원격에서 동력을 차단할 수 있고, 그러면 방벽도 무너질 테지요.$b$b이 장치들을 갖고 가서 지맥에 설치해 주세요. 그리고 여기로 돌아와서 성소에 대한 기습 공격을 시작하시죠.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44964, 'ptBR', 'Só vou deixar isso aqui', 'Posicione Interruptores de Energia nas fontes de energia em Teloth\'aran, Kel\'balor e Elor\'shan, dentro de Suramar.', 'Valtrois tem um plano para a barreira de energia do Sacrário.$B$BVários pontos-chave do Arcâneo por toda Suramar contêm a energia necessária para tanto. Esses Interruptores de Energia que ela fez vão romper remota e simultaneamente os vínculos ao serem ativados, derrubando assim a barreira.$b$bLeve os dispositivos e os instale nos meridianos, depois volte aqui para o ataque surpresa conta o Sacrário.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44964, 'ruRU', 'Я просто оставлю это здесь', 'Разместите исказители энергии у источников энергии в Телот\'аране, Кел\'балоре и Элор\'шане.', 'Вальтруа придумала, как вывести из строя барьер Святилища.$B$BВ катакомбах Сурамара расположено несколько важных источников энергии. Она же соорудила некие исказители энергии, которые позволят нам прямо отсюда одновременно разорвать энергетические связи, и барьер падет.$b$bВозьми эти устройства и установи их на силовых линиях, затем возвращайся. Мы нападем на Святилище и застанем противника врасплох.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44964, 'zhCN', '交给你了', '在苏拉玛的特洛萨兰、凯尔巴洛和艾洛珊放置能量干扰器。', '瓦尔托伊已经有了计策，可以解决大殿的能量屏障。$b$b在苏拉玛的魔法回廊里有几个地方，属于为屏障供能的关键节点。她制作了一些能量干扰器，启动后就可以远程同时切断连接，关闭屏障。$b$b拿上这些装置，安装到魔网各处，然后回到这里，对大殿发动突袭。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44964, 'zhTW', '就放在這裡', '前往蘇拉瑪爾的泰洛薩蘭、克爾巴洛和艾洛爾善，在這三個地方的能量來源放置能量干擾器。', '薇兒塔有個解除聖所能量屏障的計畫。$B$B在整個蘇拉瑪爾有許多關鍵的幽暗地道所在，正在為屏障提供能量。她做的這些能量干擾器，一啟動就能從遠端截斷各處的能量連結，讓屏障失效。$b$b帶著這些裝置，將它們裝在地脈上。然後回到這裡，準備對聖所發動奇襲。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44967, 'deDE', 'Teamworkunterricht: Ursoc', 'Besiegt Ursoc im Smaragdgrünen Alptraum in Begleitung Eures Schneefederjungen.', '<Euer Schneefederjunges hat es sich angewöhnt, in Stresssituationen gegen andere Haustiere zu kämpfen. Es muss lernen, auch unter Druck und im Kampf gegen große Tiere einen kühlen Kopf zu bewahren. Es sollte helfen, wenn Ihr ihm ein episches Beispiel für Teamwork gegen ein großes Tier zeigt.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44967, 'esES', 'Lección de trabajo en equipo: Ursoc', 'Derrota a Ursoc en la Pesadilla Esmeralda con tu prole de Plumanívea fuera.', '<Tu prole de Plumanívea sigue enfrentándose a otras mascotas cuando se siente amenazada. Debe aprender a mantener la calma bajo presión, aunque se enfrente con grandes bestias. Mostrarle un ejemplo épico de trabajo en equipo contra una gran bestia podría ayudar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44967, 'esMX', 'Lección de trabajo en equipo: Ursoc', 'Derrota a Ursoc en la Pesadilla Esmeralda con tu prole Plumanívea presente.', '<Tu prole Plumanívea pelea constantemente con otras mascotas cuando se siente amenazada. Debe aprender a mantener la calma bajo presión, incluso ante grandes bestias. Una demostración épica de trabajo en equipo contra una gran bestia podría ayudar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44967, 'frFR', 'Apprentissage de la coopération : Ursoc', 'Terrassez Ursoc dans le Cauchemar d’émeraude en vous faisant accompagner de votre Nouveau-né plume-neige.', '<Votre nouveau-né plume-neige continue à se battre avec les autres mascottes quand il se sent menacé. Il doit apprendre à rester calme sous la pression, même face aux bêtes les plus imposantes. Il serait utile de lui montrer que collaborer lors d’un combat épique contre une telle créature peut faire toute la différence.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44967, 'itIT', 'Lezione sul gioco di squadra: Ursoc', 'Sconfiggi Ursoc nell\'Incubo di Smeraldo con il Cucciolo di Piumanevosa al tuo fianco.', '<Il tuo Cucciolo di Piumanevosa continua a combattere con le altre mascotte quando si sente minacciato. Deve imparare a mantenere la calma sotto pressione, anche quando combatte contro bestie più grandi. Mostragli che un lavoro di squadra potrebbe essergli d\'aiuto contro una grande bestia.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44967, 'koKR', '팀워크 수업: 우르속', '새끼 서리깃 팔코사우루스와 함께 에메랄드의 악몽에 있는 우르속을 처치해야 합니다.', '<새끼 서리깃 팔코사우루스는 위협을 느낄 때마다 자꾸 다른 애완동물들과 싸웁니다. 거대한 야수와 마주하는 압박 속에서도 냉정을 잃지 않는 법을 배워야 합니다. 함께 거대한 야수를 상대하며 멋진 팀워크를 발휘해 보면 도움이 될지도 모릅니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44967, 'ptBR', 'Lição de trabalho em equipe: Ursoc', 'Derrote Ursoc no Pesadelo Esmeralda com o Filhote de Pena Nevada.', '<O Filhote de Pena Nevada continua brigando com as outras mascotes quando se sente acuado. Ele tem que aprender a manter a calma diante de criaturas maiores. Dar um bom exemplo de trabalho em equipe contra um monstro maior deve ajudar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44967, 'ruRU', 'Работа в команде: Урсок', 'Победите Урсока в Изумрудном Кошмаре вместе с вашим детенышем снегоспина.', '<Ваш детеныш снегоспина продолжает затевать драки с другими питомцами, когда чувствует угрозу. Он должен сохранять спокойствие в тяжелых ситуациях, даже если ему противостоят могучие звери. Возможно, ему стоит понаблюдать за слаженной работой в сражении с опасным животным.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44967, 'zhCN', '团队合作：乌索克', '带着你的雏龙击败翡翠梦魇的乌索克。', '<你的雪羽雏龙一受到威胁，就会跟其他宠物打作一团。它得学会顶住压力，保持冷静，哪怕是要面对那些巨兽。与一头巨兽打一场史诗之战，展示一下团队合作精神，这样它可能就会明白。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44967, 'zhTW', '團隊訓練：厄索克', '帶著你的雪羽小龍隼在翡翠夢境擊敗厄索克。', '<你的雪羽小龍隼在感到威脅的時候還是會跟其他的戰寵打架。牠得學會在面對壓力時處之泰然，就算眼前是巨大的怪獸也一樣。讓牠見識一下如何利用團隊合作來擊敗巨大的野獸，或許會有所幫助。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44968, 'deDE', 'Randale im Team mit Schneefeder', 'Schließt 3 Haustierkampf-Weltquests mit Eurem Schneefederjungen und 2 untoten Haustieren ab.', '<Sieht so aus, als wäre Euer Schneefederjunges bereit, sich mit Euch in ein paar neue Kämpfe zu stürzen, um zu zeigen, was es über Teamwork gelernt hat. Wenn Ihr es mit neuen Verbündeten zusammen anweisen würdet, wäre dies ein ausgezeichneter Test seiner Fähigkeiten.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44968, 'esES', 'Pelea en equipo con Plumanívea', 'Completa 3 misiones del mundo de duelos de mascotas con tu prole de Plumanívea y 2 mascotas de la familia No-muerto.', '<Tu prole de Plumanívea parece lista para asociarse contigo en otra serie de batallas y demostrar así todo lo aprendido sobre el trabajo en equipo. Darle órdenes junto a nuevos aliados sería una prueba magnífica de sus facultades.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44968, 'esMX', 'Prueba del equipo Plumanívea', 'Gana 3 misiones de mundo de duelos de mascotas con tu prole Plumanívea y 2 mascotas no muertas.', '<Tu prole Plumanívea luce preparada para formar equipo contigo en otra ronda de batallas y demostrar lo que aprendió sobre el trabajo en equipo. Añadir nuevos aliados sería una manera excelente de probar sus capacidades.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44968, 'frFR', 'Travail d’équipe avec le plume-neige', 'Accomplissez 3 expéditions de combat de mascottes avec votre Nouveau-né plume-neige et 2 Mascottes mortes-vivantes.', '<Votre nouveau-né plume-neige est prêt à livrer de nouvelles batailles à vos côtés et semble impatient de mettre en pratique tout ce qu’il a appris en matière de collaboration. Partir au combat avec de nouveaux alliés serait un excellent moyen de mettre ses nouvelles techniques à l’épreuve.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44968, 'itIT', 'Lavoro di squadra per il Piumanevosa', 'Completa 3 missioni mondiali degli scontri tra mascotte con il tuo Cucciolo di Piumanevosa e 2 mascotte Non Morte.', '<Il tuo Cucciolo di Piumanevosa sembra pronto a combattere altre battaglie assieme a te e a mostrare tutto ciò che ha imparato riguardo il lavoro di squadra. Farlo combattere assieme a nuovi alleati potrebbe mettere alla prova le sue abilità.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44968, 'koKR', '서리깃 팀 전투', '새끼 서리깃 팔코사우루스와 언데드 애완동물 2마리와 함께 애완동물 대전 전역 퀘스트 3개를 완료해야 합니다.', '<새끼 서리깃 팔코사우루스가 당신과 한 팀이 되어 다시 한 번 전투를 벌이고 그동안 갈고 닦은 팀워크를 뽐낼 준비가 된 것 같습니다. 새로운 동료들과 힘을 합쳐 싸우는 게 새끼의 능력을 시험할 더할 나위 없는 기회가 될 것입니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44968, 'ptBR', 'Superequipe Pena Nevada', 'Conclua 3 Missões Mundiais de Batalha de Mascotes com o Filhote de Pena Nevada e 2 mascotes mortas-vivas.', '<Parece que o Filhote de Pena Nevada já está pronto para enfrentar uma nova rodada de batalhas e mostrar tudo o que aprendeu sobre trabalho em equipe. Comandá-lo junto de novos aliados seria uma ótima forma de testar as habilidades dele.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44968, 'ruRU', 'Командный бой со снегоспином', 'Выполните 3 локальных задания по битвам питомцев со своим детенышем снегоспина и двумя питомцами-нежитью.', '<Ваш детеныш снегоспина готов присоединиться к вам в новых сражениях и продемонстрировать свои навыки работы в команде. Отправьте его в бой с новыми союзниками, чтобы проверить, чему он научился.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44968, 'zhCN', '雪羽小队大乱斗', '用你的雪羽雏龙和2只亡灵类宠物完成3个宠物对战世界任务。', '<你的雪羽雏龙看起来已经准备好与你一起迎接新的战斗，并展示它学会的合作技巧了。指挥它和新盟友一起战斗，好好测试一下它的能力吧。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44968, 'zhTW', '雪羽團隊大戰', '派你的雪羽小龍隼和2個不死生物贏得3個寵物對戰世界任務。', '<看樣子你的雪羽小龍隼已做好跟你一起挑戰下一輪戰鬥的準備，好顯示牠已經學會什麼叫做團隊合作。讓牠跟不同的搭檔合作應該能測出牠的能耐。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44969, 'deDE', 'Schneefederwiedersehen', 'Bringt das Schneefederjunge zurück zum Nebelufer am Hochberg.', '<In letzter Zeit verhält sich Euer Schneefederjunges lethargisch. Es scheint deprimiert zu sein. Vielleicht solltet Ihr mit ihm zum Ort seiner Geburt zurückkehren?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44969, 'esES', 'Reunión de Plumaníveas', 'Lleva la prole de Plumanívea de regreso a la Costa Brumosa, en Monte Alto.', '<Tu prole de Plumanívea parece un poco apática últimamente, casi deprimida. Tal vez sea momento de llevarla de regreso a su lugar de origen.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44969, 'esMX', 'Reunión de Plumaníveas', 'Devuelve a la prole Plumanívea a la Costa Neblinosa, en Altamontaña.', '<Últimamente, tu prole Plumanívea está letárgica. Luce deprimida. Tal vez sea hora de devolverla a su lugar de origen...>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44969, 'frFR', 'Retrouvailles des plume-neige', 'Ramenez le Nouveau-né plume-neige sur la Côte Embrumée, à Haut-Roc', '<Votre nouveau-né plume-neige est léthargique depuis quelque temps. Il a l’air déprimé. Peut-être est-il temps de le ramener chez lui ?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44969, 'itIT', 'Ricongiungimento tra Piumanevosa', 'Riporta il Cucciolo di Piumanevosa alla Costa Nebbiosa nella regione di Alto Monte.', '<Il tuo Cucciolo di Piumanevosa è piuttosto apatico ultimamente. Sembra depresso. Forse è giunto il momento di riportarlo al suo luogo di nascita?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44969, 'koKR', '서리깃 상봉', '새끼 서리깃 팔코사우루스를 높은산에 있는 안개 해안으로 돌려보내야 합니다.', '<새끼 서리깃 팔코사우루스가 요즘 들어 생기가 없는 게, 우울해 보입니다. 새끼를 태어난 곳으로 돌려보낼 시간이 된 건 아닐까요?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44969, 'ptBR', 'Reunião dos Penas Nevadas', 'Devolva o Filhote de Pena Nevada à Costa Enevoada, que fica na Alta Montanha.', '<O Filhote de Pena Nevada anda um pouco amuado ultimamente. Parece meio deprimido. De repente é hora de devolvê-lo ao seu habitat natural.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44969, 'ruRU', 'Воссоединение снегоспинов', 'Верните детеныша снегоспина на Туманный берег в Крутогорье.', '<Ваш детеныш снегоспина последнее время сам не свой. Кажется, он сильно подавлен. Быть может, пришло время вернуть его в естественную среду обитания?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44969, 'zhCN', '雪羽家族团聚', '把雪羽雏龙送回至高岭的迷雾海岸。', '<你的雪羽雏龙最近无精打采。它似乎很沮丧。或许该送它回到出生地了？>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44969, 'zhTW', '雪羽家族團聚', '讓雪羽小龍隼返回高嶺的迷霧海岸。', '<你的雪羽小龍隼最近一直無精打采的。牠似乎很憂鬱。或許讓牠返回家鄉的時候到了？>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44971, 'deDE', 'Der Geruch von Orcs', 'Bringt Euer Schneefederjunges zum Gebrochenen Hauer in Orgrimmar.', '<Ihr habt bemerkt, dass Euer Schneefederjunges immer zittert, wenn Orcs in der Nähe sind. Anscheinend hat es große Angst vor ihnen. Vielleicht solltet Ihr es irgendwo hinbringen, wo es eine Menge Orcs gibt, damit es sich an den Geruch gewöhnen kann.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44971, 'esES', 'El olor de los orcos', 'Lleva a tu prole de Plumanívea a El Colmillo Partido, en Orgrimmar.', '<Has notado que tu prole de Plumanívea tiembla cuando hay orcos cerca. Parece tenerles autentico pánico. Quizás podrías llevarla a algún lugar donde haya muchos, para que se acostumbre al olor.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44971, 'esMX', 'El olor de los orcos', 'Lleva a tu prole Plumanívea al Colmillo Partido, en Orgrimmar.', '<Notas que tu prole Plumanívea se agita cuando hay orcos cerca. Parece que se siente aterrorizada. Tal vez puedas llevarla a un sitio con muchos orcos para que se acostumbre al olor.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44971, 'frFR', 'L’odeur des Orcs', 'Amenez votre Nouveau-né plume-neige à la Broche Rompue, à Orgrimmar.', '<Vous avez remarqué que votre nouveau-né plume-neige est pris de tremblements chaque fois qu’il aperçoit des Orcs. Leur seule vue semble le terrifier. Peut-être serait-il bon de le mettre en présence d’un grand nombre d’Orcs afin qu’il s’habitue à leur odeur ?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44971, 'itIT', 'L\'odore degli Orchi', 'Porta il tuo Cucciolo di Piumanevosa alla Zanna Spezzata nella città di Orgrimmar.', '<Hai notato che il tuo Cucciolo di Piumanevosa trema quando ci sono degli Orchi nelle vicinanze. Sembra che ne sia terrorizzato. Forse potresti portarlo in un posto pieno di Orchi, in modo che si abitui al loro odore.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44971, 'koKR', '오크의 냄새', '새끼 서리깃 팔코사우루스를 오그리마에 있는 부러진 엄니 여관으로 데려가야 합니다.', '<당신은 오크가 주위에 다가올 때마다 새끼 서리깃 팔코사우루스가 몸을 떠는 걸 알아챘습니다. 오크가 엄청 무서운가 봅니다. 새끼를 오크가 많은 곳으로 데려가 보는 걸 어떨까요? 냄새에 익숙해지도록 말이죠...>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44971, 'ptBR', 'O cheiro dos orcs', 'Leve o Filhote de Pena Nevada à Presa Quebrada, que fica em Orgrimmar.', '<Você repara que o filhote de Pena Nevada treme sempre que tem orcs por perto. Parece que ele tem pavor de orcs. De repente, se você levá-lo a um lugar com muitos orcs, ele acaba se acostumando com o cheiro deles.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44971, 'ruRU', 'Запах орков', 'Отведите вашего детеныша снегоспина в \"Сломанный клык\" в Оргриммаре.', '<Вы обратили внимание, что ваш детеныш снегоспина начинает дрожать от страха всякий раз, когда поблизости оказываются орки. Похоже, он их до жути боится. Вероятно, стоит отвести его куда-нибудь, где полно орков, чтобы он к ним попривык.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44971, 'zhCN', '兽人的气味', '带着你的雪羽雏龙前往奥格瑞玛的“破裂的獠牙”。', '<你发现只要有兽人靠近，这只雪羽雏龙就会瑟瑟发抖。看来它真的很怕兽人。或许你能把它带到兽人聚集的地方，让它习惯这种气味。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44971, 'zhTW', '獸人的氣味', '帶你的雪羽小龍隼到奧格瑪的破齒旅店。', '<你發現雪羽小龍隼只要附近有獸人就會顫抖。看起來是嚇壞了。或許帶牠到獸人密集的地方，可以讓牠習慣獸人的氣味。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44973, 'deDE', 'Düsterschnabelaufzucht', 'Pflegt Eurer Düsterschnabeljunges bis zum Erreichen von Stufe 25.', '<Euer Düsterschnabeljunges scheint sich nicht ganz darüber im Klaren zu sein, was es anstellen sollte. Es verlässt sich wohl auf Eure Hilfe, um zu überleben und groß zu werden.>', '', 'Krächz! Krächz!', 'Düsterschnabeljunges', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44973, 'esES', 'Criar a tu Picoterrible', 'Cría a tu prole de Picoterrible hasta el nivel 25.', '<Tu prole de Picoterrible parece no tener claro cuál es su lugar en todo esto. Parece esperar que la ayudes a sobrevivir y crecer.>', '', '¡Erk! ¡Erk!', 'Prole de Picoterrible', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44973, 'esMX', 'Crianza del Picotemible', 'Lleva a la prole Picotemible al nivel 25.', '<Tu prole Picotemible no luce muy segura de sí misma. Parece que confía en ti para que le ayudes a sobrevivir y crecer.>', '', '¡Erk! ¡Erk!', 'Prole Picotemible', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44973, 'frFR', 'Élever votre bec-sinistre', 'Faites progresser votre Nouveau-né bec-sinistre jusqu’au niveau 25.', '<Votre nouveau-né bec-sinistre semble incapable de prendre soin de lui. On dirait que vous allez devoir l’aider à survivre et grandir.>', '', 'Erk ! Erk !', 'Nouveau-né bec-sinistre', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44973, 'itIT', 'Far crescere il Beccotruce', 'Porta il tuo Cucciolo di Beccotruce al livello 25.', '<Il tuo Cucciolo di Beccotruce pare indeciso su cosa fare. Sembra che voglia che lo aiuti a sopravvivere e crescere.>', '', 'Erk! Erk!', 'Cucciolo di Beccotruce', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44973, 'koKR', '광포부리 팔코사우루스 키우기', '새끼 광포부리 팔코사우루스를 25 레벨까지 키워야 합니다.', '<새끼 광포부리 팔코사우루스가 어찌할 바를 모르는 것 같습니다. 자기가 무사히 성장할 수 있게 도와 달라는 눈빛입니다.>', '', '아록! 아록?', '새끼 광포부리 팔코사우루스', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44973, 'ptBR', 'A criação do Bicaustero', 'Crie o Filhote de Bicaustero até ele atingir o nível 25.', '<O Filhote de Bicaustero parece não saber o que fazer. Parece que espera a sua ajuda para crescer e sobreviver.>', '', 'Erk! Erk!', 'Filhote de Bicaustero', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44973, 'ruRU', 'Воспитание злобоклюва', 'Повысьте уровень вашего детеныша злобоклюва до 25-го.', '<Ваш детеныш злобоклюва не знает, чем ему заняться. Похоже, он надеется, что вы защитите его и поможете ему вырасти большим и сильным.>', '', 'Чирик! Чирик!', 'Детеныш злобоклюва', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44973, 'zhCN', '养育你的恐嘴雏龙', '把你的恐嘴雏龙提升到25级。', '<你的恐嘴雏龙有点不知所措，似乎希望你能帮助它活下来，并健康长大。>', '', '啾！啾！', '恐嘴雏龙', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44973, 'zhTW', '培育你的恐喙小龍隼', '將你的恐喙小龍隼提升到等級25。', '<你的恐喙小龍隼看起來有點不知所措。看樣子你要負責撫育牠長大了。>', '', '咿！咿！', '恐喙小龍隼', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44974, 'deDE', 'Der Geruch von Zwergen', 'Bringt Euer Düsterschnabeljunges zur Steinfeuertaverne in Eisenschmiede.', '<Ihr habt bemerkt, dass Euer Düsterschnabeljunges immer, wenn Zwerge in der Nähe sind, zittert. Anscheinend hat es große Angst vor ihnen. Vielleicht solltet Ihr es irgendwo hinbringen, wo es eine Menge Zwerge gibt, damit es sich an den Geruch gewöhnen kann.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44974, 'esES', 'El olor de los enanos', 'Lleva a tu prole de Picoterrible a la Taberna Roca de Fuego, en Forjaz.', '<Has notado que tu prole de Picoterrible tiembla cuando hay enanos cerca. Parece tenerles autentico pánico. Quizás podrías llevarla a algún lugar donde haya muchos, para que se acostumbre al olor.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44974, 'esMX', 'El olor de los enanos', 'Lleva a tu prole Picotemible a la Taberna Roca de Fuego en Forjaz.', '<Notas que tu prole Picotemible se agita cuando hay enanos cerca. Parece que se siente aterrorizada. Tal vez puedas llevarla a un sitio con muchos enanos para que se acostumbre al olor.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44974, 'frFR', 'L’odeur des nains', 'Amenez votre Nouveau-né bec-sinistre à la Taverne de Brûlepierre, à Forgefer.', '<Vous avez remarqué que votre nouveau-né bec-sinistre est pris de tremblements chaque fois qu’il aperçoit des nains. Leur seule vue semble le terrifier. Peut-être serait-il bon de le mettre en présence d’un grand nombre de Nains afin qu’il s’habitue à leur odeur ?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44974, 'itIT', 'L\'odore dei Nani', 'Porta il tuo Cucciolo di Beccotruce alla Taverna Fuocoduro nella città di Forgiardente.', '<Hai notato che il tuo Cucciolo di Beccotruce trema quando ci sono dei Nani nelle vicinanze. Sembra che ne sia terrorizzato. Forse potresti portarlo in un posto pieno di Nani, in modo che si abitui al loro odore.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44974, 'koKR', '드워프의 냄새', '새끼 광포부리 팔코사우루스를 아이언포지에 있는 부싯돌 선술집으로 데려가야 합니다.', '<당신은 드워프가 주위에 다가올 때마다 새끼 광포부리 팔코사우루스가 몸을 떠는 걸 알아챘습니다. 드워프가 엄청 무서운가 봅니다. 새끼를 드워프가 많은 곳으로 데려가 보는 걸 어떨까요? 냄새에 익숙해지도록 말이죠...>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44974, 'ptBR', 'O cheiro dos anões', 'Leve seu Filhote de Bicaustero até a Taberna Pedra de Fogo em Altaforja.', '<Você repara que o filhote de Bicaustero treme sempre que tem anões por perto. Parece que ele tem pavor de anões. De repente, se você levá-lo a um lugar com muitos anões, ele acaba se acostumando com o cheiro deles.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44974, 'ruRU', 'Запах дворфов', 'Отведите детеныша злобоклюва в таверну \"Горючий камень\" в Стальгорне.', '<Вы обратили внимание, что ваш детеныш злобоклюва начинает дрожать от страха всякий раз, когда поблизости оказываются дворфы. Похоже, он их до жути боится. Вероятно, стоит отвести его куда-нибудь, где полно дворфов, чтобы он к ним попривык.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44974, 'zhCN', '矮人的气味', '带着你的恐嘴雏龙前往铁炉堡的石火旅店。', '<你发现只要有矮人靠近，这只恐嘴雏龙就会瑟瑟发抖。看来它真的很怕矮人。或许你能把它带到矮人聚集的地方，让它习惯这种气味。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44974, 'zhTW', '矮人的氣味', '帶你的恐喙小龍隼到鐵爐堡的石火旅店。', '<你發現恐喙小龍隼只要附近有矮人就會顫抖。看起來是嚇壞了。或許帶牠到矮人密集的地方，可以讓牠習慣矮人的氣味。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44975, 'deDE', 'Der Geruch von Tauren', 'Bringt Euer Düsterschnabeljunges zum Gasthaus von Donnerfels.', '<Ihr habt bemerkt, dass Euer Düsterschnabeljunges immer zittert, wenn Tauren in der Nähe sind. Anscheinend hat es große Angst vor ihnen. Vielleicht solltet Ihr es irgendwo hinbringen, wo es eine Menge Tauren gibt, damit es sich an den Geruch gewöhnen kann.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44975, 'esES', 'El olor de los tauren', 'Lleva a tu prole de Picoterrible a la posada de Cima del Trueno.', '<Has notado que tu prole de Picoterrible tiembla cuando hay tauren cerca. Parece tenerles autentico pánico. Quizás podrías llevarla a algún lugar donde haya muchos, para que se acostumbre al olor.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44975, 'esMX', 'El olor a tauren', 'Lleva a tu prole Picotemible a la posada de Cima del Trueno.', '<Notas que tu prole Picotemible se agita cuando los tauren están cerca. Parece que se siente aterrorizada. Tal vez puedas llevarla a un sitio con muchos Tauren para que se acostumbre al olor.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44975, 'frFR', 'L’odeur des Taurens', 'Amenez votre Nouveau-né bec-sinistre à l’Auberge des Pitons-du-Tonnerre.', '<Vous avez remarqué que votre nouveau-né bec-sinistre est pris de tremblements chaque fois qu’il aperçoit des Taurens. Leur seule vue semble le terrifier. Peut-être serait-il bon de le mettre en présence d’un grand nombre de Taurens afin qu’il s’habitue à leur odeur ?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44975, 'itIT', 'L\'odore dei Tauren', 'Porta il tuo Cucciolo di Beccotruce alla locanda di Picco del Tuono.', '<Hai notato che il tuo Cucciolo di Beccotruce trema quando ci sono dei Tauren nelle vicinanze. Sembra che ne sia terrorizzato. Forse potresti portarlo in un posto pieno di Tauren, in modo che si abitui al loro odore.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44975, 'koKR', '타우렌의 냄새', '새끼 광포부리 팔코사우루스를 썬더 블러프에 있는 여관으로 데려가야 합니다.', '<당신은 타우렌이 주위에 다가올 때마다 새끼 광포부리 팔코사우루스가 몸을 떠는 걸 알아챘습니다. 타우렌이 엄청 무서운가 봅니다. 새끼를 타우렌이 많은 곳으로 데려가 보는 걸 어떨까요? 냄새에 익숙해지도록 말이죠...>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44975, 'ptBR', 'O cheiro dos taurens', 'Leve seu Filhote de Bicaustero até a Estalagem no Penhasco do Trovão.', '<Você repara que o filhote de Bicaustero treme sempre que tem taurens por perto. Parece que ele tem pavor de taurens. De repente, se você levá-lo a um lugar com muitos taurens, ele acaba se acostumando com o cheiro deles.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44975, 'ruRU', 'Запах тауренов', 'Отведите детеныша злобоклюва в таверну на Громовом Утесе.', '<Вы обратили внимание, что ваш детеныш злобоклюва начинает дрожать от страха всякий раз, когда поблизости оказываются таурены. Похоже, он их до жути боится. Вероятно, стоит отвести его куда-нибудь, где полно тауренов, чтобы он к ним попривык.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44975, 'zhCN', '牛头人的气味', '带着你的恐嘴雏龙前往雷霆崖的旅店。', '<你发现只要有牛头人靠近，这只恐嘴雏龙就会瑟瑟发抖。看来它真的很怕牛头人。或许你能把它带到牛头人聚集的地方，让它习惯这种气味。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44975, 'zhTW', '牛頭人的氣味', '帶你的恐喙小龍隼到雷霆崖的旅店。', '<你發現恐喙小龍隼只要附近有牛頭人就會顫抖。看起來是嚇壞了。或許帶牠到牛頭人密集的地方，可以讓牠習慣牛頭人的氣味。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44976, 'deDE', 'Jagdlektion: Rosentaipan', 'Bezwingt 3 Rosentaipane in Sturmheim mit Eurem Düsterschnabeljungen.', '<Euer Düsterschnabeljunges scheint ganz aufgeregt zu sein, wenn Ihr Euch anderen Kleintieren nähert. Dies gilt insbesondere für die Rosentaipane aus Sturmheim. Es krächzt laut und rammt seinen Schnabel in den Boden, sobald einer in der Nähe ist. Anscheinend verspürt Euer Junges einen angeborenen Jagdinstinkt.> \n\n<Ihr müsst wohl mit Eurem Falkosaurier auf die Jagd gehen.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44976, 'esES', 'Lección de caza: taipan rosa', 'Derrota a 3 taipanes rosas en Tormenheim con tu prole de Picoterrible.', '<Tu prole de Picoterrible parece nerviosa siempre que te aproximas a otras alimañas, sobre todo a los taipanes rosas de Tormenheim. Grazna con fuerza y golpea el suelo con el pico siempre que hay uno cerca. Es como una necesidad depredadora primaria.>\n\n<Parece que tendrás que sacar a tu falcosaurio de caza.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44976, 'esMX', 'Lección de cacería: taipan rosa', 'Derrota a 3 taipanes rosa en Stormheim con tu prole Picotemible.', '<Tu prole Picotemible se pone nerviosa cerca de otras alimañas, especialmente el taipan rosa de Stormheim. Chilla fuerte y golpea el suelo con el pico cuando hay uno cerca. Parece sentir una necesidad primaria de depredación.> \n\n<Todo indica que vas a tener que sacar tu falcosaurio de cacería.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44976, 'frFR', 'Leçon de chasse : taïpans roses', 'Terrassez 3 Taïpans roses à Tornheim avec votre Nouveau-né bec-sinistre.', '<Votre nouveau-né bec-sinistre semble avoir du mal à se contrôler en présence d’autres bestioles, surtout s’il s’agit de taïpans roses de Tornheim. Il pousse de grands cris et frappe le sol de son bec chaque fois qu’il aperçoit l’une de ces créatures, comme si son instinct de prédateur reprenait le dessus.>\n\n<À l’évidence, il va vous falloir emmener votre falcosaure à la chasse.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44976, 'itIT', 'Lezione di caccia: Taipan Rossi', 'Sconfiggi 3 Taipan Rossi a Stromheim con il Cucciolo di Beccotruce al tuo fianco.', '<Il tuo Cucciolo di Beccotruce sembra agitarsi in presenza di altri animali, in particolare nelle vicinanze dei Taipan Rossi di Stromheim. Emette un forte suono e mette il becco nella terra ogni volta che uno di essi è nelle vicinanze. Sembra avere un istinto primordiale che lo porta a cacciarli.> \n\n<Probabilmente dovrai portarlo a caccia.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44976, 'koKR', '사냥 수업: 장밋빛 실뱀', '새끼 광포부리 팔코사우루스와 함께 스톰하임에 있는 장밋빛 실뱀 3마리를 꺾어야 합니다.', '<당신이 다른 생물체, 특히 스톰하임의 장밋빛 실뱀에 가까이 갈 때마다 새끼 광포부리 팔코사우루스가 흥분하는 것 같습니다. 주위에 장밋빛 실뱀이 있으면 항상 부리로 사납게 흙바닥을 쪼아 대며 깍깍 웁니다. 포식 본능을 갖고 있는 모양입니다.>\n\n<새끼 팔코사우루스를 데리고 사냥을 해야 할 것 같습니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44976, 'ptBR', 'Lição de caça: Rose Taipan', 'Derrote 3 Rose Taipans em Trommheim com seu Filhote de Bicaustero.', '<O Filhote de Bicaustero fica agitado quando perto de outros bichos, principalmente das rose taipans de Trommheim. Ele guincha alto e bate o bico no chão sempre que tem alguma por perto. Deve ser o instinto primitivo de predador.>\n\n<Pelo jeito, você vai ter que levar o seu falcossauro para caçar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44976, 'ruRU', 'Уроки охоты: розовые тайпаны', 'Убейте 3 розовых тайпанов в Штормхейме вместе с вашим детенышем злобоклюва.', '<Ваш детеныш злобоклюва явно возбуждается, когда поблизости оказываются другие зверьки, в особенности розовые тайпаны Штормхейма. Он громко вскрикивает и бьет клювом по земле, если видит одного из них. Судя по всему, в нем проявляется инстинкт хищника.> \n\n<Похоже, вам придется вывести вашего грифозавра на охоту.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44976, 'zhCN', '狩猎课：紫红泰斑蛇', '用你的恐嘴雏龙击败3条风暴峡湾的紫红泰斑蛇。', '<你一走近其他小动物，尤其是风暴峡湾的紫红泰斑蛇，你的恐嘴雏龙就会激动起来。它会竖起羽毛，大声嘶鸣。看来它已经展露出原始的掠食本能。>\n\n<你大概得把自己的隼龙带出去狩猎了。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44976, 'zhTW', '狩獵訓練：玫紅太攀蛇', '派你的恐喙小龍隼在斯鐸海姆擊敗3隻玫紅太攀蛇。', '<你的恐喙小龍隼在你靠近其他動物時就開始激動，看到斯鐸海姆的玫紅太攀蛇時尤其如此。有太攀蛇在附近的時候就會嘎嘎大叫，猛啄地面。看來這是牠的掠食本能。> \n\n<看來你得帶小龍隼出去狩獵了。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44977, 'deDE', 'Verbündete in Sturmheim', 'Schließt 5 Weltquests in Sturmheim in Begleitung Eures Düsterschnabeljungen ab.', '<Euer Düsterschnabeljunges scharrt im Boden und starrt finster einen der Valarjar an, der mehr als zehn Mal so groß ist.>\n\n<Die Valarjar müssen die Falkosaurier als willkommene neue Fleischquelle angesehen und sie sofort zu jagen begonnen haben. Sie sind zwar auf den Verheerten Inseln eine neue Spezies, aber sie fürchten bereits die Einwohner von Sturmheim.>\n\n<Zeigt Eurem Falkosaurier, dass Ihr gemeinsam mit den Valarjar zusammenarbeiten könnt.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44977, 'esES', 'Aliados en Tormenheim', 'Completa 5 misiones del mundo en Tormenheim con tu prole de Picoterrible fuera.', '<Tu prole de Picoterrible araña la tierra y mira con desprecio a un miembro de los Valarjar diez veces más grande.>\n\n<Los Valarjar deben de haber encontrado en los falcosaurios una magnífica fuente de carne y han comenzado a cazarlos de inmediato. A pesar de tratarse de una nueva especie en las Islas Abruptas, ya sabe lo que es temer a los habitantes de Tormenheim.>\n\n<Demuéstrale a tu falcosaurio que podéis colaborar con los Valarjar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44977, 'esMX', 'Aliados en Stormheim', 'Completa 5 misiones de mundo en Stormheim con tu prole Picotemible presente.', '<Tu prole Picotemible araña el suelo y mira con mala cara a un valarjar más de diez veces más grande.> \n\n<El valarjar habrá visto al falcosaurio como un excelente bocado y comienza a cazarlo de inmediato. Aunque es una nueva especie en las Islas Quebradas, ya teme a los habitantes de Stormheim.>\n\n<Muestra al falcosaurio que pueden trabajar juntos con el valarjar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44977, 'frFR', 'Des alliés à Tornheim', 'Accomplissez 5 expéditions à Tornheim en vous faisant accompagner de votre Nouveau-né bec-sinistre.', '<Votre nouveau-né bec-sinistre gratte le sol en lançant des regards hostiles à un Valarjar faisant plus de dix fois sa taille.>\n\n<Les Valarjar ont probablement vu dans les falcosaures une excellente source de viande et les ont chassés dès leur découverte. Bien qu’elle n’ait fait son apparition que récemment dans les îles Brisées, cette nouvelle espèce a déjà appris à craindre les habitants de Tornheim.>\n\n<Montrez à votre falcosaure qu’il est possible de collaborer avec les Valarjar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44977, 'itIT', 'Alleati a Stromheim', 'Completa 5 missioni mondiali a Stromheim con il Cucciolo di Beccotruce al tuo fianco.', '<Il tuo Cucciolo di Beccotruce scava per terra e fissa un membro dei Valarjar grosso quasi dieci volte lui.> \n\n<I Valarjar devono aver pensato ai falcosauri come a un\'eccellente fonte di carne, e hanno cominciato a cacciarli immediatamente. Sebbene i falcosauri siano una nuova specie sulle Isole Disperse, il tuo cucciolo ha già paura degli abitanti di Stromheim.>\n\n<Mostra al tuo falcosauro che tu e lui potete lavorare assieme ai Valarjar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44977, 'koKR', '스톰하임의 친구들', '새끼 광포부리 팔코사우루스와 함께 스톰하임에서 전역 퀘스트 5개를 완료해야 합니다.', '<새끼 광포부리 팔코사우루스가 바닥을 긁더니 자기보다 덩치가 열 배는 더 큰 발라리아르의 일원을 노려봅니다.>\n\n<발라리아르는 팔코사우루스를 훌륭한 고기 공급원으로 보고, 바로 팔코사우루스 사냥을 시작한 바 있습니다. 부서진 섬에 이제 발을 디딘 새로운 종이긴 하지만, 새끼는 이미 스톰하임의 거주민들을 두려워하고 있습니다.>\n\n<팔코사우루스에게 발라리아르와 조화롭게 지낼 수 있음을 보여 주십시오.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44977, 'ptBR', 'Aliados em Trommheim', 'Conclua 5 missões mundiais em Trommheim com seu Filhote de Bicaustero.', '<Seu Filhote de Bicaustero arranha o chão e encara um membro dos Valarjares dez vezes maior que ele.> \n\n<Os Valarjares devem ter usado os falcossauros como fonte de carne e começaram a caçá-los imediatamente. Embora seja uma nova espécie nas Ilhas Partidas, o animal já teme os habitantes de Trommheim.>\n\n<Mostre ao seu falcossauro que você e ele podem trabalhar junto com os Valarjares.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44977, 'ruRU', 'Союзники в Штормхейме', 'Выполните 5 локальных заданий в Штормхейме со своим детенышем злобоклюва.', '<Ваш детеныш злобоклюва роет лапой землю и пристально смотрит на одного из валарьяров в десять раз крупнее него.> \n\n<Вероятно, валарьярам понравилось мясо грифозавров, и они сразу же принялись охотиться на них. Эти создания появились на Расколотых островах совсем недавно, но уже боятся жителей Штормхейма.>\n\n<Покажите вашему грифозавру, что валарьяры ему не враги, и с ними вполне можно жить в мире.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44977, 'zhCN', '风暴峡湾的盟友', '带着你的恐嘴雏龙在风暴峡湾完成5个世界任务。', '<你的恐嘴雏龙刨着地面，死瞪着一个瓦拉加尔，这个瓦拉加尔有它的十倍大。>\n\n<瓦拉加尔一定是把隼龙当成了某种新出现的美味猎物，立刻就开始猎捕隼龙了。虽然它们是破碎群岛出现的新物种，却已经害怕起风暴峡湾的居民了。>\n\n<用行动向你的隼龙证明，它可以跟瓦拉加尔和谐相处。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44977, 'zhTW', '斯鐸海姆的盟友', '帶著你的恐喙小龍隼在斯鐸海姆完成5個世界任務。', '<你的恐喙小龍隼抓著地面，瞪視著眼前不知比牠大上幾倍的華爾拉亞。> \n\n<華爾拉亞一定認為龍隼是高級肉品，立刻就開始對牠們進行獵捕。龍隼雖然對破碎群島而言是新的物種，但是卻已經開始懼怕斯鐸海姆的住民了。>\n\n<讓你的小龍隼明白，你們和華爾拉亞可以和平共處。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44978, 'deDE', 'Team Düsterschnabel', 'Schließt 3 Haustierkampfweltquests auf den Verheerten Inseln mit Eurem Düsterschnabeljungen ab.', '<Euer Düsterschnabeljunges beißt Euch. Es scheint etwas zusammen mit Euch unternehmen zu wollen!>\n\n<Damit Euer Junges etwas lernt, muss es vor dem Beginn eines Kampfs an Eurer Seite und das erste Haustier sein, mit dem Ihr in den Kampf zieht, und darf nicht im Kampf sterben.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44978, 'esES', 'Equipo con Picoterrible', 'Completa 3 misiones del mundo de duelos de mascotas en las Islas Abruptas con tu prole de Picoterrible.', '<Tu prole de Picoterrible te muerde. ¡Parece que quiere hacer algo en equipo!>\n\n<Para aprender, tu prole debe estar a tu lado antes de que empieces un combate, debe ser tu primera mascota en entrar en combate y no puede morir en la refriega.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44978, 'esMX', 'Equipo Picotemible', 'Completa 3 misiones de mundo de duelos de mascotas en las Islas Quebradas con tu Prole picotemible.', '<Tu prole picotemible te muerde. ¡Es como si quisiera que hicieran algo juntos!>\n\n<Para aprender, tu prole tiene que estar a tu lado antes de que comience la batalla, tiene que ser la primera mascota en entrar a la lucha y no puede morir en la batalla.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44978, 'frFR', 'Au combat avec le bec-sinistre', 'Accomplissez 3 expéditions de combat de mascottes sur les îles Brisées avec votre Nouveau-né bec-sinistre.', '<Votre nouveau-né bec-sinistre vous mordille. Il semble avoir envie de faire quelque chose en équipe !>\n\n<Pour apprendre, votre nouveau-né doit être présent lorsque vous entamez un combat, doit être la première mascotte à combattre et ne doit pas mourir au cours de l’action.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44978, 'itIT', 'Fare squadra con il Beccotruce', 'Completa 3 missioni mondiali degli scontri tra mascotte con il tuo Cucciolo di Beccotruce sulle Isole Disperse.', '<Il tuo Cucciolo di Beccotruce ti morde. Sembra voler fare qualcosa assieme a te!>\n\n<Per imparare, il tuo cucciolo dev\'essere al tuo fianco all\'inizio di uno scontro, dev\'essere il primo a entrare in battaglia e non deve morire.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44978, 'koKR', '광포부리 팀 결성', '새끼 광포부리 팔코사우루스와 함께 부서진 섬 애완동물 대전 전역 퀘스트 3개를 완료해야 합니다.', '<새끼 광포부리 팔코사우루스가 당신을 깨뭅니다. 당신과 팀이 되어 무언가를 함께 해보고 싶나 봅니다!>\n\n<새끼 팔코사우루스를 가르치려면, 새끼가 전투 시작 전에 당신 곁에 있어야 하고, 가장 먼저 전투에 돌입하는 애완동물이어야 하며, 전투 중 죽지 않아야 합니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44978, 'ptBR', 'Equipe Bicaustero', 'Conclua 3 Missões Mundiais de Batalha de Mascotes nas Ilhas Partidas com seu Filhote de Bicaustero.', '<O filhote de bicaustero morde você. Parece que ele quer viver uma aventura, mas em dupla!>\n\n<Para aprender, o seu filhote precisa estar presente quando você iniciar a batalha, ser sua primeira mascote a entrar em combate e permanecer vivo durante toda a luta.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44978, 'ruRU', 'В команде со злобоклювом', 'Выполните 3 локальных задания по битвам питомцев вместе с вашим детенышем злобоклюва на Расколотых островах.', '<Птенец злобоклюва кусает вас. Похоже, ему скучно, и он хочет поразвлечься вместе с вами!>\n\n<Чтобы чему-то научиться, ваш птенец должен быть рядом с вами к началу сражения, первым вступить в бой и выжить.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44978, 'zhCN', '恐嘴小队', '在破碎群岛用你的恐嘴雏龙完成3个宠物对战世界任务。', '<你的恐嘴雏龙咬了咬你。它似乎是想参加集体活动！>\n\n<为了有所领悟，开始战斗前，你的雏龙必须在你身边，必须是你的宠物里第一个进入战斗的，且不能在战斗中死亡。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44978, 'zhTW', '與恐喙團隊合作', '派你的恐喙小龍隼在破碎群島贏得3個寵物對戰世界任務。', '<你的恐喙小龍隼在咬你。看樣子牠想跟你並肩合作！>\n\n<為了觀摩學習，戰鬥時請務必帶著小龍隼一起出戰，將其安排為第一個進入戰鬥的寵物，並確保牠不會在戰鬥中死亡。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44979, 'deDE', 'Düstere Beute', 'Schließt 1 seltene Eliteweltquest in Begleitung Eures Düsterschnabeljungen ab.', '<Euer Düsterschnabelfalkosaurier will unbedingt mit Euch in den Kampf ziehen und mehr darüber erfahren, wie man tödliche Kreaturen besiegt. Vielleicht kann er ja das ein oder andere lernen, wenn er Euch beim Kampf gegen einen mächtigen Gegner zusieht.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44979, 'esES', 'Presa nefasta', 'Completa 1 misión del mundo élite poco común con tu prole de Picoterrible fuera.', '<Tu falcosaurio Picoterrible casi te hace tropezar la siguiente vez que entras en combate. Está ansioso por aprender más sobre cómo derrotar a criaturas letales. Puede que aprenda un par de cosas si te ve luchar contra un oponente poderoso.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44979, 'esMX', 'Presa temible', 'Completa 1 misión de mundo inusual de élite con tu prole Picotemible.', '<Tu falcosaurio Picotemible casi te pisa cuando entras en combate. Está ansioso por aprender más acerca de cómo derrotar criaturas mortales. Podría aprender un par de cosas viéndote luchar contra un enemigo poderoso.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44979, 'frFR', 'Une proie redoutable', 'Accomplissez 1 expédition élite rare en vous faisant accompagner de votre Nouveau-né bec-sinistre.', '<Votre nouveau-né bec-sinistre a manqué de vous faire trébucher lors de votre dernier combat. On dirait qu’il est impatient d’apprendre à vaincre des créatures particulièrement coriaces. Vous voir affronter un adversaire puissant lui enseignerait sans doute une chose ou deux.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44979, 'itIT', 'Una preda difficile', 'Completa 1 missione mondiale rara élite con il Cucciolo di Beccotruce al tuo fianco.', '<Il tuo falcosauro Beccotruce ti fa quasi inciampare non appena provi a entrare in combattimento. Sarà meglio insegnargli qualcosa in più su come uccidere creature letali. Potrebbe imparare un paio di cose osservandoti mentre combatti contro un avversario potente.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44979, 'koKR', '광포한 먹잇감', '새끼 광포부리 팔코사우루스와 함께 희귀 정예 전역 퀘스트 1개를 완료해야 합니다.', '<당신이 새로 전투를 벌이려고 하자 새끼 광포부리 팔코사우루스가 달려드는 바람에 거의 넘어질 뻔했습니다. 강력한 상대를 쓰러트리는 법을 배우려는 새끼의 집념이 대단합니다. 당신이 강력한 상대와 싸우는 걸 옆에서 구경하면 당신의 기술 한두 개를 배울 수 있을지도 모릅니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44979, 'ptBR', 'Presa de Bicaustero', 'Conclua 1 missão mundial de elite rara com o Filhote de Bicaustero.', '<Seu falcossauro Bicaustero quase derruba você quando você entra em combate. Ele está ansioso para aprender como derrotar criaturas letais. Talvez ele aprenda algo vendo você batalhar contra um oponente poderoso.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44979, 'ruRU', 'Жуткая добыча', 'Выполните одно редкое элитное локальное задание со своим детенышем злобоклюва.', '<Ваш детеныш злобоклюва едва не сбил вас с ног, когда вы начали сражение. Он хочет побольше узнать о том, как можно победить опасного врага. Возможно, он сможет что-то у вас перенять, наблюдая за вашим боем с достаточно сильным противником.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44979, 'zhCN', '可怕的猎物', '带着你的恐嘴雏龙完成1个稀有精英世界任务。', '<在第二场战斗中，你的恐嘴雏龙差点把你绊倒。它很想学习怎么击败致命的生物。如果能让它旁观你是怎么对抗强敌的，它或许能从中领悟到些什么。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44979, 'zhTW', '恐喙獵物', '帶著你的恐喙小龍隼完成1個稀有精英世界任務。', '<當你回到戰鬥時，你的恐喙龍隼差點把你絆倒。牠太興奮了，想立即學會擊倒致命敵人的方法。若你遇到強大的敵人時能讓牠旁觀，或許牠可以因此學會幾招。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44980, 'deDE', 'Jagdlektion: Winzige Erscheinung', 'Besiegt 3 winzige Erscheinungen in Sturmheim mit Eurem Düsterschnabeljungen.', '<Euer Düsterschnabeljunges scheint Beute sehen zu können, die Euch verborgen bleibt, und starrt manchmal stundenlang die Wand an. Es muss eine neue Beuteart gefunden oder den Schnabel zu tief in den Met gesteckt haben.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44980, 'esES', 'Lección de caza: aparición diminuta', 'Derrota a 3 apariciones diminutas en Tormenheim con tu prole de Picoterrible.', '<Tu prole de Picoterrible parece ver presas que tú no puedes ver y, a veces, se queda observando un muro durante horas. Debe de haber encontrado una nueva especie que cazar. O eso, o le está dando al hidromiel.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44980, 'esMX', 'Lección de cacería: aparición diminuta', 'Derrota a 3 apariciones diminutas en Stormheim con tu prole Picotemible.', '<Tu prole Picotemible parece percibir presas que tú no ves y a veces se queda mirando varias horas a la pared. Tal vez descubrió una nueva especie a la que cazar... o bebió algo de hidromiel.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44980, 'frFR', 'Leçon de chasse : minuscules apparitions', 'Terrassez 3 Minuscules apparitions à Tornheim avec votre Nouveau-né bec-sinistre.', '<Il arrive à votre nouveau-né bec-sinistre de fixer un mur pendant des heures, comme s’il voyait des proies invisibles. Il doit avoir trouvé une nouvelle espèce à traquer... ou abusé de l’hydromel.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44980, 'itIT', 'Lezione di caccia: Piccole Apparizioni', 'Sconfiggi 3 Piccole Apparizioni a Stromheim con il tuo Cucciolo di Beccotruce.', '<Il tuo Cucciolo di Beccotruce sembra in grado di percepire delle prede che tu non riesci a vedere. A volte fissa il muro per ore. Deve aver trovato una nuova specie da cacciare... o forse è solo caduto in un barile di idromele.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44980, 'koKR', '사냥 수업: 조그마한 귀신', '새끼 광포부리 팔코사우루스와 함께 스톰하임에 있는 조그마한 귀신 3마리를 꺾어야 합니다.', '<새끼 광포부리 팔코사우루스가 당신이 보지 못하는 먹잇감을 보는지, 가끔 몇 시간씩 벽을 쳐다보곤 합니다. 먹잇감으로 삼을 새로운 종을 발견한 모양입니다. 아니면 벌꿀주를 너무 많이 마셨거나요.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44980, 'ptBR', 'Lição de caça: Apariçãozinha', 'Derrote 3 Apariçõezinhas em Trommheim com seu Filhote de Bicaustero.', '<Seu filhote de Bicaustero parece ser capaz de ver presas que você não consegue ver, e às vezes passa horas olhando para a parede. Ele deve ter encontrado uma nova espécie de presa, ou tomado uma caneca de hidromel.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44980, 'ruRU', 'Уроки охоты: крохотная тень', 'Победите 3 крохотные тени в Штормхейме со своим детенышем злобоклюва.', '<Ваш детеныш злобоклюва выслеживает видимую лишь ему одному добычу. Иногда он часами неподвижно смотрит на стену. Должно быть, он обнаружил новый вид, на который можно охотиться, ну или просто перепил меда.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44980, 'zhCN', '狩猎课：小幽灵', '用你的恐嘴雏龙击败3个风暴峡湾的小幽灵。', '<你的恐嘴雏龙似乎能看见你看不到的猎物，有时候会盯着一堵墙好几小时。它要么是发现了一种新猎物，要么就是蜜酒喝多了。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44980, 'zhTW', '狩獵訓練：小小亡魂', '派你的恐喙小龍隼在斯鐸海姆擊敗3個小小亡魂。', '<你的恐喙小龍隼似乎能發現你看不到的獵物，常常會盯著牆看上好幾小時。牠一定是找到了新的獵物，不然就是偷喝酒醉了。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44981, 'deDE', 'Die unfreundliche Fraktion', 'Schließt 3 PvP-Weltquests in Begleitung Eures Düsterschnabeljungen ab.', '<Als Ihr vor Kurzem an einem Mitglied der anderen Fraktion vorbeigegangen seid, hat Euer Düsterschnabel ihn angekrächzt, um Nahrung zu erbetteln. Er versteht wohl nicht, dass es sich um den Feind handelt. Ihr solltet es ihm besser beibringen, bevor etwas Schlimmes passiert.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44981, 'esES', 'La facción poco amable', 'Completa 3 misiones del mundo de JcJ con tu prole de Picoterrible fuera.', '<Al pasar junto a la otra facción hace poco, tu Picoterrible les graznó pidiendo comida. No parece entender que son el enemigo. Seguramente convenga enseñarle antes de que pase algo malo.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44981, 'esMX', 'La facción inamistosa', 'Completa 3 misiones de mundo en modo JcJ con tu prole Picotemible presente.', '<Al pasar hace poco junto a la otra facción, tu Picotemible chilló para pedirles comida. No parece entender que son el enemigo. Probablemente sea mejor enseñárselo antes de que suceda algo malo.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44981, 'frFR', 'Fielleuse faction', 'Accomplissez 3 expéditions JcJ en vous faisant accompagner de votre Nouveau-né bec-sinistre.', '<Alors qu’il passait devant des représentants de l’autre faction, votre bec-sinistre leur a bruyamment réclamé de la nourriture. Il ne semble pas se rendre compte que ce sont ses ennemis. Mieux vaudrait le lui faire comprendre avant qu’un drame ne se produise.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44981, 'itIT', 'La fazione opposta', 'Completa 3 missioni mondiali PvP con il Cucciolo di Beccotruce al tuo fianco.', '<Quando il tuo Beccotruce si è imbattuto in membri della fazione opposta, ha fatto alcuni versi, come se stesse chiedendo loro del cibo. Non sembra capire che sono dei nemici. Sarà meglio insegnarglielo, prima che accada qualcosa di brutto.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44981, 'koKR', '불친절한 진영', '새끼 광포부리 팔코사우루스와 함께 PvP 전역 퀘스트 3개를 완료해야 합니다.', '<최근 상대 진영의 병력을 지나친 적이 있었는데, 새끼 광포부리 팔코사우루스가 먹이를 달라고 그 병력을 향해 깍깍 운 일이 있었습니다. 새끼는 그들이 적이라는 사실을 이해하지 못하는 것 같습니다. 행여나 큰일이 나기 전에 새끼에게 이 사실을 가르쳐야 합니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44981, 'ptBR', 'A facção hostil', 'Conclua 3 missões mundiais JxJ com o Filhote de Bicaustero.', '<Ao passar por alguns membros da facção contrária recentemente, seu Bicaustero pediu comida para eles. Parece que o filhote não entende que eles são inimigos. É melhor ensinar antes que aconteça alguma coisa de ruim.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44981, 'ruRU', 'Враждебная фракция', 'Выполните три локальных PvP-задания с вашим детенышем злобоклюва.', '<Недавно вам случалось несколько раз сталкиваться с представителями противоборствующей фракции, и ваш злобоклюв радостно несся навстречу им в надежде выпросить еды. Похоже, он еще не умеет различать друзей и врагов. Нужно научить его этому, пока с ним не приключилось беды.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44981, 'zhCN', '八字不合', '带着你的恐嘴雏龙完成3个PvP世界任务。', '<最近，你路过对立阵营的地盘时，你的恐嘴雏龙朝他们大叫，讨要食物。它大概还不明白，对立阵营的人都是敌人。你最好在它惹出麻烦前先教教它。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44981, 'zhTW', '不仁慈的陣營', '帶著你的恐喙小龍隼完成3個PvP世界任務。', '<最近遇到另一個陣營的成員時，你的恐喙小龍隼會向他們發出叫聲討食物吃。牠似乎不明白對方是敵人。最好加以教導，以防萬一。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44982, 'deDE', 'Düsterschnabelbande', 'Kauft falsche Zähne vom Aufziehgehilfen in Dalaran für Euer Düsterschnabeljunges.', '<Euer Düsterschnabeljunges plustert sich auf und knurrt Euch an, um seine Dominanz zu zeigen. Es ist wohl etwas enttäuscht, dass Ihr nicht eingeschüchtert zusammenzuckt. Vielleicht möchte es sich als ausgewachsener Falkosaurier verkleiden und es erneut versuchen.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44982, 'esES', 'Estrechar lazos con tu Picoterrible', 'Compra unos dientes falsos al ayudante robot cohete de cuerda en Dalaran para tu prole de Picoterrible.', '<Tu prole de Picoterrible se hincha el plumaje y te gruñe en un alarde de dominación. No parece contenta del resultado, ya que no te encoges de miedo. Quizás estaría bien engalanarla como un falcosaurio adulto y que volviera a intentarlo.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44982, 'esMX', 'Unión de Picotemible', 'Compra a tu prole Picotemible un par de dientes falsos en Ayudante robot cohete de cuerda, en Dalaran.', '<Tu prole Picotemible hincha las plumas y te gruñe en una muestra de dominio. No parece contenta con el resultado, pues no huyes aterrorizado. Tal vez le guste vestirse de falcosaurio adulto e intentarlo otra vez.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44982, 'frFR', 'Jamais sans mon bec-sinistre', 'Achetez de Fausses dents pour votre Nouveau-né bec-sinistre à l’Assistant mécanique de Dalaran.', '<Votre nouveau-né bec-sinistre gonfle son plumage et grogne pour tenter de vous intimider. Il est manifestement déçu de constater que vous ne vous recroquevillez pas de peur. Peut-être aimerait-il se déguiser en falcosaure adulte pour réessayer ?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44982, 'itIT', 'Legame con il Beccotruce', 'Compra un paio di Denti Finti per il tuo Cucciolo di Beccotruce dal Costrutto Assistente a Dalaran.', '<Il tuo Cucciolo di Beccotruce gonfia le piume e gracchia verso di te in segno di superiorità. Non sembra molto contento del risultato, visto che non stai scappando $gterrorizzato:terrorizzata;. Potrebbe provare a travestirsi da falcosauro adulto e riprovare...>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44982, 'koKR', '광포부리와 친해지기', '달라란에 있는 태엽돌이 조수에게서 새끼 광포부리 팔코사우루스에게 줄 가짜 이빨 한 쌍을 구입해야 합니다.', '<새끼 광포부리 팔코사우루스가 깃털을 세우고 당신을 향해 으르렁댑니다. 자신의 우월함을 과시하려는 것이겠지요. 당신이 무서워하는 모습을 보이지 않자, 풀이 죽은 것 같습니다. 다 자란 팔코사우루스처럼 변장시키고 다시 한번 시켜보면 좋아할지도 모릅니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44982, 'ptBR', 'Sua relação com o Bicaustero', 'Compre um par de Dentes Falsos do Assistente de Corda em Dalaran para seu Filhote de Bicaustero.', '<Seu filhote de Bicaustero guincha e estufa o peito para você, exibindo comportamento agonístico. Ele não está gostando do resultado, pois você não está se encolhendo de medo. E se ele tentasse se fantasiar de falcossauro adulto antes de tentar de novo?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44982, 'ruRU', 'Сближение с злобоклювом', 'Купите вашему детенышу злобоклюва пару фальшивых зубов у заводного помощника в Даларане.', '<Ваш детеныш злобоклюва взъерошивает перья и рычит на вас, пытаясь продемонстрировать свое превосходство. Судя по всему, результатом он не слишком-то доволен, так как напугать вас у него не вышло. Попробуйте нарядить его взрослым грифозавром – может быть, это сработает.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44982, 'zhCN', '恐嘴的羁绊', '前往达拉然找发条助手，为你的恐嘴雏龙购买一副假牙。', '<你的恐嘴雏龙竖起身上的羽毛，对你低声咆哮，宣示自己的权威。但它对结果不太满意，因为你没有吓得瑟瑟发抖。也许它想要打扮成成年隼龙再试一次。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44982, 'zhTW', '跟恐喙小龍隼培養感情', '從達拉然的發條助手那裡買一副假牙給你的恐喙小龍隼。', '<你的恐喙小龍隼對你豎起全身羽毛，發出帶有爭霸意味的吼叫。看到你並沒有因害怕而退縮，牠似乎很不滿意。牠可能會想要裝扮成成年龍隼，再試一次。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44983, 'deDE', 'Jagdlektion: Goldadlerjunges', 'Bezwingt 3 Goldadlerjunge in Sturmheim mit Eurem Düsterschnabeljungen und 2 fliegenden Haustieren.', '<Euer Düsterschnabeljunges hat sich mit einigen der anderen fliegenden Haustiere angefreundet. Die Schar ist darauf aus, ein paar Adler aus Sturmheim zu jagen.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44983, 'esES', 'Lección de caza: aguilucho dorado', 'Derrota a 3 aguiluchos dorados en Tormenheim con tu prole de Picoterrible y 2 mascotas de la familia Volador.', '<Tu prole de Picoterrible se ha hecho amiga de otras mascotas voladoras. La bandada parece interesada en cazar unas cuantas águilas de Tormenheim.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44983, 'esMX', 'Lección de cacería: aguilucho dorado', 'Derrota a 3 aguiluchos dorados en Stormheim con tu prole Picotemible y 2 mascotas voladoras.', '<Tu prole Picotemible se hizo amiga de otras mascotas voladoras. La bandada parece interesada en cazar águilas en Stormheim.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44983, 'frFR', 'Leçon de chasse : aiglons dorés', 'Terrassez 3 Aiglons dorés à Tornheim avec votre Nouveau-né bec-sinistre et 2 Mascottes volantes.', '<Votre nouveau-né bec-sinistre s’est lié d’amitié avec certaines des autres mascottes volantes. Ces dernières semblent vouloir partir chasser des aigles de Tornheim.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44983, 'itIT', 'Lezione di caccia: Cuccioli d\'Aquila Dorato', 'Sconfiggi 3 Cuccioli d\'Aquila Dorati a Stromheim con il tuo Cucciolo di Beccotruce e 2 mascotte Volanti.', '<Il tuo Cucciolo di Beccotruce ha fatto amicizia con altre mascotte volanti. Sembra che lo stormo voglia cacciare alcune aquile di Stromheim.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44983, 'koKR', '사냥 수업: 작은 황금독수리', '새끼 광포부리 팔코사우루스와 비행 애완동물 2마리와 함께 스톰하임에 있는 작은 황금독수리 3마리를 꺾어야 합니다.', '<새끼 서리깃 팔코사우루스가 다른 비행 애완동물 몇 마리와 친구가 되었습니다. 새끼와 친구들은 스톰하임의 독수리 사냥에 관심이 있는 모양입니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44983, 'ptBR', 'Lição de caça: Aguiazinha Dourada', 'Derrote 3 Aguiazinhas Douradas em Trommheim com seu Filhote de Bicaustero e 2 mascotes do tipo Voador.', '<O Filhote de Bicaustero fez amizade com outros bichos voadores. Parece que o bando está a fim de caçar águias em Trommheim.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44983, 'ruRU', 'Уроки охоты: золотой орленок', 'Победите 3 золотых орлят вместе с вашим детенышем злобоклюва и двумя летающими питомцами.', '<Ваш детеныш злобоклюва подружился с другими летающими питомцами. Стая хочет поохотиться на орлов в Штормхейме.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44983, 'zhCN', '狩猎课：金色小鹰', '用你的恐嘴雏龙和2只飞行类宠物击败3只风暴峡湾的金色小鹰。', '<你的恐嘴雏龙跟其他飞行宠物交上了朋友。它们好像对狩猎风暴峡湾的鹰很有兴趣。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44983, 'zhTW', '狩獵訓練：黃金雛鷹', '派你的恐喙小龍隼和2隻飛行寵物在斯鐸海姆擊敗3隻黃金雛鷹。', '<你的恐喙小龍隼已經和其他飛行寵物變成朋友了。牠們似乎對獵捕斯鐸海姆的鷹類很感興趣。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44984, 'deDE', 'Teamworkunterricht: Fenryr', 'Besiegt Fenryr in den Hallen der Tapferkeit in Sturmheim in Begleitung Eures Düsterschnabeljungen.', '<Euer Düsterschnabeljunges ist den anderen fliegenden Haustieren bei den letzten gemeinsamen Kämpfen scheu ausgewichen. Vielleicht solltet Ihr ihm zeigen, wie gutes Teamwork funktioniert.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44984, 'esES', 'Lección de trabajo en equipo: Fenryr', 'Derrota a Fenryr en las Cámaras del Valor de Tormenheim con tu prole de Picoterrible fuera.', '<Tu prole de Picoterrible evitó con timidez al resto de mascotas voladoras durante los últimos duelos. Darle un buen ejemplo de trabajo en equipo podría ayudar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44984, 'esMX', 'Lección de trabajo en equipo: Fenryr', 'Derrota a Fenryr en los Salones del Valor en Stormheim con tu prole Picotemible fuera.', '<Tu prole Picotemible evitó tímidamente a otras mascotas voladoras durante sus últimas batallas juntos. Mostrarle un buen ejemplo de trabajo en equipo podría ayudar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44984, 'frFR', 'Apprentissage de la coopération : Fenryr', 'Terrassez Fenryr dans les Salles des Valeureux à Tornheim en vous faisant accompagner de votre Nouveau-né bec-sinistre.', '<Votre nouveau-né bec-sinistre n’a pas osé s’approcher de vos autres mascottes volantes lors de vos derniers combats. Lui enseigner les rudiments de la coopération pourrait lui être profitable.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44984, 'itIT', 'Lezione sul lavoro di squadra: Fenryr', 'Sconfiggi Fenryr alle Sale del Valore nella regione di Stromheim con il Cucciolo di Beccotruce al tuo fianco.', '<Il tuo Cucciolo di Beccotruce ha timidamente evitato le altre mascotte Volanti durante le loro ultime battaglie. Una lezione sul lavoro di squadra potrebbe essergli d\'aiuto.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44984, 'koKR', '팀워크 수업: 펜리르', '새끼 광포부리 팔코사우루스와 함께 스톰하임에 있는 용맹의 전당의 펜리르를 처치해야 합니다.', '<지난 몇 번의 싸움에서 새끼 광포부리 팔코사우루스는 다른 비행 애완동물을 주저주저 피하는 모습을 보였습니다. 팀워크가 어떤 것인지 본보기를 보여 주는 게 도움이 될 것 같습니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44984, 'ptBR', 'Lição de trabalho em equipe: Fenryr', 'Derrote Fenryr nos Salões da Bravura em Trommheim com seu Filhote de Bicaustero.', '<Seu Filhote de Bicaustero ficou evitando as outras mascotes voadoras nas últimas batalhas. Mostre a ele um bom exemplo de trabalho em equipe.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44984, 'ruRU', 'Работа в команде: Фенрир', 'Победите Фенрира в Чертогах Доблести в Штормхейме со своим детенышем злобоклюва.', '<Ваш детеныш злобоклюва избегал других летающих питомцев во время их недавних совместных сражений. Нужно продемонстрировать ему преимущества слаженной командной игры.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44984, 'zhCN', '团队合作：芬雷尔', '带着你的恐嘴雏龙，在风暴峡湾击败英灵殿的芬雷尔。', '<在前几次战斗中，你的恐嘴雏龙很害羞，总是躲着其他的飞行宠物。让它瞧瞧，团队合作应该是什么样子的，这样也许有帮助。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44984, 'zhTW', '團隊訓練：芬里爾', '帶著你的恐喙小龍隼在斯鐸海姆的英靈殿擊敗芬里爾。', '<你的恐喙小龍隼先前幾場戰鬥中，會怯懦地避開其他飛行生物。讓牠明白什麼叫做團隊合作應該會有所幫助。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44985, 'deDE', 'Jagdlektion: Sturmschlagbiber', 'Besiegt 3 Sturmschlagbiber in Sturmheim mit Eurem Düsterschnabeljungen und 2 mechanischen Haustieren.', '<Euer Düsterschnabeljunges betrachtet misstrauisch jede laute Maschine, an der Ihr vorbeigeht. Ihr beginnt Euch zu fragen, ob etwas zwischen dem Jungen und einem mechanischen Haustier vorgefallen ist. Wenn es mit ihnen zurechtkommen soll, muss es lernen, an ihrer Seite zu kämpfen. Vielleicht hilft es, wenn Ihr gemeinsam eine neue Beute verfolgt.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44985, 'esES', 'Lección de caza: castor Tormentogolpeado', 'Derrota a 3 castores Tormentogolpeados en Tormenheim con tu prole de Picoterrible y 2 mascotas de la familia Máquina.', '<Tu prole de Picoterrible mira con desconfianza cuando pasáis junto a cualquier máquina que haga ruido. Empiezas a preguntarte si alguna vez le pasó algo con una mascota mecánica. Tendrán que aprender a trabajar juntas si quieren llevarse bien. Podría venirles bien cazar juntas la nueva presa que ha empezado a acechar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44985, 'esMX', 'Lección de cacería: Castor tormentoso', 'Derrota a 3 castores tormentosos en Stormheim con tu prole Picotemible y 2 mascotas mecánicas.', '<Tu prole Picotemible tiene una cautela especial con las maquinarias ruidosas junto a las que pasas. Te preguntas si algo le sucedió con una mascota mecánica. Deben aprender a trabajar juntos. Una buena ocasión para aprender podría ser la cacería de la nueva presa que hay al acecho.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44985, 'frFR', 'Leçon de chasse : castors frappés par la foudre', 'Terrassez 3 Castors frappés par la foudre à Tornheim avec votre Nouveau-né bec-sinistre et 2 Mascottes mécaniques.', '<Votre nouveau-né bec-sinistre jette un regard méfiant à toutes les machines bruyantes devant lesquelles vous passez. Vous commencez à vous demander s’il n’aurait pas eu maille à partir avec une mascotte mécanique par le passé. Quoi qu’il en soit, vos mascottes vont devoir apprendre à coopérer. Profitez du fait qu’il s’intéresse à une nouvelle proie pour organiser une chasse commune.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44985, 'itIT', 'Lezione di caccia: Castori Folgoranti', 'Sconfiggi 3 Castori Folgoranti a Stromheim con il tuo Cucciolo di Beccotruce e 2 mascotte Meccaniche.', '<Il tuo Cucciolo di Beccotruce osserva circospetto ogni pezzo di macchinario in cui si imbatte. Cominci a chiederti se in passato sia successo qualcosa tra il tuo cucciolo e qualche mascotte Meccanica. Se vogliono lavorare insieme, dovranno imparare il lavoro di squadra. Cacciare insieme una nuova preda potrebbe essere una buona idea.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44985, 'koKR', '사냥 수업: 폭풍일격 비버', '새끼 광포부리 팔코사우루스와 기계 애완동물 2마리와 함께 스톰하임에 있는 폭풍일격 비버 3마리를 꺾어야 합니다.', '<당신이 시끄러운 기계 옆을 지나갈 때마다 새끼 광포부리 팔코사우루스가 기계를 경계하는 눈초리로 쳐다봅니다. 어쩌면 예전에 새끼와 기계 애완동물 사이에 무슨 일이 있었던 건 아닌가 하는 생각이 듭니다. 서로 사이좋게 지내려면 협동하는 법을 배우는 게 좋겠습니다. 함께, 새끼가 요즘 쫓아다니기 시작한 새로운 먹잇감을 사냥해 보는 건 어떨까요?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44985, 'ptBR', 'Lição de caça: Castor Tempestífero', 'Derrote 3 Castores Tempestíferos em Trommheim com seu Filhote de Bicaustero e 2 mascotes do tipo Mecânico.', '<O Filhote de Bicaustero olha preocupado para os itens mecânicos pelos quais você passa. Será que aconteceu alguma coisa entre ele e uma mascote mecânica? Eles vão ter que aprender a trabalhar em equipe para conviver. Se forem caçar juntos a presa que o Bicaustero anda perseguindo, o problema deve se resolver.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44985, 'ruRU', 'Уроки охоты: грозовые бобры', 'Победите 3 грозовых бобров вместе с вашим детенышем злобоклюва и двумя механическими питомцами.', '<Ваш детеныш злобоклюва с опаской смотрит на все встречающиеся механизмы. Вас посещает мысль, что, возможно, его когда-то обидел механический питомец. Нужно найти им общее занятие, чтобы они подружились. Нужно дать ему поохотиться в компании на новую жертву, которую он как раз начал выслеживать, и тогда, возможно, все получится.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44985, 'zhCN', '狩猎课：风暴海狸', '用你的恐嘴雏龙和2只机械类宠物击败3只风暴海狸。', '<你每次经过声音嘈杂的机器，你的恐嘴雏龙就会警戒地看着它。你不禁怀疑，它以前是不是跟机械类宠物有什么过节。要让它们和睦相处，就得让它们学会合作。让它们一起狩猎新猎物的话，或许就能达成这个目的。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44985, 'zhTW', '狩獵訓練：雷擊海狸', '派你的恐喙小龍隼和2隻機械寵物在斯鐸海姆擊敗3隻雷擊海狸。', '<每次你們經過聲音響亮的機械，你的恐喙小龍隼就會用充滿戒備地眼神盯著看。你開始猜想牠是不是曾經跟某個機械寵物有什麼過節。牠們需要學習該如何合作才能和睦相處。一起獵殺牠最近喜歡的新獵物或許能改善這一點。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44986, 'deDE', 'Teamworkunterricht: Krosus', 'Besiegt Krosus in der Nachtfestung in Begleitung Eures Düsterschnabeljungen.', '<Euer Düsterschnabeljunges krächzt laut und wird panisch, wenn sich große Wesen in der Nähe befinden. Und das sogar, wenn sie friedlich sind. Es muss lernen, den Belastungen standzuhalten, selbst wenn es Kreaturen gegenübersteht, die viele Male größer sind. Es sollte helfen, wenn Ihr ihm ein herausragendes Beispiel für Teamwork gegen ein gigantisches Monster zeigt.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44986, 'esES', 'Lección de trabajo en equipo: Krosus', 'Derrota a Krosus en el Bastión Nocturno con tu prole de Picoterrible fuera.', '<Tu prole de Picoterrible suelta fuertes graznidos y entra en pánico cuando hay criaturas muy grandes cerca, aunque sean amistosas. Debe aprender a mantener la calma bajo presión, aun cuando se enfrente a criaturas de un tamaño varias veces superior al suyo. Darle un ejemplo épico de trabajo en equipo contra un monstruo grande como una montaña podría ayudar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44986, 'esMX', 'Lección de trabajo en equipo: Krosus', 'Derrota a Krosus en la Fortaleza Nocturna con la prole Picotemible fuera.', '<Tu prole Picotemible chilla y se aterroriza cuando hay cerca criaturas muy grandes, aunque sean amistosas. Debe aprender a mantener la calma bajo presión, incluso ante criaturas de un tamaño mucho más grande. Una demostración épica de trabajo en equipo contra un monstruo del tamaño de una montaña podría ayudar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44986, 'frFR', 'Apprentissage de la coopération : Krosus', 'Terrassez Krosus dans le Palais Sacrenuit en vous faisant accompagner de votre Nouveau-né bec-sinistre.', '<Votre nouveau-né bec-sinistre pousse de grands cris de panique lorsqu’il se trouve en présence de créatures particulièrement imposantes, qu’elles soient hostiles ou amicales. Il doit apprendre à garder son calme sous la pression, même quand il affronte des adversaires faisant plusieurs fois sa taille. Il pourrait être utile de lui montrer que collaborer lors d’un combat épique contre un monstre aussi grand qu’une montagne peut faire toute la différence.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44986, 'itIT', 'Lezione sul lavoro di squadra: Krosus', 'Sconfiggi Krosus nella Rocca della Notte nella regione di Suramar con il Cucciolo di Beccotruce al tuo fianco.', '<Il tuo Cucciolo di Beccotruce gracchia vivacemente e va nel panico quando delle creature più grandi sono nelle vicinanze, anche se sono amichevoli. Deve imparare a rimanere calmo sotto pressione, anche quando affronta delle creature molto più grandi di lui. Mostrargli un esempio di lavoro di squadra contro un mostro grande come una montagna potrebbe aiutare.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44986, 'koKR', '팀워크 수업: 크로서스', '새끼 광포부리 팔코사우루스와 함께 밤의 요새에 있는 크로서스를 처치해야 합니다.', '<새끼 광포부리 팔코사우루스가 적이든 아군이든 큰 생물체 옆에 가기만 하면 기겁을 하고 크게 울어댑니다. 자기보다 몇 배는 큰 생물체와 마주하는 압박 속에서도 냉정을 잃지 않는 법을 배워야 합니다. 함께 산만큼 큰 괴물을 상대하며 멋진 팀워크를 발휘해 보면 도움이 될지도 모릅니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44986, 'ptBR', 'Lição de trabalho em equipe: Krosus', 'Derrote Krosus no Baluarte da Noite com seu Filhote de Bicaustero.', '<O Filhote de Bicaustero guincha alto e entra em pânico quando criaturas muito grandes estão por perto, mesmo se forem amigas. Ele tem que aprender a manter a calma diante de criaturas maiores. Dar um bom exemplo de trabalho em equipe contra um monstro do tamanho de uma montanha deve ajudar.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44986, 'ruRU', 'Работа в команде: Крос', 'Победите Кроса в Цитадели Ночи вместе с вашим детенышем злобоклюва.', '<Ваш детеныш злобоклюва громко вскрикивает и пугается, когда видит очень больших существ, даже если они дружелюбные. Он должен научиться сохранять спокойствие в тяжелых ситуациях, даже когда сталкивается с существами намного больше его самого. Возможно, ему стоит понаблюдать за слаженной работой в сражении с гигантским чудовищем.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44986, 'zhCN', '团队合作：克洛苏斯', '带着你的恐嘴雏龙击败暗夜要塞的克洛苏斯。', '<你的恐嘴雏龙只要看到巨兽，就会惊慌大叫，哪怕那些生物并无敌意。它得学会顶住压力，保持冷静，哪怕是要面对那些巨兽。与一头巨兽打一场史诗之战，展示一下团队合作精神，这样它可能就会明白。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44986, 'zhTW', '團隊訓練：克羅索斯', '帶著你的恐喙小龍隼在暗夜堡擊敗克羅索斯。', '<你的恐喙小龍隼在附近有巨大生物時都會驚恐地嘎嘎大叫，就算這些生物很友善也一樣。牠得學會在壓力之下保持冷靜，就算眼前是體型好幾倍大的生物也一樣。讓牠見識一下如何利用團隊合作來擊敗像山一樣巨大的怪物，或許會有所幫助。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44987, 'deDE', 'Randale im Team mit Düsterschnabel', 'Schließt 3 Haustierkampf-Weltquests mit Eurem Düsterschnabeljungen und 2 humanoiden Haustieren ab.', '<Sieht so aus, als wäre Euer Düsterschnabeljunges bereit, sich mit Euch in ein paar neue Kämpfe zu stürzen, um zu zeigen, was es über Teamwork gelernt hat. Wenn Ihr es mit neuen Verbündeten zusammen anweisen würdet, wäre dies ein ausgezeichneter Test seiner Fähigkeiten.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44987, 'esES', 'Pelea en equipo con Picoterrible', 'Completa 3 misiones del mundo de duelos de mascotas con tu prole de Picoterrible y 2 mascotas de la familia Humanoide.', '<Tu prole de Picoterrible parece lista para asociarse contigo en otra serie de batallas y demostrar así todo lo aprendido sobre el trabajo en equipo. Darle órdenes junto a nuevos aliados sería una prueba magnífica de sus facultades.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44987, 'esMX', 'Prueba del Equipo Picotemible', 'Derrota 3 misiones de mundo de duelos de mascotas con tu prole Picotemible y 2 mascotas humanoides.', '<Tu prole Picotemible luce preparada para formar equipo contigo en otra ronda de batallas y demostrar lo que aprendió sobre trabajo en equipo. Añadir nuevos aliados sería una prueba excelente de sus facultades.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44987, 'frFR', 'Travail d’équipe avec le bec-sinistre', 'Accomplissez 3 expéditions de combat de mascottes avec votre Nouveau-né bec-sinistre et 2 Mascottes humanoïdes.', '<Votre nouveau-né bec-sinistre est prêt à livrer de nouvelles batailles à vos côtés et semble impatient de mettre en pratique tout ce qu’il a appris en matière de collaboration. Partir au combat avec de nouveaux alliés serait un excellent moyen de mettre ses nouvelles techniques à l’épreuve.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44987, 'itIT', 'Lavoro di squadra per il Beccotruce', 'Completa 3 missioni mondiali degli scontri tra mascotte con il tuo Cucciolo di Beccotruce e 2 mascotte Umanoidi.', '<Il tuo Cucciolo di Beccotruce sembra pronto a combattere altre battaglie assieme a te e a mostrare tutto ciò che ha imparato riguardo il lavoro di squadra. Farlo combattere assieme a nuovi alleati potrebbe mettere alla prova le sue abilità.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44987, 'koKR', '광포부리 팀 전투', '새끼 광포부리 팔코사우루스와 인간형 애완동물 2마리와 함께 애완동물 대전 전역 퀘스트 3개를 완료해야 합니다.', '<새끼 광포부리 팔코사우루스가 당신과 한 팀이 되어 다시 한번 전투를 벌이고 그동안 갈고 닦은 팀워크를 뽐낼 준비가 된 것 같습니다. 새로운 동료들과 힘을 합쳐 싸우는 게 새끼의 능력을 시험할 더할 나위 없는 기회가 될 것입니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44987, 'ptBR', 'A equipe Bicaustero detona!', 'Conclua 3 Missões Mundiais de Batalha de Mascotes com seu Filhote de Bicaustero e 2 mascotes do tipo Humanoide.', '<Parece que o Filhote de Bicaustero já está pronto para enfrentar uma nova rodada de batalhas e mostrar tudo o que aprendeu sobre trabalho em equipe. Comandá-lo junto de novos aliados seria uma ótima forma de testar as habilidades dele.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44987, 'ruRU', 'Командный бой со злобоклювом', 'Выполните 3 локальных задания по битвам питомцев вместе с вашим детенышем злобоклюва и двумя питомцами-гуманоидами.', '<Ваш детеныш злобоклюва готов присоединиться к вам в новых сражениях и продемонстрировать свои навыки работы в команде. Отправьте его в бой с новыми союзниками, чтобы проверить, чему он научился.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44987, 'zhCN', '恐嘴小队大乱斗', '用你的恐嘴雏龙和2只人形类宠物完成3个宠物对战世界任务。', '<你的恐嘴雏龙看起来已经准备好与你一起迎接新的战斗，并展示它学会的合作技巧了。指挥它和新盟友一起战斗，好好测试一下它的能力吧。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44987, 'zhTW', '恐喙團隊大戰', '派你的恐喙小龍隼和2隻人形生物贏得3個寵物對戰世界任務。', '<看樣子你的恐喙小龍隼已做好跟你一起挑戰下一輪戰鬥的準備，好顯示牠已經學會什麼叫做團隊合作。讓牠跟不同的搭檔合作應該能測出牠的能耐。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44988, 'deDE', 'Düsterschnabelwiedersehen', 'Bringt das Düsterschnabeljunge zum Möwenfels in Sturmheim.', '<In letzter Zeit gibt Euer Düsterschnabeljunges immer wieder ein leises, trauriges Krächzen von sich. Es scheint deprimiert zu sein. Vielleicht solltet Ihr mit ihm zum Ort seiner Geburt zurückkehren?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44988, 'esES', 'Reunión de Picoterribles', 'Lleva a la prole de Picoterrible de regreso a Roca Gaviota, en Tormenheim.', '<Tu prole de Picoterrible no para de emitir graznidos suaves y melancólicos. Parece deprimida. Tal vez sea momento de llevarla de regreso a su lugar de origen.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44988, 'esMX', 'Reunión de Picotemibles', 'Devuelve a la prole Picotemible a Roca de gaviotas, en Stormheim.', '<Tu prole Picotemible no deja de emitir suaves chillidos melancólicos y luce deprimida. Tal vez sea hora de devolverla a su lugar de origen...>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44988, 'frFR', 'Retrouvailles des becs-sinistres', 'Ramenez le Nouveau-né bec-sinistre au Rocher de la Mouette à Tornheim.', '<Votre nouveau-né bec-sinistre ne cesse de laisser échapper de petits cris mélancoliques. Il a l’air déprimé. Peut-être est-il temps de le ramener chez lui ?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44988, 'itIT', 'Ricongiungimento tra Beccotruce', 'Riporta il Cucciolo di Beccotruce a Roccia del Gabbiano nella regione di Stromheim.', '<Il tuo Cucciolo di Beccotruce non la smette di strillare. Sembra depresso. Forse è ora di riportarlo al suo luogo di nascita?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44988, 'koKR', '광포부리 상봉', '새끼 광포부리 팔코사우루스를 스톰하임에 있는 갈매기 바위로 돌려보내야 합니다.', '<새끼 광포부리 팔코사우루스가 구슬픈 울음을 멈추지 않는 게, 우울해 보입니다. 새끼를 태어난 곳으로 돌려보낼 시간이 된 건 아닐까요?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44988, 'ptBR', 'Reunião dos Bicaustero', 'Devolva o Filhote de Bicaustero para a Rocha da Gaivota em Trommheim.', '<O Filhote de Bicaustero fica emitindo guinchos curtos, baixinhos e melancólicos. Ele parece meio deprimido. De repente é hora de devolvê-lo ao seu habitat natural.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44988, 'ruRU', 'Воссоединение злобоклювов', 'Верните детеныша злобоклюва на Скалу Чаек в Штормхейме.', '<Ваш детеныш злобоклюва тихо и грустно чирикает. Кажется, он сильно подавлен. Быть может, пришло время вернуть его в естественную среду обитания?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44988, 'zhCN', '恐嘴家族团聚', '把恐嘴雏龙送回风暴峡湾的海鸥岩。', '<你的恐嘴雏龙不断发出轻柔、忧郁的叫声。它似乎很沮丧。或许该送它回到出生地了？>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44988, 'zhTW', '恐喙家族團聚', '讓恐喙小龍隼返回斯鐸海姆的海鷗岩。', '<你的恐喙小龍隼不斷發出微弱又憂鬱的叫聲。牠似乎很憂鬱。或許讓牠返回家鄉的時候到了？>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44989, 'deDE', 'Scharfkrallenaufzucht', 'Pflegt Eurer Scharfkrallenjunges bis zum Erreichen von Stufe 25.', '<Euer Scharfkrallenjunges zittert noch, während es sich neben Euch setzt. Es sieht Euch mit großen und weit offenen Augen entgegen. Es verlässt sich wohl auf Eure Hilfe, um zu überleben und groß zu werden.>', '', 'Krächz?', 'Scharfkrallenjunges', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44989, 'esES', 'Criar a tu Garfafilada', 'Cría a tu prole de Garfafilada hasta el nivel 25.', '<Tu prole de Garfafilada tiembla mientras se instala a tu lado. Te mira con los ojos bien abiertos y sin pestañear. Parece que espera que la ayudes a sobrevivir y crecer.>', '', '¿Erk?', 'Prole de Garfafilada', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44989, 'esMX', 'Crianza del Garfafilada', 'Lleva a tu prole Garfafilada al nivel 25.', '<La prole Garfafilada sigue temblando y se acurruca a tu lado. Te mira sin parpadear con sus ojos imperturbables. Parece querer tu ayuda para sobrevivir y crecer.>', '', '¿Erk?', 'Prole Garfafilada', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44989, 'frFR', 'Élever votre serre-tranchante', 'Faites progresser votre Nouveau-né serre-tranchante jusqu’au niveau 25.', '<Votre nouveau-né serre-tranchante continue à trembler en se blottissant tout contre vous. Il lève vers vous de grands yeux écarquillés. On dirait que vous allez devoir l’aider à survivre et grandir.>', '', 'Erk ?', 'Nouveau-né serre-tranchante', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44989, 'itIT', 'Far crescere il Grinfiafina', 'Porta il tuo Cucciolo di Grinfiafina al livello 25.', '<Il tuo Cucciolo di Grinfiafina continua a rabbrividire, poi si sistema vicino a te e ti guarda con i suoi grandi occhi. Sembra che voglia che lo aiuti a sopravvivere e crescere.>', '', 'Erk?', 'Cucciolo di Grinfiafina', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44989, 'koKR', '뾰족발톱 팔코사우루스 키우기', '새끼 뾰족발톱 팔코사우루스를 25 레벨까지 키워야 합니다.', '<새끼 뾰족발톱 팔코사우루스가 당신 곁에 앉아서는 계속 덜덜 떨고 있습니다. 새끼가 크고 또랑또랑한 눈으로 당신을 올려다봅니다. 자기가 무사히 성장할 수 있게 도와 달라는 눈빛입니다.>', '', '아록?', '새끼 뾰족발톱 팔코사우루스', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44989, 'ptBR', 'A criação do Garraguda', 'Crie o Filhote de Garraguda até ele atingir o nível 25.', '<O Filhote de Garraguda ainda treme enquanto se ajeita ao seu lado. Ele olha para você com olhos grandes que não piscam. Parece que espera a sua ajuda para crescer e sobreviver.>', '', 'Erk?', 'Filhote de Garraguda', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44989, 'ruRU', 'Воспитание острокогтя', 'Повысьте уровень вашего детеныша острокогтя до 25-го.', '<Ваш детеныш острокогтя пристраивается рядом с вами, продолжая дрожать от страха. Он глядит на вас во все глаза. Похоже, он надеется, что вы защитите его и поможете ему вырасти большим и сильным.>', '', 'Чирик?', 'Детеныш острокогтя', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44989, 'zhCN', '养育你的利爪雏龙', '把你的利爪雏龙提升到25级。', '<你的利爪雏龙靠在你身边，不断瑟瑟发抖。它瞪大眼睛，眨也不眨地盯着你，似乎希望你能帮助它活下来，并健康长大。>', '', '啾？', '利爪雏龙', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44989, 'zhTW', '培育你的銳爪小龍隼', '將你的銳爪小龍隼提升到等級25。', '<你的銳爪小龍隼窩在你身邊的時候一直顫抖，抬起頭，眼睛眨也不眨地看著你。看樣子你要負責撫育牠長大了。>', '', '咿？', '銳爪小龍隼', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44990, 'deDE', 'Blutstarreraufzucht', 'Pflegt Eurer Blutstarrerjunges bis zum Erreichen von Stufe 25.', '<Euer Blutstarrerjunges betrachtet Euch neugierig. Es neigt seinen Kopf zur Seite und pfeift leise. Es verlässt sich wohl auf Eure Hilfe, um zu überleben und groß zu werden.>', '', 'Baruu!', 'Blutstarrerjunges', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44990, 'esES', 'Criar a tu Contemplasangre', 'Cría a tu prole de Contemplasangre hasta el nivel 25.', '<Tu prole de Contemplasangre te mira con curiosidad, ladea la cabeza y emite un leve silbido. Parece esperar que la ayudes a sobrevivir y crecer.>', '', '¡Barú!', 'Prole de Contemplasangre', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44990, 'esMX', 'Crianza del Observasangre', 'Lleva a tu prole Observasangre al nivel 25.', '<Tu prole Observasangre te mira con curiosidad e inclina la cabeza a un lado mientras emite un silbido grave. Parece querer tu ayuda para sobrevivir y crecer.>', '', '¡Baroo!', 'Prole Observasangre', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44990, 'frFR', 'Élever votre scrutesang', 'Faites progresser votre Nouveau-né scrutesang jusqu’au niveau 25.', '<Votre nouveau-né scrutesang vous regarde d’un air curieux en penchant la tête de côté et en sifflant doucement. On dirait que vous allez devoir l’aider à survivre et grandir.>', '', 'Arou !', 'Nouveau-né scrutesang', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44990, 'itIT', 'Far crescere lo Scrutasangue', 'Porta il tuo Cucciolo di Scrutasangue al livello 25.', '<Il tuo Cucciolo di Scrutasangue ti guarda con fare curioso, muovendo la testa di lato ed emettendo uno strano suono. Sembra che voglia che lo aiuti a sopravvivere e crescere.>', '', 'Baru!', 'Cucciolo di Scrutasangue', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44990, 'koKR', '핏빛눈 팔코사우루스 키우기', '새끼 핏빛눈 팔코사우루스를 25 레벨까지 키워야 합니다.', '<새끼 핏빛눈 팔코사우루스가 호기심 어린 눈으로 당신을 보더니, 고개를 옆으로 젖히고는 낮은 울음소리를 냅니다. 자기가 무사히 성장할 수 있게 도와 달라는 눈빛입니다.>', '', '바루!', '새끼 핏빛눈 팔코사우루스', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44990, 'ptBR', 'A criação do Sanguinolhos', 'Crie o Filhote de Sanguinolhos até ele atingir o nível 25.', '<O Filhote de Sanguinolhos olha para você com curiosidade, inclinando a cabeça de lado e assobiando baixinho. Parece que espera a sua ajuda para crescer e sobreviver.>', '', 'Baroo!', 'Filhote de Sanguinolhos', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44990, 'ruRU', 'Воспитание кровогляда', 'Повысьте уровень вашего детеныша кровогляда до 25-го.', '<Ваш детеныш кровогляда с любопытством смотрит на вас, склонив набок голову и тихонько посвистывая. Похоже, он надеется, что вы защитите его и поможете ему вырасти большим и сильным.>', '', 'Курлы!', 'Детеныш кровогляда', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44990, 'zhCN', '养育你的血眼雏龙', '把你的血眼雏龙提升到25级。', '<你的血眼雏龙好奇地看着你，歪着头发出低沉的叫声，似乎希望你能帮助它活下来，并健康长大。>', '', '咕噜！', '血眼雏龙', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44990, 'zhTW', '培育你的嗜血小龍隼', '將你的嗜血小龍隼提升到等級25。', '<你的嗜血小龍隼好奇地看著你，把頭歪到一邊，發出低沉的聲音。看樣子你要負責撫育牠長大了。>', '', '吧嚕！', '嗜血小龍隼', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44991, 'deDE', 'Der Geruch von Draenei', 'Bringt Euer Blutstarrerjunges zum Gasthaus der Exodar.', '<Ihr habt bemerkt, dass Euer Blutstarrerjunges immer zittert, wenn Draenei in der Nähe sind. Anscheinend hat es große Angst vor ihnen. Vielleicht solltet Ihr es irgendwo hinbringen, wo es eine Menge Draenei gibt, damit es sich an den Geruch gewöhnen kann.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44991, 'esES', 'El olor de los draenei', 'Lleva a tu prole de Contemplasangre a la posada de El Exodar.', '<Has notado que tu prole de Contemplasangre tiembla cuando hay draenei cerca. Parece tenerles autentico pánico. Quizás podrías llevarla a algún lugar donde haya muchos, para que se acostumbre al olor.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44991, 'esMX', 'El olor a draenei', 'Lleva a tu prole Observasangre a la posada en El Exodar.', '<Notas que tu prole Observasangre se agita cuando hay draenei cerca. Parece que se siente aterrorizada. Tal vez puedas llevarla a un sitio con muchos draenei para que se acostumbre al olor.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44991, 'frFR', 'L’odeur des Draeneï', 'Amenez votre Nouveau-né scrutesang à l’Auberge de l’Exodar.', '<Vous avez remarqué que votre nouveau-né scrutesang est pris de tremblements chaque fois qu’il aperçoit des Draeneï. Leur seule vue semble le terrifier. Peut-être serait-il bon de le mettre en présence d’un grand nombre de Draeneï afin qu’il s’habitue à leur odeur ?>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44991, 'itIT', 'L\'odore dei Draenei', 'Porta il tuo Cucciolo di Scrutasangue alla locanda di Exodar.', '<Hai notato che il tuo Cucciolo di Scrutasangue trema quando ci sono dei Draenei nelle vicinanze. Sembra che ne sia terrorizzato. Forse potresti portarlo in un posto pieno di Draenei, in modo che si abitui al loro odore.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44991, 'koKR', '드레나이의 냄새', '새끼 핏빛눈 팔코사우루스를 엑소다르에 있는 여관으로 데려가야 합니다.', '<당신은 드레나이가 주위에 다가올 때마다 새끼 핏빛눈 팔코사우루스가 몸을 떠는 걸 알아챘습니다. 드레나이가 엄청 무서운가 봅니다. 새끼를 드레나이가 많은 곳으로 데려가 보는 걸 어떨까요? 냄새에 익숙해지도록 말이죠...>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44991, 'ptBR', 'O cheiro dos draeneis', 'Leve seu Filhote de Sanguinolhos até a Estalagem no Exodar.', '<Você repara que o filhote de Sanguinolhos treme sempre que tem draeneis por perto. Parece que ele tem pavor de draeneis. De repente, se você levá-lo a um lugar com muitos draeneis, ele acaba se acostumando com o cheiro deles.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44991, 'ruRU', 'Запах дренеев', 'Отведите вашего детеныша кровогляда в таверну Экзодара.', '<Вы обратили внимание, что ваш детеныш кровогляда начинает дрожать от страха всякий раз, когда поблизости оказываются дренеи. Похоже, он их до жути боится. Вероятно, стоит отвести его куда-нибудь, где полно дренеев, чтобы он к ним попривык.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44991, 'zhCN', '德莱尼的气味', '带着你的血眼雏龙前往埃索达的旅店。', '<你发现只要有德莱尼靠近，这只血眼雏龙就会瑟瑟发抖。看来它真的很怕德莱尼。或许你能把它带到德莱尼聚集的地方，让它习惯这种气味。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44991, 'zhTW', '德萊尼的氣味', '帶你的嗜血小龍隼到艾克索達的旅店。', '<你發現嗜血小龍隼只要附近有德萊尼就會顫抖。看起來是嚇壞了。或許帶牠到德萊尼密集的地方，可以讓牠習慣德萊尼的氣味。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44992, 'deDE', 'Der Geruch von Nachtelfen', 'Bringt Euer Scharfkrallenjunges zum Gasthaus von Darnassus.', '<Ihr habt bemerkt, dass Euer Scharfkrallenjunges immer zittert, wenn Nachtelfen in der Nähe sind. Anscheinend hat es große Angst vor ihnen. Vielleicht solltet Ihr es irgendwo hinbringen, wo es eine Menge Nachtelfen gibt, damit es sich an den Geruch gewöhnen kann.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (44992, 'esES', 'El olor de los elfos de la noche', 'Lleva a tu prole de Garfafilada a la posada de Darnassus.', '<Has notado que tu prole de Garfafilada tiembla cuando hay elfos de la noche cerca. Parece tenerles autentico pánico. Quizás podrías llevarla a algún lugar donde haya muchos, para que se acostumbre al olor.>', '', '', '', '', '', '', 0);
