INSERT INTO `quest_template_locale` VALUES (44993, 'esES', 'El olor de los elfos de sangre', 'Lleva a tu prole de Contemplasangre a la Posada de Ciudad de Lunargenta, en Lunargenta.', '<Has notado que tu prole de Contemplasangre tiembla cuando hay elfos de sangre cerca. Parece tenerles autentico pánico. Quizás podrías llevarla a algún lugar donde haya muchos, para que se acostumbre al olor.>', '', '', '', '', '', '', 0);
