INSERT INTO `quest_template_locale` VALUES (44993, 'koKR', '블러드 엘프의 냄새', '새끼 핏빛눈 팔코사우루스를 실버문에 있는 실버문 여관으로 데려가야 합니다.', '<당신은 블러드 엘프가 주위에 다가올 때마다 새끼 핏빛눈 팔코사우루스가 몸을 떠는 걸 알아챘습니다. 블러드 엘프가 엄청 무서운가 봅니다. 새끼를 블러드 엘프가 많은 곳으로 데려가 보는 걸 어떨까요? 냄새에 익숙해지도록 말이죠...>', '', '', '', '', '', '', 0);
