INSERT INTO `quest_template_locale` VALUES (45060, 'deDE', 'Beschädigtes Insigne der Kirin Tor', 'Bringt das Insigne zum Rat der Sechs in Dalaran.', 'Ganz gleich, ob es verloren oder gestohlen wurde, es ist seltsam, dass ein Dämon ein Insigne der Kirin Tor in seinem Besitz hat.\n\nDer Rat der Sechs wäre zweifellos an diesem Fund interessiert.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45060, 'esES', 'Insignia del Kirin Tor dañada', 'Lleva la insignia al Consejo de los Seis en Dalaran.', 'Ya la haya encontrado o robado, es extraño que un demonio tenga una insignia del Kirin Tor.\n\nSin duda al Consejo de los Seis le interesará este hallazgo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45060, 'frFR', 'Insigne du Kirin Tor endommagé', 'Apportez l’insigne au Conseil des Six, à Dalaran.', 'Qu’il ait été perdu ou volé, cet insigne du Kirin Tor n’a rien à faire entre les mains d’un démon.\n\nCette découverte devrait beaucoup intéresser le Conseil des Six.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45061, 'esES', 'Pergaminos de los Faldrottin', 'Lleva los pergaminos al Consejo de los Seis en Dalaran.', 'Aunque es muy extraño que un demonio llevara un pergamino con escritos de los vrykuls, el contenido es aún más interesante.\n\nLos escritos parecen hacer referencia a una profecía vrykul que habla de alguien que unirá a los Mareskorn para luchar contra la Legión Ardiente. Hay que informar de esto al Consejo de los Seis de inmediato.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45061, 'frFR', ' Les parchemins des Faldrottins', 'Apportez les parchemins au Conseil des Six, à Dalaran.', 'Il est étrange qu’un démon ait en sa possession un parchemin d’écrits vrykuls, mais le plus étonnant reste encore le contenu dudit document.\n\nOn y fait référence à une prophétie vrykule selon laquelle une personne rallierait les Mörgestran sous sa bannière dans sa lutte contre la Légion ardente. Il faut immédiatement en avertir le Conseil des Six.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45062, 'deDE', 'Weiter, immer weiter...', 'Benutzt die Verdorrten, um die Machtquelle in der Seelenmaschine der Teufelsseelenbastion zu zerlegen.', 'Eure Entdeckungen sind alles andere als enttäuschend, $n.\n\nBegebt Euch zur Seelenmaschine und nutzt Eure neu beschafften Waffen, um die Machtquelle zu zerlegen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45062, 'esES', 'Trabajando sin descanso', 'Usa a los Marchitos para desmontar la fuente de alimentación de la Máquina de Almas, en el Bastión Alma Vil.', 'Tus hallazgos nunca decepcionan, $n.\n\nDirígete a la Máquina de Almas y usa nuestras nuevas armas para desmontar la fuente de alimentación.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45062, 'esMX', 'Resistirse al arresto', 'Usa a los marchitos para desmantelar la fuente de poder dentro de la Máquina de almas en el Bastión Almavil.', 'Tus descubrimientos no nos decepcionan, $n.\n\nÁbrete paso hasta la máquina de almas y usa nuestras armas recién adquiridas para desmantelar la fuente de poder.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45062, 'frFR', 'Coupure forcée', 'Utilisez les Flétris pour démanteler la Source d’énergie du Moteur d’âmes au Bastion Gangrâme.', 'Vos découvertes dépassent mes attentes, $n.\n\nApprochez-vous du moteur d’âmes et utilisez nos nouvelles armes pour démanteler la source d’énergie.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45062, 'itIT', 'Resistere all\'arresto', 'Usa gli Avvizziti per smantellare la Fonte di Potere all\'interno del Congegno delle Anime nel Forte Vilanima.', 'Le tue scoperte saranno molto utili, $n.\n\nFatti strada verso il Congegno delle Anime e usa le tue nuove armi per smantellare la sorgente di potere.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45062, 'koKR', '억압에 저항하기', '메마른 자들을 사용하여 지옥영혼 요새, 영혼의 기계 내부에 동력원을 파괴해야 합니다.', '역시 당신의 능력은 우릴 실망시키지 않는군요, $n.\n\n영혼의 기계로 가서, 우리가 새롭게 획득한 무기로 동력원을 파괴해 주세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45062, 'ptBR', 'Resistência à prisão', 'Use os fenecidos para desmantelar a Fonte de Energia dentro do Motor da Alma, no Castelo Almavil.', 'Seus resultados nunca desapontam, $n.\n\nAbra caminho até o Motor da Alma e use nossas armas recém adquiridas para desmantelar a fonte de energia.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45062, 'ruRU', 'Сопротивление аресту', 'Используйте иссохших, чтобы разрушить источник энергии в Реакторе Душ в Оплоте Оскверненной Души.', 'Ты не перестаешь меня радовать, $n.\n\nОтправляйся к Реактору Душ и используй наши новые орудия, чтобы разрушить источник энергии.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45062, 'zhCN', '拒捕', '前往邪魂堡垒的灵魂引擎，使用枯法者来拆除能量源。', '你的发现没有让人失望，$n。\n\n去灵魂引擎吧，使用我们的新武器拆除能量源。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45062, 'zhTW', '抵抗加劇', '前往魔魂堡，利用凋萎者來摧毀靈魂引擎中的能量來源。', '你的發現幫了大忙，$n。\n\n快前往靈魂引擎，用我們剛得到的武器來摧毀能量來源。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45063, 'deDE', 'Teufelsseelenexperimente', 'Erprobt den Widerstand der Verdorrten gegen Teufelsmagie in der Teufelsseelenbastion.', 'Andaris\' Kontrolle über die Verdorrten stellt eine echte Bedrohung dar. Wenn wir den Widerstand der Verdorrten gegen diese Effekte stärken wollen, müssen wir es in einer Umgebung tun, die ebenjener Magie entsprungen ist.\n\nBegebt Euch sofort zur Teufelsseelenbastion. Wenn Ihr dort ankommt, schicke ich Verdorrte, die Euch unterstützen sollen. Es ist äußerst wichtig, dass Ihr nicht aufgebt, bevor Ihr sicher seid, dass die Experimente die passenden Ergebnisse liefern.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45063, 'esES', 'Los experimentos Alma Vil', 'Pon a prueba la resistencia de los Marchitos a la magia vil en el Bastión Alma Vil.', 'El control de Andaris sobre los Marchitos supone una amenaza real. Si queremos aumentar la resistencia de los Marchitos contra estos efectos, debemos hacerlo en un entorno creado a partir de esta misma magia.\n\nVe enseguida al Bastión Alma Vil. Cuando llegues, enviaré Marchitos en tu ayuda. Es primordial que no te vayas hasta asegurarte de que los experimentos producen resultados apropiados.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45063, 'esMX', 'Los experimentos almavil', 'Prueba la resistencia de los marchitos a la magia vil en el Bastión Almavil.', 'El control de Andaris sobre los marchitos es una amenaza real. Si queremos aumentar la resistencia de los marchitos a estos efectos, debemos hacerlo en un entorno creado con esta magia misma.\n\nDirígete hacia el Bastión Almavil de inmediato. Al llegar, enviaré marchitos para que te ayuden en tus esfuerzos. Es de suma importancia que no te vayas sino hasta tener certeza de que los experimentos tienen resultados apropiados.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45063, 'frFR', 'Les expériences gangrâmes', 'Testez la résistance des Flétris à la magie gangrenée au Bastion Gangrâme.', 'L’emprise d’Andaris sur les Flétris représente une terrible menace. Si nous voulons accroître leur résistance à ces effets, nous devons travailler dans un environnement issu de cette même magie.\n\nRendez-vous immédiatement au bastion Gangrâme. Dès votre arrivée sur place, je vous enverrai des Flétris pour vous épauler. Il est impératif que vous ne repartiez pas avant d’être $gsûr:sûre; que nos expériences aboutissent aux résultats escomptés.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45063, 'itIT', 'Gli esperimenti di Vilanima', 'Metti alla prova la resistenza alla vilmagia degli Avvizziti nel Forte Vilanima.', 'Il controllo di Andaris sugli Avvizziti rappresenta una vera minaccia. Se vogliamo incrementare la resistenza degli Avvizziti a tali effetti, dobbiamo farlo in un ambiente creato proprio da questo tipo di magia.\n\nDirigiti immediatamente al Forte Vilanima. Al tuo arrivo, invierò degli Avvizziti in tuo aiuto. È di estrema importanza che tu non te ne vada finché non sarai $gcerto:certa; dei risultati ottenuti dagli esperimenti.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45063, 'koKR', '지옥영혼 실험', '지옥영혼 요새에서 메마른 자들의 지옥 마법에 대한 저항을 시험해야 합니다.', '메마른 자들을 통제하는 안다리스의 능력이 정말 심각한 위협이 됩니다. 메마른 자들의 저항을 강화하려면, 이 마법이 생성된 환경에서 직접 시도해 봐야 합니다.\n\n즉시 지옥영혼 요새로 가세요. 그곳에 도착하시면, 제가 메마른 자들을 보내 당신을 돕겠습니다. 실험을 마치고 적절한 결과를 얻을 때까지 중단하시면 안 돼요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45063, 'ptBR', 'Os experimentos com almavil', 'Teste a resistência dos fenecidos à magia vil no Castelo Almavil.', 'O domínio de Andaris sobre os fenecidos representa uma verdadeira ameaça. Se quisermos aumentar a resistência deles, teremos que providenciar um ambiente com a mesma magia.\n\nDirija-se ao Castelo Almavil. Assim que chegar, eu mandarei fenecidos para ajudá-$Glo:la;. É da maior importância que você permaneça até alcançar os resultados esperados nos experimentos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45063, 'ruRU', 'Скверные эксперименты', 'Испытайте сопротивление иссохших магии Скверны в Оплоте Оскверненной Души.', 'Власть Андарис над иссохшими представляет серьезную угрозу. Если мы хотим повысить сопротивляемость иссохших этим эффектам, нужно сделать это в аналогичной магической среде.\n\nСкорее отправляйся в Оплот Оскверненной Души. Когда будешь на месте, я отправлю иссохших тебе на подмогу. Ни в коем случае не уходи, пока не получишь приемлемые результаты.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45063, 'zhCN', '邪魂实验', '在邪魂堡垒测试枯法者对邪能魔法的抵抗能力。', '安达丽斯控制枯法者的能力实在是一大威胁。如果我们要提高枯法者对这些效果的抵抗能力，就得在用这种魔法构筑的环境里尝试。\n\n马上到邪魂堡垒去吧。你到了之后，我会派一些枯法者来帮助你。记住，你得待在那里，直到确定实验获得了理想的效果为止。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45063, 'zhTW', '魔魂堡實驗', '前往魔魂堡，測試凋萎者對魔能的抗性。', '安妲麗絲對凋萎者的控制會造成很大的威脅。如果我們要強化凋萎者對這種效果的抗性，就需要處在由這種魔法所建造的環境當中。\n\n立刻前往魔魂堡。等你抵達以後，我會派凋萎者協助你。這件事非常重要，你得確保實驗出現有用的結果才能離開。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45064, 'deDE', 'Schluss mit den Teufelsgeborenen', 'Tötet $2oa Teufelsgeborene und ihren Anführer, Valthis Ama\'ran, am Außenposten der Teufelsgeborenen im Purpurdickicht.', 'Die Teufelsgeborenen weiten ihre Kontrolle über die Stadtgrenzen hinaus aus. Uns interessiert insbesondere ein Lager im Osten, im Purpurdickicht. Der Anführer dieses Lagers, Valthis Ama\'ran, ist Andaris direkt unterstellt.$B$BEs ist an der Zeit, die Ausbildung unserer Verdorrten und Oculeths Gerät auf die Probe zu stellen. Wir dürfen nicht zulassen, dass sie weiter vorrücken, $n.$B$BBegebt Euch zum Lager und benutzt die Kugel, um der Bestie den Kopf abzutrennen. Möge unser Vorstoß beginnen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45064, 'esES', 'Adiós, vilificados', 'Mata a $2oa vilificados y a su líder, Valthis Ama\'ran, en la Avanzada Vilificada de la Espesura Carmesí.', 'Los vilificados han empezado a expandirse más allá de la ciudad. En la Espesura Carmesí, al este, tienen un campamento de especial interés. El líder de su avanzada, Valthis Ama\'ran, sirve directamente a Andaris.$b$bEs el momento de poner a prueba el entrenamiento de nuestros Marchitos y el dispositivo de Oculeth. No podemos dejar que sigan avanzando, $n.$b$bVe al campamento y usa el orbe para decapitar a la bestia. Ha llegado la hora de iniciar nuestro avance.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45064, 'esMX', 'Se acabaron los vilnatos', 'Asesina $2oa vilnatos y a su líder, Valthis Ama\'ran, en la avanzada vilnata de Zarzal carmesí.', 'Los vilnatos comenzaron a expandirse más allá del alcance de la ciudad. Nos interesa especialmente un campamento al este en el Zarzal carmesí. Su líder, Valthis Ama\'ran, sirve directamente a Andaris.$B$BEs hora de poner a prueba nuestro entrenamiento marchito y el dispositivo de Oculeth. No podemos permitir que avancen más, $n.$B$BVe al campamento y usa el orbe para cortar la cabeza de la bestia. Llegó el momento de iniciar nuestro propio avance.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45064, 'frFR', 'Sus aux Gangrenuit', 'Tuez $2oa Gangrenuit ainsi que leur chef, Valthis Ama’ran, à l’Avant-poste du Gangrenuit, dans le Fourré Cramoisi.', 'Les Gangrenuit ont commencé à étendre leur emprise au-delà de la ville. À l’est, au cœur du fourré Cramoisi, se trouve un camp qui nous intéresse tout particulièrement. Le chef de l’avant-poste, Valthis Ama’ran, reçoit ses ordres d’Andaris en personne.$B$BL’heure est venue de tester en conditions réelles l’entraînement de nos flétris et le dispositif d’Oculeth. Il faut à tout prix enrayer leur progression, $n.$B$BRendez-vous au camp et utilisez l’orbe pour décapiter la bête. C’est notre tour de prendre la tête de la course, à présent.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45064, 'itIT', 'Niente più Stirpevile', 'Uccidi $2oa Stirpevile e il loro capo, Valthis Ama\'ran, all\'Avamposto degli Stirpevile nel Bosco Cremisi.', 'Gli Stirpevile hanno iniziato a espandere la loro presa oltre i confini della città. Un accampamento a est nel Bosco Cremisi è di particolare interesse per noi. Il capo del loro avamposto, Valthis Ama\'ran, è agli ordini diretti di Andaris.$B$BOra è il momento di mettere alla prova l\'addestramento degli Avvizziti e il dispositivo di Oculeth. Non possiamo permettere loro di avanzare ancora, $n.$B$BDirigiti all\'accampamento e usa il globo per tagliare la testa alla bestia. È giunto il momento di dare inizio all\'avanzata.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45064, 'koKR', '지옥살이는 없다', '진홍빛 숲의 지옥살이 전초기지에 있는 지옥살이 $2oa명과 지도자 발티스 아마란을 처치해야 합니다.', '지옥살이가 성의 방어 범위를 넘어선 곳까지 마수를 뻗치기 시작했어요. 그 중 진홍빛 숲의 동쪽에 있는 야영지가 우리에겐 특히 중요해요. 그 전초기지의 지도자인 발티스 아마란은 안다리스의 직속 부하거든요.$B$B이제 메마른 자들에 대한 훈련과 오큘레스의 장치를 시험해 봐야 할 때가 왔어요. 적들이 세력을 더 넓힐 수 있게 내버려 둬서는 안 되니까요, $n 님.$B$B야영지로 가서 보주를 사용하여 야수의 머리를 잘라 주세요. 우리가 진군해야 할 시간이 됐어요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45064, 'ptBR', 'Chega de Vilanescos', 'Mate $2oa Vilanescos e o líder deles, Valthis Ama\'ran, no Posto Vilanesco, que fica na Mata Carmesim.', 'Os Vilanescos estão se espalhando para além da cidade. Um acampamento à leste, na Mata Carmesim, é de muita importância para nós. O líder do posto deles, Valthis Ama\'ran, responde diretamente a Andaris.$B$BChegou a hora de treinarmos os nossos fenecidos e testarmos o aparelho de Oculeth. Não podemos deixar que avancem mais, $n.$B$BVá até o acampamento e use o orbe para cortar a cabeça daquela criatura. Hoje começaremos a nossa investida.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45064, 'ruRU', 'Прощание со Скверной', 'Убейте $2oa |4присягнувшего Скверне:присягнувших Скверне:присягнувших Скверне; и их предводителя Валтиса Ама\'рана на аванпосте присягнувших Скверне в Багровой чаще.', 'Порождения Скверны начали распространяться за пределы города. Наше внимание привлек их лагерь к востоку от Багровой чащи. Их предводитель, Валтис Ама\'ран, подчиняется непосредственно Андарис.$B$BДумаю, пора испытать подготовку иссохших и устройство Окулета на практике. Мы должны остановить наступление.$B$BОтправляйся в лагерь и используй сферу, чтобы лишить чудовище головы. Пора нам перехватить инициативу.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45064, 'zhCN', '邪脉断绝', '前往深红林地的邪脉岗哨，消灭$2oa个邪脉部队成员，以及他们的首领瓦提斯·阿玛兰。', '邪脉精灵已经开始把黑手伸向城外了。他们在深红林地东边的营地引起了我们的注意。这个岗哨的首领瓦提斯·阿玛兰直接听命于安达丽斯。$B$B是时候了，让我们来测试一下枯法者的训练成效以及欧库勒斯的新设备。不能再让他们更进一步了，$n。$B$B到那个营地去，使用这颗宝珠完成一次斩首行动吧。该轮到我们出击了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45064, 'zhTW', '終結魔裔', '在緋紅灌木林的魔裔營地殺死$2oa個魔裔與其領袖瓦席斯‧亞瑪蘭。', '魔裔開始把勢力擴張到這座城市之外。在緋紅灌木林東邊有個營地，我們觀察了很久。領導營地的是瓦席斯‧亞瑪蘭，他是安妲麗絲手下的直屬大將。$B$B是時候把我們訓練的凋萎者和歐庫雷斯的裝置送上戰場了。不能讓他們繼續得逞，$n。$B$B前往那個營地，用寶珠切下那個怪獸的頭。進軍的時刻到了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45065, 'deDE', 'Stadtvermessung', 'Verwendet den verbesserten telemantischen Signalgeber, um die Terrasse der Ordnung in Suramar zu vermessen.', 'Hier wird es nie langweilig! Nun... vielleicht ist das nicht ganz wahr.\n\nIch fürchte, ich muss Euch bitten, noch einmal zur Stadt zurückzukehren und Informationen zu sammeln. Aber nicht irgendwelche Informationen... Ich brauche ganz bestimmte Angaben!\n\nIch brauche bestimmte Koordinaten, um meine Teleportationen mit äußerster Genauigkeit fokussieren zu können. Wenn wir die Informationen haben, wird es leider noch eine ganze Weile dauern, bis wir sie durchgegangen sind. Doch in der Zwischenzeit haben wir ohnehin noch einiges zu tun.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45065, 'esES', 'Inspeccionar la ciudad', 'Usa la baliza de telemancia avanzada para inspeccionar el Paseo del Orden en la Ciudad de Suramar.', '¡No hay tiempo para aburrirse! Bueno... quizás no sea del todo cierto.\n\nMe temo que debo pedirte de nuevo que vuelvas a la ciudad a por información. Pero no una información cualquiera: ¡extracción de información!\n\nNecesito coordenadas específicas que me permitan centrar mis teletransportaciones con una precisión extrema. Por desgracia, hará falta algo de tiempo para examinar la información una vez obtenida, pero da igual: hay mucho por hacer mientras tanto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45065, 'esMX', 'Investiga la ciudad', 'Usa la baliza de telemancia avanzada para investigar la Terraza de la orden en Ciudad de Suramar.', 'Aquí no perdemos el tiempo. Bueno... quizá eso no sea del todo cierto.\n\nMe temo que debo pedirte que vuelvas a la ciudad para reunir información. Pero esta vez no se trata de algo simple, ¡sino de extraer información!\n\nNecesito las coordenadas específicas que me permitirán dar a mis teletransportes una precisión extrema. Por desgracia, tendré que estudiar durante algún tiempo la información cuando la recuperemos, pero no importa. Tenemos muchas otras cosas que hacer mientras tanto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45065, 'frFR', 'Topographie de la ville', 'Utilisez la Balise de télémancie avancée pour effectuer un levé de la Terrasse de l’Ordre à Suramar.', 'Ma foi, on n’a pas le temps de s’ennuyer ! Encore que... Ce n’est peut-être pas entièrement vrai.\n\nJe crains de devoir vous demander une fois de plus de retourner en ville en quête d’informations. Sauf que cette fois-ci, il va falloir les extraire, ces maudites données !\n\nJ’ai besoin de coordonnées exactes pour optimiser la précision de mes téléportations. Hélas, l’exploitation des renseignements que vous aurez obtenus s’annonce longue et fastidieuse. Par chance, nous avons largement de quoi nous occuper pendant ce temps.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45065, 'itIT', 'Esaminare la città', 'Usa il Segnalatore Telemantico Avanzato per analizzare la Terrazza dell\'Ordine a Suramar.', 'Non c\'è mai un attimo di tregua! Be\'... forse non è del tutto vero.\n\nTemo di doverti chiedere un\'altra volta di tornare in città per recuperare informazioni, ma non si tratta di informazioni qualsiasi...\n\nMi serviranno delle coordinate specifiche per indirizzare i miei teletrasporti con estrema precisione. Ci vorrà un po\' di tempo per analizzare il tutto una volta recuperato, ma non fa niente. Avremo molto altro da fare nel frattempo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45065, 'koKR', '성 조사', '고급 이동술 신호 장치를 사용하여 수라마르 성에 있는 질서의 터를 조사해야 합니다.', '심심할 날이 없군! 뭐... 정말 그렇지는 않을 수도 있지만...\n\n미안하지만 다시 한 번 성으로 돌아가 정보를 수집해 줘야 하겠네. 하지만 이번에는 아무 정보나 필요한 게 아니야. 정확한 정보를 추출해 내야 하네!\n\n극도로 정확한 위치에 순간이동 주문을 사용하려면 구체적인 좌표가 필요하네. 아쉽게도 그런 정보를 확인하려면 시간이 꽤나 필요하겠지만, 상관은 없겠지. 그것 말고도 해야 할 일은 아주 많으니까 말이야.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45065, 'ptBR', 'Sondagem da cidade', 'Use o Sinalizador de Telemancia Avançado para sondar o Terraço da Ordem na cidade de Suramar.', 'Não temos um momento tedioso! Bem... talvez seja exagero meu.\n\nInfelizmente, preciso que você volte à cidade para obter mais informações... só que, dessa vez, informações de resgate!\n\nPreciso de coordenadas específicas para que eu possa concentrar meus teleportes com precisão cirúrgica. Precisarei de um certo tempo para analisar todas as informações que você me trouxer, mas enfim. Temos muito o que fazer enquanto esperamos.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45065, 'ruRU', 'Экскурсия по городу', 'Используйте улучшенный телемантический маяк и осмотрите террасу Порядка в Сурамаре.', 'Ни минуты покоя! Ну... может, парочка и есть.\n\nБоюсь, мне придется попросить тебя вернуться в город собрать кое-какую информацию. И не простую, а очень даже важную!\n\nМне нужны определенные координаты, которые позволят управлять телепортацией с небывалой точностью. К сожалению, потребуется немало времени, чтобы разобрать все собранные данные, но это уже мелочи. Нам в это время будет чем заняться.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45065, 'zhCN', '调查城市', '利用高级传送道标，调查苏拉玛城的谕令平台。', '这还真是一波三折啊！呃……好吧，其实也没这么严重。\n\n我恐怕还得再请你回城去搜集一次情报。但这次不是一般的情报收集工作——而是信息提取！\n\n我需要特定的坐标，才能将我的传送器校准到最精确的状态。但不幸的是，获得这些情报后，我们还需要花大量时间来梳理它们。但没关系，在这段时间里，我们还有很多事情可以忙。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45065, 'zhTW', '調查城市', '使用進階傳送信標調查蘇拉瑪爾城的秩序平台。', '每一刻都精采無比！嗯…好像也不是每一刻。\n\n總之我得請你再回到城裡收集情報。不過這次不是任何情報就好，我要篩選過的特定情報！\n\n我需要特定座標才能進行精確的傳送。這得花費不少時間，才能從情報中篩選出我要的資訊。但不要緊，這段時間還有很多事情可以做。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45066, 'deDE', 'Experimentelle Instabilität', 'Sammelt die Telemantieaufzeichnungen und $3oa zurückgelassene Kugeln in Oculeths Testkammer.', 'Um unsere Forschungen voranzubringen, muss ich Euch bitten, ein paar ganz bestimmte Gegenstände aus meiner lange verlassenen Unterkunft zu beschaffen.$B$BIch versichere Euch, dass es dort... größtenteils sicher ist. Abgesehen von ein paar instabilen Elementen.$B$BBegebt Euch in meine Werkstatt und sucht den Teleporter. Ich werde die Tarnvorrichtung abschalten, sobald Ihr ankommt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45066, 'esES', 'Inestabilidad experimental', 'Reúne las notas de telemancia y $3oa orbes desechados de la Cámara de Pruebas de Oculeth.', 'Con el fin de avanzar en nuestros estudios, debo pedirte que recuperes unos cuantos objetos concretos de mis cámaras abandonadas.$B$BTe aseguro que allí... casi no hay peligro, aparte de unos pocos elementos inestables.$B$BVe a mi taller y localiza el teletransportador. Desactivaré su dispositivo de invisibilidad cuando llegues.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45066, 'esMX', 'Inestabilidad experimental', 'Reúne las notas de telemancia y $3oa orbes desechados de la Cámara de prueba de Oculeth.', 'Para avanzar en nuestro estudio, debo pedirte que recuperes algunos objetos especiales de mis cámaras, olvidadas hace ya mucho tiempo.$B$BTe aseguro que ir allá es... bastante seguro... salvo por algunos elementos inestables.$B$BVe a mi taller y localiza el teletransportador. Desactivaré el dispositivo de invisibilidad cuando llegues.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45066, 'frFR', 'Instabilité expérimentale', 'Récupérez les Notes de télémancie et $3oa Orbes abandonnés dans la Chambre de test d’Oculeth.', 'Pour progresser dans nos recherches, je dois vous demander de récupérer un certain nombre d’objets bien particuliers dans mes anciens quartiers.$B$BRassurez-vous, les lieux sont… relativement sûrs, exception faite de quelques éléments instables.$B$BRendez-vous dans mon atelier et trouvez le téléporteur. Je désactiverai son système d’occultation intégré à votre arrivée.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45066, 'itIT', 'Instabilità sperimentale', 'Recupera gli Appunti sulla Telemanzia e $3oa Globi Abbandonati nella Sala delle Prove di Oculeth.', 'Per avanzare con i nostri studi devo chiederti di recuperare alcuni oggetti dalle mie vecchie stanze.$b$bTe lo assicuro, è... quasi del tutto sicuro entrarci, a parte alcuni piccoli elementi instabili.$B$BFatti strada verso il mio laboratorio e trova il teletrasporto. Io disattiverò il dispositivo di camuffamento quando arriverai.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45066, 'koKR', '실험적인 불안정성', '오큘레스의 실험실에서 이동술 기록과 $3oa개의 버려진 보주를 모아야 합니다.', '우리 연구에 진전이 있으려면, 오래전 잊혀진 내 방에서 특정한 물품들을 자네가 좀 가져와 줘야 하겠네.$B$B장담하건대... 아주 위험하지만은 않을 걸세. 불안정한 원소가 조금 있기는 하지만...$B$B내 작업장으로 가서 순간이동기를 찾게. 자네가 도착하면 은폐 장치를 해제해 주겠네.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45066, 'ptBR', 'Instabilidade experimental', 'Colete as Notas sobre Telemancia e $3oa Orbes Descartados na Câmara de Teste de Oculeth.', 'Para que nossos estudos avancem, tenho que lhe pedir para obter alguns itens específicos em meus aposentos esquecidos.$B$BEu asseguro que eles são... relativamente seguros, exceto por alguns elementos instáveis$B$BSiga até a minha oficina e encontre o teleportador. Vou desativar o dispositivo de camuflagem dele quando você chegar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45066, 'ruRU', 'Экспериментальная нестабильность', 'Добудьте заметки о телемантии и $3oa |4выброшенную сферу:выброшенные сферы:выброшенных сфер; в Зале испытаний Окулета.', 'В интересах наших исследований я вынужден попросить тебя принести кое-какие вещи из моих заброшенных покоев.$B$BУверяю тебя, там... в целом безопасно, если не учитывать несколько нестабильных стихий.$B$BИди в мою мастерскую и воспользуйся телепортом. Я отключу маскировку, когда ты будешь на месте.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45066, 'zhCN', '不稳定性', '前往欧库勒斯的测试间收集传送笔记和$3oa个废弃的宝珠。', '为了进一步研究，我得请求你从我废弃已久的测试间里找回几件特别的物品。$b$b我保证，那里除了少数不稳定的元素，基本还算是……安全。$b$b去我的测试间找到传送器吧。你到了之后，我会把它的隐形设备关掉的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45066, 'zhTW', '實驗的未知性', '從歐庫雷斯的試驗室收集傳送術筆記和$3oa個被丟棄的寶珠。', '為了能進一步研究，我需要你去我那遺忘已久的試驗室中拿回幾樣特定物品。$B$B我向你保證，那裡很安全…大概，只有幾個不是很穩定的元素。$B$B進入我的工作室，然後找到傳送門。等你抵達，我再解除傳送門的匿蹤裝置。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45067, 'deDE', 'Telemantische Weiten', 'Sprecht mit Oculeth in Shal\'Aran.', 'Möglicherweise reicht das Verständnis der Teufelsmagie allein noch nicht aus, $n.\n\nSprecht mit Oculeth darüber, wie wir unsere Bemühungen ausweiten können. Ich habe gehört, er hätte etwas... Einzigartiges mit uns vor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45067, 'esES', 'Extensión telemántica', 'Habla con Oculeth en Shal\'Aran.', 'Puede que no baste con entender la magia vil, $n.\n\nHabla con Oculeth acerca de diversificar esfuerzos. He oído que tiene algo... especial planeado para nosotros.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45067, 'esMX', 'Expansión telemántica', 'Habla con Oculeth en Shal\'Aran.', 'Comprender la magia vil por sí sola puede que no sea suficiente, $n.\n\nHabla con Oculeth sobre la diversificación de nuestros intentos. He escuchado que tiene algo... único planeado para nosotros.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45067, 'frFR', 'Étendue télémantique', 'Parlez à Oculeth à Shal’Aran.', 'Il ne suffira peut-être pas de comprendre la magie gangrenée, $n.\n\nAllez voir Oculeth pour savoir comment diversifier nos tactiques. J’ai entendu dire qu’il nous réservait quelque chose de proprement... unique.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45067, 'itIT', 'Espansione telemantica', 'Parla con Oculeth a Shal\'aran.', 'Capire la vilmagia potrebbe non essere sufficiente, $n.\n\nDovremo diversificare i nostri sforzi. Parla con Oculeth, so che ha preparato qualcosa... di unico per noi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45067, 'koKR', '이동술의 달인', '샬아란에 있는 오큘레스와 대화해야 합니다.', '지옥 마법을 이해하는 것만으로는 충분하지 않을 수도 있어요, $n.\n\n오큘레스와 대화하여 우리 작전을 다변화하는 방안을 마련해 주세요. 그에게 뭔가... 독특한 계획이 있다고 들었거든요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45067, 'ptBR', 'Vastidão telemântica', 'Fale com Oculeth em Shal\'Aran.', 'Não basta apenas entender a magia vil, $n.\n\nConverse com Oculeth sobre o rumo das nossas operações. Ouvi dizer que ele tem... algo extraordinário em mente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45067, 'ruRU', 'Телемантические просторы', 'Поговорите с Окулетом в Шал\'Аране.', 'Нам недостаточно одного лишь понимания магии Скверны, $n.\n\nПоговори с Окулетом. Кажется, он приготовил для нас кое-что... особенное.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45067, 'zhCN', '传送扩张', '在沙尔艾兰与欧库勒斯谈一谈。', '光是理解邪能魔法可能还不够，$n。\n\n去跟欧库勒斯谈谈，我们要从多方面来努力。我听说他有些……独特的计划。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45067, 'zhTW', '傳送術的威力', '在沙亞蘭與歐庫雷斯交談。', '$n，光了解魔化魔法或許還不夠。\n\n去告訴歐庫雷斯，我們需要分頭進行。我聽說他為我們擬定了…獨一無二的計畫。', '', '', '', '', '', '', 0);
