INSERT INTO `quest_template_locale` VALUES (45532, 'deDE', 'Mutters Befehle', 'Tötet Mutter Mirenda.', 'Das große, hässliche Ding sieht aus, als ob es den Angriff hier anführt. Bringen wir es zur Strecke!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45532, 'esES', 'Órdenes de la madre', 'Mata a la Madre Mirenda.', 'Esa bestia horrible tiene pinta de liderar el ataque. ¡A por ella!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45532, 'esMX', 'Órdenes de madre', 'Mata a la Madre Mirenda.', 'Parece que esa cosa grande y horrible es la que lidera el ataque aquí. ¡Vamos a matarla!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45532, 'frFR', 'Ordres de la mère', 'Tuez Mère Mirenda.', 'On dirait que c’est cette ignoble créature qui dirige l’attaque. Éliminons-la !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45532, 'itIT', 'Gli ordini della Madre', 'Uccidi Madre Mirenda.', 'Quell\'enorme e orrenda creatura sembra essere al comando dell\'attacco. Tiriamola giù!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45532, 'koKR', '어머니의 명령', '어미 미렌다를 처치해야 합니다.', '저 크고 흉측한 것이 공격을 지휘하는 모양입니다. 저걸 쓰러트립시다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45532, 'ptBR', 'Ordens da mãe', 'Mate a Mãe Mirenda.', 'Parece que aquela coisa grande e feia está liderando o ataque! Vamos derrubá-la!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45532, 'ruRU', 'Материнские приказы', 'Убейте мать Миренду.', 'Похоже, эта громадная уродина командует демонами. Разделаемся с ней!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45532, 'zhCN', '主母的命令', '消灭主母米兰达。', '那个又大又丑的家伙似乎是这里的指挥官。我们把她干掉吧！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45532, 'zhTW', '鬼母的命令', '殺死鬼母米蘭妲。', '看來帶頭的就是那個醜陋的大傢伙。打倒她！', '', '', '', '', '', '', 0);
