INSERT INTO `quest_template_locale` VALUES (45652, 'deDE', 'Eine \"bescheidene\" Anfrage', 'Trefft Eure Besucherin an der Sturmaugenwarte.', '$ct, wir haben... Besuch erhalten. Sie begehrt Eintritt, doch die Wachen lassen sie nicht ohne Erlaubnis hindurch.$b$bBitte begebt Euch so bald wie möglich zur Warte.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45652, 'esES', 'Una petición \"humilde\"', 'Reúnete con tu visitante en el Mirador Ojo de la Tormenta.', '$ct, hemos recibido una... visita. Nos ha ordenado que la dejemos pasar, pero los guardias se niegan a dejarla entrar sin permiso.$b$bDirígete al mirador en cuanto puedas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45652, 'esMX', 'Una petición \"humilde\"', 'Encuéntrate con el visitante en el Mirador del Ojo del huracán.', '$ct, hemos recibido a una... visitante. Ordena que la dejen pasar, pero los guardias no la dejarán entrar sin tu aprobación.$b$bDirígete al mirador tan pronto te sea posible.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45652, 'frFR', 'Une humble requête', 'Allez trouver votre visiteur au Surplomb de l’Œil de la tempête.', '$ct, nous avons reçu… de la visite. Elle a ordonné qu’on la laisse passer, mais sans approbation, les gardes lui ont refusé le passage.$b$bVeuillez vous rendre au surplomb dès que possible.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45652, 'itIT', 'Una richiesta \"umile\"', 'Incontra la visitatrice al Promontorio Occhio della Tempesta.', '$Ct, c\'è qui una... visitatrice. Pretende di entrare, ma le guardie non la faranno passare senza il tuo permesso.$b$bRecati al Promontorio il prima possibile.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45652, 'koKR', '간단한 부탁', '폭풍의 눈 전망대에서 손님을 맞이해야 합니다.', '$ct, 뭐랄까... 손님이 찾아왔네. 그녀는 들여보내 달라 요구하고 있지만, 허락받지 않은 방문자를 경비병들이 들어가게 할 순 없을 테지.$b$b되도록 빨리 폭풍의 눈 전망대로 가보게.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45652, 'ptBR', 'Um \"humilde\" pedido', 'Encontre seu visitante no Mirante do Olho da Tormenta.', '$ct, nós temos... visita. Ela exige entrar, mas os guardas não a deixam passar sem aprovação.$b$bPor favor, vá ao mirante assim que puder.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45652, 'ruRU', '\"Скромная\" просьба', 'Поговорите с посетительницей на утесе Глаза Бури.', '$ct, у нас гостья. Она просит принять ее, но стража не может ее пропустить без соответствующего распоряжения.$b$bПожалуйста, поднимись на утес как можно скорее.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45652, 'zhCN', '“卑微的”请求', '去风暴之眼瞭望台与你的访客见面。', '$ct，外面来了位……客人。她想进来，但在没有得到允许前，卫兵是不会让她通过的。$b$b请尽快到瞭望台去见见她吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (45652, 'zhTW', '「謙卑」的請求', '在風暴之眼瞰臺與你的訪客碰面。', '$ct，我們有位…訪客。她想進來，但沒有許可，守衛是不會放行的。$b$b可以的話，請盡速前往瞰臺。', '', '', '', '', '', '', 0);
