INSERT INTO `quest_template_locale` VALUES (45785, 'koKR', '스카라베의 부름', '실리더스에 있는 장군 고르척에게 가야 합니다.', '스카라베의 부름 이벤트가 오늘 진행됩니다. 안퀴라즈 관문 근처에 있는 진영의 지도자와 대화하십시오.', '', '', '', '', '', '', 0);
