INSERT INTO `quest_template_locale` VALUES (45989, 'esES', 'Una advertencia urgente', 'Habla con Gakin el Presotenebra.', '<La soldado está pálida y herida, y le atenaza un dolor desmedido.>\n\nLlevo prisionera... desde el asalto a... la Costa Abrupta...\n\n<Se estremece.>\n\nLos demonios... E-están reuniéndose... Se preparan...\n\nTuve que huir... para avisaros... Hay que... ¡detenerlos!', '', '', '', '', '', '', 0);
