INSERT INTO `quest_template_locale` VALUES (45993, 'koKR', '다급한 경고', '코르바스 블러드쏜과 대화해야 합니다.', '<이 병사는 부상을 입어 창백해진 얼굴입니다. 분명히 끔찍한 고통을 겪고 있을 겁니다.>\n\n지금까지 포로로 붙잡혀 있었습니다... 부서진 해변을... 공격했던 이후로...\n\n<몸이 부들부들 떨립니다.>\n\n악마들이... 모여들어... 준비를 하고 있습니다...\n\n탈출해야 했습니다... 경고를 하려고요. 놈들을... 막아야 합니다!', '', '', '', '', '', '', 0);
