INSERT INTO `quest_template_locale` VALUES (46173, 'itIT', 'Pianificazione tattica', 'Incontra Odyn nella Grande Sala dell\'Idromele della Rocca Celeste.', '$gSignore della Battaglia:Signora della Battaglia; $n! Dovremmo discutere delle nostre prossime mosse!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46173, 'koKR', '작전을 짜야할 때', '하늘보루의 봉밀주 전당에서 오딘을 만나야 합니다.', '전쟁군주 $n! 다음 작전을 논해야 한다!', '', '', '', '', '', '', 0);
