INSERT INTO `quest_template_locale` VALUES (46205, 'deDE', 'Verbindungsabbruch', 'Zerstört die Legionsverbindung am Tempel der Tausend Lichter.', 'Es bleibt uns noch eine letzte Aufgabe, um die Verbindung der Brennenden Legion zu den anderen Welten zu kappen.$B$BIch stelle Euch ein Transportmittel zur Verfügung, um Euch näher an die Energiequelle zu bringen, aber Ihr müsst der Verbindung den Todesstoß versetzen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46205, 'esES', 'Una conexión menos', 'Destruye la conexión de la Legión en el Templo de las Mil Luces.', 'Queda una última tarea para cortar la conexión de la Legión Ardiente con otros mundos.$B$BTe transportaré hasta dejarte cerca de la fuente de poder, pero debes ser tú quien aseste el golpe definitivo a esa conexión.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46205, 'esMX', 'Un conducto hacia la nada', 'Destruye el conducto de la Legión en el Templo de las mil luces.', 'Aún queda una tarea más para cercenar la conexión de la Legión Ardiente con los otros mundos.$B$BYo seré el responsable de acercarte hasta la fuente de poder, pero tú deberás darle el golpe de gracia al conducto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46205, 'frFR', 'Le conduit n’est plus', 'Détruisez le Conduit de la Légion au Temple des Mille lumières.', 'Notre dernier objectif consiste à couper le lien entre la Légion ardente et les autres mondes.$B$BJe ferai en sorte de vous amener au plus près de la source, mais vous devez porter le coup fatal au conduit.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46205, 'itIT', 'Niente più condotti', 'Distruggi il Condotto della Legione nel Tempio delle Mille Luci.', 'Rimane una sola cosa da fare per tagliare il collegamento tra la Legione Infuocata e gli altri mondi.$B$BTi teletrasporterò vicino alla sorgente di potere, ma dovrai essere tu a sferrare il colpo finale al condotto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46205, 'koKR', '연결 해제', '천 갈래 빛의 사원에 있는 군단의 연결로를 파괴해야 합니다.', '최후의 과제가 남아 있습니다. 바로 붙타는 군단과 다른 세계들과의 연결을 끊는 것입니다.$B$B탈것으로 그대를 그 힘의 원천 근처까지 데려다 드리겠습니다. 하지만 연결로에 최후의 일격을 가하는 건 그대의 몫입니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46205, 'ptBR', 'O fim do conduto', 'Destrua o Conduto da Legião no Templo de Mil Luzes.', 'Resta uma última tarefa: romper a conexão da Legião Ardente com outros mundos.$B$BEu fornecerei transporte para que você se aproxime da fonte de poder, mas você precisa dar o golpe final no conduto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46205, 'ruRU', 'Без проводника', 'Уничтожьте проводник Легиона в храме Тысячи Огней.', 'Чтобы разорвать связь Пылающего Легиона с другими мирами, осталось сделать последний шаг.$B$BНужно уничтожить проводник. Я помогу тебе добраться до него.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46205, 'zhCN', '终止传导', '摧毁千光神殿的军团导管。', '我们最终的任务依然是切断燃烧军团与其他世界的联系。$b$b我会把你送到力量源头附近，但是对导管的最后一击必须由你完成。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46205, 'zhTW', '斷開連結', '在千燈神廟摧毀燃燒軍團的導引裝置。', '最後一個任務是要中斷燃燒軍團和其他世界的連結。$B$B我會送你到能量來源附近，但你必須對導引裝置進行最後一擊。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46206, 'deDE', 'Flüstern einer verängstigten Welt', 'Trefft Euch mit Magni im Sholazarbecken und hört Euch die Warnung an.', '\"Champion! Azeroth braucht unsere Hilfe.\"$b$b<Magni Bronzebarts Worte hallen durch Euren Verstand, als würden sie aus uralten, längst vergangenen Zeiten zu Euch sprechen. Azeroths Weltenseele scheint dem Sprecher eine Warnung zukommen lassen zu wollen, aber irgendeine Kraft stört die Übermittlung dieser Botschaft.>$b$b<Magni hat Euch aufgefordert, in das Sholazarbecken, das lebhafte Herz des eingefrorenen Nordends, zu reisen, um ihm zu helfen.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46206, 'esES', 'Susurros de un mundo asustado', 'Reúnete con Magni en la Cuenca de Sholazar y escucha la advertencia.', '\"¡$gCampeón:Campeona;! Azeroth necesita tu ayuda\".$b$b<Las palabras de Magni Barbabronce resuenan en tu cabeza, como si surgiesen de un lugar recóndito y ancestral. El alma del mundo de Azeroth pretende enviar una advertencia al Portavoz, pero algún tipo de fuerza interfiere con el mensaje.>$b$b<Magni te ha instado a viajar a la Cuenca de Sholazar, el animado corazón del gélido Rasganorte, para ayudarlo.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46206, 'esMX', 'Susurros de un mundo aterrado', 'Encuéntrate con Magni en la Cuenca de Sholazar y escucha la advertencia.', '\"¡$GCampeón:Campeona;! Azeroth necesita nuestra ayuda\".$b$b<Las palabras de Magni Barbabronce resuenan en tu cabeza, como si salieran de las profundidades de un lugar antiguo. El alma del mundo de Azeroth quiere hacerle una advertencia al Portavoz pero una fuerza extraña se lo impide.>$b$b<Magni te ha convocado con urgencia a la Cuenca de Sholazar, el corazón vibrante de la gélida zona de Rasganorte, para que lo ayudes.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46206, 'frFR', 'Murmures d’un monde effrayé', 'Allez voir Magni dans le Bassin de Sholazar et écoutez l’avertissement.', '« $gChampion:Championne; ! Azeroth a besoin de votre aide. »$b$b<Les mots de Magni Barbe-de-Bronze résonnent dans votre esprit comme s’ils montaient de profondeurs ancestrales. L’âme-monde d’Azeroth cherche à transmettre un avertissement à l’orateur, mais une force mystérieuse semble s’y opposer.>$b$b<Magni vous priait de l’aider en vous rendant dans le bassin de Sholazar, en plein cœur du glacial Norfendre.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46206, 'itIT', 'Sussurri di un mondo terrorizzato', 'Incontra Magni al Bacino di Sholazar e ascolta il suo avvertimento.', '\"$gCampione:Campionessa;, Azeroth ha bisogno del nostro aiuto!\"$b$b<Le parole di Magni Barbabronzea echeggiano nella tua mente, come se provenissero da qualche luogo profondo e antico. L\'anima di Azeroth sta cercando di inviare un avvertimento all\'Oratore, ma delle forze sconosciute stanno interferendo con il messaggio.>$b$b<Magni ti invita a viaggiare verso il Bacino di Sholazar, il cuore pulsante della gelida Nordania, per aiutarlo.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46206, 'koKR', '겁에 질린 세계의 속삭임', '숄라자르 분지에 있는 마그니를 만나 경고의 말을 들어야 합니다.', '\"용사여! 아제로스에 우리 도움이 필요하네.\"$b$b<마그니 브론즈비어드의 말이, 마치 깊고 아득한 곳에서 들려오는 듯 마음속에 울려 퍼집니다. 아제로스의 세계혼이 대변자에게 경고의 말을 전하려 하지만, 어떠한 힘이 이를 방해하고 있습니다.>$b$b<마그니는 당신이 얼어붙은 노스렌드의 활기찬 심장부인 숄라자르 분지로 와 자신을 도와주기를 간청했습니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46206, 'ptBR', 'Sussurros de um Mundo Assustado', 'Encontre-se com Magni na Bacia Sholazar e ouça o alerta.', '\"$GCampeão:Campeã;! Azeroth precisa de nós.\"$b$b<As palavras de Magni Barbabronze ecoam dentro da sua mente, como se surgissem de algum lugar profundo e ancestral. A alma do mundo de Azeroth deseja alertar o Mensageiro sobre algo, mas alguma força está interferindo na comunicação.>$b$b<Magni insistiu para que você viajasse até a Bacia Sholazar, o coração vibrante das terras gélidas de Nortúndria, para ajudá-lo.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46206, 'ruRU', 'Шепот ужаснувшегося мира', 'Отправляйтесь к Магни в Низину Шолазар и выслушайте его предупреждение.', '$gЗащитник:Защитница;! Азерот нуждается в твоей помощи!$b$b<Слова Магни Бронзоборода доносятся из глубин вашего сознания, подобно древнему завету. Душа мира желает предупредить Вестника, но послание искажено некоей силой.>$b$b<Магни призвал вас отправиться к нему на помощь в Низину Шолазар, цветущий оазис в самом сердце холодного Нордскола.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46206, 'zhCN', '惊恐世界的低语', '前往索拉查盆地与麦格尼见面，并听取警告。', '勇士！艾泽拉斯需要我们的帮助。$b$b<麦格尼·铜须的话在你的脑海中回荡，仿佛来自某个幽深而古老的地方。艾泽拉斯的世界之魂想要向代言人发出警告，但某种力量干扰了讯息的传递。>$b$b<麦格尼敦促你前往索拉查盆地帮助他，那里是冰冷的诺森德大陆最生机勃勃的地方。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46206, 'zhTW', '世界之魂的恐懼', '在休拉薩盆地與麥格尼碰面，並聆聽他的警示。', '「勇士！艾澤拉斯需要我們。」$b$b<你的腦中響起麥格尼‧銅鬚的聲音，聽起來深沉、古老。艾澤拉斯的世界之魂想傳達警訊給代言者，可是被某種力量干擾。>$b$b<麥格尼急著要你前往北裂境中充滿生命力的休拉薩盆地，他需要你幫忙。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46207, 'deDE', 'Die Prüfung des Zorns', 'Sprecht mit Odyn, um das Szenario \"Prüfung des Zorns\" zu beginnen. Schließt das Szenario ab und beansprucht Euren Preis.', 'Ich möchte Euch eine prestigeträchtige Einladung aussprechen.$b$bJedes Jahr lasse ich meinen besten Krieger eine Prüfung durchlaufen, um zu beweisen, dass ich die Stärksten des Landes befehlige. Ich habe Euch als meinen Champion für diese Prüfung erwählt.$b$bSolltet Ihr sie erfolgreich absolvieren, wartet auf Euch am Ende ein Preis, der Euren Leistungen angemessen ist.$b$bNehmt Euch Zeit, um Euch vorzubereiten. Kommt zurück und sprecht mich an, wenn Ihr bereit seid, zu beginnen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46207, 'esES', 'La prueba de ira', 'Habla con Odyn para iniciar la gesta \"Prueba de ira\". Complétala y reclama tu premio.', 'Deseo invitarte a un evento de gran prestigio.$B$BCada año celebro una prueba para que mi mejor guerrero demuestre que cuento con los más fuertes de esta tierra. Te he elegido a ti para que seas mi $gguerrero:guerrera; en esta prueba.$B$BSi logras completarla, te espera un premio digno de tus logros.$B$BTómate el tiempo que necesites para prepararte. Ven a verme cuando quieras empezar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46207, 'esMX', 'La prueba de ira', 'Habla con Odyn para comenzar la gesta Prueba de ira. Complétala y recoge tu premio.', 'Quiero invitarte a un evento prestigioso.$b$bTodos los años, llevo a cabo una prueba en la que participan mis guerreros más valientes. El objetivo es demostrar que yo comando a los seres más fuertes de esta tierra. Para esta prueba, te he elegido a ti como mi $gcampeón:campeona;.$b$bSi completas la prueba con éxito, te daré un premio digno de tus logros al final del evento.$b$bTómate todo el tiempo necesario para prepararte. Regresa a hablar conmigo cuando estés $glisto:lista; para comenzar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46207, 'frFR', 'L’épreuve de la rage', 'Parlez à Odyn pour commencer le scénario « L’épreuve de la rage » et terminez-le pour obtenir votre récompense.', 'Je souhaite vous inviter à un évènement des plus prestigieux.$b$bJ’organise tous les ans une épreuve pour mon meilleur guerrier afin de prouver que j’ai sous mes ordres l’élite de la région. Et c’est vous que j’ai $gchoisi:choisie; pour être $gmon champion:ma championne;.$b$bSi vous parvenez à surmonter cette épreuve, vous recevrez une récompense digne de vos exploits.$b$bPrenez tout le temps nécessaire pour vous préparer et revenez me parler lorsque vous serez $gprêt:prête;.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46207, 'itIT', 'La prova della rabbia', 'Parla con Odyn per iniziare lo scenario \"Prova della Rabbia\". Completa lo scenario e reclama il tuo premio.', 'Desidero estenderti un prestigioso invito.$b$bOgni anno i miei più grandi guerrieri si cimentano in una prova per dimostrare il loro valore al mondo intero e quest\'anno ho scelto te come $gmio campione:mia campionessa;.$b$bSe dovessi avere successo, ti aggiudicherai un premio degno delle tue grandi gesta.$b$bPrenditi tutto il tempo che ti serve per prepararti, poi torna da me non appena sarai $gpronto:pronta; a iniziare.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46207, 'koKR', '분노의 시험', '오딘과 대화하고 분노의 시험 시나리오를 시작해야 합니다. 시나리오를 완료하고 보상을 획득하십시오.', '특별한 자리에 너를 초대하고 싶구나.$b$b나는 매년 최고의 전사를 위한 시험의 장을 마련하여, 내 휘하의 군대가 이 땅에서 가장 강하다는 것을 증명케 한다. 올해의 시험에서는 너를 나의 용사로 선택했다.$b$b시험을 끝까지 치러 내면, 너의 성취에 걸맞은 상품이 주어질 것이다.$b$b천천히 준비해도 좋다. 시작할 준비가 되면 돌아와서 내게 말하거라.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46207, 'ptBR', 'A Provação da Raiva', 'Fale com Odyn para iniciar o cenário Provação da Raiva. Conclua o cenário e pegue seu prêmio.', 'Quero estender a você um convite de prestígio.$b$bTodos os anos eu organizo um teste para meu melhor guerreiro para provar que eu comando os mais fortes nesta terra. Escolhi você para ser $gmeu:minha; $gcampeão:campeã; nesse teste.$b$bSe você se sair bem, tenho um prêmio digno do feito à sua espera.$b$bLeve o tempo que precisar para se preparar. Volte e fale comigo quando estiver $Gpronto:pronta; para começar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46207, 'ruRU', 'Испытание ярости', 'Поговорите с Одином, чтобы начать сценарий \"Испытание ярости\". Завершите сценарий и получите награду.', 'Я хочу пригласить тебя на престижное состязание.$b$bРаз в год я устраиваю испытание для сильнейших бойцов, которое призвано продемонстрировать всем, что именно у меня самые лучшие воины в этих краях. В этот раз я выбрал своим чемпионом тебя.$b$bЕсли ты успешно пройдешь испытание, тебя ждет приз, достойный твоих достижений.$b$bМожешь готовиться, сколько сочтешь нужным. Когда решишь, что $gготов:готова;, дай мне знать.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46207, 'zhCN', '怒火试炼', '与奥丁谈一谈，开启怒火试炼场景战役。完成场景战役并获取你的奖励。', '我想邀请你参加一场荣耀的试练。$b$b我每年都会为我最出色的战士举办一场试练，以证明我号令着这片土地上最强大的战士。在这次的试练，我将选择你作为我的勇士。$b$b如果你在试练中获胜，我也会给出配得上你成就的奖励。$b$b你可以好好花时间准备一下，等准备好后再回来告诉我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46207, 'zhTW', '怒氣試煉', '與歐丁交談以開始進行怒氣試煉事件。完成事件並獲取你的獎勵。', '我希望有這個榮幸邀請你。$b$b每年，我會為我最厲害的戰士舉辦一場試煉，證明這片土地上最強大的戰士都聽令於我。我已經選擇你為參加這場試煉的勇士。$b$b如果你成功完成試煉，我將在最後給予你應得的獎賞。$b$b請花點時間，做好萬全準備。當你準備好開始時，就回來找我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46208, 'deDE', 'Eine göttliche Einladung', 'Sprecht mit Odyn in Eurer Ordenshalle in der Himmelsfeste.', '$gSchlachtenfürst:Schlachtenfürstin; $p, Odyn hat Euch um eine Audienz in der Himmelsfeste gebeten.$b$bTrefft ihn dort bitte, sobald es Euch möglich erscheint.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46208, 'esES', 'Invitación divina', 'Habla con Odyn en tu sede de clase en el Bastión Celestial.', '$gSeñor:Señora; de la batalla $p, Odyn ha solicitado verte en el Bastión Celestial.$B$BPor favor, reúnete con él en cuanto te sea posible.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46208, 'esMX', 'Invitación divina', 'Habla con Odyn en tu Salón de la orden en Bastión del cielo.', '$gSeñor de la batalla:Señora de la batalla; $p, Odyn ha solicitado tener una audiencia contigo en el Bastión del cielo.$b$bVe a hablar con él cuando lo creas conveniente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46208, 'frFR', 'Une invitation divine', 'Parlez à Odyn dans votre domaine de classe du Séjour céleste.', 'Seigneur de bataille $p, Odyn demande à vous voir au Séjour céleste.$b$bAllez le trouver dès que possible.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46208, 'itIT', 'Un invito divino', 'Parla con Odyn nella tua Enclave di Classe alla Rocca Celeste.', '$gSignore della Battaglia:Signora della Battaglia; $p, Odyn ha richiesto un\'udienza con te alla Rocca Celeste.$b$bVai da lui non appena possibile.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46208, 'koKR', '신의 초대', '하늘보루의 연맹 전당에 있는 오딘과 대화해야 합니다.', '$p 전투군주여, 오딘께서 하늘보루에서 뵙고자 하신다.$b$b되도록 빨리 가 보아라.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46208, 'ptBR', 'Convite divino', 'Fale com Odyn no seu Salão da Ordem, na Fortaleza Celeste.', 'Senho$Gr:ra; da batalha $p, Odyn solicitou uma audiência com você na Fortaleza Celeste.$b$bPor favor, vá ter com ele assim que possível.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46208, 'ruRU', 'Божественное приглашение', 'Поговорите с Одином в Небесной Цитадели.', 'Воевода $p, Один желает видеть тебя в Небесной Цитадели.$b$bОтправляйся туда при первой же возможности.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46208, 'zhCN', '神之邀约', '在你位于苍穹要塞的职业大厅内与奥丁谈一谈。', '战争领主$p，奥丁请你返回苍穹要塞觐见他。$b$b请在方便时尽快前往。', '', '', '', '', '', '', 0);
