INSERT INTO `quest_template_locale` VALUES (46266, 'deDE', 'Die Rückkehr des Rächers', 'Kehrt zu Kor\'vas an Bord der Teufelshammer zurück.', 'Fallss Ihr mir nur ein wenig ähnlich seid, freut Ihr Euch sicher, fessten Boden unter Euren Füßen zu spüren!\n\nLeider aber solltet Ihr wohl lieber gleich zur Teufelsshammer zurückkehren, damit Kor\'vass sich um die Reparaturen kümmern kann.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46266, 'esES', 'El regreso del Asesino', 'Vuelve con Kor\'vas a bordo del Martillo Vil.', 'Si te pareces a mí, seguro que prefieres quedarte en tierra firme.\n\nPor desgracia, probablemente sea mejor regresar al Martillo Vil de inmediato para que Kor\'vas pueda finalizar las reparaciones.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46266, 'esMX', 'El regreso del Destripador', 'Regresa con Kor\'vas a bordo del Martillo vil.', 'Si eres como yo, no me caben dudas de que preferirás quedarte aquí, en tierra firme.\n\nPor desgracia, es probable que lo mejor sea regresar al Martillo vil enseguida para que Kor\'vass pueda terminar con las reparacioness.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46266, 'frFR', 'Le retour du tueur', 'Retournez voir Kor’vas à bord du Marteau gangrené.', 'Si vous êtes un tant sssoit peu comme moi, vous préféreriez certainement ressster sur la terre ferme.\n\nHélas, il vaut sssûrement mieux que vous retourniez immédiatement auprès de Kor’vasss afin qu’elle puisse terminer les réparations du Marteau gangrené.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46266, 'itIT', 'Il ritorno del Flagello dei Demoni', 'Torna da Kor\'vas a bordo della Vilmartello.', 'Se anche tu sei come me, di sicuro preferiresssti rimanere qui sulla terraferma!\n\nIn ogni caso, faresssti meglio a tornare immediatamente sulla Vilmartello affinché Kor\'vas posssa portare a termine le riparazioni.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46266, 'koKR', '학살자의 귀환', '지옥 망치호에 있는 코르바스에게 돌아가야 합니다.', '당신이 저와 비슷한 구석이 있다면, 분명히 단단한 지상에 머무르길 원하실 거라 생각합니다.\n\n불행하게도, 지금은 지옥 망치호로 바로 돌아가셔야할 것 같습니다. 코르바스가 수리를 마칠 수 있게 말이죠.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46266, 'ptBR', 'O retorno do assassino', 'Fale novamente com Kor\'vas a bordo do Martelo Vil.', 'Se você for como eu, tenho certeza de que preferiria permanecer aqui em terra firme!\n\nNo entanto, é melhor você voltar para o Martelo Vil imediatamente para que Kor\'vasss possa terminar os reparosss.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46266, 'ruRU', 'Возвращение истребителя', 'Вернитесь к Кор\'вас на \"Молот Скверны\".', 'Не знаю, как ты, но я точно предпочитаю с-стоять на твердой земле!\n\nОднако тебе с-сейчас надо как можно с-скорее вернутьс-ся на \"Молот С-скверны\" и помочь Кор\'вас-с с ремонтом.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46266, 'zhCN', '屠魔者归来', '向邪能之槌号上的考瓦斯复命。', '如果你是我，你肯定也更想呆在地面上！\n\n不幸的是，你最好还是马上回到邪能之槌号上去，这样考瓦斯才能完成修理工作。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46266, 'zhTW', '斬魔者回歸', '登上魔錘號找珂娃絲回報。', '如果你跟我一樣，一定也會想待在平穩的地面上！\n\n但是，看來我們最好還是立刻回到魔錘號上，讓珂娃絲完成維修工作。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46267, 'deDE', 'Die Rückkehr des Schlachtenfürsten', 'Sprecht mit Odyn in der Himmelsfeste.', '$gSchlachtenfürst:Schlachtenfürstin; $n, Odyn möchte von Euch die Geschichten über Eure glorreichen Siege hören!\n\nIch helfe Euch, in die Himmelsfeste zurückzukehren, sobald Ihr dazu bereit seid!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46267, 'esES', 'El regreso del Señor de la batalla', 'Habla con Odyn en el Bastión Celestial.', '¡$gSeñor:Señora; de la batalla $n, Odyn querrá oír la historia de tus gloriosas victorias!\n\nTe ayudaré a regresar al Bastión Celestial cuando quieras.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46267, 'esMX', 'El regreso del Señor de la batalla', 'Habla con Odyn en el Bastión del cielo.', '$gSeñor de la batalla:Señora de la batalla; $n, ¡Odyn seguramente querrá oír la historia sobre tus victorias gloriosas en este lugar!\n\nCuando estés $glisto:lista;, te ayudaré a regresar al Bastión del cielo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46267, 'frFR', 'Le retour du seigneur de bataille', 'Parlez à Odyn au Séjour céleste.', 'Seigneur de bataille $n, Odyn doit avoir hâte d’entendre le récit de vos glorieuses victoires en ces lieux !\n\nJe vous aiderai à regagner le Séjour céleste dès que vous serez $gprêt:prête;.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46267, 'itIT', 'Il ritorno del Signore della Battaglia', 'Parla con Odyn alla Rocca Celeste.', '$gSignore della Battaglia:Signora della Battaglia; $n, Odyn vorrà ascoltare il racconto delle tue gloriose vittorie!\n\nTi aiuterò a tornare alla Rocca Celeste quando sarai $gpronto:pronta;.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46267, 'koKR', '전쟁군주의 귀환', '하늘보루에서 오딘과 대화해야 합니다.', '전쟁군주 $n, 오딘께서 너의 영광스러운 승리에 대해 듣고 싶어 하실 것이다!\n\n준비가 되면 하늘보루로 돌아가는 걸 도와주마.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46267, 'ptBR', 'O retorno do Senhor da Batalha', 'Fale com Odyn na Fortaleza Celeste.', 'Senho$Gr:ra; da batalha $n, Odyn vai querer ouvir as histórias das suas gloriosas vitórias aqui!\n\nEu ajudarei você a retornar para Fortaleza Celeste quando você estiver pront$Go:a;.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46267, 'ruRU', 'Возвращение воеводы', 'Поговорите с Одином в Небесной Цитадели.', 'Воевода $n, Один наверняка захочет услышать о твоих славных победах из первых уст.\n\nКогда будешь $gготов:готова; только скажи – и я верну тебя в Небесную Цитадель.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46267, 'zhCN', '战争领主归来', '与苍穹要塞的奥丁谈一谈。', '战争领主$n，奥丁非常想听听你的辉煌胜利！\n\n你准备好后，我会帮你返回苍穹要塞。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46267, 'zhTW', '戰鬥領主回歸', '在擎天堡與歐丁交談。', '戰鬥領主$n，歐丁一定想聽聽你在這裡的光榮戰績！\n\n等你準備好了，我會協助你返回擎天堡。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46268, 'deDE', 'Ein gefundenes Andenken', 'Bringt den Kompass zu König Anduin Wrynn in der Burg Sturmwind.', '<Die schöne, bronzene Außenseite des Kleinods weist komplizierte und filigrane Einlegearbeiten auf, obwohl Felsen und Wellen viele der Einzelheiten zerstört haben. Ihr wischt den Schmutz und das Salz fort, die den Verschluss verkrusten, und öffnet es.\n\nEs ist ein Kompass - oder wenigstens war es früher einer. Die Glasoberfläche ist zersplittert, die Nadel ist verbogen und bewegt sich nicht.\n\nIn den Deckel ist ein Porträt eingelassen. Die Farbe ist verblasst und das Bild verschwommen, doch das Motiv ist unverkennbar: das Gesicht des jungen Anduin Wrynn.>', '', '', '', '', '', 'Bringt den Kompass zu König Anduin Wrynn in der Burg Sturmwind.', 0);
INSERT INTO `quest_template_locale` VALUES (46268, 'esES', 'El hallazgo de un recuerdo', 'Llévale la brújula al rey Anduin Wrynn en el Castillo de Ventormenta.', '<El exquisito exterior de latón del abalorio tiene incrustada una intrincada filigrana, aunque buena parte de los detalles han sufrido los golpes de rocas y olas. Tras limpiar la mugre y la sal que recubren el broche, logras abrirlo.\n\nEs una brújula, o por lo menos lo era. La esfera de cristal está hecha pedazos y la aguja está torcida, inmóvil.\n\nREPLACEado en la tapa hay un retrato. La pintura está desvaída y empapada, pero la imagen es inconfundible: el rostro de un joven Anduin Wrynn.>', '', '', '', '', '', 'Llévale la brújula a: rey Anduin Wrynn. Zona: Castillo de Ventormenta.', 0);
INSERT INTO `quest_template_locale` VALUES (46268, 'esMX', 'Un recuerdo encontrado', 'Llévale la brújula al rey Anduin Wrynn en el Castillo de Ventormenta.', '<El exterior de latón fino del abalorio tiene tallada una filigrana intrincada, aunque gran parte de los detalles se borraron por la acción de las rocas y las olas. Tras quitar la mugre y la sal pegadas al broche, logras abrirlo.\n\nEs una brújula, o al menos lo fue. El frente de vidrio está hecho añicos y la aguja está doblada, inerte.\n\nEn la cara interna de la tapa hay un retrato. La pintura está borrada y empapada, pero la persona es inconfundible: es el rostro de Anduin Wrynn de joven.>', '', '', '', '', '', 'Llévale la brújula al rey Anduin Wrynn en el Castillo de Ventormenta.', 0);
INSERT INTO `quest_template_locale` VALUES (46268, 'frFR', 'Un souvenir retrouvé', 'Apportez la boussole au Roi Anduin Wrynn dans le Donjon de Hurlevent.', '<Ce bibelot en cuivre de très bonne qualité est décoré d’arabesques sophistiquées, malheureusement abîmées par les rochers et le ressac. Après avoir retiré la crasse et le sel du fermoir, vous parvenez à l’ouvrir.\n\nIl s’agit d’une boussole, du moins à l’origine. Le verre est brisé, l’aiguille est tordue et ne bouge plus.\n\nÀ l’intérieur du couvercle se trouve un portrait. Malgré l’eau et les années, il est impossible de ne pas reconnaître ce visage, celui d’Anduin Wrynn dans sa jeunesse.>', '', '', '', '', '', 'Apportez la boussole au Roi Anduin Wrynn dans le Donjon de Hurlevent.', 0);
INSERT INTO `quest_template_locale` VALUES (46268, 'itIT', 'Un ricordo ritrovato', 'Porta la bussola a Re Anduin Wrynn al Forte di Roccavento.', '<La superficie esterna in ottone del monile è intarsiata di intricata filigrana, anche se gran parte dei dettagli è stata rovinata dal lento e costante infrangersi delle onde e delle rocce. Dopo averlo ripulito dal fango e dal sale incrostato, riesci con fatica ad aprirlo.>\n\n<È una bussola... o lo era, quantomeno. Il vetro è infranto, l\'ago piegato e incastrato.>\n\n<All\'interno del coperchio si trova un disegno, ormai logoro e dai colori sbiaditi, ma il soggetto è inequivocabile: il volto di un giovane Anduin Wrynn.>', '', '', '', '', '', 'Porta la bussola a Re Anduin Wrynn al Forte di Roccavento.', 0);
INSERT INTO `quest_template_locale` VALUES (46268, 'koKR', '찾아낸 기억의 조각', '스톰윈드 왕궁에 있는 국왕 안두인 린에게 나침반을 건네주어야 합니다.', '<놋쇠로 만들어진 장신구는 정교한 금장식으로 꾸며져 있지만, 풍파로 인해 장식이 많이 닳았습니다. 걸쇠를 덮은 흙과 소금을 닦아내니 장신구가 열립니다.\n\n장신구는 나침반이었습니다. 적어도, 예전에는. 겉유리가 깨져있고, 바늘은 구부러진 채 움직이지 않습니다.\n\n뚜껑 안쪽에는 초상화가 있습니다. 그림은 빛바래고 물을 잔뜩 머금었지만, 누구의 초상화인지는 분명합니다. 어린 안두인 린의 얼굴이 그려져 있습니다.>', '', '', '', '', '', '스톰윈드 왕궁에 있는 국왕 안두인 린에게 나침반을 건네주어야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (46268, 'ptBR', 'Lembrança encontrada', 'Leve a bússola para o Rei Anduin Wrynn na Bastilha Ventobravo.', '<O refinado exterior metálico do berloque é decorado com intrincadas filigranas em relevo, apesar de grande parte dos detalhes ter sido apagada pelas rochas e marés. Ao remover a sujeira e o sal encrustados no fecho, você consegue abri-lo.\n\nÉ — ou ao menos já foi — uma bússola. O vidro está rachado, e a agulha, retorcida, não se move.\n\nHá um retrato na tampa. Mesmo com a tinta desbotada, a imagem é inconfundível: o rosto de Anduin Wrynn quando mais novo.>', '', '', '', '', '', 'Leve a bússola para o Rei Anduin Wrynn na Bastilha Ventobravo.', 0);
INSERT INTO `quest_template_locale` VALUES (46268, 'ruRU', 'Памятная вещь', 'Отнесите компас королю Андуину Ринну в крепость Штормграда.', '<Крышка медальона покрыта филигранью, хотя во многих местах изящный узор стерся от ударов о камни. Счистив с замка налипшую грязь и морскую соль, вы осторожно открываете его.\n\nМедальон оказывается компасом – во всяком случае, он им был когда-то. Теперь же защитное стекло испещрено трещинами; погнутая стрелка больше не крутится.\n\nНа внутренней стороне крышки написан портрет. Краски выцвели от морской воды, но разобрать, кто на нем изображен, можно без труда – это совсем еще юный Андуин Ринн.>', '', '', '', '', '', 'Отнесите компас королю Андуину Ринну в крепость Штормграда.', 0);
INSERT INTO `quest_template_locale` VALUES (46268, 'zhCN', '重拾记忆', '把罗盘带给暴风要塞中的安度因·乌瑞恩国王。', '<这个小玩意精美的黄铜外壳上镶嵌着繁复的金银丝线，但是许多细致的花纹都被岩石和海浪磨蚀殆尽了。把锁扣上的尘垢和盐渍清理掉后，你终于把它打开了。>\n\n这是个罗盘——或者说，至少曾经是。玻璃的表面已经碎裂，指针也已弯曲，无法动弹。\n\n罗盘的盖子里有一张画像。颜料在水里泡了很久，已经褪色，但是画中人依然清晰可辨：这是安度因·乌瑞恩的年轻脸庞。>', '', '', '', '', '', '把罗盘交给暴风要塞的安度因国王。', 0);
INSERT INTO `quest_template_locale` VALUES (46268, 'zhTW', '尋獲的紀念物', '把羅盤帶到暴風要塞交給安杜因‧烏瑞恩國王。', '<飾品的黃銅外殼以精巧的花紋雕飾，由於屢經岩石和海浪擊打，許多細節已經模糊。如果清除扣環的鹽和泥沙，應該可以打開蓋子。\n\n是一個羅盤…至少曾經是。盤面的玻璃已經碎了，指針扭曲，沒辦法動。\n\n蓋內畫有肖像，顏料因為長期泡在海水中已經變淡，但肖像的主角毋庸置疑：是年幼的安杜因‧烏瑞恩。>', '', '', '', '', '', '把羅盤帶到暴風要塞交給安杜因‧烏瑞恩國王。', 0);
