INSERT INTO `quest_template_locale` VALUES (46707, 'koKR', '하수인! 저 놈도 죽여!', '', '', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46707, 'ptBR', 'Lacaio! Mate aquele também!', '', '', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46707, 'ruRU', 'Слуга! Этого тоже убить!', '', '', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46707, 'zhCN', '仆从！把那个也干掉！', '', '', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46707, 'zhTW', '手下！那傢伙也必須死！', '', '', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46719, 'deDE', 'Amal\'thazads Botschaft', 'Sprecht mit Amal\'thazad in Acherus.', '$gTodesgebieter:Todesgebieterin;, Amal\'thazad hat Eure Anwesenheit in Acherus erbeten.\n\nEr stand in Kontakt mit dem Lichkönig und hat Verbesserungen an der Zitadelle vorgenommen.\n\nOb diese zu seinem oder Eurem Vorteil sind, weiß ich nicht.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46719, 'esES', 'Mensaje de Amal\'thazad', 'Habla con Amal\'thazad en Acherus.', '$gSeñor:Señora; de la Muerte, Amal\'thazad solicita tu presencia en Acherus.\n\nHa estado en contacto con el Rey Exánime y realizando mejoras en la ciudadela.\n\nNo sé si por su bien o el tuyo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46719, 'esMX', 'El mensaje de Amal\'thazad', 'Habla con Amal\'thazad en Acherus.', '$gSeñor:Señora; de la muerte, Amal\'thazad solicitó tu presencia en Acherus.\n\nHa estado en contacto con el Rey Exánime y estuvo haciendo mejoras en la ciudadela.\n\nDesconozco si por tu bien o el suyo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46719, 'frFR', 'Le message d’Amal’thazad', 'Parlez à Amal’thazad à Achérus.', '$gSeigneur:Dame; de la mort, Amal’thazad a réclamé votre présence à Achérus.\n\nDepuis qu’il a pris contact avec le roi-liche, il apporte des améliorations à la citadelle.\n\nJ’ignore cependant s’il œuvre au service de ses intérêts ou des vôtres.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46719, 'itIT', 'Il messaggio di Amal\'thazad', 'Parla con Amal\'thazad ad Acherus.', '$gSignore della Morte:Signora della Morte;, Amal\'thazad ha richiesto la tua presenza ad Acherus.\n\nHa preso contatti con il Re dei Lich e sta supervisionando dei miglioramenti alla cittadella.\n\nNon so se a tuo o suo vantaggio.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46719, 'koKR', '아말타자드의 전갈', '아케루스에 있는 아말타자드와 대화해야 합니다.', '죽음군주여, 아말타자드께서 아케루스에서 뵙고자 하십니다.\n\n최근 리치 왕과 접촉하며 성채를 개선하고 계십니다.\n\n본인의 이익 때문인지, 당신의 이익 때문인지는 모르겠습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46719, 'ptBR', 'A mensagem de Amal\'thazad', 'Fale com Amal\'thazad em Áquerus.', 'Senho$Gr:ra da Morte;, Amal\'thazad solicitou sua presença em Áquerus.\n\nEle tem mantido contato com o Lich Rei e feito melhorias na cidadela.\n\nSe para o bem dele ou o seu, eu não sei.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46719, 'ruRU', 'Послание Амал\'тазада', 'Поговорите с Амал\'тазадом в Акерусе.', '$gВладыка:Владычица; смерти, Амал\'тазад ждет твоего возвращения в Акерус.\n\nОн связался с Королем-личом и сейчас занимается улучшением цитадели.\n\nДля своего блага или же ради тебя – мне неведомо.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46719, 'zhCN', '阿曼萨加德的信息', '与阿彻鲁斯的阿曼萨加德谈一谈。', '死亡领主，阿曼萨加德请求与你在阿彻鲁斯会面。\n\n他一直与巫妖王保持着联系，修缮着与冰冠堡垒的关系。\n\n这是为了他自己，还是为了你，我无从得知。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46719, 'zhTW', '艾默薩札德的訊息', '在亞榭洛與艾默薩札德交談。', '死亡領主，艾默薩札德要你回亞榭洛一趟。\n\n他一直與巫妖王保持聯繫，還改造了城塞。\n\n但我不知道這是為了你好，還是為了他好。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46720, 'deDE', 'Gefrorene Erinnerungen', 'Nehmt das Portal zum oberen Bereich von Acherus, hört Euch den Plan des Lichkönigs an und werdet Zeuge seiner Vision in Eiskrone.', 'Schließt Eure Augen, $gTodesgebieter:Todesgebieterin;. Könnt Ihr seine Präsenz nicht bereits spüren?\n\n<Die eisige Präsenz des Lichkönigs macht sich augenblicklich in Eurem Geist bemerkbar.>\n\nStellt Euch in den Runenkreis an der Spitze von Acherus, $gTodesgebieter:Todesgebieterin;.\n\nIch werde Euch zeigen, was ich gesehen habe.\n\n<Die Präsenz schwindet.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46720, 'esES', 'Recuerdos congelados', 'Utiliza el portal a lo alto de Acherus, escucha el plan del Rey Exánime y después presencia su visión en Corona de Hielo.', 'Cierra los ojos, $gSeñor:Señora; de la Muerte. ¿No sientes ya su presencia?\n\n<La gélida presencia del Rey Exánime invade enseguida tu mente.>\n\nSitúate en el círculo de runas sobre Acherus, $gSeñor:Señora; de la Muerte.\n\nTe mostraré lo que he visto.\n\n<La presencia se desvanece.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46720, 'esMX', 'Recuerdos congelados', 'Toma el portal hacia la cima de Acherus, oye los planes del Rey Exánime y luego sé testigo de su visión en Corona de Hielo.', 'Cierra los ojos, $GSeñor:Señora; de la muerte. ¿Sientes su presencia?\n\n<La presencia gélida del Rey Exánime aparece en tu mente de inmediato.>\n\nPárate en el círculo de runas en la cima de Acherus, $GSeñor:Señora; de la muerte.\n\nTe mostraré lo que he visto.\n\n<La presencia se disipa.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46720, 'frFR', 'Souvenirs gelés', 'Empruntez le portail vers le sommet d’Archérus, écoutez les plans du Roi-liche, puis assistez à sa vision à la Couronne de glace.', 'Fermez les yeux, $gseigneur:dame; de la mort. Ne sentez-vous pas déjà sa présence ?\n\n<La présence glaciale du roi-liche investit soudain votre esprit.>\n\nTenez-vous dans le cercle de runes au sommet d’Achérus.\n\nJe vais vous montrer ce que j’ai vu.\n\n<La présence s’estompe.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46720, 'itIT', 'Ricordi congelati', 'Prendi il portale per la cima di Acherus, ascolta il piano del Re dei Lich e assisti alla sua visione alla Corona di Ghiaccio.', 'Chiudi gli occhi, $gSignore della Morte:Signora della Morte;. Non riesci a percepirlo?\n\n<La presenza gelida del Re dei Lich invade la tua mente in un istante.>\n\nEntra nella runa in cima ad Acherus, $gSignore della Morte:Signora della Morte;.\n\nTi mostrerò quello che ho visto.\n\n<La presenza svanisce.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46720, 'koKR', '얼어붙은 기억', '아케루스 꼭대기로 통하는 차원문을 사용하고, 리치 왕의 계획을 들은 뒤, 얼음왕관에서 그가 본 환영을 목격해야 합니다.', '눈을 감거라, 죽음군주여. 그의 존재가 이미 느껴지지 않느냐?\n\n<리치 왕의 차가운 존재감이 순식간에 머릿속을 파고듭니다.>\n\n아케루스 꼭대기에 있는 룬 마법진 안에 서라, 죽음군주여.\n\n내가 본 것을 보여 줄 테니.\n\n<존재감이 사라집니다.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46720, 'ptBR', 'Memórias congeladas', 'Use o portal para o topo de Áquerus, ouça o plano do Lich Rei e receba a visão dele na Coroa de Gelo.', 'Feche os olhos, $gSenhor:Senhora; da Morte. Você ainda não consegue sentir a presença dele?\n\n<A presença gélida do Lich Rei penetra sua mente instantaneamente.>\n\nVá para o círculo de runas gélidas no topo de Áquerus, $gSenhor:Senhora; da Morte.\n\nEu lhe mostrarei o que vi.\n\n<A presença desvanece.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46720, 'ruRU', 'Замерзшие воспоминания', 'Используйте портал, ведущий на вершину Акеруса, выслушайте план Короля-лича и станьте свидетелем его видения в Ледяной Короне.', 'Закрой глаза, $gвладыка:владычица; смерти. Ты ощущаешь его присутствие?\n\n<В то же мгновение вы явственно ощущаете леденящее присутствие Короля-лича в своем разуме.>\n\n$gВладыка:Владычица; смерти, встань в рунический круг на вершине Акеруса.\n\nЯ покажу тебе то, что видел сам.\n\n<Король-лич покидает ваши мысли.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46720, 'zhCN', '冰冻的回忆', '使用传送门到达阿彻鲁斯顶层，听取巫妖王的计划，然后见证他有关冰冠冰川的幻象。', '闭上你的眼睛，死亡领主。你还没感受到他的存在吗？\n\n<巫妖王冰冷的气息立刻出现在了你的脑海中。>\n\n站到阿彻鲁斯顶部的符文法阵里，死亡领主。\n\n我会把我看到的展示给你。\n\n<巫妖王的气息消散了。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46720, 'zhTW', '冰封的記憶', '使用前往亞榭洛頂端的傳送門，聆聽巫妖王的計畫，並在寒冰皇冠親眼看看他的幻象。', '閉上你的雙眼，死亡領主。難道你沒感覺到他嗎？\n\n<巫妖王的冰冷形體瞬間浮現在你腦海中。>\n\n站到亞榭洛最頂端的符文法陣中，死亡領主。\n\n我會讓你看到我看見了什麼。\n\n<形體消逝。>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46722, 'deDE', 'Nichts in meinem Ärmel', 'Benutzt Akazamzaraks Hut, um ein magisches Kunststück durchzuführen.', 'Also, Ihr mögt ja glauben, dass ein Säckchen voller Gold eine gute Belohnung für Eure Mühen ist, aber ich denke da an noch etwas Besseres für Euch.\n\nIch werde Euch mein bestes Kunststück beibringen!\n\nIch weiß, ich weiß, ich bin ein sehr großzügiger Goblin. Lasst mich Euch zeigen, wie man es richtig macht.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46722, 'esES', 'Nada en la manga', 'Usa el sombrero de Akazamzarak para realizar un truco de magia.', 'Tal vez pienses que un saco de oro sería una buena recompensa por las molestias, pero tengo pensado algo mejor para ti.\n\n¡Voy a enseñarte mi mejor truco!\n\nLo sé, lo sé: soy un goblin la mar de generoso. ¡Te enseñaré cómo se hace!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46722, 'esMX', 'Nada bajo la manga', 'Usa el Sombrero de Akazamzarak para hacer un truco de magia.', 'Ahora, tú podrás pensar que un saco de oro sería una buena recompensa por tu ayuda, pero tengo algo mejor en mente para ti.\n\n¡Voy a enseñarte mi mejor truco!\n\nYa sé, ya sé: soy un goblin muy generoso. ¡Déjame mostrarte cómo se hace!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46722, 'frFR', 'Rien dans la manche', 'Utilisez le Chapeau d’Akazamzarak pour réaliser un tour de magie.', 'Vous pensez peut-être qu’un sac d’or ferait une récompense acceptable pour vos efforts, mais j’ai pensé à quelque chose de mieux encore.\n\nJe vais vous apprendre mon meilleur tour !\n\nJe sais, je sais, ma générosité me perdra… Laissez-moi vous révéler son secret !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46722, 'itIT', 'Niente assi nella manica', 'Usa il Cappello di Akazamzarak per eseguire un trucco magico.', 'Forse starai pensando che una borsa piena d\'oro sarebbe un\'ottima ricompensa per il disturbo, ma in realtà ho qualcosa di meglio in mente per te.\n\nTi insegnerò il mio trucco migliore!\n\nLo so, lo so, sono un Goblin estremamente generoso. Lascia che ti mostri come si fa!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46722, 'koKR', '속임수가 아니야', '아카잠자락의 모자를 사용하여 마술을 보여주어야 합니다.', '돈주머니 하나 던져 주는 게 가장 좋은 보상이라고 자네는 생각할지도 모르지만, 나는 더 좋은 걸 생각해 놨어.\n\n자네에게 내가 알고있는 가장 멋진 마술을 보여주지!\n\n알아, 알아. 난 정말 그릇이 큰 고블린이라는 걸. 내가 어떻게 하는지도 알려주지!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46722, 'ptBR', 'Nada nas minhas mangas', 'Use o Chapéu de Akazamzarak para fazer um truque de mágica.', 'Talvez você considere uma sacola de ouro uma boa recompensa pelo seu trabalho, mas eu estou pensando em algo melhor para você.\n\nEu vou ensinar meu melhor truque para você!\n\nEu sei, eu sei, eu sou um goblin muito generoso. Vou mostrar como se faz!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46722, 'ruRU', 'И никакого туза в рукаве', 'Исполните фокус с помощью шляпы Ахалаймахалая.', 'Ты, наверное, думаешь, что мешок золота стал бы неплохой наградой за твои старания, но у меня для тебя найдется кое-что получше.\n\nЯ научу тебя своему лучшему фокусу!\n\nДа, знаю, знаю, я очень щедрый гоблин. Сейчас я покажу тебе, как это делается!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46722, 'zhCN', '袖子里什么都没有', '使用阿卡扎曼扎拉克的帽子表演魔术。', '现在，你大概会觉得，给你一袋金币就可以犒劳你了，但是我打算给你更棒的东西。\n\n我会把我最棒的戏法教给你！\n\n我知道，我知道，我真是个慷慨的地精。我来演示给你看吧！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46722, 'zhTW', '兩袖空空', '使用阿卡贊札拉克的帽子變魔術。', '你可能會以為一袋黃金作為獎勵，對你來說就綽綽有餘，但我想給你更好的東西。\n\n我要教你我最拿手的絕活！\n\n我知道，不用稱讚我，我確實是非常慷慨的哥布林。讓我為你展示一遍！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46723, 'deDE', 'Hinunter in den Kaninchenbau', 'Begebt Euch in Akazamzaraks Hut hinein und findet heraus, was mit seinen Kaninchen passiert.', 'Also schön. Ihr müsst für mich dort hineingehen und herausfinden, was schiefläuft. Ich würde das ja selbst machen, aber... naja, es ist kompliziert.\n\nAuf jeden Fall haben wir keine Zeit zu verlieren! Springt einfach nur in den Hut und findet heraus, was los ist.\n\nIch halte mich bereit, um Euch herauszuziehen, sobald Ihr alles im Griff habt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46723, 'esES', 'Tralarí, tralará...', 'Entra en el sombrero de Akazamzarak y averigua qué les sucede a sus conejos.', 'Vale, colega. Voy a necesitar que te metas ahí y averigües qué pasa. Entraría yo, pero... bueno, es complicado.\n\n¡En fin, no hay tiempo que perder! Métete en el sombrero y averigua qué ocurre.\n\nYo estaré preparado para sacarte en cuanto lo tengas todo listo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46723, 'esMX', 'Por la madriguera del conejo', 'Entra al Sombrero de Akazamzarak y averigua qué está ocurriendo con sus conejos.', 'A ver. Voy a necesitar que te metas ahí y veas qué está pasando. Iría yo, pero... Bueno, es complicado.\n\nEn fin, ¡no hay tiempo que perder! Tú métete en el sombrero y averigua qué está pasando.\n\nEstaré listo para sacarte cuando ya hayas arreglado el problema.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46723, 'frFR', 'Dans le terrier dimensionnel', 'Entrez dans le Chapeau d’Akazamzarak et découvrez ce qui est arrivé à ses lapins.', 'Vous allez devoir entrer là-dedans pour voir ce qui ne tourne pas rond. J’irais bien moi-même, mais… c’est compliqué.\n\nBref, il n’y a pas une minute à perdre ! Sautez dans le chapeau et tirez ça au clair.\n\nJe me chargerai de vous sortir de là une fois que vous aurez réglé le problème.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46723, 'itIT', 'Nella tana del coniglio', 'Entra nel Cappello di Akazamzarak e scopri cosa sta succedendo ai conigli.', 'D\'accordo, $gamico:amica;, mi serve che tu vada a scoprire cosa c\'è che non va. Andrei di persona, ma... Be\', è complicato.\n\nComunque, non abbiamo tempo da perdere! Salta nel cappello e cerca di scoprire cosa sta succedendo.\n\nTi tirerò fuori di lì non appena avrai sistemato tutto.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46723, 'koKR', '토끼 굴 속으로', '아카잠자락의 모자 안에 들어가 토끼들에게 무슨 일이 벌어졌는지 알아봐야 합니다.', '좋아, 친구. 자네가 저 안에 들어가서 무슨 문제가 있는지 알아봐 주어야겠네. 내가 직접 가고 싶지만...음, 사연이 좀 복잡해.\n\n어쨌든, 시간이 없다고! 어서 저 모자 안에 들어가서 무슨 일이 벌어지고 있는지 살펴보게나.\n\n자네가 상황을 해결하고 나면 내가 밖으로 끄집어내 주겠네.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46723, 'ptBR', 'Na toca do coelho', 'Entre no Chapéu de Akazamzarak e descubra o que está havendo com os coelhos dele.', 'Beleza, cara. Vou precisar que você vá lá e descubra o que tá pegando. Eu até iria pessoalmente, mas... bom, é meio complicado.\n\nEnfim, não temos tempo a perder! Pule logo no chapéu e descubra o que tá rolando.\n\nEu vou estar a postos pra te puxar quando você conseguir controlar a situação.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46723, 'ruRU', 'В кроличью нору', 'Залезьте в шляпу Ахалаймахалая и выясните, что стряслось с его кроликами.', 'Короче, смотри. Тебе нужно будет отправиться туда и выяснить, что пошло не так. Я бы и сам пошел, но... в общем, долгая история.\n\nТак что не будем зря терять время! Прыгай в шляпу и выясни, что там творится.\n\nЯ тебя вытащу назад, как только ты там все устаканишь.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46723, 'zhCN', '进入兔子洞', '进入阿卡扎曼扎拉克的帽子，查出他的兔子发生了什么事。', '好了，小家伙。我需要你进去查清楚出了什么问题。我本来想自己进去的，不过……一言难尽。\n\n不管怎么说，我们没时间了！快跳进帽子，查出问题。\n\n你把事情处理妥当后，我就可以把你拉出来。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46723, 'zhTW', '進入兔子洞', '進入阿卡贊札拉克的帽子，看看他的兔子發生了什麼事。', '來吧，好傢伙。我得請你進去裡面，幫我看看出了什麼問題。我也想自己進去，但是…呃，一言難盡。\n\n總之，我們時間不多了！跳進帽子裡面，看看發生了什麼事。\n\n等你處理完後，我會把你拉出來。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46724, 'deDE', 'Champion: Der Große Akazamzarak', '', 'Wisst Ihr, es ist ziemlich nützlich, Euch in der Nähe zu haben, $n.\n\nIch sag Euch was: Ich glaube, ich werde Euch bei Euren Abenteuern begleiten. Man scheint dabei eine Menge Gold verdienen zu können und ich glaube, es wäre ein guter Weg, um sich nach ein paar neuen Geschäftsgelegenheiten auf den Verheerten Inseln umzusehen.\n\nWas meint Ihr?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46724, 'esES', 'Campeón: El Gran Akazamzarak', '', 'Eres $gun tipo:una tipa; muy $gválido:válida;, $n.\n\n¿Sabes qué? Creo que voy a acompañarte en tus aventuras. Parece que habrá oro de por medio, y me da que será una buena manera de explorar nuevas oportunidades de negocio en las Islas Abruptas.\n\n¿Qué me dices?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46724, 'esMX', 'Campeón: El Gran Akazamzarak', '', '¿Sabes qué? Eres $gun tipo:una dama; útil, $n. \n\nMira, creo que te acompañaré en tus aventuras. Me parece que podremos sacar bastante oro y creo que será una buena manera de explorar algunas oportunidades de negocios nuevos en las Islas Quebradas.\n\n¿Qué opinas?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46724, 'frFR', 'Champion : le grand Akazamzarak', '', 'En fait, c’est plutôt utile de vous avoir sous la main, $n.\n\nVous savez quoi ? Je crois que je vais vous accompagner dans vos aventures. On dirait qu’il y a de l’or à se faire et c’est l’occasion rêvée de développer mes activités dans les îles Brisées.\n\nQu’est-ce que vous en dites ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46724, 'itIT', 'Campione: Akazamzarak il Grande', '', 'È un piacere lavorare con te, $n.\n\nAnzi, sai che ti dico? Credo proprio che ti accompagnerò nelle tue avventure. Vedo un sacco di profitti all\'orizzonte, e credo sia una buona occasione per scoprire nuove opportunità di mercato sulle Isole Disperse.\n\nChe ne dici?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46724, 'koKR', '용사: 위대한 아카잠자락', '', '이보게, 자네는 같이 있으면 참 도움이 많이 되는 친구야, $n.\n\n들어보게. 내가 자네의 모험 길에 동참하면 어떻겠나. 돈 냄새가 나는 데다가, 부서진 섬을 돌아다니다 보면 새로운 사업 기회가 보일지도 모르니까.\n\n내 제안이 어떤가?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46724, 'ptBR', 'Campeão: O Grande Akazamzarak', '', 'Sabe, você é $gum cara:uma gata; bem útil pra se ter por perto, $n.\n\nTe digo mais, acho que vou acompanhar você nas suas aventuras. Parece que elas dão um bom ouro, e acho que vai ser um bom modo de ver se tem alguma oportunidade nova de negócio nas Ilhas Partidas.\n\nO que você me diz?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46724, 'ruRU', 'Защитник: Великий Ахалаймахалай', '', 'Знаешь, $gприятель:дамочка;, а ты мне нравишься. Думаю, от тебя мне будет толк.\n\nЯ, пожалуй, даже составлю тебе компанию в твоих приключениях. Внакладе я точно не останусь, да заодно и погляжу, какие еще выгодные предприятия можно организовать на Расколотых островах.\n\nЧто скажешь, $n?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46724, 'zhCN', '勇士：伟大的阿卡扎曼扎拉克', '', '有你这样一个$g小伙子:小姑娘;在身边真好，$n。\n\n我决定了！和你一起去冒险，看起来能赚大钱。而且也可以借这个机会在破碎群岛寻找新的生意机会。\n\n你没意见吧？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46724, 'zhTW', '勇士：魔術大師阿卡贊札拉克', '', '有你這$g傢伙:女士;在身邊真好，$n。\n\n這樣好了，我決定和你一同踏上冒險的旅途。似乎能賺大錢，在破碎海岸尋找新的商機是個不錯的方法。\n\n你覺得如何？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46725, 'deDE', 'Den Saft abgedreht', 'Füllt den Energiekern der Teufelshammer mit Teufelsenergie auf.', 'Kor\'vass bat noch um ein paar andere Dinge, aber ess würde ihr nicht schaden, ein wenig Geduld zu lernen. Meint Ihr nicht auch?\n\nSie erwähnte, dasss dass Waffenarsenal dess Schiffs schwer beschädigt isst und nur noch über gefährlich wenig Energie verfügt. Aber so, wie ich sie kenne, übertreibt sie bestimmt mal wieder.\n\nIch denke, Ihr könntet diesen Energiekern, den ich konfissziert habe, mitnehmen. Ihr könnt ihn an den widerlichen Legionssgeysiren und Energiekugeln wiederaufladen, die am Grabmal herumschweben.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46725, 'esES', 'Apagón', 'Rellena el núcleo de energía de El Martillo Vil con energía vil.', 'Kor\'vas hizo un pedido de otras cosas, pero no le vendrá mal aprender a tener algo de paciencia, ¿no crees?\n\nMencionó que el arsenal de armas de la nave está seriamente dañado y a un nivel de potencia peligrosamente bajo, aunque seguro que exagera.\n\nPero más vale que le lleves este núcleo de energía. Puedes recargarlo con esos repugnantes géiseres de la Legión y los globos de energía que flotan en torno a la Tumba.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46725, 'esMX', 'Apagón', 'Recarga el núcleo de poder de Martillo vil con energía vil.', 'Kor\'vass pidió unas cuantas cosas más, pero no le vendría mal aprender a tener un poco de paciencia, ¿no crees?\n\nMencionó que las armass de la nave están sseveramente dañadas y que su potencia está en un nivel peligrosamente bajo. Pero la conozco bien, y estoy ssegura que es una exageración.\n\nSupongo que puedes llevarte este núcleo de poder que confisqué. Puedes recargarlo con esos dessagradables géiseres y los globoss de energía de la Legión que flotan alrededor de la Tumba.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46725, 'frFR', 'En panne sèche', 'Rechargez la batterie du Marteau gangrené avec de l’énergie gangrenée.', 'Kor’vasss a demandé plusieurs autres choses, mais apprendre la patienssce ne lui fera pas de mal, n’est-ce pas ?\n\nÀ l’en croire, les armes du vaisseau sssont dans un état lamentable et elles ssseront bientôt à court d’énergie. Mais telle que je la connais, je parie qu’elle exagère.\n\nEnfin… Prenez quand même sscette batterie que j’ai confisssquée. Vous pourrez la recharger avec les immondes geysers et globes d’énergie de la Légion qui flottent autour de la tombe.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46725, 'itIT', 'Interruzione d\'energia', 'Ricarica il Nucleo Energetico della Vilmartello con energia vile.', 'Kor\'vas ha chiesssto anche delle altre cose, ma non le farà male avere un po\' di pazienza, non credi?\n\nHa anche detto che la batteria d\'armi della nave è gravemente danneggiata e pericolosamente a corto di energia, ma conoscendola, sono certa si tratti solo di un\'esagerazione.\n\nForse dovresssti portare con te quesssto Nucleo Energetico. Puoi ricaricarlo sfruttando quei disgussstosi Vilgeyser della Legione e i Globi di Energia Vile che si trovano intorno alla Tomba.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46725, 'koKR', '동력 부족', '지옥 망치호의 동력핵에 지옥 마력을 충전시켜야 합니다.', '코르바스는 엄청나게 많은 일들을 요청했습니다만, 이번 기회에 인내심을 길러 보는 것도 나쁘지 않을 겁니다. 안 그렇나요?\n\n코르바스가 하나 이야기한 것이 있는데, 함선의 무기들이 심각하게 손상되어 동력이 위험할 정도로 부족하다는군요. 제가 그 친구를 아는데, 분명히 과장이 좀 들어갔을 겁니다.\n\n제가 빼앗은 이 동력핵을 받으십시오. 저 역겨운 군단 간헐천과 무덤 주위에 떠다니는 마력 수정구를 통해 동력핵을 충전할 수 있을 겁니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46725, 'ptBR', 'Falta de energia', 'Reabasteça o Núcleo de Força do Martelo Vil com energia vil.', 'Kor\'vasss pediu um monte de outras coisas, mas ela bem poderia aprender a ter um pouco de paciência. Não concorda?\n\nEla também mencionou que as armasss do navio foram bastante danificadas e estão com pouquíssima energia. Como eu a conheço, tenho certeza de que é exagero.\n\nAcho que, de qualquer forma, você podia pegar esse núcleo de força que eu confisquei. Você pode reabassstecê-lo com os globos de energia e gêiseres vis nojentos que estão flutuando pela Tumba.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46725, 'ruRU', 'Сбой энергоснабжения', 'Зарядите энергией Скверны сердечник \"Молота Скверны\".', 'Кор\'вас-с запрос-сила целую кучу вс-сего, но, думаю, ей не помешает поучиться терпению. Как ты с-считаешь?\n\nА еще она с-сказала, что орудия корабля с-серьезно повреждены и запас-с энергии критически низок. Наверняка преувеличивает, как вс-с-сегда.\n\nНо, впрочем, можешь взять этот с-сердечник. Зарядить его энергией можно в гейзерах Легиона и энергетичес-с-ских с-сферах, парящих вокруг Гробницы.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46725, 'zhCN', '能量不足', '用邪能给邪能之槌号的动力核心补充能量。', '考瓦斯还需要一大堆别的东西，但是让她多等等也没什么坏处。你觉得呢？\n\n她还说，飞船的武器阵列受到了严重伤害，能量已经低到危险的程度。但是我对她再了解不过了，我觉得这是在夸大其词。\n\n不过我觉得你最好还是拿上这颗我收缴的动力核心。你可以利用墓穴附近那些恶心的军团喷泉和能量球为核心补充能量。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46725, 'zhTW', '能量短缺', '讓魔錘號的能量核心重新充滿魔化能量。', '珂娃絲又有一堆其他的要求，不過讓她學著有點耐心無傷大雅。你不覺得嗎？\n\n她是有提到船上的武器系統嚴重受損，動力也快消耗殆盡。我敢說是她一定誇大了事實。\n\n不過你還是帶著這個我之前扣留的能量核心吧。你可以用燃燒軍團噁心的魔化噴泉和飄浮在古墓周圍的能量球體補充它的能量。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46730, 'deDE', 'Die Legionsrichter', 'Begleitet Erzmagier Khadgar an Krasus\' Landeplatz in Dalaran.', 'Unter Eurer Führung ist unser Orden mächtiger geworden, als ich je zu hoffen gewagt hatte. Gemeinsam mit den Champions der anderen Orden haben wir jetzt eine Streitmacht, die die Brennende Legion aus unserer Welt vertreiben kann.\n\nZeit, dieser Invasion ein Ende zu setzen! Wir werden die Säulen der Schöpfung nutzen, um den Teufelssturm aufzulösen und das Grabmal des Sargeras zu versiegeln.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46730, 'esES', 'El Ocaso de la Legión', 'Escolta al archimago Khadgar en el Alto de Krasus, en Dalaran.', 'Gracias a tu liderazgo, $c, tu orden se ha hecho aún más poderosa de lo que yo me había atrevido a esperar. Junto a campeones de las otras órdenes, ahora tenemos una fuerza de combate capaz de expulsar a la Legión Ardiente de nuestro mundo.\n\n¡Es hora de acabar con esta invasión! Si usamos los Pilares de la Creación, disiparemos la tormenta vil y sellaremos la Tumba de Sargeras.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46730, 'esMX', 'Ejércitos de Legión caída', 'Acompaña al Archimago Khadgar en el Alto de Krasus en Dalaran.', 'Gracias a tu capacidad de liderazgo, la orden de $c se ha vuelto mucho más fuerte de lo que yo había imaginado. Junto a los campeones de las demás órdenes, ahora tenemos una fuerza de lucha capaz de expulsar a la Legión ardiente de nuestro mundo.\n\n¡Es hora de ponerle fin a esta invasión! Usando los Pilares de la creación, disiparemos la Tormenta vil y sellaremos la Tumba de Sargeras.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46730, 'frFR', 'Les armées du Déclin de la Légion', 'Escortez l’Archimage Khadgar jusqu’à l’Aire de Krasus, à Dalaran.', 'Sous votre direction, $c, notre ordre est devenu encore plus puissant que je n’aurais jamais osé l’imaginer. Avec les champions des autres ordres, nous possédons désormais une force de combat capable de repousser la Légion ardente de notre monde.\n\nL’heure est venue de mettre un terme à cette invasion ! Avec l’aide des piliers de la Création, nous allons pouvoir dissiper le gangrorage et condamner la tombe de Sargeras !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46730, 'itIT', 'Armata Spezzalegione', 'Scorta l\'Arcimago Khadgar sulla Terrazza di Krasus a Dalaran.', 'Grazie alla tua guida, il tuo ordine è diventato ancora più forte di quanto sperassi. Insieme ai campioni degli altri ordini, ora abbiamo un\'armata capace di scacciare la Legione Infuocata dal nostro mondo.\n\nÈ ora di porre fine a questa invasione! Usando i Pilastri della Creazione, dissiperemo la Viltempesta e sigilleremo la Tomba di Sargeras.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46730, 'koKR', '군단척결군', '달라란의 크라서스 착륙장에서 대마법사 카드가를 호위해야 합니다.', '당신 덕분에 $c 연맹은 내가 감히 바랐던 것보다 더욱 강해졌소. 다른 연맹의 용사들과 함께하여, 우리는 이제 불타는 군단을 우리 세계에서 몰아낼 힘을 갖추게 되었소.\n\n이제 이 침공의 끝을 볼 시간이 왔소! 창조의 근원을 사용하여, 지옥폭풍을 소멸시키고 살게라스의 무덤을 봉인해야 하오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46730, 'ptBR', 'Exércitos da Queda da Legião', 'Escolte o Arquimago Hadggar na Plataforma de Krasus, em Dalaran.', 'Graças à sua liderança, $c, sua ordem ficou muito mais forte do que eu achava possível. Agora, junto com os campeões das outras ordens, temos um exército capaz de expulsar a Legião Ardente do nosso mundo.\n\nÉ hora de dar fim a essa invasão! Com os Pilares da Criação, vamos dissipar a Tempestavil e selar a Tumba de Sargeras.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46730, 'ruRU', 'Армия Погибели Легиона', 'Сопроводите верховного мага Кадгара на Площадку Краса в Даларане.', '$n, твой орден достиг невероятного могущества. Собрав защитников из разных орденов, мы стали той силой, которая действительно может изгнать Пылающий Легион из нашего мира.\n\nПора покончить с этим вторжением! С помощью Столпов Созидания мы разгоним бурю Скверны и запечатаем гробницу Саргераса.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46730, 'zhCN', '抗魔联军', '前往达拉然的克拉苏斯平台，陪同大法师卡德加。', '多亏了你的英明领导，$c的组织已经成长壮大，甚至远超我的想象。再加上其他职业的勇士帮忙，我们已经有了足够的力量，能把燃烧军团赶出我们的世界了。\n\n是时候终结这场入侵了！我们可以使用创世之柱，驱散邪能风暴，并且封印萨格拉斯之墓。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46730, 'zhTW', '軍團之殞部隊', '在達拉然的卡薩斯平臺護送卡德加。', '在你的領導之下，$c組織遠比我預期更加強大。現在又有其他職業幫忙，應該有辦法把燃燒軍團趕出艾澤拉斯了。\n\n該結束這場戰爭了！我們可以用創世之柱消滅魔化風暴，封印薩格拉斯之墓。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46732, 'deDE', 'Der Schachzug des Propheten', 'Folgt Prophet Velen.', 'Ich spüre ihre Gegenwart gleich da vorn. Es ist nur ein ganz leiser Hauch von Wahrnehmung, aber ich kann mit Sicherheit sagen, dass es sich nicht um die Legion handelt.$B$BWir müssen herausfinden, ob diese Zerschlagenen potenzielle Verbündete sind. Es ist ein großes Risiko, aber unsere Ränge sind so dünn, dass wir diese Chance nutzen müssen.', '', '', '', '', '', 'Sprecht mit Prophet Velen im Krokullager.', 0);
INSERT INTO `quest_template_locale` VALUES (46732, 'esES', 'La táctica del profeta', 'Sigue al profeta Velen.', 'Noto su presencia ahí delante. Es tan tenue como la más ligera de las brisas que una vez soplara en estas colinas, pero no pertenece a la Legión, de eso no hay duda.$b$bDebemos averiguar si estos tábidos podrían llegar a convertirse en aliados. Es un gran riesgo, pero somos demasiado pocos como para desaprovechar la oportunidad.', '', '', '', '', '', 'Habla con: Profeta Velen. Zona: Recoveco Krokul.', 0);
INSERT INTO `quest_template_locale` VALUES (46732, 'esMX', 'La táctica del Profeta', 'Sigue al Profeta Velen.', 'Percibo su presencia adelante. Es débil como la brisa más ligera que alguna vez surcó estas colinas, pero no tiene nada que ver con la Legión, de eso no tengo dudas.$B$BTenemos que descubrir si estos tábidos son potenciales aliados. Es un riesgo muy grande, pero nuestras tropas son demasiado escasas como ignorar esta oportunidad.', '', '', '', '', '', 'Habla con el Profeta Velen en el Refugio krokul.', 0);
INSERT INTO `quest_template_locale` VALUES (46732, 'frFR', 'Le stratagème du prophète', 'Suivez le Prophète Velen.', 'Je sens leur présence droit devant nous. L’aura qui s’en dégage est aussi ténue que la brise qui soufflait autrefois sur ces collines, mais une chose est sûre : il ne s’agit pas de la Légion.$B$BÀ nous de voir si ces Roués peuvent devenir nos alliés. Le risque est considérable, mais nous sommes trop peu nombreux pour passer à côté d’une telle occasion.', '', '', '', '', '', 'Parlez au Prophète Velen au Taudis krokul.', 0);
INSERT INTO `quest_template_locale` VALUES (46732, 'itIT', 'La scommessa del Profeta', 'Segui il Profeta Velen.', 'Percepisco la loro presenza poco più avanti: è debole quanto la leggera brezza che un tempo soffiava su queste colline, ma sono certo non si tratti della Legione.$B$BDobbiamo scoprire se questi Corrotti sono potenziali alleati. Si tratta di un grosso rischio, ma il nostro numero è troppo ridotto per non provarci.', '', '', '', '', '', 'Parla con il Profeta Velen nel Tugurio dei Krokul.', 0);
INSERT INTO `quest_template_locale` VALUES (46732, 'koKR', '예언자의 한 수', '예언자 벨렌을 따라가야 합니다.', '바로 앞에서 그들의 존재가 느껴지네. 한때 이 언덕에 불었던 옅은 바람처럼 희미하지만, 확실히 군단의 것은 아닐세.$B$B이 뒤틀린 자들이 앞으로 동맹군이 되어줄지 확인해야 하네. 굉장히 위험한 일이지만 이런 기회를 그냥 보내기엔 우리 수가 너무나 적다네.', '', '', '', '', '', '크로쿨 거처에 있는 예언자 벨렌과 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (46732, 'ptBR', 'O truque do profeta', 'Siga o Profeta Velen.', 'Posso sentir a presença deles mais à frente. É suave como a brisa que soprava por essas colinas, mas tenho certeza que não é da Legião.$B$BTemos que descobrir se esses degradados podem ser nossos aliados. É um risco muito grande, mas estamos em menor número... não podemos perder essa chance.', '', '', '', '', '', 'Falar com o Profeta Velen no Enclave Krokul.', 0);
INSERT INTO `quest_template_locale` VALUES (46732, 'ruRU', 'Гамбит пророка', 'Следуйте за пророком Веленом.', 'Я чувствую их присутствие. Оно подобно легчайшему ветерку, что когда-то обдувал эти холмы, и исходит не от Легиона.$B$BВозможно, эти сломленные – наши потенциальные союзники. Конечно, это большой риск, но нас слишком мало, и такой шанс упускать нельзя.', '', '', '', '', '', 'Поговорите с пророком Веленом в пристанище крокулов.', 0);
INSERT INTO `quest_template_locale` VALUES (46732, 'zhCN', '先知的策略', '跟随先知维伦。', '我可以感觉到他们就在我们前方。气息很轻微，就像曾经吹拂在这些山丘上的微风，但我很肯定，它绝不是军团的气息。$B$B我们必须了解这些破碎者是否可以作为潜在的盟友。虽然此举风险很大，但我们的兵力不够，不能对这个机遇坐视不管。', '', '', '', '', '', '与克罗库营地的先知维伦谈一谈。', 0);
INSERT INTO `quest_template_locale` VALUES (46732, 'zhTW', '預言者的賭注', '跟隨預言者費倫。', '我能感覺到他們就在前面，雖然輕得就像拂過山丘的微風，但我可以確定這絕對不是燃燒軍團。$B$B我們必須弄清楚破碎者能不能成為盟友。這是很大的風險，可是我們的戰力太薄弱，任何機會都要把握。', '', '', '', '', '', '在克庫營地與預言者費倫交談。', 0);
INSERT INTO `quest_template_locale` VALUES (46734, 'deDE', 'Angriff auf die Verheerte Küste', 'Schließt das Szenario \"Angriff auf die Verheerte Küste\" ab.', 'Das hier war ein langer und verlustreicher Krieg, $n. Zeit, ihn zu Ende zu bringen.\n\nWir werden die Brennende Legion aus ihrem Machtzentrum vertreiben und ihr Portal nach Azeroth schließen. Als Erstes müssen wir eine Position an der Verheerten Küste sichern.\n\nAls Gegner waren Allianz und Horde dem Untergang geweiht. Doch als Legionsrichter vereint werden wir siegreich sein!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46734, 'esES', 'Asalto a la Costa Abrupta', 'Completa la gesta \"Asalto a la Costa Abrupta\".', 'Ha sido una guerra larga y costosa, $n. Es hora de ponerle fin.\n\nExpulsaremos a la Legión Ardiente de su sede de poder y le cerraremos las puertas de Azeroth. Nuestro primer paso es tomar posiciones en la Costa Abrupta.\n\nDivididas, la Alianza y la Horda cayeron derrotadas, pero unidas como los Ejércitos del Ocaso de la Legión, ¡lograremos la victoria!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46734, 'esMX', 'Asalto a la Costa Quebrada', 'Completa el asalto en la gesta de la Costa Quebrada.', 'Ha sido una guerra larga y costosa, $n. Llegó la hora de ponerle fin.\n\nExpulsaremos a la Legión Ardiente del poder y cerraremos su portal hacia Azeroth. El primer paso será ganar una posición en la Costa Quebrada.\n\nDivididas, la Alianza y la Horda estaban destinadas una derrota segura; pero unidas como los Ejércitos de Legión caída, ¡nos alzaremos con la victoria!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46734, 'frFR', 'L’assaut du rivage Brisé', 'Terminez le scénario « L’assaut du rivage Brisé ».', 'Cette guerre est longue et coûteuse, $n. Il est temps d’y mettre un terme.\n\nNous allons expulser la Légion ardente du siège de son pouvoir et fermer son portail vers Azeroth. Pour cela, il nous faut tout d’abord une tête de pont sur le rivage Brisé.\n\nDivisées, l’Alliance et la Horde ont échoué. Mais réunis sous la bannière du Déclin de la Légion, nous serons victorieux !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46734, 'itIT', 'Assalto alla Riva Dispersa', 'Completa lo scenario \"Assalto alla Riva Dispersa\".', 'È stata una guerra lunga ed estenuante, $n, ed è ora che finisca.\n\nScacceremo la Legione Infuocata e chiuderemo i varchi in tutta Azeroth. Il primo passo da compiere è stabilire il controllo della Riva Dispersa.\n\nDivise, l\'Alleanza e l\'Orda hanno fallito, ma ora che sono finalmente riunite nell\'Armata Spezzalegione, la vittoria sarà nostra!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46734, 'koKR', '부서진 해변 공격', '부서진 해변 공격 시나리오를 완료해야 합니다.', '정말이지 길고 아픈 전쟁이었소, $n. 이제 끝장을 냅시다.\n\n우리는 불타는 군단을 놈들의 거점에서 몰아내고, 아제로스로 통하는 관문을 닫을 것이오. 그걸 위해 가장 먼저 해야 할 일은, 부서진 해변에 거점을 구축하는 것이고.\n\n분열하면 얼라이언스와 호드는 패배할 것이오. 하지만 군단척결군으로 하나가 되면, 우리는 승리할 것이오!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46734, 'ptBR', 'Ataque à Costa Partida', 'Conclua o cenário Ataque à Costa Partida.', 'Foi uma guerra longa e custosa, $n. É hora de darmos fim nela.\n\nVamos expulsar a Legião Ardente do trono e fechar o portal por onde eles entram em Azeroth. O primeiro passo é nos estabelecermos na Costa Partida.\n\nDivididas, a Aliança e a Horda foram derrotadas. Mas unidos nos Exércitos da Queda da Legião, nós conquistaremos a vitória!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46734, 'ruRU', 'Штурм Расколотого берега', 'Завершите сценарий \"Штурм Расколотого берега\".', 'Эта война была долгой и кровопролитной, $n. Пора положить ей конец.\n\nМы лишим Пылающий Легион источника силы и закроем портал в Азерот. Сначала нам нужно закрепиться на Расколотом берегу.\n\nПорознь и Альянс, и Орда потерпели поражение. Но объединившись в единую армию, Армию погибели Легиона, мы добьемся победы!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46734, 'zhCN', '突袭破碎海滩', '完成突袭破碎海滩场景战役。', '这场战争旷日持久，而且代价高昂，$n。是时候为它画上休止符了。\n\n我们会把燃烧军团赶下宝座，并且关闭他们进入艾泽拉斯的通路。而我们的第一步，就是要在破碎海滩上站稳脚跟。\n\n联盟和部落原本各自为战，结果都失败了。但是我们团结一心，组成了抗魔联军，胜利必将属于我们！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46734, 'zhTW', '突襲破碎海岸', '完成突襲破碎海岸事件。', '$n，這場痛苦漫長的戰爭該結束了。\n\n我們會擊退燃燒軍團，並讓它們再也沒辦法踏上艾澤拉斯。但要做到這些事，首先得在破碎海岸取得據點。\n\n聯盟、部落因為分裂，最後失敗了，但團結一心的軍團之殞部隊一定會成功！', '', '', '', '', '', '', 0);
