INSERT INTO `quest_template_locale` VALUES (46773, 'ruRU', 'Ставка командования', 'Завершите строительство ставки командования.', 'Отлично, $n. Мы собрались здесь, чтобы завершить строительство ставки командования, что ознаменует наше единство в борьбе с Легионом.\n\nТебе осталось только осмотреть результаты наших трудов и завершить строительство.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46773, 'zhCN', '指挥中心', '完成指挥中心的建造。', '干得好，$n。在所有在此奋战的人们共同的努力下，我们携手完成了指挥中心的建造。\n\n你现在只需要检查一下我们的杰作，并完成建造即可。', '', '', '', '', '', '', 0);
