INSERT INTO `quest_template_locale` VALUES (46777, 'deDE', 'Kriegsbeute der Legionsrichter', 'Sprecht mit Hauptmann Ruysantos an der Verheerten Küste, um Eure Belohnung zu erhalten.', '$ct, danke für Eure Mühen. Unsere Truppen konnten dadurch weitere Ressourcen auf dem Schlachtfeld sicherstellen.\n\nJe deutlicher Ihr das Schlachtfeld beherrscht, desto mehr Ressourcen können unsere Truppen erübrigen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46777, 'esES', 'Las recompensas del Ocaso de la Legión', 'Habla con el capitán Ruysantos para recibir tu recompensa. Zona: Costa Abrupta.', '$ct, gracias a tus esfuerzos, nuestras fuerzas han podido obtener recursos adicionales del campo de batalla.\n\nCuanto mayor sea tu dominio del campo de batalla, mayores serán los recursos que podremos compartir contigo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46777, 'esMX', 'Los botines de Legión caída', 'Habla con el capitán Ruysantos en la Costa Quebrada para recoger tu recompensa.', '$ct, gracias a tus esfuerzos, nuestras fuerzas pudieron conseguir más recursos del campo de batalla.\n\nCuanto más extenso sea tu dominio del campo de batalla, más recursos tendrán nuestras fuerzas a disposición.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46777, 'frFR', 'Les butins du Déclin', 'Parlez au Capitaine Ruysantos, au Rivage Brisé pour récupérer votre récompense.', '$ct, grâce à vos efforts, nos troupes ont pu se procurer des ressources supplémentaires sur le champ de bataille.\n\nPlus vous imposerez votre suprématie au combat, plus nous pourrons économiser de matériel à votre intention.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46777, 'itIT', 'Le taglie degli Spezzalegione', 'Parla con il Capitano Ruysantos alla Riva Dispersa per ottenere la tua ricompensa.', '$Ct, ti ringrazio per i tuoi sforzi. I nostri soldati riusciranno a mettere al sicuro altri rifornimenti dal campo di battaglia.\n\nPiù a lungo dominerai il campo di battaglia, più risorse riusciranno a trovare le nostre forze.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46777, 'koKR', '군단척결군의 전리품', '부서진 해변에 있는 대장 루이산토스와 대화해서 보상을 받아야 합니다.', '$ct, 그대의 노력 덕분에 우리 군대가 전장에서 추가로 물자를 확보할 수 있었소.\n\n전장을 계속 이렇게 휩쓸어 준다면 우리 군대가 더 많은 자원을 모아 줄 수 있을 거요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46777, 'ptBR', 'As recompensas da Queda da Legião', 'Fale com o Capitão Ruysantos na Costa Partida para receber sua recompensa.', '$ct, graças aos seus esforços, nossas tropas conseguiram obter recursos adicionais no campo de batalha.\n\nQuanto mais você dominar o campo de batalha, mais recursos nossas tropas poderão doar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46777, 'ruRU', 'Награда Армии погибели Легиона', 'Поговорите с капитаном Руисантосом на Расколотом берегу, чтобы получить награду.', '$ct, благодаря твоей помощи мы смогли собрать на поле боя дополнительные ресурсы.\n\nЧем больше побед ты одержишь, тем больше ресурсов мы сможем выделить.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46777, 'zhCN', '抗魔联军的馈赠', '与破碎海滩的瑞桑托斯上尉谈一谈，获取你的奖励。', '$ct，多亏了你的努力，我们的军队从战场上成功获得了额外的资源。\n\n只要你继续在战场上大杀四方，我们的军队就有更多的资源可以使用。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46777, 'zhTW', '軍團之殞的饋贈', '到破碎海岸與路易桑多斯隊長交談，取得你的獎勵。', '$ct，因為你努力不懈，讓我們的部隊能從戰場取得額外資源。\n\n只要你繼續在戰場上立功，我們的部隊就能分出更多資源給你。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46778, 'deDE', 'Weiterentwicklung', 'Sucht Einar den Runenmagier auf.', 'Während der Krieg gegen unsere Feinde voranschreitet, müssen auch wir uns weiterentwickeln.\n\nWir können jetzt neue Forschungen betreiben. Wendet Euch an Einar den Runenmagier, um die Ordenshalle zu verbessern.', '', '', '', '', '', '', 0);
