INSERT INTO `quest_template_locale` VALUES (46791, 'itIT', 'Sulle ali del vento', 'Torna nel Maelstrom e parla con il Consigliere Celestos.', 'Chiaroveggente $p, il Consigliere Celestos ha richiesto la tua presenza nel Maelstrom. Va\' da lui appena possibile.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46791, 'koKR', '바람에 실려', '혼돈의 소용돌이로 돌아가 집정관 셀레스토스와 대화해야 합니다.', '선견자 $p 님, 집정관 셀레스토스가 혼돈의 소용돌이로 돌아와 달라는 전갈을 보냈습니다. 빠른 시일 내에 그를 찾아가 주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46791, 'ptBR', 'Levado pelo vento', 'Volte à Voragem e fale com o Cônsul Celestos.', 'Clarividente $p, o Cônsul Celestos solicitou a sua presença na Voragem. Por favor, encontre-se com ele assim que for possível.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46791, 'ruRU', 'На крыльях ветра', 'Вернитесь к Водовороту и поговорите с консулом Селестом.', '$gПредсказатель:Предсказательница; $p, консул Селест хочет поговорить с тобой у Водоворота. Пожалуйста, встреться с ним при первой же возможности.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46791, 'zhCN', '乘风而行', '返回大漩涡，并与赛莱托斯大使谈一谈。', '先知$p，赛莱托斯大使请你返回大漩涡。请尽快与他会面。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46791, 'zhTW', '乘風而起', '回到大漩渦與賽勒斯多領事交談。', '先知$p，賽勒斯多領事請你回大漩渦一趟。請你盡快去見他一面。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46792, 'deDE', 'Zorn der Stürme', 'Sprecht mit Konsul Celestos, wenn Ihr bereit seid, das Szenario \"Zorn der Stürme\" zu beginnen. Schließt das Szenario ab und kehrt dann zu Celestos zurück.', 'Donneraan hat mich gebeten, Euch zum Thron der Vier Winde zu entsenden, weil er Eure Hilfe in einer bestimmten Angelegenheit benötigt.$b$bSprecht mich einfach an, wenn Ihr bereit seid aufzubrechen, $gScharfseher:Scharfseherin;.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46792, 'esES', 'Concentración de tormentas', 'Habla con el cónsul Celestos para comenzar la gesta \"Concentración de tormentas\". Completa la gesta y ve a ver a Celestos.', 'Thunderaan me ha pedido que te envíe al Trono de los Cuatro Vientos para que lo ayudes con un problema.$B$BVuelve a verme cuando estés $glisto:lista; para partir, Clarividente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46792, 'esMX', 'Acumulación de las tormentas', 'Habla con Consular Celestos para comenzar la gesta Acumulación de las tormentas. Completa la gesta y regresa con Celestos.', 'Thunderaan me pidió que te envíe al Trono de los Cuatro Vientos para ayudarlo con un asunto.$b$bVuelve a hablarme cuando estés $Glisto:lista; para partir, Clarividente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46792, 'frFR', 'Rassemblement des tempêtes', 'Parlez au Consulaire Célestos pour lancer le scénario « Rassemblement des tempêtes ». Terminez le scénario, puis retournez voir Célestos.', 'Tonneraan m’a demandé de vous dépêcher au Trône des quatre vents pour l’aider à résoudre une affaire pressante.$b$bRevenez me voir quand vous serez $gprêt:prête; à partir, $glong-voyant:long-voyante;.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46792, 'itIT', 'Tempesta incombente', 'Parla con il Consigliere Celestos per iniziare lo scenario \"Tempesta incombente\". Completa lo scenario e torna da Celestos.', 'Tuonelaar mi ha chiesto di mandarti al Trono dei Quattro Venti per aiutarlo con un problema.$b$bParla con me quando sarai $gpronto:pronta; a partire, Chiaroveggente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46792, 'koKR', '모여드는 폭풍', '집정관 셀레스토스와 대화하여 모여드는 폭풍 시나리오를 시작해야 합니다. 시나리오를 완료한 후에 셀레스토스에게 돌아오십시오.', '썬더란 님께서 널 네 바람의 왕좌로 보내 달라고 하셨다. 네 도움을 받을 일이 있다고 하신다.$b$b떠날 준비가 되거든 내게 말해라, 선견자여.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46792, 'ptBR', 'Junção das tempestades', 'Fale com o Cônsul Celestos para iniciar o cenário Junção das Tempestades. Conclua o cenário e volte até Celestos.', 'Trovejardus me pediu que eu lhe enviasse ao Trono dos Quatro Ventos para ajudá-lo com um problema.$b$bFale comigo novamente quando você estiver pront$go:a; para partir, Clarividente.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46792, 'ruRU', 'Надвигающийся шторм', 'Поговорите с консулом Селестом, чтобы начать сценарий \"Надвигающийся шторм\". По завершении сценария вернитесь к Селесту.', 'Громораан просил направить тебя к трону Четырех Ветров, ему нужна помощь в одном деле.$b$bКогда будешь $gготов:готова; отправиться в путь, скажи мне.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46792, 'zhCN', '风暴汇聚', '与赛莱托斯大使谈一谈，开始“风暴汇聚”场景战役。完成场景战役后向赛莱托斯复命。', '桑德兰让我请你去风神王座，协助他处理问题。$b$b你准备好离开的话就跟我谈一谈，先知。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46792, 'zhTW', '風暴交會', '與賽勒斯多領事交談以開啟風暴交會事件。完成事件並向賽勒斯多回報。', '桑德蘭請我通知你去四風王座，幫他解決困境。$b$b先知，等你準備好要出發再跟我說一聲。', '', '', '', '', '', '', 0);
