INSERT INTO `quest_template_locale` VALUES (46827, 'deDE', 'Mit den Schatten verschmelzen', 'Kehrt zur Halle der Schatten zurück und sucht Lord Jorach Rabenholdt auf.', 'Wir sollten uns wieder dem Rest des Rats anschließen und ihnen berichten, was wir an diesem Tag erreicht haben. Kehrt zu uns zurück, wann immer es Euch beliebt. Wir werden auf Euch warten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46827, 'esES', 'Fundirse con las sombras', 'Vuelve a la Morada de las Sombras y reúnete con lord Jorach Ravenholdt.', 'Deberíamos reunirnos con el resto del consejo y contarles lo que hemos conseguido hoy. Vuelve con nosotros cuando te venga bien. Te estaremos esperando.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46827, 'esMX', 'Al amparo de las sombras', 'Regresa al Salón de las Sombras y reúnete con Lord Jorach Ravenholdt.', 'Deberíamos volver a encontrarnos con el resto del consejo para contarles lo que hemos conseguido el día de hoy. Cuando lo creas conveniente, vuelve con nosotros. Te estaremos esperando.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46827, 'frFR', 'Se fondre dans l’ombre', 'Retournez dans la Salle des Ombres et parlez au Seigneur Jorach Ravenholdt.', 'Nous ferions mieux d’aller informer le reste du conseil de ce que nous avons accompli aujourd’hui. Retournez-y quand vous le pourrez. Nous vous attendrons.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46827, 'itIT', 'Infiltrarsi tra le ombre', 'Torna alla Dimora delle Ombre e incontra Ser Jorach Corvolesto.', 'Dovremmo raggiungere il resto del Concilio e fare rapporto su ciò che abbiamo compiuto oggi. Torna da noi quando vorrai, ti aspetteremo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46827, 'koKR', '어둠 속으로 스며들어', '그림자의 전당으로 돌아가서 조라크 라벤홀트 경을 만나야 합니다.', '이제 의회의 나머지를 만나 오늘 우리가 이뤄낸 일에 대해 알려 줘야 합니다. 편하신 때 저희를 찾아오십시오. 기다리고 있겠습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46827, 'ptBR', 'Mesclar-se às sombras', 'Retorne ao Salão das Sombras e encontre Lorde Jorach Corvoforte.', 'Nós devemos nos encontrar com o resto do Concílio e contar o que conseguimos fazer hoje. Quando puder, volte para nós. Estaremos esperando.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46827, 'ruRU', 'Слейся с тенью', 'Вернитесь в Оплот Теней и встретьтесь с лордом Джорахом Черным Вороном.', 'Нам нужно собраться с остальными членами Совета и рассказать им о наших сегодняшних достижениях. Заглядывай, как будет время. Мы будем ждать.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46827, 'zhCN', '融入暗影', '回到暗影大厅，与乔拉齐·拉文霍德公爵见面。', '我们应该回去与议会的其他成员见面，告诉他们我们今天的战绩。你有空时回来一下吧。我们会等你的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46827, 'zhTW', '融入暗影', '回到潛影之廳並與喬拉齊‧拉文霍德領主碰面。', '我們應該回去找議會的其他人，告知我們今天的成功。潛影議會隨時恭候你回來。', '', '', '', '', '', '', 0);
