INSERT INTO `quest_template_locale` VALUES (46924, 'deDE', 'Die Geschichte des Wolfs', 'Findet Goldrinn außerhalb von Sturmgrimms Grabhügel.', 'Goldrinn hat wirklich keine Zeit verstreichen lassen, bevor er von hier abgehauen ist, was?$b$bSchauen wir mal, ob wir ihn draußen finden können.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46924, 'esES', 'La historia del lobo', 'Encuentra a Goldrinn en el exterior del Túmulo de Tempestira.', 'Goldrinn no ha tardado nada en salir corriendo de aquí, ¿verdad?$b$bVamos a ver si lo encontramos en el exterior.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46924, 'esMX', 'El cuento del lobo', 'Encuentra a Goldrinn afuera del Túmulo de Tempestira.', 'Goldrinn no perdió tiempo en irse de aquí, ¿verdad?$b$bVeamos si podemos encontrarlo afuera.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46924, 'frFR', 'Le récit du loup', 'Trouvez Goldrinn devant le Refuge des saisons de Malfurion.', 'Goldrinn n’a vraiment pas perdu de temps pour sortir d’ici, pas vrai ?$B$BVoyons si nous pouvons le trouver à l’extérieur.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46924, 'itIT', 'Il racconto del lupo', 'Trova Goldrinn fuori dagli Eremi di Grantempesta.', 'Goldrinn non ci ha messo molto per fuggire da questo posto, eh?$b$bAndiamo a vedere se lo troviamo fuori.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46924, 'koKR', '늑대의 이야기', '스톰레이지 지하굴에서 골드린을 찾아야 합니다.', '골드린 님이 어느새 여길 빠져나가셨군요.$B$B밖에서 골드린 님을 찾아보도록 하죠.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46924, 'ptBR', 'O conto do lobo', 'Encontre Goldrinn perto dos Templos de Tempesfúria.', 'Goldrinn nem precisou se esforçar para sair de lá, não foi?$b$bVamos ver se o encontramos do lado de fora.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46924, 'ruRU', 'История о волке', 'Найдите Голдринна у келий Малфуриона.', 'Быстро же Голдринн отсюда выбрался, да?$b$bПойдем поищем его наверху.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (46924, 'zhCN', '孤狼传说', '在怒风兽穴外面找到戈德林。', '戈德林没花什么时间就从这里离开了，是吗？$b$b我们看看他在不在外面。', '', '', '', '', '', '', 0);
