INSERT INTO `quest_template_locale` VALUES (47118, 'koKR', '살게라스의 무덤: 고통받는 존재의 해방', '살게라스의 무덤에서 황폐의 숙주를 처치해야 합니다.', '무덤 안의 영혼들이 공포스러운 기계 때문에 뒤틀리고 있습니다. 영혼의 기계를 파괴하고, 영혼들을 이 고통에서 해방시켜 주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (47118, 'ptBR', 'Tumba de Sargeras: Liberdade aos atormentados', 'Derrote o Anfitrião Desolado na Tumba de Sargeras.', 'Um Engenho do Terror corrompe as almas dentro da Tumba. Acabe com ele para libertá-las desse tormento.', '', '', '', '', '', '', 0);
