INSERT INTO `quest_template_locale` VALUES (47122, 'itIT', 'Tomba di Sargeras: L\'Avatar Caduto', 'Sconfiggi l\'Avatar Caduto nella Tomba di Sargeras.', 'L\'Avatar Caduto è l\'ultimo ostacolo che ci separa dalla battaglia finale. Uccidilo e facciamola finita.', '', '', '', '', '', '', 0);
